INSERT INTO `creature_text` VALUES (99267, 0, 0, 'Она точно была тут. Ищем дальше!', 12, 0, 100, 1, 0, 67554, 100003, 0, 0, 0, 'Nightborne Scion to Player');
INSERT INTO `creature_text` VALUES (99267, 1, 0, 'Die, outlander!', 12, 0, 100, 0, 0, 65820, 116708, 0, 0, 0, 'Nightborne Scion to Player');
