INSERT INTO `creature_text` VALUES (99313, 0, 0, 'Greetings, $n. I trust this projection is clear! This is Modera, Archmage of the Council of Six. I\'ve come across a lead on a weapon that could significantly aid your fight against the Legion.', 12, 0, 100, 0, 0, 0, 100946, 0, 0, 0, 'Проекция верховного мага Модеры to Player');
INSERT INTO `creature_text` VALUES (99313, 1, 0, 'My source is... well... unable to join us within the city. I ask that you meet with me at the crater of Dalaran\'s foundation in Hillsbrad.', 12, 0, 100, 0, 0, 0, 100947, 0, 0, 0, 'Проекция верховного мага Модеры to Player');
INSERT INTO `creature_text` VALUES (99313, 2, 0, 'Due to the sensitive nature of this matter, I must ask for your discretion. If you attempt to speak to me at any other location, I will disavow all knowledge of this message!', 12, 0, 100, 0, 0, 0, 100990, 0, 0, 0, 'Проекция верховного мага Модеры to Player');
INSERT INTO `creature_text` VALUES (99313, 3, 0, 'I await your arrival.', 12, 0, 100, 0, 0, 0, 100948, 0, 0, 0, 'Проекция верховного мага Модеры to Player');
INSERT INTO `creature_text` VALUES (99333, 0, 0, 'На Храбростан напали! Нас бомбят инферналами!', 14, 1, 100, 0, 0, 0, 120227, 0, 0, 0, 'Чог');
INSERT INTO `creature_text` VALUES (99336, 0, 0, 'В этом давно заброшенном доме все еще чувствуются следы слабеющих чар.', 42, 0, 100, 0, 0, 0, 100917, 0, 0, 0, '99336 to Player');
INSERT INTO `creature_text` VALUES (99343, 0, 0, 'Кайн и все остальные собрались на площадке Краса. Увидимся там.', 12, 0, 100, 1, 0, 58224, 100927, 0, 0, 0, 'Kor\'vas Bloodthorn to Player');
INSERT INTO `creature_text` VALUES (99343, 1, 0, '|3-6($ct) $n, We urgently need to talk!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Kor\'vas Bloodthorn to Player');
INSERT INTO `creature_text` VALUES (99370, 0, 0, 'Судьба этого мира уже решена!', 14, 0, 100, 0, 0, 0, 99769, 0, 0, 0, 'Таз\'гул');
INSERT INTO `creature_text` VALUES (99374, 0, 0, 'Вухо! Тебе удалось найти монстра?', 12, 0, 100, 0, 0, 0, 101776, 0, 0, 0, 'Апата Крутогор to Player');
INSERT INTO `creature_text` VALUES (99374, 1, 0, 'Мы должны как-то пробраться! Вухо, расскажи мне все, что знаешь, пока $gнаш союзник:наша союзница; осматривает порт. $n, если хочешь, я расскажу тебе все, что знаю.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Апата Крутогор to Player');
