INSERT INTO `creature_text` VALUES (99406, 3, 0, 'Я думала, что погибну!', 12, 0, 100, 0, 0, 0, 101020, 510909, 510909, 0, 'Тауренка из племени Речной Гривы to Player');
INSERT INTO `creature_text` VALUES (99415, 0, 0, 'The portal will take you directly inside Icecrown. Hurry, it won\'t last long! I\'ll follow after you as soon as I can.', 12, 0, 100, 0, 0, 0, 101025, 0, 0, 0, 'Этас Похититель Солнца to Player');
