INSERT INTO `creature_text` VALUES (99517, 1, 0, 'A stranger from another land has come to honor my name? I\'m pleased to see this. The knowledge I have to pass on to you is of totality.', 12, 0, 0, 0, 0, 0, 0, 0, 0, 0, 'Лофал Речная Шкура');
