INSERT INTO `creature_text` VALUES (99859, 1, 0, 'Наконец-то свобода! Сразим это зло!', 12, 0, 100, 0, 0, 0, 101639, 0, 0, 0, 'Страж Лазурного Водопада to Player');
INSERT INTO `creature_text` VALUES (99859, 2, 0, 'Благодарю тебя. Ярость... порча отступила. Теперь я должен позаботиться о водах, которые так долго оставались без моего внимания.', 12, 0, 100, 0, 0, 0, 119825, 0, 0, 0, 'Страж Лазурного Водопада to Player');
INSERT INTO `creature_text` VALUES (99866, 0, 0, 'Yandvik lost all the leaders, and now I can easily destroy it from the inside!', 12, 0, 100, 0, 0, 0, 105967, 0, 0, 0, 'Повелительница приливов Садж\'тар to 0');
INSERT INTO `creature_text` VALUES (99866, 1, 0, 'Довольно!', 12, 0, 100, 0, 0, 0, 129681, 0, 0, 0, 'Повелительница приливов Садж\'тар to 0');
INSERT INTO `creature_text` VALUES (99868, 0, 0, '|TInterface\\Icons\\spell_hunter_lonewolf:20|t Фенрир издает |cFFFF0404|Hspell:196543|h[пугающий вой]|h|r!', 41, 0, 100, 0, 0, 0, 104038, 0, 0, 0, 'Фенрир to 0');
INSERT INTO `creature_text` VALUES (99868, 1, 0, '|TInterface\\Icons\\ability_fixated_state_red:20|t Фенрир учуял |cFFFF0404|Hspell:196838|h[запах вашей крови]|h|r! Бегите!', 42, 0, 100, 0, 0, 0, 104505, 0, 0, 0, 'Фенрир to Player');
INSERT INTO `creature_text` VALUES (99875, 0, 0, 'Мы не пощадим никого!', 14, 0, 100, 0, 0, 0, 104862, 0, 0, 0, 'Темный всадник to Player');
INSERT INTO `creature_text` VALUES (99890, 0, 0, 'Говори, демон. Отведешь меня к хозяину, и я сохраню тебе жизнь.', 12, 0, 100, 0, 0, 71820, 105176, 0, 0, 0, 'Лиана Отчаяние Тьмы to Player');
INSERT INTO `creature_text` VALUES (99915, 0, 0, '$n, у нас большая проблема.', 12, 0, 100, 0, 0, 55342, 101663, 0, 0, 0, 'Севис Яркое Пламя to Player');
INSERT INTO `creature_text` VALUES (99915, 1, 0, 'Ты делаешь мне честь...', 12, 0, 100, 0, 0, 55341, 101653, 0, 0, 0, 'Севис Яркое Пламя to Player');
INSERT INTO `creature_text` VALUES (99915, 2, 0, 'Твоя жертва не будет напрасной!', 12, 0, 0, 0, 0, 55343, 101655, 0, 0, 0, 'Севис Яркое Пламя to Player');
INSERT INTO `creature_text` VALUES (99916, 0, 0, 'Здесь было принесено в жертву множество душ. Врата готовы к активации.', 12, 0, 100, 273, 0, 55344, 101656, 0, 0, 0, 'Севис Яркое Пламя to Player');
INSERT INTO `creature_text` VALUES (99916, 1, 0, 'I will go further and find Allary.', 12, 0, 100, 0, 0, 55346, 101657, 0, 0, 0, 'Севис Яркое Пламя to Player');
INSERT INTO `creature_text` VALUES (99917, 0, 0, 'Поторопись, $n! У тебя мало времени.', 12, 0, 100, 3, 0, 55345, 101658, 0, 0, 0, 'Севис Яркое Пламя to Player');
INSERT INTO `creature_text` VALUES (99917, 1, 0, 'Севис презрительно смотрит на умирающего сломленного мистика.', 16, 0, 100, 3, 0, 55345, 101659, 0, 0, 0, 'Севис Яркое Пламя to Player');
INSERT INTO `creature_text` VALUES (99917, 2, 0, 'Увидимся у последних врат.', 12, 0, 100, 1, 0, 55347, 101662, 0, 0, 0, 'Севис Яркое Пламя to Player');
INSERT INTO `creature_text` VALUES (99919, 3, 0, 'For Lord Illidan! For the Illidari!', 12, 0, 100, 0, 0, 55350, 101666, 66652, 66652, 0, 'Севис Яркое Пламя to Player');
INSERT INTO `creature_text` VALUES (99919, 4, 0, 'I\'m with you to the very end, $n.', 12, 0, 100, 0, 0, 55349, 101667, 90379, 90379, 197639, 'Севис Яркое Пламя to Player');
INSERT INTO `creature_text` VALUES (99919, 4, 0, 'I\'ve been looking forward to this for a long time.', 12, 0, 100, 0, 0, 55351, 101665, 50471, 50471, 0, 'Севис Яркое Пламя to Player');
INSERT INTO `creature_text` VALUES (99919, 4, 0, 'You and me to the very end, $n.', 12, 0, 100, 0, 0, 55349, 101667, 82404, 82404, 0, 'Sevis Brightflame to Player');
INSERT INTO `creature_text` VALUES (99919, 4, 0, 'За владыку Иллидана! За иллидари!', 12, 0, 100, 0, 0, 55350, 101666, 7016, 7016, 0, 'Севис Яркое Пламя to Player');
INSERT INTO `creature_text` VALUES (99919, 4, 0, 'Я давно ждал этого момента.', 12, 0, 100, 0, 0, 55351, 101665, 24853, 24853, 0, 'Севис Яркое Пламя to Player');
INSERT INTO `creature_text` VALUES (99921, 3, 0, 'Another huge demon headin\' fer Kharanos! Come too far ta lose it now!', 14, 0, 100, 0, 0, 0, 116397, 459231, 459231, 0, 'Капитан Тарран');
INSERT INTO `creature_text` VALUES (99929, 0, 0, 'Why I eat so many murlocs?', 14, 0, 100, 0, 0, 0, 120812, 0, 0, 0, 'Обломок to 0');
INSERT INTO `creature_text` VALUES (99929, 0, 1, 'Tummy too rumbly...', 14, 0, 100, 0, 0, 0, 120811, 0, 0, 0, 'Обломок to 0');
INSERT INTO `creature_text` VALUES (99929, 1, 0, 'Вечный... сон...', 12, 0, 100, 0, 0, 0, 105859, 0, 0, 0, 'Обломок to Player');
INSERT INTO `creature_text` VALUES (99938, 3, 0, 'Just me and you. Fightin\' off those demons!', 12, 0, 100, 44, 0, 0, 114900, 86445, 86445, 0, 'Вембил Озаданч to Player');
INSERT INTO `creature_text` VALUES (99948, 0, 0, 'Жители Яндвика! Братья и сестры! Калдер и Эйнар пали в битве. Они не были достойны возглавить Яндвик.', 14, 0, 100, 0, 0, 0, 104569, 0, 0, 0, 'Торил to Player');
INSERT INTO `creature_text` VALUES (99948, 1, 0, 'I am the last contender for power, and I have to lead our people into battle against the naj Saj\'tar!', 14, 0, 100, 0, 0, 0, 104570, 0, 0, 0, 'Торил to Player');
INSERT INTO `creature_text` VALUES (99948, 2, 0, '$gЭтот чужестранец:Эта чужестранка; под моей защитой. Те, кто чем-то $gего:ее; обидят, будут иметь дело со мной.', 14, 0, 100, 0, 0, 0, 104571, 0, 0, 0, 'Торил to Player');
INSERT INTO `creature_text` VALUES (99948, 3, 0, 'Без меня Яндвику не выстоять в этой битве. Скорее!', 12, 0, 100, 0, 0, 0, 105994, 0, 0, 0, 'Торил to Player');
INSERT INTO `creature_text` VALUES (99958, 0, 0, 'I have not seen him, but he is the one we are looking for. Local kvaldirs are afraid of a huge cat that settled in the swamps. They erected watch stones that block the path not only to her, but to us.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Вухо Крутогор to Player');
INSERT INTO `creature_text` VALUES (99959, 3, 0, 'Esteemed Archmages of the Council, may I present to you $n, bearer of Felo\'melorn.', 12, 0, 100, 0, 0, 60482, 101709, 6983, 6983, 0, 'Этас Похититель Солнца to Player');
INSERT INTO `creature_text` VALUES (99995, 0, 0, 'Как скажешь. Я подготовлю отчет о состоянии наших войск и операций.', 12, 0, 100, 0, 0, 68363, 114970, 0, 0, 0, 'Карательница Джулия Селеста to Player');
INSERT INTO `creature_text` VALUES (99995, 1, 0, 'Бесстрашие – мое кредо! За Орден!', 12, 0, 100, 66, 0, 69642, 120615, 0, 0, 0, 'Карательница Джулия Селеста to Player');
INSERT INTO `creature_text` VALUES (99995, 2, 0, 'Позвольте вставить ремарку. Бальназар не ждет совместного удара войск Конклава и Ордена Серебряной Длани.', 12, 0, 100, 1, 0, 71944, 120190, 0, 0, 0, 'Карательница Джулия Селеста to Player');
INSERT INTO `creature_text` VALUES (99997, 0, 0, 'До меня дошли слухи о неком сообщении от отца. Мои родители живы?', 12, 0, 100, 6, 0, 68329, 114983, 0, 0, 0, 'Аратор Искупитель to Player');
INSERT INTO `creature_text` VALUES (100005, 0, 0, 'Наконец-то чудовище убито, а наша реликвия возвращена. Теперь я могу покинуть это место...', 12, 0, 100, 0, 0, 0, 103406, 0, 0, 0, 'Дорро Крутогор to 973');
INSERT INTO `creature_text` VALUES (100013, 4, 0, 'Never caught a glimpse of what it was until it was too late.', 12, 0, 100, 0, 0, 0, 114866, 26447, 26447, 0, 'Тамнер Пол');
INSERT INTO `creature_text` VALUES (100025, 1, 0, 'Свобода!', 12, 0, 100, 0, 0, 0, 101724, 0, 0, 0, 'Самоцветный бес to Грязнорыл-собиратель');
INSERT INTO `creature_text` VALUES (100025, 3, 0, 'Свобода! Да начнется тысячелетие тьмы!', 12, 0, 100, 0, 0, 0, 101729, 122, 122, 0, 'Самоцветный бес to Грязнорыл-собиратель');
INSERT INTO `creature_text` VALUES (100026, 0, 0, 'Вы сгорите!', 14, 0, 100, 0, 0, 0, 133394, 0, 0, 0, 'Вор\'затул to 958');
INSERT INTO `creature_text` VALUES (100053, 0, 0, 'Портал работает стабильно, путь к Азорану открыт.', 12, 0, 100, 0, 0, 67601, 105686, 0, 0, 0, 'Лиана Отчаяние Тьмы to Player');
INSERT INTO `creature_text` VALUES (100054, 1, 0, 'Не стоило сюда приходить.', 12, 0, 100, 0, 0, 0, 102404, 0, 0, 0, 'Завоеватель из племени Тотема Скверны to 78116');
INSERT INTO `creature_text` VALUES (100055, 1, 0, 'Союзники помогут нам!', 12, 0, 100, 0, 0, 0, 102406, 0, 0, 0, 'Воспеватель крови из племени Тотема Скверны to Дрогбар из племени Темного Камня');
INSERT INTO `creature_text` VALUES (100169, 0, 0, 'Поторопись. Ты – последняя надежда Сурамара.', 12, 0, 100, 25, 0, 67269, 100096, 0, 0, 0, 'Эхо первой чародейки Талисры to Player');
INSERT INTO `creature_text` VALUES (100169, 1, 0, 'Ит\'\'нала канеш!', 12, 0, 100, 0, 0, 67270, 100256, 0, 0, 0, 'Эхо первой чародейки Талисры to Player');
INSERT INTO `creature_text` VALUES (100169, 2, 0, 'Надеюсь, нас кто-нибудь услышит.', 12, 0, 100, 0, 0, 67540, 100262, 0, 0, 0, 'Эхо первой чародейки Талисры to Player');
INSERT INTO `creature_text` VALUES (100169, 3, 0, '%s морщится от боли и держится за бок.', 16, 0, 100, 0, 0, 0, 100097, 0, 0, 0, 'Эхо первой чародейки Талисры to Player');
INSERT INTO `creature_text` VALUES (100169, 4, 0, 'Ох... Нельзя останавливаться!', 12, 0, 100, 0, 0, 67541, 100265, 0, 0, 0, 'Эхо первой чародейки Талисры to Player');
INSERT INTO `creature_text` VALUES (100172, 3, 0, 'Слушаюсь, предводительница следопытов. Но нужно соблюдать осторожность! Ход времени в Пустоте непредсказуем.', 12, 0, 100, 273, 0, 61785, 0, 9466, 9466, 0, 'Чародейка Солнечный Взор to Player');
INSERT INTO `creature_text` VALUES (100173, 0, 0, 'Не приближайся к сторожевому камню!', 12, 0, 100, 0, 0, 0, 103865, 0, 0, 0, 'Страж туманов to Player');
INSERT INTO `creature_text` VALUES (100174, 3, 0, 'Испепелитель...', 12, 0, 100, 0, 0, 0, 101431, 405, 405, 0, 'Гриан Камнегрив to Player');
INSERT INTO `creature_text` VALUES (100190, 0, 0, 'Если он лжет, мы казним его. Но что если он говорит правду? Мы должны проверить!', 12, 0, 100, 0, 0, 0, 104469, 0, 0, 0, 'Вериса Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (100190, 1, 0, 'Моя старшая сестра Аллерия пропала много лет назад. Мы поймали демона, который уверяет, будто бы она попала в лапы инквизиции в одном из миров Легиона. Этот мир зовется Нискара. Вернув ее, ты окажешь мне неоценимую услугу!', 12, 0, 100, 0, 0, 0, 101867, 0, 0, 0, 'Вериса Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (100190, 2, 0, 'Знания Аллерии помогут нам уничтожить Легион раз и навсегда. Наша цель – обзавестись нужным союзником, более ничего!', 12, 0, 100, 0, 0, 0, 101869, 0, 0, 0, 'Вериса Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (100190, 3, 0, 'Нет, Орест. Больше я никого не могу послать на риск, пока нет уверенности, что Аллерия действительно там. Нельзя принимать на веру слова демона. Но это может быть наш единственный шанс спасти мою... то есть обзавестись могущественным союзником.', 12, 0, 100, 0, 0, 0, 101877, 0, 0, 0, 'Вериса Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (100190, 4, 0, 'Чародейка Солнечный Взор, заряди силой кристалл портала.', 12, 0, 100, 0, 0, 0, 104364, 0, 0, 0, 'Вериса Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (100190, 5, 0, 'Орест, болван!', 12, 0, 100, 0, 0, 0, 104280, 0, 0, 0, 'Вериса Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (100190, 6, 0, 'Теперь нам надо спасать двоих! За мной!', 12, 0, 100, 0, 0, 0, 104281, 0, 0, 0, 'Вериса Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (100191, 0, 0, 'Вот этот тупой, но кирка у него острая.', 12, 0, 100, 0, 0, 0, 102244, 2045, 2045, 0, 'Стажер Джеки to Player');
INSERT INTO `creature_text` VALUES (100191, 1, 0, 'Грязекоп не лучший. Но достаточно хорош.', 12, 0, 100, 0, 0, 0, 102245, 3360, 3360, 0, 'Стажер Джеки to Player');
INSERT INTO `creature_text` VALUES (100191, 2, 0, 'Я кресло погрею, пока босса нет.', 12, 0, 100, 0, 0, 0, 103034, 66402, 110836, 0, 'Стажер Джеки to Player');
INSERT INTO `creature_text` VALUES (100193, 0, 0, 'Ох, чую я, это ловушка. Нельзя верить демону на слово!', 12, 0, 100, 0, 0, 0, 104468, 0, 0, 0, 'Стрелок Орест to Player');
INSERT INTO `creature_text` VALUES (100193, 1, 0, 'Признай, Вериса! Это нужно лично тебе!', 12, 0, 100, 0, 0, 0, 101870, 0, 0, 0, 'Стрелок Орест to Player');
INSERT INTO `creature_text` VALUES (100193, 2, 0, 'Мне лучше пойти с тобой. Если это ловушка, мой лук тебе понадобится. Будем прикрывать друг друга.', 12, 0, 100, 0, 0, 0, 101876, 0, 0, 0, 'Стрелок Орест to Player');
INSERT INTO `creature_text` VALUES (100193, 3, 0, 'Никогда не умел слепо следовать приказам. Увидимся по ту сторону!', 12, 0, 100, 0, 0, 0, 104361, 0, 0, 0, 'Стрелок Орест to Player');
INSERT INTO `creature_text` VALUES (100196, 0, 0, 'Уделишь мне минутку? У меня тут есть кое-что для тебя интересное.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Эдрик Чистый to Player');
INSERT INTO `creature_text` VALUES (100207, 0, 0, 'Не приближайся к сторожевому камню!', 12, 0, 100, 0, 0, 0, 103865, 0, 0, 0, 'Страж туманов to Player');
INSERT INTO `creature_text` VALUES (100207, 1, 0, 'Прочь!', 12, 0, 100, 0, 0, 0, 135117, 0, 0, 0, 'Страж туманов to Player');
INSERT INTO `creature_text` VALUES (100207, 2, 0, 'Ты не $gдостоин:достойна; находиться здесь!', 12, 0, 100, 0, 0, 0, 103866, 0, 0, 0, 'Страж туманов to Player');
INSERT INTO `creature_text` VALUES (100216, 0, 0, 'Здесь землеходы! Клянусь солью и чешуей – мы обратим их бегство!', 14, 0, 100, 0, 0, 0, 104346, 0, 0, 0, 'Ловчий из клана Колец Ненависти to Player');
INSERT INTO `creature_text` VALUES (100221, 0, 0, 'Успокойся, Вухо!', 12, 0, 100, 0, 0, 0, 106425, 0, 0, 0, 'Апата Крутогор to Player');
INSERT INTO `creature_text` VALUES (100221, 1, 0, 'To even see the Spear will be something to remember. Look! Our friend returns!', 12, 0, 100, 0, 0, 0, 106428, 0, 0, 0, 'Апата Крутогор to Player');
INSERT INTO `creature_text` VALUES (100221, 2, 0, 'Прекрасно! Сейчас я прикреплю руны и...', 12, 0, 100, 0, 0, 0, 101860, 0, 0, 0, 'Апата Крутогор to Player');
INSERT INTO `creature_text` VALUES (100221, 3, 0, 'Готово, можем двигаться дальше!', 12, 0, 100, 0, 0, 0, 101862, 0, 0, 0, 'Апата Крутогор to Player');
INSERT INTO `creature_text` VALUES (100222, 0, 0, 'I will not be stopped by some fog magic! Our tribe\'s greatest weapon and greatest enemy are but a few strides from us!', 12, 0, 100, 0, 0, 0, 106427, 0, 0, 0, 'Вухо Крутогор to Player');
INSERT INTO `creature_text` VALUES (100230, 0, 0, 'Нас точно пустят в Приют, если мы принесем голову этого льва!', 12, 0, 100, 0, 0, 0, 111974, 0, 3957, 0, '\"Меткий\" Арни to Бросок ловушки');
INSERT INTO `creature_text` VALUES (100230, 1, 0, 'Эй! Осторожно!', 14, 0, 100, 0, 0, 0, 111974, 0, 3957, 0, '\"Меткий\" Арни to Бросок ловушки');
INSERT INTO `creature_text` VALUES (100230, 2, 0, 'I do not miss!', 14, 0, 100, 0, 0, 0, 132435, 0, 32139, 0, '\"Меткий\" Арни to Player');
INSERT INTO `creature_text` VALUES (100231, 0, 0, 'Парни, мы окружили эту тварь!', 14, 0, 100, 0, 0, 0, 101878, 0, 0, 0, 'Даргок Разрушительный Гром to Player');
INSERT INTO `creature_text` VALUES (100231, 1, 0, 'А вот и БАБАХ!', 14, 0, 100, 0, 0, 0, 111962, 0, 46742, 0, 'Даргок Разрушительный Гром to Player');
INSERT INTO `creature_text` VALUES (100235, 0, 0, 'Здесь... подозрительно тихо.', 12, 0, 100, 0, 0, 0, 101983, 0, 0, 0, 'Апата Крутогор to 0');
INSERT INTO `creature_text` VALUES (100235, 1, 0, 'Стойте, у меня такое ощущение, что за нами следят...', 12, 0, 100, 0, 0, 0, 101985, 0, 0, 0, 'Апата Крутогор to 0');
INSERT INTO `creature_text` VALUES (100235, 2, 0, 'Защищайтесь!', 12, 0, 100, 0, 0, 0, 65559, 0, 0, 0, 'Апата Крутогор to Player');
INSERT INTO `creature_text` VALUES (100235, 3, 0, 'Эта скрытность меня бесит! Кто-нибудь, бросьте ракету!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Апата Крутогор to Призрачный ловец');
INSERT INTO `creature_text` VALUES (100235, 4, 0, 'Эти призраки скрываются в тенях! У кого-нибудь есть осветительная ракета?', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Апата Крутогор to Призрачный ловец');
INSERT INTO `creature_text` VALUES (100235, 6, 0, 'Чудовище скрылось в густом тумане. Оно может быть где угодно. У тебя есть достаточно мощная ловушка, чтобы удерживать тварь, пока мы ее не убьем?', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Апата Крутогор to 973');
INSERT INTO `creature_text` VALUES (100235, 7, 0, 'Чудовище нападает на нас из тумана! Поторопись!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Апата Крутогор to Player');
INSERT INTO `creature_text` VALUES (100235, 8, 0, 'Задержим их, пока ловушка не сработает!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Апата Крутогор to Туманная ловушка');
INSERT INTO `creature_text` VALUES (100235, 11, 0, 'Мы... Вухо! Нет!', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Апата Крутогор to Дакарр');
INSERT INTO `creature_text` VALUES (100235, 12, 0, 'Брат... Эх, не надо было брать тебя с собой.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Апата Крутогор to Дакарр');
INSERT INTO `creature_text` VALUES (100235, 13, 0, 'I will avenge you. We have to get to the end. Follow the trail of the monster.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Апата Крутогор to 100376');
INSERT INTO `creature_text` VALUES (100235, 14, 0, 'Мои раны... не заживут. Мы должны побыстрее с покончить с чудовищем.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Апата Крутогор to Player');
INSERT INTO `creature_text` VALUES (100235, 15, 0, 'Мы не сможем отбиваться, пока ты не установишь ловушку! Поторопись!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Апата Крутогор to Player');
INSERT INTO `creature_text` VALUES (100235, 18, 0, 'Сработало! Убей монстра!', 12, 0, 100, 0, 0, 0, 102062, 0, 0, 0, 'Апата Крутогор to Дакарр');
INSERT INTO `creature_text` VALUES (100235, 20, 0, 'Только не это! Берегись!', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Апата Крутогор to Дакарр');
INSERT INTO `creature_text` VALUES (100235, 21, 0, 'I ... reached the end of the trail ... then you have to $go  one:one ;.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Апата Крутогор to Дакарр');
INSERT INTO `creature_text` VALUES (100236, 0, 0, 'I do not feel the presence of a single beast!', 12, 0, 100, 0, 0, 0, 101984, 0, 0, 0, 'Вухо Крутогор to 0');
INSERT INTO `creature_text` VALUES (100237, 2, 0, 'Араш-фолас…', 12, 0, 100, 0, 0, 65689, 116791, 0, 0, 228533, 'Охотник за силовыми линиями to Player');
INSERT INTO `creature_text` VALUES (100238, 0, 0, 'Не надо! Вы не понимаете... Я же не дичь!', 12, 0, 100, 1, 0, 0, 101880, 0, 0, 0, 'Мара Стремительная Грива to \"Меткий\" Арни');
INSERT INTO `creature_text` VALUES (100238, 1, 0, 'Спасибо. Эти охотники не успокоились бы, пока не отрубили бы мне голову. Можешь забрать себе все их добро.', 12, 0, 100, 1, 0, 0, 101956, 80245, 80245, 0, 'Мара Стремительная Грива to \"Меткий\" Арни');
INSERT INTO `creature_text` VALUES (100248, 0, 0, 'Нам нужно больше времени!', 14, 0, 100, 0, 0, 0, 100045, 0, 0, 0, 'Ритуалистка Лиша to Player');
INSERT INTO `creature_text` VALUES (100249, 0, 0, 'Он еще формируется!', 14, 0, 100, 0, 0, 0, 100042, 0, 0, 0, 'Чародейка Варис to Player');
INSERT INTO `creature_text` VALUES (100250, 0, 0, 'Нет! Ритуал еще не закончен!', 14, 0, 100, 0, 0, 0, 100040, 0, 0, 0, 'Подчинительница Ашиой to Player');
INSERT INTO `creature_text` VALUES (100298, 4, 0, 'Stand firm. Do not let them break through!', 12, 0, 100, 0, 0, 57304, 98807, 537478, 537478, 0, 'Мариус Проклятие Скверны to Player');
INSERT INTO `creature_text` VALUES (100299, 3, 0, 'Hold the line, demon hunters. We fight in Lord Illidan\'s name!', 12, 0, 100, 0, 0, 57303, 98806, 183733, 183733, 0, 'Lyana Darksorrow to Player');
INSERT INTO `creature_text` VALUES (100299, 4, 0, 'Держать строй, охотники на демонов. За владыку Иллидана!', 12, 0, 100, 0, 0, 57303, 98806, 232038, 232038, 0, 'Лиана Отчаяние Тьмы to Player');
INSERT INTO `creature_text` VALUES (100301, 3, 0, 'Надо уходить. Скоро мы будем дома.', 12, 0, 100, 0, 0, 65731, 102081, 2218, 2218, 0, 'Асторил to Player');
INSERT INTO `creature_text` VALUES (100302, 0, 0, 'Видите? Не обязательно лезть в данж за крутым лутом. Чур, мне перчатки!', 12, 0, 100, 0, 0, 0, 121572, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (100302, 1, 0, 'Где... были... лекари...', 12, 0, 100, 0, 0, 0, 121567, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (100303, 0, 0, 'Раны можно исцелить, но вот глупость неизлечима...', 12, 0, 100, 0, 0, 0, 121570, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (100306, 0, 0, 'Сюда, друг. Я помогу тебе пройти испытания.', 12, 0, 100, 0, 0, 0, 102457, 0, 0, 0, 'Регар Ярость Земли to Player');
INSERT INTO `creature_text` VALUES (100306, 1, 0, 'Буйный Портер! Ты тоже ищешь оружие бури?', 12, 0, 100, 0, 0, 0, 102460, 0, 0, 0, 'Регар Ярость Земли to Player');
INSERT INTO `creature_text` VALUES (100306, 2, 0, 'Ты в надежных руках, друг. Я говорил, что тренировал короля Ринна перед боями на арене?', 12, 0, 100, 0, 0, 0, 102463, 0, 0, 0, 'Регар Ярость Земли to Player');
INSERT INTO `creature_text` VALUES (100306, 3, 0, 'Этот врайкул не так опасен, как кажется. Используй его силу против него самого.', 12, 0, 100, 0, 0, 0, 102466, 0, 0, 0, 'Регар Ярость Земли to Player');
INSERT INTO `creature_text` VALUES (100306, 4, 0, 'Я буду сражаться вместе со своим защитником. Кто выступит против нас?', 12, 0, 100, 0, 0, 0, 102476, 0, 0, 0, 'Регар Ярость Земли to Player');
INSERT INTO `creature_text` VALUES (100306, 5, 0, 'Буйный Портер?! Ха! Давненько хотел сойтись с тобой на арене. Сейчас я собью с тебя спесь.', 12, 0, 100, 0, 0, 0, 102478, 0, 0, 0, 'Регар Ярость Земли to Player');
INSERT INTO `creature_text` VALUES (100306, 6, 0, 'Ignore your pride. Take out the pandaren girl first!', 12, 0, 100, 0, 0, 0, 102484, 0, 0, 0, 'Регар Ярость Земли to Player');
INSERT INTO `creature_text` VALUES (100306, 7, 0, 'I know a champion when I see one!', 12, 0, 100, 0, 0, 0, 102497, 0, 0, 0, 'Регар Ярость Земли to Чэнь Буйный Портер');
INSERT INTO `creature_text` VALUES (100306, 8, 0, 'Я верю в тебя, шаман.', 12, 0, 100, 0, 0, 0, 102502, 0, 0, 0, 'Регар Ярость Земли to Player');
INSERT INTO `creature_text` VALUES (100306, 9, 0, 'Сосредоточься!', 12, 0, 100, 0, 0, 0, 102507, 0, 0, 0, 'Регар Ярость Земли to Player');
INSERT INTO `creature_text` VALUES (100306, 10, 0, 'Stay focused, champion. You were born to command these elements. You can do this!', 12, 0, 100, 0, 0, 0, 102513, 0, 0, 0, 'Регар Ярость Земли to Player');
INSERT INTO `creature_text` VALUES (100306, 11, 0, 'Нас атакует Легион!', 12, 0, 100, 0, 0, 0, 102521, 0, 0, 0, 'Регар Ярость Земли to Player');
INSERT INTO `creature_text` VALUES (100306, 12, 0, 'Storm, earth, and fire! Heed my call!', 12, 0, 100, 0, 0, 0, 104821, 0, 0, 0, 'Регар Ярость Земли to Младший инквизитор');
INSERT INTO `creature_text` VALUES (100306, 13, 0, 'Этот шаман обрел полную гармонию со стихиями. Я не видел более достойного шамана с тех пор, как сражался вместе с самим Траллом.', 12, 0, 100, 0, 0, 0, 102545, 0, 0, 0, 'Регар Ярость Земли to 976');
INSERT INTO `creature_text` VALUES (100307, 0, 0, 'I do. \'Storm, Earth, Fire...\' that\'s kind of my thing.', 12, 0, 100, 0, 0, 0, 102461, 0, 0, 0, 'Чэнь Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (100307, 1, 0, 'Incredible form! A fighter in perfect harmony with the elements!', 12, 0, 100, 0, 0, 0, 102469, 0, 0, 0, 'Чэнь Буйный Портер to Сигурд Убийца Великанов');
INSERT INTO `creature_text` VALUES (100307, 2, 0, 'Думаю, сейчас моя очередь.', 12, 0, 100, 0, 0, 0, 102477, 0, 0, 0, 'Чэнь Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (100307, 3, 0, 'Is that so? We shall see who the real \'Hero of the Storm\' is around here. But first, I need a partner.', 12, 0, 100, 0, 0, 0, 102480, 0, 0, 0, 'Чэнь Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (100307, 4, 0, 'Oh no no, Li Li, I don\'t think--', 12, 0, 100, 0, 0, 0, 102482, 0, 0, 0, 'Чэнь Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (100307, 5, 0, 'Покатились!', 12, 0, 100, 0, 0, 0, 102490, 0, 0, 0, 'Чэнь Буйный Портер to 0');
INSERT INTO `creature_text` VALUES (100307, 12, 0, 'Ты сильнее меня.', 12, 0, 100, 0, 0, 0, 102494, 0, 0, 0, 'Чэнь Буйный Портер to 0');
INSERT INTO `creature_text` VALUES (100307, 13, 0, 'Знаешь, Регар, возможно, это самый сильный боец из всех, кого ты выводил на арену.', 12, 0, 100, 0, 0, 0, 102496, 0, 0, 0, 'Чэнь Буйный Портер to 976');
INSERT INTO `creature_text` VALUES (100307, 14, 0, 'I\'ll be honest... those things scare me.', 12, 0, 100, 0, 0, 0, 102504, 0, 0, 0, 'Чэнь Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (100307, 15, 0, 'Hmm. Maybe we should do this outdoors.', 12, 0, 100, 0, 0, 0, 102511, 0, 0, 0, 'Чэнь Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (100307, 16, 0, 'О, благодарю.', 12, 0, 100, 0, 0, 0, 104819, 0, 0, 0, 'Чэнь Буйный Портер to Младший инквизитор');
INSERT INTO `creature_text` VALUES (100308, 0, 0, 'Я всегда рада приключениям!', 12, 0, 100, 0, 0, 0, 102481, 0, 0, 0, 'Ли Ли Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (100308, 1, 0, 'Будет весело!', 12, 0, 100, 0, 0, 0, 127817, 0, 0, 0, 'Ли Ли Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (100308, 2, 0, 'Да уж, для врагов нет запрещенных приемов.', 12, 0, 100, 0, 0, 0, 102493, 0, 0, 0, 'Ли Ли Буйный Портер to 0');
INSERT INTO `creature_text` VALUES (100308, 3, 0, 'Ого! Осторожно!', 12, 0, 100, 0, 0, 0, 102505, 0, 0, 0, 'Ли Ли Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (100308, 4, 0, 'Whew! That feels much better! Let me at \'em!', 12, 0, 100, 0, 0, 0, 104820, 0, 0, 0, 'Ли Ли Буйный Портер to Младший инквизитор');
INSERT INTO `creature_text` VALUES (100322, 4, 0, '%s исчезает в облаке волшебного сияния.', 16, 0, 100, 0, 0, 0, 103565, 97265, 97265, 0, 'Облачный змей');
INSERT INTO `creature_text` VALUES (100323, 0, 0, 'I hear the truth in your words, $c. We may walk very different paths, but I see you fight for a noble cause.', 12, 0, 100, 0, 0, 0, 102051, 0, 0, 0, 'Ревил Кост to Player');
INSERT INTO `creature_text` VALUES (100323, 1, 0, 'This can only mean the Scythe of Elune has returned to Duskwood... and the Dark Riders cannot be far behind.', 12, 0, 100, 0, 0, 0, 104556, 0, 0, 0, 'Ревил Кост to Player');
INSERT INTO `creature_text` VALUES (100323, 2, 0, 'Скажи мне, что ты ищешь, друид. Возможно, я сумею помочь.', 12, 0, 100, 0, 0, 0, 104557, 0, 0, 0, 'Ревил Кост to Player');
INSERT INTO `creature_text` VALUES (100331, 3, 0, 'I don’t understand ... This is not a city. Where...', 12, 0, 100, 0, 0, 65933, 102101, 6439, 6439, 0, 'Иадрет to Player');
INSERT INTO `creature_text` VALUES (100344, 0, 0, 'А ты $gупорный:упорная;.', 12, 0, 100, 0, 0, 0, 103613, 0, 0, 0, 'Ариден to Player');
INSERT INTO `creature_text` VALUES (100344, 1, 0, 'Ну хорошо! Если ты так жаждешь завладеть силой Жнеца Мертвого Ветра, сейчас я сам продемонстрирую ее тебе!', 12, 0, 100, 0, 0, 0, 103614, 0, 0, 0, 'Ариден to Player');
INSERT INTO `creature_text` VALUES (100344, 2, 0, 'Сегодня Ултхалеш ждет кровавый пир!', 14, 0, 100, 0, 0, 0, 103615, 0, 0, 0, 'Ариден to Player');
INSERT INTO `creature_text` VALUES (100344, 3, 0, 'Нет! Я не поддамся!', 14, 0, 100, 0, 0, 0, 103616, 0, 0, 0, 'Ариден to 1860');
INSERT INTO `creature_text` VALUES (100344, 4, 0, 'НЕ-Е-ЕТ!', 14, 0, 100, 0, 0, 0, 123493, 0, 0, 0, 'Ариден to 1860');
INSERT INTO `creature_text` VALUES (100352, 3, 0, 'Оро, сколько раз нужно говорить – не бегай ты с этой палкой в руке!', 14, 0, 100, 15, 0, 0, 102030, 30200, 30200, 0, 'Гура Каменное Копыто to Оро-младший');
INSERT INTO `creature_text` VALUES (100355, 0, 0, 'Прекрасно! Я все приготовила. Прошу следовать за мной.', 12, 0, 100, 0, 0, 0, 102087, 0, 0, 0, 'Девятая Цзя to Player');
INSERT INTO `creature_text` VALUES (100355, 1, 0, 'Благодарю всех учителей и учеников наших объединенных школ за то, что присоединились к нам.', 12, 0, 100, 0, 0, 0, 102070, 0, 0, 0, 'Девятая Цзя to Player');
INSERT INTO `creature_text` VALUES (100355, 2, 0, 'После нападения на Пик Безмятежности ясно одно. Пылающий Легион хочет уничтожить всю нашу жизнь. Они разрушат наши дома, осквернят священные храмы и постараются сломать наш дух.', 12, 0, 100, 0, 0, 0, 102073, 0, 0, 0, 'Девятая Цзя to Player');
INSERT INTO `creature_text` VALUES (100355, 3, 0, 'Let us all give honor to Master $n! May she lead us with strength and wisdom.', 12, 0, 100, 0, 0, 0, 102080, 0, 0, 0, 'Девятая Цзя to Player');
INSERT INTO `creature_text` VALUES (100355, 4, 0, 'Monks, our greatest challenge still lies ahead. Under our new master, we will send a message of defiance to our enemies. They will not take our home without a fight!', 12, 0, 100, 0, 0, 0, 102077, 0, 0, 0, 'Девятая Цзя to Player');
INSERT INTO `creature_text` VALUES (100357, 3, 0, 'Не очень. Оро всю рыбу распугал.', 12, 0, 100, 0, 0, 0, 102034, 734677, 734677, 0, 'Мика Каменное Копыто to Гура Каменное Копыто');
INSERT INTO `creature_text` VALUES (100358, 3, 0, 'Неправда!', 14, 0, 100, 0, 0, 0, 102053, 737104, 737104, 0, 'Оро-младший to Мика Каменное Копыто');
INSERT INTO `creature_text` VALUES (100363, 0, 0, 'Кто вызывает меня на бой?! Оружие бури по праву принадлежит моему клану!', 12, 0, 100, 0, 0, 0, 102464, 0, 0, 0, 'Сигурд Убийца Великанов to Player');
INSERT INTO `creature_text` VALUES (100363, 1, 0, 'I defeated the kraken. I killed the giants. You are a jerk!', 12, 0, 100, 0, 0, 0, 102430, 0, 0, 0, 'Сигурд Убийца Великанов to Player');
INSERT INTO `creature_text` VALUES (100363, 2, 0, 'I give up...', 12, 0, 100, 0, 0, 0, 102431, 0, 0, 0, 'Сигурд Убийца Великанов to Player');
INSERT INTO `creature_text` VALUES (100363, 3, 0, 'This isn\'t over, shaman. Someday we will meet on a true field of battle.', 12, 0, 100, 0, 0, 0, 102432, 0, 0, 0, 'Сигурд Убийца Великанов to Player');
INSERT INTO `creature_text` VALUES (100376, 0, 0, 'Туман рассеивается, и вы видите ловушку, а в ней – %s!', 41, 0, 100, 0, 0, 0, 103937, 0, 0, 0, 'Дакарр to Призрачный ловец');
INSERT INTO `creature_text` VALUES (100387, 3, 0, 'Lady Ysera, thank you for answering our call. We face a dire threa--', 12, 0, 100, 2, 0, 52204, 97620, 30559, 30559, 0, 'Малфурион Ярость Бури to Player');
INSERT INTO `creature_text` VALUES (100387, 3, 0, 'Спасибо, что откликнулась на наш зов, Изера. Нам грозит смерте–', 12, 0, 100, 2, 0, 52204, 97620, 31750, 31750, 0, 'Малфурион Ярость Бури to Player');
INSERT INTO `creature_text` VALUES (100388, 0, 0, 'Such poor souls. You don\'t even belong here, but you will be forever bound to this place.', 12, 0, 100, 1, 0, 61895, 103123, 0, 0, 0, 'Сатаэль to Player');
INSERT INTO `creature_text` VALUES (100388, 1, 0, 'Не бойтесь. Силою Ултхалеша в смерти вы будете могущественней, чем были при жизни.', 12, 0, 100, 38, 0, 61896, 103125, 0, 0, 0, 'Сатаэль to Player');
INSERT INTO `creature_text` VALUES (100388, 2, 0, 'Надо же... Вы правда думаете, что этот ваш Свет небес защитит от Легиона?', 12, 0, 100, 6, 0, 61899, 103128, 0, 0, 0, 'Сатаэль to Player');
INSERT INTO `creature_text` VALUES (100388, 3, 0, 'Where is your Light to protect you now?', 12, 0, 100, 38, 0, 61900, 103129, 0, 0, 0, 'Сатаэль to Player');
INSERT INTO `creature_text` VALUES (100388, 4, 0, 'Глупо думать, что столь ничтожный отряд может противостоять правой руке Саргераса!', 12, 0, 100, 5, 0, 61897, 103126, 0, 0, 0, 'Сатаэль to Player');
INSERT INTO `creature_text` VALUES (100388, 5, 0, 'Вашими ничтожными жизнями я вымощу путь для Легиона!', 12, 0, 100, 38, 0, 61898, 103127, 0, 0, 0, 'Сатаэль to Player');
INSERT INTO `creature_text` VALUES (100397, 0, 0, 'Ореста нигде не видно. Он, конечно, упрямый, но должно же ему хватить соображения дождаться нас здесь!', 12, 0, 100, 0, 0, 0, 103289, 0, 0, 0, 'Вериса Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (100397, 1, 0, 'Поднимись вместе со мной и взгляни, с чем нам предстоит столкнуться.', 12, 0, 100, 0, 0, 0, 103242, 0, 0, 0, 'Вериса Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (100397, 2, 0, 'Часовой! Уничтожь его, пока он не оповестил остальных!', 14, 0, 100, 0, 0, 0, 103441, 0, 0, 0, 'Вериса Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (100397, 3, 0, 'Не стой на месте! Это было только начало!', 12, 0, 100, 0, 0, 0, 103275, 0, 0, 0, 'Вериса Ветрокрылая to Дозорный стражи Скверны');
INSERT INTO `creature_text` VALUES (100397, 4, 0, 'Маги-создатели порталов! Уничтожьте их, пока они не привели подмогу!', 12, 0, 100, 0, 0, 0, 103443, 0, 0, 0, 'Вериса Ветрокрылая to Дозорный стражи Скверны');
INSERT INTO `creature_text` VALUES (100397, 5, 0, 'Это строение вдалеке, должно быть, и есть собор. Веди, я с тобой!', 12, 0, 100, 0, 0, 0, 103446, 0, 0, 0, 'Вериса Ветрокрылая to 100758');
INSERT INTO `creature_text` VALUES (100397, 6, 0, 'Вот там, впереди! Это Орест!', 14, 0, 100, 0, 0, 0, 101871, 0, 0, 0, 'Вериса Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (100397, 8, 0, 'Идем. Отыщем Аллерию и покончим с этим.', 12, 0, 100, 0, 0, 0, 103670, 0, 0, 0, 'Вериса Ветрокрылая to 0');
INSERT INTO `creature_text` VALUES (100397, 9, 0, 'Еще немного... Продолжайте сражаться!', 14, 0, 100, 0, 0, 0, 101890, 0, 0, 0, 'Вериса Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (100397, 10, 0, 'I am happy to shut the mouth of this bastard!', 12, 0, 100, 0, 0, 0, 103652, 0, 0, 0, 'Вериса Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (100397, 11, 0, 'Если Аллерия здесь, она должна быть где-то рядом!', 12, 0, 100, 0, 0, 0, 101892, 0, 0, 0, 'Вериса Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (100397, 12, 0, 'Up ahead... looks like the interrogation area.', 12, 0, 100, 0, 0, 0, 106844, 0, 0, 0, 'Вериса Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (100397, 13, 0, 'Что вы сделали с Аллерией, негодяи?!', 14, 0, 100, 0, 0, 0, 107106, 0, 0, 0, 'Вериса Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (100397, 14, 0, 'Нет!', 14, 0, 100, 0, 0, 0, 135514, 0, 0, 0, 'Вериса Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (100397, 15, 0, 'Он разрывает мой разум!', 14, 0, 100, 0, 0, 0, 103233, 0, 0, 0, 'Вериса Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (100397, 16, 0, 'I felt his eyes staring right into my soul. If you hadn’t freed: freed; me…', 12, 0, 100, 0, 0, 0, 103234, 0, 0, 0, 'Вериса Ветрокрылая to Пылающий взгляд Кормаладона');
INSERT INTO `creature_text` VALUES (100397, 17, 0, 'Enough! I must know if Alleria was truly held captive here, or if this was all a ruse to trap us!', 12, 0, 100, 0, 0, 0, 103232, 0, 0, 0, 'Вериса Ветрокрылая to Пылающий взгляд Кормаладона');
INSERT INTO `creature_text` VALUES (100397, 18, 0, 'Сколько держали ее здесь? Дни? Годы? Столетия? Чародейка была права... время здесь течет по-другому.', 12, 0, 100, 0, 0, 0, 107108, 0, 0, 0, 'Вериса Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (100397, 19, 0, 'Клянусь Светом... Вот! Это ее лук! Тас\'дора!', 12, 0, 100, 0, 0, 0, 106996, 0, 0, 0, 'Вериса Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (100397, 20, 0, 'Alleria was here... her bow\'s presence is the proof! Doubtless she escaped, and the Inquisitor sought to use me to lure her back.', 12, 0, 100, 0, 0, 0, 106997, 0, 0, 0, 'Вериса Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (100397, 21, 0, 'Thas\'dorah began as my family\'s legacy... but the weapon has a greater purpose now.', 12, 0, 100, 0, 0, 0, 107189, 0, 0, 0, 'Вериса Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (100397, 22, 0, 'This is a bow worthy of a hero like Alleria... a hero like you. Take Thas\'dorah, $n. Use it to lead the fight to save Azeroth!', 12, 0, 100, 0, 0, 0, 106998, 0, 0, 0, 'Вериса Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (100397, 23, 0, 'Я знаю, что лук Аллерии в надежных руках.', 12, 0, 100, 0, 0, 0, 107282, 0, 0, 0, 'Вериса Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (100397, 24, 0, 'Нас вот-вот окружат со всех сторон. Лук у нас. Время уйти и оставить это омерзительное место демонам.', 12, 0, 100, 0, 0, 0, 103501, 0, 0, 0, 'Вериса Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (100399, 3, 0, 'I\'ll open it at once. Then I must inform the ranger-general of my findings. Good luck!', 12, 0, 100, 0, 0, 0, 119635, 964, 964, 0, 'Чародейка Солнечный Взор to Player');
INSERT INTO `creature_text` VALUES (100403, 3, 0, 'Kill her! Protect the grove!', 14, 0, 100, 0, 0, 54407, 102283, 595038, 595038, 0, 'Druidic Preserver to Player');
INSERT INTO `creature_text` VALUES (100403, 3, 0, 'Убейте его! Защитите рощу!', 14, 0, 100, 0, 0, 54407, 0, 503243, 503243, 0, 'Druidic Preserver to Player');
INSERT INTO `creature_text` VALUES (100427, 1, 0, 'Слезы Му\'ши – не для простого народа!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Бурлящий дух живой ярости to Player');
INSERT INTO `creature_text` VALUES (100427, 1, 0, 'Эта вода священна!', 12, 0, 100, 0, 0, 0, 102259, 0, 0, 0, 'Бурлящий дух живой ярости to Player');
INSERT INTO `creature_text` VALUES (100427, 1, 0, 'Эта магия не для тебя!', 12, 0, 100, 0, 0, 0, 102261, 0, 0, 0, 'Бурлящий дух живой ярости to Player');
INSERT INTO `creature_text` VALUES (100430, 1, 0, 'Do not touch the candle!', 12, 0, 100, 0, 0, 54334, 102744, 0, 0, 0, 'Пещероус из племени Синего Воска to Player');
INSERT INTO `creature_text` VALUES (100431, 1, 0, 'I will teach you to obey the masters.', 12, 0, 100, 0, 0, 65800, 100932, 0, 0, 0, 'Ночнорожденный-исказитель времени to Player');
INSERT INTO `creature_text` VALUES (100431, 1, 0, 'Пробил твой час!', 12, 0, 100, 0, 0, 65799, 114571, 0, 0, 0, 'Ночнорожденный-исказитель времени to Player');
INSERT INTO `creature_text` VALUES (100431, 3, 0, 'Кто идет?', 12, 0, 100, 0, 0, 65802, 112905, 1407146, 1407146, 0, 'Ночнорожденный-исказитель времени to Player');
INSERT INTO `creature_text` VALUES (100433, 0, 0, 'Stone! Obey me!', 12, 0, 100, 0, 0, 57963, 107094, 0, 0, 0, 'Хранитель камня из племени Камня Силы to Player');
INSERT INTO `creature_text` VALUES (100433, 0, 1, 'Darkness take you!', 12, 0, 100, 0, 0, 0, 107093, 0, 0, 0, 'Хранитель камня из племени Камня Силы to Player');
INSERT INTO `creature_text` VALUES (100433, 0, 2, 'Die, shan ronir!', 12, 0, 100, 0, 0, 0, 107090, 0, 0, 0, 'Хранитель камня из племени Камня Силы to Player');
INSERT INTO `creature_text` VALUES (100433, 0, 3, 'Foolish karkun! Let\'s see how fast you die!', 12, 0, 100, 0, 0, 0, 107095, 0, 0, 0, 'Хранитель камня из племени Камня Силы to Player');
INSERT INTO `creature_text` VALUES (100437, 3, 0, 'В Крутогорье есть такой обычай – платить за товары. Если тебе нужны воздушные искры, обращайся ко мне.', 12, 0, 100, 274, 0, 0, 102241, 2241392, 2241392, 0, 'Ланна Небесная Искра to Player');
INSERT INTO `creature_text` VALUES (100438, 0, 0, '$ct, Li Li should be at the Laughing Crane. That\'s the village inn just down the stairs from the temple.', 12, 0, 100, 0, 0, 0, 104761, 0, 0, 0, 'Железный Поньшу to Player');
INSERT INTO `creature_text` VALUES (100438, 1, 0, 'Поспеши, Великий мастер! Сражение бушует на хребте над нами. Надо спешить туда!', 12, 0, 100, 5, 0, 0, 116322, 0, 0, 0, 'Железный Поньшу to Player');
INSERT INTO `creature_text` VALUES (100439, 0, 0, 'Шал\'дорай будут править миром!', 12, 0, 100, 0, 0, 65698, 116792, 0, 0, 0, 'Помраченный-защитник to Player');
INSERT INTO `creature_text` VALUES (100439, 1, 0, 'Ваша судьба решена!', 12, 0, 100, 0, 0, 65814, 116703, 0, 0, 0, 'Помраченный-защитник to Player');
INSERT INTO `creature_text` VALUES (100439, 2, 0, 'Араш-фолас...', 12, 0, 100, 0, 0, 65689, 116771, 0, 0, 0, 'Помраченный-защитник to Player');
INSERT INTO `creature_text` VALUES (100439, 3, 0, 'Шал\'дорай победят!', 12, 0, 100, 0, 0, 65801, 116707, 0, 0, 0, 'Помраченный-защитник to Player');
INSERT INTO `creature_text` VALUES (100439, 4, 0, 'Пробил твой час!', 12, 0, 100, 0, 0, 65799, 114571, 0, 0, 0, 'Помраченный-защитник to Player');
INSERT INTO `creature_text` VALUES (100439, 5, 0, 'Смерть чужакам!', 12, 0, 100, 0, 0, 65820, 116708, 0, 0, 0, 'Помраченный-защитник to Player');
INSERT INTO `creature_text` VALUES (100439, 8, 0, 'Наша магия сокрушит вас!', 12, 0, 100, 0, 0, 65817, 116704, 0, 0, 0, 'Помраченный-защитник to Player');
INSERT INTO `creature_text` VALUES (100439, 9, 0, 'Тор\'терас фалар!', 12, 0, 100, 0, 0, 65690, 116790, 0, 0, 0, 'Помраченный-защитник to Player');
INSERT INTO `creature_text` VALUES (100445, 0, 0, 'Этот кристалл принадлежит земле!', 12, 0, 100, 0, 0, 0, 102262, 0, 0, 0, 'Кристалл темнозема to Player');
INSERT INTO `creature_text` VALUES (100450, 3, 0, 'Надеюсь, ты сможешь контролировать свои... способности – здесь святое место, \"охотник на демонов\". Мы будем следить за тобой.', 12, 0, 100, 6, 0, 0, 122090, 128530, 128530, 0, 'Жрица храма to Player');
INSERT INTO `creature_text` VALUES (100452, 3, 0, 'Темная Госпожа выделила тебе в помощь своих следопытов, |3-6($ct).', 12, 0, 100, 6, 0, 61862, 105670, 123855, 123855, 0, 'Темный следопыт Велонара to Player');
INSERT INTO `creature_text` VALUES (100453, 0, 0, 'Best get to Grommash Hold straightaway, $n.', 12, 0, 100, 0, 0, 0, 122112, 0, 0, 0, 'Эйтригг to Player');
INSERT INTO `creature_text` VALUES (100475, 0, 0, 'Okay, Pop. It\'s been... it\'s been something. I need to get on with my travels.', 12, 0, 100, 1, 0, 61994, 103325, 0, 0, 0, 'Ли Ли Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (100475, 1, 0, 'I thought you might say that... Look, I have a guardian, and it\'s even the master of the monks. I\'ll be fine!', 12, 0, 100, 0, 0, 61996, 103327, 0, 0, 0, 'Ли Ли Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (100475, 2, 0, 'Yay! Okay $n, let\'s go!', 12, 0, 100, 0, 0, 61998, 103335, 0, 0, 0, 'Ли Ли Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (100475, 3, 0, 'My friend Mr. Crane can fly me to Uldum. Here, you can use my kite to follow us.', 12, 0, 100, 0, 0, 61999, 103339, 0, 0, 0, 'Ли Ли Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (100475, 4, 0, 'Okay, Mr. Crane. Here\'s two juicy fish if you\'ll take me to Uldum.', 12, 0, 100, 0, 0, 62000, 103340, 0, 0, 0, 'Ли Ли Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (100481, 0, 0, '%s scowls.', 16, 0, 100, 0, 0, 0, 103329, 0, 0, 0, 'Тен По Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (100481, 1, 0, 'Li Li, I told you I don\'t want you going off on your own again.', 12, 0, 100, 0, 0, 61995, 103326, 0, 0, 0, 'Тен По Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (100481, 2, 0, 'Clearly nothing I say is going to change your mind. Fine, go on your adventure. Just come back to me unharmed.', 12, 0, 100, 0, 0, 61997, 103331, 0, 0, 0, 'Тен По Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (100482, 0, 0, 'Стеллагоса, окажи мне последнюю услугу. Помоги своим братьям и сестрам.', 12, 0, 100, 0, 0, 52855, 93123, 0, 0, 0, 'Сенегос to Player');
INSERT INTO `creature_text` VALUES (100482, 1, 0, 'Крепись. Никто не защищен от цепких объятий Кошмара.', 12, 0, 100, 1, 0, 0, 119152, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (100482, 2, 0, 'Кажется, я знаю, как помочь тебе, дитя.', 12, 0, 100, 1, 0, 0, 119733, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (100482, 3, 0, 'Калесгос, мы должны помочь! Слишком много силы!', 12, 0, 100, 1, 0, 0, 119188, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (100482, 4, 0, 'Калесгос… Это только начало. Теперь мы должны сами погрузиться в Кошмар.', 12, 0, 100, 1, 0, 0, 119168, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (100482, 5, 0, 'Погрузившись во Тьму, немногие могут вернуться.', 12, 0, 100, 1, 0, 0, 119193, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (100482, 6, 0, 'Помоги, кроха...', 12, 0, 100, 1, 0, 0, 114475, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (100482, 7, 0, 'Осторожно! Слуги Азшары не уступят Сердце без боя!', 12, 0, 100, 1, 0, 0, 119147, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (100482, 8, 0, 'У нас получилось! Демонам не выстоять против такой мощи.', 12, 0, 100, 1, 0, 0, 119836, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (100482, 9, 0, 'Я должен поблагодарить за это твоего союзника.', 12, 0, 100, 1, 0, 0, 119144, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (100488, 3, 0, 'Рад тебя видеть, командир.', 12, 0, 100, 0, 0, 0, 102425, 8176, 8176, 0, 'Часовой Пирс to Player');
INSERT INTO `creature_text` VALUES (100497, 0, 0, 'You should\'ve run! Now your bones will encircle the ravens!', 14, 0, 100, 0, 0, 58379, 106101, 0, 0, 0, 'Урсок to Урсок');
INSERT INTO `creature_text` VALUES (100497, 0, 0, 'Надо было бежать! Теперь ваши кости обклюют вороны!', 14, 0, 100, 0, 0, 58379, 106101, 0, 0, 0, 'Урсок to Урсок');
INSERT INTO `creature_text` VALUES (100497, 1, 0, '|TInterface\\Icons\\spell_druid_bearhug:20|t%s направляет на персонажа |cFFFF0000$n|r |cFFFF0000|Hspell:198006|h[\"Пристальный взгляд\"]|h|r!', 41, 0, 100, 0, 0, 58386, 106100, 0, 0, 0, 'Урсок to Player');
INSERT INTO `creature_text` VALUES (100497, 2, 0, 'STOMP, SQUEEZE, CRUSH, TORMENT.!', 14, 0, 100, 0, 0, 58386, 106106, 0, 0, 0, 'Урсок to Player');
INSERT INTO `creature_text` VALUES (100497, 2, 0, 'ТОПТАТЬ, ДАВИТЬ, КРУШИТЬ, ТЕРЗАТЬ!', 14, 0, 100, 0, 0, 58386, 106106, 0, 0, 0, 'Урсок to Player');
INSERT INTO `creature_text` VALUES (100497, 2, 1, 'I\'LL MAKE A CAKE OUT OF YOU.!', 12, 0, 100, 0, 0, 58385, 108500, 0, 0, 0, 'Урсок to Player');
INSERT INTO `creature_text` VALUES (100497, 2, 1, 'Я ИЗ ТЕБЯ ЛЕПЕШКУ СДЕЛАЮ!', 12, 0, 100, 0, 0, 58385, 108500, 0, 0, 0, 'Урсок to Player');
INSERT INTO `creature_text` VALUES (100497, 3, 0, 'NIGHTMARE GROWS! DEVOURING NIGHTMARE!', 14, 0, 100, 0, 0, 58387, 106107, 0, 0, 0, 'Урсок to 0');
INSERT INTO `creature_text` VALUES (100497, 3, 0, 'КОШМАР РАСТЕТ! КОШМАР ПОЖИРАЕТ!', 14, 0, 100, 0, 0, 58387, 106107, 0, 0, 0, 'Урсок to 0');
INSERT INTO `creature_text` VALUES (100497, 3, 1, 'THE FOREST IS SHAKING IN MY FURY!', 14, 0, 100, 0, 0, 58390, 108501, 0, 0, 0, 'Урсок to 0');
INSERT INTO `creature_text` VALUES (100497, 3, 1, 'ЛЕС ТРЕПЕЩЕТ ОТ МОЕЙ ЯРОСТИ!', 14, 0, 100, 0, 0, 58390, 108501, 0, 0, 0, 'Урсок to 0');
INSERT INTO `creature_text` VALUES (100497, 3, 2, 'Волею Владыки Кошмара!', 14, 0, 100, 0, 0, 58382, 106104, 0, 0, 0, 'Урсок to Урсок');
INSERT INTO `creature_text` VALUES (100497, 3, 3, 'РР-РА!', 14, 0, 100, 0, 0, 58388, 108502, 0, 0, 0, 'Урсок to 0');
INSERT INTO `creature_text` VALUES (100497, 4, 0, 'Пелена Кошмара развеялась... я возвращаюсь... в бесконечный сон.', 14, 0, 100, 0, 0, 58383, 106102, 0, 0, 0, 'Урсок to Player');
INSERT INTO `creature_text` VALUES (100497, 4, 1, 'Свобода от Кошмара... Я тебе завидую...', 14, 0, 100, 0, 0, 58381, 106103, 0, 0, 0, 'Урсок to Урсок');
INSERT INTO `creature_text` VALUES (100497, 5, 0, '|TInterface\\Icons\\ability_deathwing_assualtaspects:20|t%s впадает в |cFFFF0000|Hspell:198388|h[\"Кровавое бешенство\"]|h|r!', 41, 0, 100, 0, 0, 58385, 106113, 0, 0, 0, 'Урсок to 0');
INSERT INTO `creature_text` VALUES (100497, 6, 0, 'Это место не властно над вами... Завидую!', 14, 0, 100, 0, 0, 58381, 106103, 0, 0, 0, 'Урсок to Урсок');
INSERT INTO `creature_text` VALUES (100497, 7, 0, 'Надо было бежать. НАДО БЫЛО ПОСЛУШАТЬ!', 14, 0, 100, 0, 0, 58389, 106105, 0, 0, 0, 'Урсок to 0');
INSERT INTO `creature_text` VALUES (100498, 0, 0, 'That feels SOO much better. Thank you, errr... what do I call you?', 12, 0, 100, 0, 0, 0, 103141, 0, 0, 0, 'Jabrul to Player');
INSERT INTO `creature_text` VALUES (100500, 0, 0, 'OOPS! It\'s better this way.', 12, 0, 100, 53, 0, 0, 103936, 0, 0, 0, 'Jabrul to Player');
INSERT INTO `creature_text` VALUES (100500, 0, 0, 'ОЙ! Так ГОРАЗДО лучше.', 12, 0, 100, 53, 0, 0, 103936, 0, 0, 0, 'Джабрул to Player');
INSERT INTO `creature_text` VALUES (100500, 1, 0, 'So that\'s Dalaran? Jabrul is impressed. To the jewelers, here?', 12, 0, 100, 589, 0, 0, 103938, 0, 0, 0, 'Jabrul to Player');
INSERT INTO `creature_text` VALUES (100500, 1, 0, 'Значит, это Даларан? Джабрул поражен. К ювелирам – сюда?', 12, 0, 100, 589, 0, 0, 103938, 0, 0, 0, 'Джабрул to Player');
INSERT INTO `creature_text` VALUES (100520, 0, 0, 'The drogbar are coming?', 12, 0, 100, 0, 0, 0, 109471, 0, 0, 0, 'Таурен из племени Речной Гривы to Player');
INSERT INTO `creature_text` VALUES (100520, 0, 1, 'Evacuate? But we have tilled this soil for generations.', 12, 0, 100, 0, 0, 0, 109476, 0, 0, 0, 'Таурен из племени Речной Гривы to Player');
INSERT INTO `creature_text` VALUES (100520, 0, 2, 'But... this is our land.', 12, 0, 100, 0, 0, 0, 109472, 0, 0, 0, 'Таурен из племени Речной Гривы to Player');
INSERT INTO `creature_text` VALUES (100520, 0, 3, 'Drogbar do not come above ground. That is something they just don\'t do.', 12, 0, 100, 0, 0, 0, 109478, 0, 0, 0, 'Таурен из племени Речной Гривы to Player');
INSERT INTO `creature_text` VALUES (100520, 0, 4, 'Bah! The drogbar can\'t win. They are just a bunch of savages.', 12, 0, 100, 0, 0, 0, 109477, 0, 0, 0, 'Таурен из племени Речной Гривы to Player');
INSERT INTO `creature_text` VALUES (100520, 0, 5, 'What? The Underking is coming? Oh no!', 12, 0, 100, 0, 0, 0, 109473, 0, 0, 0, 'Таурен из племени Речной Гривы to Player');
INSERT INTO `creature_text` VALUES (100534, 3, 0, 'Стой! Ты слишком резво машешь своим копьишком!', 12, 0, 100, 396, 0, 0, 105478, 155292, 155292, 0, 'Гарсет Верная Цель to Player');
INSERT INTO `creature_text` VALUES (100538, 0, 0, 'I\'m going to Dalaran. See you, $n.', 12, 0, 100, 1, 0, 0, 103017, 0, 0, 0, 'Тимоти Джонс to Player');
INSERT INTO `creature_text` VALUES (100543, 3, 0, 'Я в долгу не останусь.', 12, 0, 100, 0, 0, 57055, 102567, 33474, 33474, 0, 'Охотница на демонов to Player');
INSERT INTO `creature_text` VALUES (100545, 3, 0, 'Мы добудем ключ!', 12, 0, 100, 0, 0, 57123, 102571, 9857, 9857, 0, 'Охотник на демонов to Player');
INSERT INTO `creature_text` VALUES (100546, 0, 0, 'Leave the mortals alive. We must find the weapons of their so-called \'Storm-God.\'', 12, 0, 100, 0, 0, 0, 102527, 0, 0, 0, 'Лорд Кра\'вос to 0');
INSERT INTO `creature_text` VALUES (100546, 1, 0, 'Твоя душа станет угощением на пиру победителя.', 12, 0, 100, 0, 0, 0, 102528, 0, 0, 0, 'Лорд Кра\'вос to 0');
INSERT INTO `creature_text` VALUES (100546, 2, 0, 'Glory to the Legion!', 12, 0, 100, 0, 0, 0, 102539, 0, 0, 0, 'Лорд Кра\'вос to 0');
INSERT INTO `creature_text` VALUES (100546, 4, 0, 'Мой хозяин овладеет силой бури!', 12, 0, 100, 0, 0, 0, 102538, 0, 0, 0, 'Лорд Кра\'вос to 0');
INSERT INTO `creature_text` VALUES (100549, 3, 0, 'I\'m not going to die on this rock!', 12, 0, 100, 0, 0, 56819, 102557, 13930, 13930, 0, 'Охотник на демонов to Player');
INSERT INTO `creature_text` VALUES (100556, 0, 0, 'Filthy $r, I\'ll bring your head to lord Typhinius!', 12, 0, 100, 0, 0, 0, 103427, 0, 0, 0, 'Надер to Player');
INSERT INTO `creature_text` VALUES (100556, 1, 0, 'Lord Typhinius... I have failed you...', 12, 0, 100, 0, 0, 0, 103428, 0, 0, 0, 'Надер to Player');
INSERT INTO `creature_text` VALUES (100558, 0, 0, 'Скорее! Мы должны отыскать Таона, пока еще не поздно!', 12, 0, 100, 0, 0, 0, 102577, 0, 0, 0, 'Эвель Шепот Ночи to Player');
INSERT INTO `creature_text` VALUES (100571, 0, 0, 'Привет, $n. Ну и котеночек! Как думаешь, король разрешит мне его погладить?', 12, 0, 100, 0, 0, 0, 102591, 0, 0, 0, 'Ли Ли Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (100571, 1, 0, 'Ох! Магия!!! Давайте скорее посмотрим, что делает эта штука!', 12, 0, 100, 0, 0, 0, 104093, 0, 0, 0, 'Ли Ли Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (100578, 0, 0, 'За тобой преследователи, |3-6($c)! Темные всадники!', 12, 0, 100, 0, 0, 0, 102814, 0, 0, 0, 'Ревил Кост to Player');
INSERT INTO `creature_text` VALUES (100578, 1, 0, 'Эту битву мы выиграли, но впереди нас ждут другие. Быстро, в Лагерь Аридена!', 12, 0, 100, 0, 0, 0, 102816, 0, 0, 0, 'Ревил Кост to Player');
INSERT INTO `creature_text` VALUES (100578, 2, 0, 'Не отставай, |3-6($c)!', 14, 0, 100, 0, 0, 0, 102818, 0, 0, 0, 'Ревил Кост to Player');
INSERT INTO `creature_text` VALUES (100578, 3, 0, 'Эй, сзади!', 14, 0, 100, 0, 0, 0, 102822, 0, 0, 0, 'Ревил Кост to Player');
INSERT INTO `creature_text` VALUES (100578, 4, 0, 'Я восхищен тем, как ты сражаешься. Быть может, мы еще выйдем из этой передряги живыми.', 12, 0, 100, 0, 0, 0, 102824, 0, 0, 0, 'Ревил Кост to Player');
INSERT INTO `creature_text` VALUES (100578, 5, 0, 'Скорее! Нельзя терять ни минуты!', 14, 0, 100, 0, 0, 0, 102820, 0, 0, 0, 'Ревил Кост to Player');
INSERT INTO `creature_text` VALUES (100578, 6, 0, 'Riders on our flank! Be ready!', 14, 0, 100, 0, 0, 0, 102827, 0, 0, 0, 'Ревил Кост to Player');
INSERT INTO `creature_text` VALUES (100578, 7, 0, 'Надо поднажать. Мы почти у цели.', 12, 0, 100, 0, 0, 0, 102830, 0, 0, 0, 'Ревил Кост to Player');
INSERT INTO `creature_text` VALUES (100578, 9, 0, 'Here! The camp is in this clearing. Let us search it.', 12, 0, 100, 0, 0, 0, 102831, 0, 0, 0, 'Ревил Кост to Player');
INSERT INTO `creature_text` VALUES (100595, 1, 0, 'Ты не получишь боевой клинок!', 12, 0, 100, 0, 0, 0, 102723, 0, 0, 0, 'Бельбуг to Player');
INSERT INTO `creature_text` VALUES (100595, 3, 0, 'Проваливай, грязная тварь! Бельбуг нашел клинок и Бельбуг оставит его себе!', 12, 0, 100, 0, 0, 0, 106040, 8210, 8210, 0, 'Бельбуг to Player');
INSERT INTO `creature_text` VALUES (100622, 0, 0, '$gПовелитель небес:Повелительница небес;, если ты $gзакончил:закончила; улучшать свое оружие, я хочу тебе кое-что показать.', 14, 0, 100, 0, 0, 0, 105599, 0, 0, 0, 'Даника Возродительница to Player');
INSERT INTO `creature_text` VALUES (100622, 0, 0, 'Lord of Heaven, if you\'ve finished improving your weapons, I want to show you something.', 14, 0, 100, 0, 0, 0, 105599, 0, 0, 0, 'Danica the Rebirthright to Player');
INSERT INTO `creature_text` VALUES (100622, 1, 0, 'If you will follow me. The Eye of Odyn is a powerful scrying and communication device. With it you will be able to see the Broken Isles, send orders to your champions, and enact your own special operations. Skyseer Ghrent will help you manage the Eye.', 12, 0, 100, 0, 0, 0, 105603, 0, 0, 0, 'Даника Возродительница to Player');
INSERT INTO `creature_text` VALUES (100622, 2, 0, 'Here comes heavenly seer Grant, and Eye of Odin. |3-6($ct), may your orders be passed on quickly, and may there be only victory in battle!', 12, 0, 100, 0, 0, 0, 105604, 0, 0, 0, 'Даника Возродительница to Player');
INSERT INTO `creature_text` VALUES (100622, 2, 0, 'А вот и небесный провидец Грент, и Око Одина. |3-6($ct), пусть твои приказы передаются быстро, и пусть в бою тебя ждет только победа!', 12, 0, 100, 0, 0, 0, 105604, 0, 0, 0, 'Даника Возродительница to Player');
INSERT INTO `creature_text` VALUES (100623, 0, 0, 'Tell me if you need my help, |3-6($c).', 12, 0, 100, 0, 0, 0, 43342, 0, 0, 0, 'Тиффани Картье to Player');
INSERT INTO `creature_text` VALUES (100633, 3, 0, 'Beware the shadows, for that is where I hunt!', 12, 0, 100, 23, 0, 0, 102708, 714634, 714634, 0, 'Death Hunter Moorgoth to Player');
INSERT INTO `creature_text` VALUES (100633, 3, 0, 'Узри Мургота, повелителя черной бездны!', 12, 0, 100, 273, 0, 0, 102709, 22881, 22881, 0, 'Охотник смерти Мургот to Player');
INSERT INTO `creature_text` VALUES (100652, 0, 0, 'A well-earned victory! We must, however, stay vigilant. Deep inside the Nightmare Xavius remains, unvanquished. The enemy you fought here was merely his shadow.', 12, 0, 100, 0, 0, 54496, 102808, 0, 0, 0, 'Малфурион Ярость Бури to Тень Ксавия');
INSERT INTO `creature_text` VALUES (100660, 3, 0, 'Вождь! $c здесь. Осталось дождаться Сильвану.', 12, 0, 100, 1, 0, 0, 120743, 1940, 1940, 0, 'Лор\'темар Терон to Player');
INSERT INTO `creature_text` VALUES (100664, 3, 0, 'Кажется, кто-то из них уже вернулся. Такое впечатление, что все плохо.', 12, 0, 100, 274, 0, 0, 102997, 1961, 1961, 0, 'Жительница Громового Утеса');
INSERT INTO `creature_text` VALUES (100671, 0, 0, 'Oh really? You think I\'ll just give them up that easily? Over my dead body!', 12, 0, 100, 1, 0, 0, 102853, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (100673, 3, 0, 'Среди моего народа ходили легенды о таких духах, но мне никогда не доводилось встретиться с ними.', 12, 0, 100, 1, 0, 0, 106254, 119237, 119237, 0, 'Кари Повелительница Зверей to Player');
INSERT INTO `creature_text` VALUES (100675, 0, 0, 'Say the word and I shall reveal the enemy!', 12, 0, 100, 0, 0, 0, 122115, 0, 0, 0, 'Джейс Темный Ткач to Player');
INSERT INTO `creature_text` VALUES (100679, 3, 0, 'Что произошло на Расколотом берегу?', 12, 0, 100, 0, 0, 0, 102994, 26787, 26787, 0, 'Житель Кезана');
INSERT INTO `creature_text` VALUES (100693, 3, 0, 'Этот орел смог бы летать еще выше и быстрее с механическими крыльями!', 12, 0, 100, 273, 0, 0, 106255, 121653, 121653, 0, 'Бамм Взрыбомм to Player');
INSERT INTO `creature_text` VALUES (100695, 3, 0, 'Орел-лоа! Хвала духам!', 12, 0, 100, 0, 0, 0, 106253, 119236, 119236, 0, 'Охотница Кузари to Player');
INSERT INTO `creature_text` VALUES (100697, 3, 0, 'Это такая честь – участвовать в возрождении Незримого пути!', 12, 0, 100, 1, 0, 0, 104208, 31323, 31323, 0, 'Ними Светлый Оплот to Player');
INSERT INTO `creature_text` VALUES (100702, 3, 0, 'На дорогах к приюту, похоже, чисто.', 12, 0, 100, 0, 0, 0, 106360, 5694, 5694, 0, 'Разведчик Сияющее Копье to Player');
INSERT INTO `creature_text` VALUES (100702, 3, 0, 'Серебряный союз готов помочь Незримому пути.', 12, 0, 100, 0, 0, 0, 106361, 5273, 5273, 0, 'Разведчик Сияющее Копье to Player');
INSERT INTO `creature_text` VALUES (100704, 0, 0, 'Мы возьмем то, что принадлежит нам!', 14, 0, 100, 0, 0, 0, 104860, 0, 0, 0, 'Темный всадник to Player');
INSERT INTO `creature_text` VALUES (100707, 0, 0, 'Артефакт зовет нас!', 14, 0, 100, 0, 0, 0, 104861, 0, 0, 0, 'Темный всадник to Player');
INSERT INTO `creature_text` VALUES (100727, 3, 0, 'Oh... hold on! I\'ll... I\'ll figure out some way to follow you! Just hold on!', 14, 0, 100, 20, 0, 62074, 103099, 277660, 277660, 0, 'Ли Ли Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (100727, 4, 0, 'That was awesome!! I was like, \"Oh no!\" And you were like, \"Don\'t worry, leave it to me!\"', 12, 0, 100, 0, 0, 0, 0, 86583, 86583, 0, 'Ли Ли Буйный Портер to Тайфуний');
INSERT INTO `creature_text` VALUES (100729, 0, 0, 'Understand, I cannot be a part of what you must do. When you are finished, meet me outside Karazhan.', 12, 0, 100, 0, 0, 0, 104670, 0, 0, 0, 'Ревил Кост to Player');
INSERT INTO `creature_text` VALUES (100729, 1, 0, 'I will visit the church and pray to the Light for mercy upon both our souls.', 12, 0, 100, 0, 0, 0, 104671, 0, 0, 0, 'Ревил Кост to Player');
INSERT INTO `creature_text` VALUES (100738, 0, 0, 'На тебе знак самого Одина. Как это возможно?', 12, 0, 100, 1, 0, 0, 119416, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (100738, 1, 0, 'Стойте! Этот герой здесь по делу.', 12, 0, 100, 1, 0, 0, 103115, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (100738, 2, 0, 'Готовься к бою! Даже валарьяры не смеют подходить так близко к владениям Хелии.', 12, 0, 100, 1, 0, 0, 119317, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (100738, 3, 0, 'Доблесть Одина сравнима лишь с его аппетитом. Истинный воин.', 12, 0, 100, 1, 0, 0, 119309, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (100738, 4, 0, 'Демоны стекаются к Вратам Доблести. Нам бы пригодилась твоя помощь.', 12, 0, 100, 1, 0, 0, 108085, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (100740, 0, 0, 'Get me out of this thing!', 14, 0, 100, 0, 0, 0, 103081, 0, 0, 0, 'Ли Ли Буйный Портер to Загадочный вихрь');
INSERT INTO `creature_text` VALUES (100740, 1, 0, 'Ohhhh... My head still feels like it\'s spinning!', 12, 0, 100, 0, 0, 0, 103083, 0, 0, 0, 'Ли Ли Буйный Портер to Загадочный вихрь');
INSERT INTO `creature_text` VALUES (100740, 2, 0, 'What\'s going on over there? Hello? Hi!', 12, 0, 100, 0, 0, 0, 103084, 0, 0, 0, 'Ли Ли Буйный Портер to 100741');
INSERT INTO `creature_text` VALUES (100740, 3, 0, 'They don\'t look happy to see us.', 12, 0, 100, 0, 0, 0, 103086, 0, 0, 0, 'Ли Ли Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (100740, 4, 0, 'Hmm... These tornadoes simply won\'t do.', 12, 0, 100, 0, 0, 0, 103305, 0, 0, 0, 'Ли Ли Буйный Портер to Малый песчаник');
INSERT INTO `creature_text` VALUES (100740, 5, 0, 'Wanna see a trick my uncle Chen taught me?', 12, 0, 100, 0, 0, 0, 104266, 0, 0, 0, 'Ли Ли Буйный Портер to Малый песчаник');
INSERT INTO `creature_text` VALUES (100740, 6, 0, 'Wheeeee! Bet you can\'t catch me!', 12, 0, 100, 0, 0, 0, 103088, 0, 0, 0, 'Ли Ли Буйный Портер to Малый песчаник');
INSERT INTO `creature_text` VALUES (100740, 7, 0, 'Pretty neat, huh?', 12, 0, 100, 0, 0, 0, 103113, 0, 0, 0, 'Ли Ли Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (100740, 8, 0, 'Look at those big sparking orbs! I wonder what happens if we poke one?', 12, 0, 100, 0, 0, 0, 103092, 0, 0, 0, 'Ли Ли Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (100740, 9, 0, 'Blah, blah, blah! You sure do talk a lot.', 12, 0, 100, 0, 0, 0, 103091, 0, 0, 0, 'Ли Ли Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (100749, 0, 0, 'Да уж, кожа да кости, но сойдешь, куда деваться.', 12, 0, 100, 0, 0, 0, 103462, 0, 0, 0, 'Госпожа Торвис to Player');
INSERT INTO `creature_text` VALUES (100760, 0, 0, 'Неужели все мои слуги – слабаки? Ладно, я сделаю это сам!', 14, 0, 100, 0, 0, 0, 103300, 0, 0, 0, 'Тайфуний to Player');
INSERT INTO `creature_text` VALUES (100760, 1, 0, 'Ветра порвут тебя в клочья!', 12, 0, 100, 0, 0, 0, 104098, 0, 0, 0, 'Тайфуний to 0');
INSERT INTO `creature_text` VALUES (100762, 0, 0, 'Смертные в моих владениях? Они не должны помешать моим планам! Убить их всех!', 14, 0, 100, 0, 0, 0, 103293, 0, 0, 0, 'Проекция Тайфуния to Player');
INSERT INTO `creature_text` VALUES (100762, 1, 0, 'Жалкие смертные, теперь вы в моем царстве! Никто вас здесь не спасет!', 14, 0, 100, 0, 0, 0, 103294, 0, 0, 0, 'Проекция Тайфуния to Player');
INSERT INTO `creature_text` VALUES (100762, 2, 0, 'Дерзкие глупцы! Узрите мощь, которая мне повинуется! А\'бурак ва Ра\'айд хайя!', 14, 0, 100, 0, 0, 0, 103295, 0, 0, 0, 'Проекция Тайфуния to Player');
INSERT INTO `creature_text` VALUES (100762, 3, 0, 'Ничтожная победа – и, возможно, последняя. Наслаждайся ею, пока можешь!', 12, 0, 100, 0, 0, 0, 103296, 0, 0, 0, 'Проекция Тайфуния to Порождение Тайфуния');
INSERT INTO `creature_text` VALUES (100762, 4, 0, 'Придите, служители бури! Защищайте своего господина!', 14, 0, 100, 0, 0, 0, 103297, 0, 0, 0, 'Проекция Тайфуния to 983');
INSERT INTO `creature_text` VALUES (100762, 5, 0, 'На\'сер, услышь зов господина!', 14, 0, 100, 0, 0, 0, 103482, 0, 0, 0, 'Проекция Тайфуния to Воющие ветры');
INSERT INTO `creature_text` VALUES (100762, 6, 0, 'Это недопустимо! Мелезан, ты уничтожишь их – или я низвергну твою ничтожную сущность в Подземье!', 14, 0, 100, 0, 0, 0, 103298, 0, 0, 0, 'Проекция Тайфуния to Воющие ветры');
INSERT INTO `creature_text` VALUES (100762, 7, 0, 'Эти смертные не желают умирать! Заурак, защити своего господина!', 14, 0, 100, 0, 0, 0, 103299, 0, 0, 0, 'Проекция Тайфуния to Проекция Тайфуния');
INSERT INTO `creature_text` VALUES (100777, 3, 0, 'Ночноглаз благодарно ухает.', 16, 0, 100, 0, 0, 0, 103174, 22768, 22768, 0, 'Ночноглаз to Player');
INSERT INTO `creature_text` VALUES (100778, 0, 0, 'Прости меня… Элисанда…', 12, 0, 100, 0, 0, 65655, 116779, 0, 0, 0, 'Ночнорожденный-зверолов to Животное');
INSERT INTO `creature_text` VALUES (100778, 1, 0, 'Жалкое отребье!', 12, 0, 100, 0, 0, 65666, 116699, 0, 0, 0, 'Ночнорожденный-зверолов to Player');
INSERT INTO `creature_text` VALUES (100778, 3, 0, 'Анат\'ашар!', 12, 0, 100, 0, 0, 65669, 116709, 0, 0, 0, 'Ночнорожденный-зверолов to Player');
INSERT INTO `creature_text` VALUES (100779, 0, 0, 'I will help you. Let\'s banish this rubbish from Tel\'anor.', 12, 0, 100, 0, 0, 65623, 103154, 0, 0, 0, 'Таэдрис Песнь Пера to Player');
INSERT INTO `creature_text` VALUES (100779, 1, 0, 'Вот и все. Латара, любовь моя. Я буду сражаться за тебя. За нас.', 12, 0, 100, 0, 0, 65624, 103813, 0, 0, 0, 'Таэдрис Песнь Пера to Player');
INSERT INTO `creature_text` VALUES (100779, 2, 0, 'Благодаря тебе я снова обрел цель в жизни. Встретимся в Шал\'Аране. Мне еще понадобится источник магии.', 12, 0, 100, 0, 0, 65622, 103155, 0, 0, 0, 'Таэдрис Песнь Пера to Player');
INSERT INTO `creature_text` VALUES (100786, 0, 0, 'Снегокрыл спускается с небес и зависает в воздухе, чего-то ожидая. Похоже, у него для вас сообщение!', 42, 0, 100, 0, 0, 0, 103162, 0, 0, 0, 'Снегокрыл to Player');
INSERT INTO `creature_text` VALUES (100790, 0, 0, 'Подойди поближе, и мы начнем.', 12, 0, 100, 1, 0, 0, 103181, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (100790, 1, 0, 'А теперь ступай!', 12, 0, 100, 1, 0, 0, 103187, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (100790, 2, 0, 'Носи же наш знак с гордостью, и, быть может, Один благословит тебя.', 12, 0, 100, 1, 0, 0, 103186, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (100790, 3, 0, 'Склонись, смертное создание.', 12, 0, 100, 1, 0, 0, 103182, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (100790, 4, 0, 'Твоя доблесть бесспорна, но впереди – суровые испытания.', 12, 0, 100, 1, 0, 0, 103185, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (100790, 5, 0, 'Доблестными деяниями Хирья заслужила право на этот священный ритуал. Она вознесется и станет моей избранной валь\'кирой. Да будет вечно звучать ее имя в этих чертогах.', 12, 0, 100, 1, 0, 0, 102965, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (100812, 0, 0, 'The sewer entrance is this way. Follow me!', 12, 0, 100, 0, 0, 0, 104759, 0, 0, 0, 'Ревил Кост to Player');
INSERT INTO `creature_text` VALUES (100812, 1, 0, 'The worgen led you here as well? This must be where the scythe is kept!', 12, 0, 100, 1, 0, 61874, 104436, 0, 0, 0, 'Ревил Кост to Player');
INSERT INTO `creature_text` VALUES (100823, 3, 0, 'Не тревожься о подкреплении. Я буду защищать портал любой ценой!', 12, 0, 100, 0, 0, 71817, 105725, 5137, 5137, 0, 'Лиана Отчаяние Тьмы to Player');
INSERT INTO `creature_text` VALUES (100836, 0, 0, 'Вы стоите на пороге забвения! Ваши жизни принадлежат инквизиции!', 14, 0, 100, 0, 0, 0, 103558, 0, 0, 0, 'Глашатай Зарбизульд to Player');
INSERT INTO `creature_text` VALUES (100836, 1, 0, 'Жизнь... за... инквизицию!', 12, 0, 100, 0, 0, 0, 103540, 0, 0, 0, 'Глашатай Зарбизульд to Player');
INSERT INTO `creature_text` VALUES (100850, 0, 0, 'Не думай, что ты победил! Хранитель, делай свое дело!', 14, 0, 100, 0, 0, 0, 103602, 0, 0, 0, 'Ариден to Ариден');
INSERT INTO `creature_text` VALUES (100850, 1, 0, 'If you want the blade, death knight, come and claim it!', 14, 0, 100, 0, 0, 0, 104762, 0, 0, 0, 'Ариден to Player');
INSERT INTO `creature_text` VALUES (100860, 0, 0, 'Jabrul was on his way to the harpy nest when suddenly he was ambushed. Lets go there and look for those rocks.', 12, 0, 100, 0, 0, 0, 104286, 0, 0, 0, 'Jabrul to Player');
INSERT INTO `creature_text` VALUES (100860, 0, 0, 'Джабрул шел к гнезду гарпий, как вдруг на него напали из засады. Давай отправимся туда и поищем эти камни.', 12, 0, 100, 0, 0, 0, 104286, 0, 0, 0, 'Джабрул to Player');
INSERT INTO `creature_text` VALUES (100860, 1, 0, 'Keep the harpies away from Jabrul and protect him while he looks for suitable necklaces.', 12, 0, 100, 0, 0, 0, 103247, 0, 0, 0, 'Jabrul to Player');
INSERT INTO `creature_text` VALUES (100860, 1, 0, 'Не подпускай к Джабрулу гарпий и защищай его, пока он будет искать подходящие ожерелья.', 12, 0, 100, 0, 0, 0, 103247, 0, 0, 0, 'Джабрул to Player');
INSERT INTO `creature_text` VALUES (100860, 2, 0, 'Эти гарпии совсем не берегут свои украшения. Почти все они повреждены.', 12, 0, 100, 0, 0, 0, 103248, 0, 0, 0, 'Джабрул to Player');
INSERT INTO `creature_text` VALUES (100860, 3, 0, 'Джабрул нашел один из камней, которые ему нужны. Второй должен быть где-то совсем рядом.', 12, 0, 100, 0, 0, 0, 103249, 0, 0, 0, 'Джабрул to Player');
INSERT INTO `creature_text` VALUES (100860, 4, 0, 'Джабрулу снова потребуется твоя защита. Джабрул постарается сделать все быстро.', 12, 0, 100, 0, 0, 0, 103251, 0, 0, 0, 'Джабрул to Player');
INSERT INTO `creature_text` VALUES (100860, 5, 0, 'Джабрул не понимает, как можно относиться к ювелирным изделиям с таким пренебрежением.', 12, 0, 100, 0, 0, 0, 103252, 0, 0, 0, 'Джабрул to Player');
INSERT INTO `creature_text` VALUES (100860, 6, 0, 'Джабрул нашел второй камень. Теперь пора возвращаться в безопасное место, к ночным эльфам.', 12, 0, 100, 0, 0, 0, 103253, 0, 0, 0, 'Джабрул to Player');
INSERT INTO `creature_text` VALUES (100860, 7, 0, 'Джабрул будет ждать тебя в домике ночных эльфов. Приходи к нему, когда будешь $gготов:готова,.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Джабрул to Player');
INSERT INTO `creature_text` VALUES (100866, 0, 0, 'I accept your pledge of loyalty, $n. The Horde endures!', 12, 0, 100, 1, 0, 0, 121949, 0, 0, 0, 'Леди Сильвана Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (100867, 3, 0, 'Так и будет.', 12, 0, 100, 1, 0, 0, 121950, 4438, 4438, 0, 'Лор\'темар Терон to Player');
INSERT INTO `creature_text` VALUES (100870, 3, 0, 'Хорошо сказано!', 12, 0, 100, 1, 0, 0, 121951, 6196, 6196, 0, 'Бейн Кровавое Копыто to Player');
INSERT INTO `creature_text` VALUES (100872, 1, 0, 'Содрать плоть! Раскидать кости!', 12, 0, 100, 0, 0, 61238, 121827, 0, 0, 0, 'Опустошительница из племени Каменной Крови to Джабрул');
INSERT INTO `creature_text` VALUES (100873, 0, 0, 'Say the word and I shall reveal the hidden menace!', 12, 0, 100, 0, 0, 0, 122071, 0, 0, 0, 'Аллари Пожирательница Душ to Player');
INSERT INTO `creature_text` VALUES (100878, 0, 0, 'Сегодня мы нанесли Легиону удар, но впереди еще много дел.', 12, 0, 100, 0, 0, 67600, 105972, 0, 0, 0, 'Лиана Отчаяние Тьмы to Player');
