INSERT INTO `creature_text` VALUES (101061, 0, 0, 'Привет, $n. Наконец-то мне удалось тебя найти. Тебя ждут на Лунной поляне.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Верховный друид Хамуул Рунический Тотем to Player');
