INSERT INTO `creature_text` VALUES (101064, 0, 0, 'Зен\'табру найти несложно – ее лагерь на западном берегу озера. Наралекс стал лагерем на восточном берегу, но он любит внезапно сниматься с места. Ты, главное, смотри в оба да навостри уши – и тогда отыщешь его без труда.', 12, 0, 100, 0, 0, 0, 106011, 0, 0, 0, 'Верховный друид Хамуул Рунический Тотем to Player');
INSERT INTO `creature_text` VALUES (101064, 1, 0, 'Малфурион ждет тебя у нашей обители к востоку отсюда. Следуй за мной.', 12, 0, 100, 0, 0, 0, 103484, 0, 0, 0, 'Верховный друид Хамуул Рунический Тотем to Player');
INSERT INTO `creature_text` VALUES (101065, 3, 0, 'When you are ready, step through the portal into the Dreamway. I will meet with you on the other side.', 12, 0, 100, 0, 0, 0, 106489, 14684, 14684, 0, 'Хранитель Ремул to Player');
INSERT INTO `creature_text` VALUES (101075, 0, 0, 'Take me, Mother of Worms!', 14, 0, 100, 0, 0, 0, 114791, 0, 0, 0, 'Преданный заклинатель червей to 0');
