INSERT INTO `creature_text` VALUES (101083, 3, 0, 'Значит, попросим их оттуда убраться.', 12, 0, 100, 1, 0, 0, 119499, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (101083, 4, 0, 'Будь начеку. Помраченные не станут доверять тебе сразу.', 12, 0, 100, 1, 0, 0, 119273, 0, 0, 0, '');
