INSERT INTO `creature_text` VALUES (101148, 2, 0, 'Никому нельзя доверять – даже собственному разуму!', 12, 0, 100, 0, 0, 0, 105388, 0, 0, 0, 'Сумеречный дьякон Фартинг to 0');
INSERT INTO `creature_text` VALUES (101148, 3, 0, 'Видишь, с какой легкостью я подчиняю твой разум своей воле?', 12, 0, 100, 0, 0, 0, 105390, 0, 0, 0, 'Сумеречный дьякон Фартинг to 0');
INSERT INTO `creature_text` VALUES (101148, 4, 0, 'Тьма поглотит все!', 12, 0, 100, 0, 0, 0, 105391, 0, 0, 0, 'Сумеречный дьякон Фартинг to 0');
INSERT INTO `creature_text` VALUES (101195, 0, 0, 'Кошмар разросся сильнее, чем мы полагали. Я должен подумать, как нам быть.', 12, 0, 100, 396, 0, 6515, 121599, 0, 0, 0, 'Ренсар Большое Копыто to Player');
INSERT INTO `creature_text` VALUES (101195, 1, 0, 'Ступай к Семени веков, Верховный друид. Там мы раскроем все тайны твоего оружия.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Ренсар Большое Копыто to Player');
INSERT INTO `creature_text` VALUES (101195, 2, 0, 'Сквозь него течет сущность природы! В твоих руках оно станет грозой темных сил, что хотят уничтожить Азерот! Анду-фала-дор!', 12, 0, 100, 0, 0, 69029, 118565, 0, 0, 0, 'Ренсар Большое Копыто to Player');
INSERT INTO `creature_text` VALUES (101254, 0, 0, 'Используйте |TInterface\\Icons\\spell_warlock_demonicportal_green.blp:20|t |cFFFF0000|Hspell:111771|h[демонические врата]|h|r, чтобы миновать преграду Аридена!', 41, 0, 100, 0, 0, 60279, 108803, 0, 0, 0, 'Контроллер - шаблон to 988');
INSERT INTO `creature_text` VALUES (101254, 1, 0, 'Используйте |TInterfaceIconsspell_deathknight_strangulate.blp:20|t |cFFFF0000|Hspell:49576|h[\"Хватку смерти\"]|h|r, чтобы прервать заклинание Аридена.', 41, 0, 100, 0, 0, 60279, 120451, 0, 0, 0, 'Контроллер - шаблон to 988');
INSERT INTO `creature_text` VALUES (101259, 0, 0, 'You\'re not one of my pack. What brings you to the den?', 12, 0, 100, 0, 0, 0, 107679, 0, 0, 0, 'Леа Каменная Лапа to Player');
INSERT INTO `creature_text` VALUES (101259, 1, 0, 'Don\'t leave the Dreamgrove without the moonwell water, it\'s essential! I\'ll wait for your arrival in Grizzly Hills.', 12, 0, 100, 0, 0, 0, 107608, 0, 0, 0, 'Леа Каменная Лапа to Player');
INSERT INTO `creature_text` VALUES (101269, 0, 0, 'Мое место займут другие.', 12, 0, 100, 0, 0, 0, 15167, 0, 0, 0, 'Верховный инквизитор Кормаладон to Player');
INSERT INTO `creature_text` VALUES (101282, 0, 0, 'Берегись. Духи этого места источают темную энергию. Лучше не подходить близко.', 12, 0, 100, 0, 0, 0, 103594, 0, 0, 0, 'Ревил Кост to Player');
INSERT INTO `creature_text` VALUES (101282, 2, 0, 'Дело сделано. Темные всадники повержены.', 12, 0, 100, 0, 0, 0, 103618, 0, 0, 0, 'Ревил Кост to Player');
INSERT INTO `creature_text` VALUES (101282, 3, 0, 'Теперь я заберу эти краденые артефакты и верну их законным владельцам.', 12, 0, 100, 0, 0, 0, 103619, 0, 0, 0, 'Ревил Кост to Player');
INSERT INTO `creature_text` VALUES (101282, 4, 0, 'Честно говоря, я не ожидал обрести в тебе союзника, |3-6($c). Оружие твое. Будем надеяться, оно поможет изменить ход войны.', 12, 0, 100, 0, 0, 0, 103620, 0, 0, 0, 'Ревил Кост to Player');
INSERT INTO `creature_text` VALUES (101282, 5, 0, 'The Dark Riders lost this battle, but I fear they will soon regroup. We had best be gone before they do.', 12, 0, 100, 0, 0, 0, 103621, 0, 0, 0, 'Ревил Кост to Player');
INSERT INTO `creature_text` VALUES (101286, 0, 0, 'Путь прегражден!', 12, 0, 100, 0, 0, 0, 103596, 0, 0, 0, 'Ревил Кост to Player');
INSERT INTO `creature_text` VALUES (101286, 1, 0, 'Сюда! Эту реликвию ты ищешь?', 12, 0, 100, 0, 0, 0, 103684, 0, 0, 0, 'Ревил Кост to Player');
INSERT INTO `creature_text` VALUES (101313, 3, 0, 'Маги организовали для нас портал. Идем. Я хочу рассказать тебе кое-что о наару и дренеях.', 12, 0, 100, 0, 0, 63401, 104673, 847, 847, 0, 'Пророк Велен to Player');
INSERT INTO `creature_text` VALUES (101313, 3, 0, 'На протяжении очень долгих лет дренеи скрывались от Пылающего Легиона. Все это время наару очень помогали нам; иногда даже ценой своей жизни. Без их помощи нам бы точно пришел конец.', 12, 0, 100, 0, 0, 0, 0, 15703, 15703, 0, 'Пророк Велен to Player');
INSERT INTO `creature_text` VALUES (101314, 0, 0, 'Уж извини за такую секретность – старая привычка. Меня зовут Алонсий Фаол. За свою жизнь мне довелось побывать и главой церкви, и безмозглым слугой Плети, и организатором тайной войны. Но в первую очередь я всегда был и остаюсь жрецом.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Алонсий Фаол to Player');
INSERT INTO `creature_text` VALUES (101314, 1, 0, 'Легион представляет собой угрозу, которую нельзя оставлять без внимания, поэтому я и решил снова выйти из тени. Сейчас я собираю все духовенство, чтобы выработать совместный план противостояния этой угрозе.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Алонсий Фаол to Player');
INSERT INTO `creature_text` VALUES (101314, 2, 0, 'И по моему замыслу возглавить этот совет должен поистине великий жрец, своими деяниями снискавший славу героя не только среди своих, а повсеместно. Я полагаю, что ты и есть $gтот самый герой:та самая героиня;, но нужно будет еще и убедить в этом остальных. Ты $gготов:готова; к испытаниям?', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Алонсий Фаол to Player');
INSERT INTO `creature_text` VALUES (101314, 3, 0, 'Клинок перенесли куда-то в северо-западную часть Тирисфаля. Я отметил на твоей карте место, где тебе следует начать свои поиски.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Алонсий Фаол to Player');
INSERT INTO `creature_text` VALUES (101326, 0, 0, '$n поглощает силу прославленного предка.', 16, 0, 100, 0, 0, 0, 103666, 0, 0, 0, 'Прославленный предок to Король Тор');
INSERT INTO `creature_text` VALUES (101344, 0, 0, 'Отлично, теперь зайди в портал в Даларанский кратер, а затем просто лети на север. Он будет тебя ждать!', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Прячущая лицо жрица to Player');
INSERT INTO `creature_text` VALUES (101362, 0, 0, 'Vile beasts of the nightmare, you will never take my claws!', 14, 0, 100, 0, 0, 0, 103685, 0, 0, 0, 'Дух Урсока to 0');
INSERT INTO `creature_text` VALUES (101386, 0, 0, 'Foolish mortals! Soon, this place will belong to the master!', 14, 0, 100, 0, 0, 0, 107950, 0, 0, 0, 'Осквернитель из племени Гнилого Копыта to Player');
INSERT INTO `creature_text` VALUES (101390, 0, 0, 'Yes, Lord Xavius! I\'ll be happy to tear them apart in your name!', 12, 0, 100, 0, 0, 0, 108230, 0, 0, 0, 'Верховный осквернитель Малитар to 101403');
INSERT INTO `creature_text` VALUES (101390, 1, 0, 'AAARRRRGGGHHHH!!! THE CLAWS! THEY THIRST FOR BLOOD!!', 14, 0, 100, 0, 0, 0, 108231, 0, 0, 0, 'Верховный осквернитель Малитар to 101403');
INSERT INTO `creature_text` VALUES (101393, 0, 0, 'I heard your call for aid, great one. Are there any teeny lil\' fuzzy animals in danger? Oh, oh my!', 12, 0, 100, 0, 0, 0, 108041, 0, 0, 0, 'Милуна to Player');
INSERT INTO `creature_text` VALUES (101393, 1, 0, '%s faints and falls onto the ground.', 41, 0, 100, 0, 0, 0, 103700, 0, 0, 0, 'Милуна to 0');
INSERT INTO `creature_text` VALUES (101430, 0, 0, 'Мойра говорила, что ты придешь. Я Шлакомолот. Вот исполняю семейный долг – тусуюсь тут с этими паразитами. Давай-ка я сниму этот барьер.', 12, 0, 100, 0, 0, 0, 104103, 0, 0, 0, 'Повелитель теней Шлакомолот to Сумеречная - изогнутый клинок');
INSERT INTO `creature_text` VALUES (101430, 1, 0, 'Темный Дьякон уже начал свой ритуал там внизу. Чтобы добраться до него, нам сперва надо будет сделать небольшую работенку за следующей дверью. Если есть какие вопросы – спрашивай, но имей в виду, что нам нужно поторопиться!', 12, 0, 100, 0, 0, 0, 104104, 0, 0, 0, 'Повелитель теней Шлакомолот to Сумеречная - изогнутый клинок');
INSERT INTO `creature_text` VALUES (101441, 0, 0, 'Направляешься на Расколотый берег? Зеомий вмиг доставит тебя туда, куда нужно.', 12, 0, 100, 0, 0, 0, 105157, 0, 0, 0, 'Герцог Ланкрал to Player');
INSERT INTO `creature_text` VALUES (101476, 0, 0, 'Раскаленный углешкур получает |cFFFF0000|Hspell:200672|h[\"Кристальную нестабильность\"]|h|r!', 41, 0, 100, 0, 0, 0, 114713, 0, 0, 0, 'Раскаленный углешкур to Кристаллическая стена');
INSERT INTO `creature_text` VALUES (101492, 0, 0, 'Well why didn\'t you just say so to begin with? There aren\'t any guards around, we\'re perfectly safe to speak freely.', 12, 0, 100, 1, 0, 0, 108787, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (101492, 1, 0, 'And here is your diamond. A pleasure doing business with you. Please return soon.', 12, 0, 100, 1, 0, 0, 103396, 0, 0, 0, 'Miss Xulang to Player');
INSERT INTO `creature_text` VALUES (101499, 0, 0, 'Достаточно.', 12, 0, 100, 0, 0, 67418, 103939, 0, 0, 0, 'Главный телемант Окулет to Player');
INSERT INTO `creature_text` VALUES (101499, 1, 0, 'Ты не из дворца... но и не демон... по крайней мере, не полностью.', 12, 0, 100, 274, 0, 67419, 103940, 0, 0, 0, 'Главный телемант Окулет to Player');
INSERT INTO `creature_text` VALUES (101499, 2, 0, 'Что это? Монета...', 12, 0, 100, 0, 0, 67421, 103949, 0, 0, 0, 'Главный телемант Окулет to Player');
INSERT INTO `creature_text` VALUES (101499, 3, 0, 'Тут знак темной лилии... Печать первой колдуньи! Так она жива?', 12, 0, 100, 1, 0, 67422, 103950, 0, 0, 0, 'Главный телемант Окулет to Player');
INSERT INTO `creature_text` VALUES (101499, 4, 0, 'Значит, еще есть шанс... Скорее! Мне нужна твоя помощь.', 12, 0, 100, 1, 0, 67423, 103969, 0, 0, 0, 'Главный телемант Окулет to Player');
INSERT INTO `creature_text` VALUES (101499, 5, 0, 'Clear. Trap.', 12, 0, 100, 1, 0, 67424, 103951, 0, 0, 0, 'Главный телемант Окулет to Player');
INSERT INTO `creature_text` VALUES (101499, 6, 0, 'Н\'ет ана!', 12, 0, 100, 463, 0, 67425, 103967, 0, 0, 0, 'Главный телемант Окулет to Player');
INSERT INTO `creature_text` VALUES (101499, 7, 0, 'Телепорт снаружи. Активируй его и вставай на площадку.', 12, 0, 100, 0, 0, 67484, 103970, 0, 0, 0, 'Главный телемант Окулет to Player');
INSERT INTO `creature_text` VALUES (101499, 8, 0, 'Давай, я за тобой.', 12, 0, 100, 1, 0, 67287, 104149, 0, 0, 0, 'Главный телемант Окулет to Player');
INSERT INTO `creature_text` VALUES (101499, 9, 0, 'Назовись, пока я не отправил тебя в пучину Великого моря.', 12, 0, 100, 25, 0, 67420, 103943, 0, 0, 0, 'Главный телемант Окулет to Player');
INSERT INTO `creature_text` VALUES (101506, 1, 0, 'Пылающий Легион поглотит все.', 12, 0, 100, 0, 0, 55198, 102133, 0, 0, 0, 'Страж Скверны - штурмовик to Player');
INSERT INTO `creature_text` VALUES (101507, 1, 0, 'Нас больше, и мы сильнее.', 12, 0, 100, 0, 0, 55367, 98764, 0, 0, 0, 'Страж гнева - легат to Player');
INSERT INTO `creature_text` VALUES (101508, 1, 0, 'От меня не спрячешься.', 12, 0, 100, 0, 0, 55374, 102155, 0, 0, 0, 'Страж ужаса to Player');
INSERT INTO `creature_text` VALUES (101513, 0, 0, 'Hear, hear. I\'d like to propose a toast.', 12, 0, 100, 0, 0, 0, 104699, 0, 0, 0, 'Лорд Джорах Черный Ворон to Player');
INSERT INTO `creature_text` VALUES (101513, 1, 0, 'Today, $p secured the legendary Dreadblades, and in doing so has enabled us to strike at the Legion directly.', 12, 0, 100, 0, 0, 0, 104703, 0, 0, 0, 'Лорд Джорах Черный Ворон to Player');
INSERT INTO `creature_text` VALUES (101513, 2, 0, 'Join me in raising a glass not only to our newest Shadow, but to the dawn of a new era for the Uncrowned.', 12, 0, 100, 0, 0, 0, 109228, 0, 0, 0, 'Лорд Джорах Черный Ворон to Player');
INSERT INTO `creature_text` VALUES (101513, 3, 0, 'VanCleef, the Uncrowned still have use of you. You have a choice - live and work as one of $p\'s agents... or die where you stand.', 12, 0, 100, 0, 0, 0, 104822, 0, 0, 0, 'Лорд Джорах Черный Ворон to Ванесса ван Клиф');
INSERT INTO `creature_text` VALUES (101513, 4, 0, 'Then it\'s settled. Congratulations, $p. VanCleef is one of our best - as you\'ve seen firsthand.', 12, 0, 100, 0, 0, 0, 104812, 0, 0, 0, 'Лорд Джорах Черный Ворон to Ванесса ван Клиф');
INSERT INTO `creature_text` VALUES (101513, 5, 0, 'Your father would have been disappointed in you, Vanessa', 12, 0, 100, 0, 0, 0, 104813, 0, 0, 0, 'Лорд Джорах Черный Ворон to Ванесса ван Клиф');
INSERT INTO `creature_text` VALUES (101513, 6, 0, 'Тогда по рукам. Поздравляю тебя, $p. Ван Клиф у нас одна из лучших – как тебе уже пришлось убедиться.', 12, 0, 100, 0, 0, 0, 104812, 0, 0, 0, 'Лорд Джорах Черный Ворон to Ванесса ван Клиф');
INSERT INTO `creature_text` VALUES (101513, 7, 0, 'Твой отец был бы очень разочарован тобой, Ванесса.', 12, 0, 100, 0, 0, 0, 104813, 0, 0, 0, 'Лорд Джорах Черный Ворон to Ванесса ван Клиф');
INSERT INTO `creature_text` VALUES (101527, 0, 0, 'Мне нужно отлучиться по служебным делам. Прямо сейчас.', 12, 0, 100, 0, 0, 0, 84480, 0, 0, 0, 'Блескотрон-6000 to 0');
INSERT INTO `creature_text` VALUES (101532, 3, 0, 'Нет! Мое!', 14, 0, 100, 0, 0, 0, 103908, 1836019, 2241945, 0, 'Жаблин-мародер');
INSERT INTO `creature_text` VALUES (101539, 0, 0, 'I owe you $n. I thought that eternal peace had come for me.', 12, 0, 100, 0, 0, 0, 104626, 0, 0, 0, 'Барон Раздор to Player');
INSERT INTO `creature_text` VALUES (101539, 1, 0, 'The Lich King said my partner would be one of the mightiest knights ever known. I assume he was speaking of you? I\'ll rely upon your skills to get us through this in one piece.', 12, 0, 100, 0, 0, 0, 104719, 0, 0, 0, 'Барон Раздор to Player');
INSERT INTO `creature_text` VALUES (101539, 2, 0, 'Let\'s get going. Felguard roam the path ahead. I\'ve kept my distance from them, but with you leading the way, we can fight through.', 12, 0, 100, 0, 0, 0, 104720, 0, 0, 0, 'Барон Раздор to Player');
INSERT INTO `creature_text` VALUES (101539, 3, 0, 'I\'ll bring down the barrier. Watch my back!', 12, 0, 100, 0, 0, 0, 104551, 0, 0, 0, 'Барон Раздор to Player');
INSERT INTO `creature_text` VALUES (101539, 4, 0, 'Еще и немного... все готово!', 12, 0, 100, 0, 0, 0, 104553, 0, 0, 0, 'Барон Раздор to Нискарская вестница погибели');
INSERT INTO `creature_text` VALUES (101539, 5, 0, 'Margrave led the previous team that tried to kill Gorelix. I\'m surprised he\'s alive! I didn\'t think the Legion kept prisoners.', 12, 0, 100, 0, 0, 0, 104942, 0, 0, 0, 'Барон Раздор to Player');
INSERT INTO `creature_text` VALUES (101539, 6, 0, 'Did you hear that? I think something\'s in that building up ahead. Let\'s investigate.', 12, 0, 100, 0, 0, 0, 104993, 0, 0, 0, 'Барон Раздор to Player');
INSERT INTO `creature_text` VALUES (101539, 7, 0, 'Well... if it isn\'t Margrave\'s protege, Minerva.', 12, 0, 100, 0, 0, 0, 105008, 0, 0, 0, 'Барон Раздор to Player');
INSERT INTO `creature_text` VALUES (101539, 8, 0, 'Finding you strung up like this is a surprise. I would have expected a more... gory... end.', 12, 0, 100, 0, 0, 0, 105009, 0, 0, 0, 'Барон Раздор to Player');
INSERT INTO `creature_text` VALUES (101539, 9, 0, 'Думаю, от живого всегда пользы больше, чем от мертвого. Ладно, тогда надо поторопиться.', 12, 0, 100, 0, 0, 0, 105078, 0, 0, 0, 'Барон Раздор to 0');
INSERT INTO `creature_text` VALUES (101539, 10, 0, 'Finally the keystone is ours! Let\'s head back.', 12, 0, 100, 0, 0, 0, 105079, 0, 0, 0, 'Барон Раздор to Player');
INSERT INTO `creature_text` VALUES (101539, 11, 0, 'We\'re here to slay a demon, not rescue every failure who couldn\'t get the job done.', 12, 0, 100, 0, 0, 0, 105300, 0, 0, 0, 'Барон Раздор to Player');
INSERT INTO `creature_text` VALUES (101539, 12, 0, 'I don\'t care! Slaying the demon is our first priority. Come, we\'ve wasted enough time with this blathering.', 12, 0, 100, 0, 0, 0, 105301, 0, 0, 0, 'Барон Раздор to Player');
INSERT INTO `creature_text` VALUES (101539, 13, 0, 'Ну тупица! У тебя совсем мозги скисли? От магии Легиона очень просто уклониться.', 12, 0, 100, 0, 0, 0, 105236, 0, 0, 0, 'Барон Раздор to Player');
INSERT INTO `creature_text` VALUES (101539, 14, 0, 'Another damned barrier! Watch my back... we\'ll probably get jumped again.', 12, 0, 100, 0, 0, 0, 105286, 0, 0, 0, 'Барон Раздор to Player');
INSERT INTO `creature_text` VALUES (101539, 15, 0, 'Эй, мне нужна помощь! Я не привык просить дважды!', 12, 0, 100, 0, 0, 0, 104554, 0, 0, 0, 'Барон Раздор to Барон Раздор');
INSERT INTO `creature_text` VALUES (101539, 19, 0, 'The barrier\'s down. Move quickly!', 12, 0, 100, 0, 0, 0, 105289, 0, 0, 0, 'Барон Раздор to Барон Раздор');
INSERT INTO `creature_text` VALUES (101539, 20, 0, 'Good, now let\'s get out of here. The Lich King will be eager to hear of our success.', 12, 0, 100, 0, 0, 0, 105370, 0, 0, 0, 'Барон Раздор to Player');
INSERT INTO `creature_text` VALUES (101578, 0, 0, 'You cannot hide from me.', 12, 0, 100, 0, 0, 0, 102155, 0, 0, 0, 'Младший инквизитор to Player');
INSERT INTO `creature_text` VALUES (101578, 1, 0, 'Бежать некуда.', 12, 0, 100, 0, 0, 0, 101965, 0, 0, 0, 'Младший инквизитор to Player');
INSERT INTO `creature_text` VALUES (101644, 1, 0, 'Шан ронир! Вали к тауренам!', 12, 0, 100, 0, 0, 57968, 107087, 0, 0, 0, 'Заклинатель земли из племени Серого Камня to Player');
INSERT INTO `creature_text` VALUES (101644, 1, 0, 'Эти скалы принадлежат дрогбарам!', 12, 0, 100, 0, 0, 54871, 96416, 0, 0, 0, 'Заклинатель земли из племени Серого Камня to Player');
INSERT INTO `creature_text` VALUES (101644, 4, 0, 'Сейчас захрустят твои кости!', 12, 0, 100, 0, 0, 54860, 96412, 2285, 2285, 0, 'Заклинатель земли из племени Серого Камня to Player');
INSERT INTO `creature_text` VALUES (101645, 1, 0, 'Мы захватим гору!', 12, 0, 100, 0, 0, 54861, 96413, 0, 0, 0, 'Землекоп из племени Серого Камня to Player');
INSERT INTO `creature_text` VALUES (101645, 1, 0, 'Шан ронир! Вали к тауренам!', 12, 0, 100, 0, 0, 57968, 107087, 0, 0, 0, 'Землекоп из племени Серого Камня to Player');
INSERT INTO `creature_text` VALUES (101645, 1, 0, 'Эти скалы принадлежат дрогбарам!', 12, 0, 100, 402, 0, 54871, 96416, 0, 0, 0, 'Землекоп из племени Серого Камня to Player');
INSERT INTO `creature_text` VALUES (101651, 0, 0, 'Валорн, нет!', 12, 0, 100, 0, 0, 0, 104056, 0, 0, 0, 'Белриса Звездный Ветер to Player');
INSERT INTO `creature_text` VALUES (101651, 1, 0, 'Он ушел...', 12, 0, 100, 0, 0, 0, 104059, 0, 0, 0, 'Белриса Звездный Ветер to Player');
INSERT INTO `creature_text` VALUES (101653, 3, 0, 'Коса у него! За ним!', 14, 0, 100, 22, 0, 61868, 0, 84, 84, 0, 'Часовой Мисара to Player');
