INSERT INTO `creature_text` VALUES (101766, 0, 0, 'Спасибо за отвлекающий маневр.', 12, 0, 100, 2, 0, 66367, 108941, 0, 0, 0, 'Талрен Речное Дерево to Player');
INSERT INTO `creature_text` VALUES (101766, 1, 0, 'Я воспользуюсь возможностью расставить ловушки для незваных гостей. До встречи!', 12, 0, 100, 0, 0, 66360, 117046, 0, 0, 0, 'Талрен Речное Дерево to Player');
