INSERT INTO `creature_text` VALUES (101766, 3, 0, 'У нас мало времени. Я могу телепортировать нас отсюда.', 12, 0, 100, 1, 0, 66388, 117145, 0, 0, 0, 'Талрен Речное Дерево to Player');
INSERT INTO `creature_text` VALUES (101766, 4, 0, 'Вот так!', 12, 0, 100, 1, 0, 66389, 124067, 0, 0, 0, 'Талрен Речное Дерево to Player');
INSERT INTO `creature_text` VALUES (101767, 3, 0, 'I did not have time on time ...', 12, 0, 100, 0, 0, 0, 105980, 35464, 35464, 0, 'Лирея Перо Ветра to Player');
INSERT INTO `creature_text` VALUES (101767, 3, 0, 'Не возвращайся за нами. Из-за предательства ночнорожденных мы уже обречены.', 12, 0, 100, 0, 0, 0, 109645, 29962, 29962, 0, 'Лирея Перо Ветра to Player');
INSERT INTO `creature_text` VALUES (101768, 0, 0, 'Порой я забываю, как талантлива Серена. Ты очень кстати. Давно пора разделаться с этими захватчиками.', 12, 0, 100, 603, 0, 68991, 109661, 0, 0, 0, 'Лотрий Зовущий Луну to Player');
INSERT INTO `creature_text` VALUES (101768, 1, 0, 'Будь начеку. Похоже, мои руны не действуют на их големов.', 12, 0, 100, 1, 0, 68992, 109405, 0, 0, 0, 'Лотрий Зовущий Луну to Player');
INSERT INTO `creature_text` VALUES (101768, 2, 0, 'Прочь отсюда!', 12, 0, 100, 0, 0, 66253, 146371, 0, 0, 0, 'Лотрий Зовущий Луну to Имперская разрушительница чарЛотрий Зовущий Луну to Имперская разрушительница чар');
