INSERT INTO `creature_text` VALUES (101848, 3, 0, 'Ее больше нет. Я думал, что разделю ее участь, и моя лишенная разума оболочка будет скитаться по этим рунам, но ты...', 12, 0, 100, 0, 0, 0, 119838, 15635, 15635, 0, 'Абсолон to Player');
