INSERT INTO `creature_text` VALUES (101899, 2, 0, 'Отлично! Путь к темнице теперь должен быть расчи... а, да это же наш старый приятель. Думаю, мы окажем миру большую услугу, избавив его от очередного \"плохого парня\".', 12, 0, 100, 0, 0, 0, 104275, 0, 0, 0, 'Повелитель теней Шлакомолот to Player');
INSERT INTO `creature_text` VALUES (101899, 3, 0, 'Дьякон снова играет с нами в свои игры разума. Можно применить к ним \"Массовое рассеивание\" или просто прорваться, но я бы не стал подходить к ним близко.', 12, 0, 100, 0, 0, 0, 106116, 0, 0, 0, 'Повелитель теней Шлакомолот to Player');
INSERT INTO `creature_text` VALUES (101899, 4, 0, 'Плохо дело. Я слышу их наверху. Они приближаются. Ладно, я разберусь с ними сам, а ты разделайся с Дьяконом!', 12, 0, 100, 0, 0, 0, 104477, 0, 0, 0, 'Повелитель теней Шлакомолот to Player');
INSERT INTO `creature_text` VALUES (101899, 5, 0, 'Ты $gсправился:справилась;! Сектанты бежали, поджав хвосты! Хо-хо! Ну что, возвращайся к Фаолу. Я сам активирую камень Сумеречного портала.', 12, 0, 100, 0, 0, 0, 104550, 0, 0, 0, 'Повелитель теней Шлакомолот to Player');
