INSERT INTO `creature_text` VALUES (102337, 5, 0, 'Моя смерть не остановит это вторжение!', 14, 0, 100, 0, 0, 0, 31655, 0, 0, 0, 'Страж портала to Player');
INSERT INTO `creature_text` VALUES (102347, 0, 0, 'На горе тебе не место!', 12, 0, 100, 0, 0, 0, 107085, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (102347, 0, 1, 'Я выпущу тебе кишки.', 12, 0, 100, 0, 0, 0, 96414, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (102347, 0, 2, 'Мы захватим гору!', 12, 0, 100, 0, 0, 0, 96413, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (102358, 3, 0, 'И по моему замыслу возглавить этот совет должен поистине великий жрец, своими деяниями снискавший славу героя не только среди своих, а повсеместно. Я полагаю, что ты и есть $gтот самый герой:та самая героиня;, но нужно будет еще и убедить в этом остальных. Ты $gготов:готова; к испытаниям?', 12, 0, 100, 0, 0, 0, 0, 23986, 23986, 0, 'Алонсий Фаол to Player');
INSERT INTO `creature_text` VALUES (102358, 3, 0, 'Легион представляет серьезную угрозу, поэтому я и решил снова выйти из тени. Сейчас я собираю духовенство, чтобы мы вместе решили, как бороться с Легионом.', 12, 0, 100, 0, 0, 63427, 104643, 22748, 22748, 0, 'Алонсий Фаол to Player');
INSERT INTO `creature_text` VALUES (102358, 3, 0, 'Уж извини за такую секретность – старая привычка. Меня зовут Алонсий Фаол. За свою жизнь мне довелось побывать и главой церкви, и безмозглым слугой Плети, и организатором тайной войны. Но в первую очередь я всегда был и остаюсь жрецом.', 12, 0, 100, 1, 0, 0, 0, 1287, 1287, 0, 'Алонсий Фаол to Player');
INSERT INTO `creature_text` VALUES (102362, 0, 0, 'Глупое смертное существо! Явилось прямо в мою ловушку!', 12, 0, 100, 0, 0, 0, 104627, 0, 0, 0, 'Нискарский тюремщик to Player');
INSERT INTO `creature_text` VALUES (102362, 1, 0, 'Тебя ждут вечные муки!', 12, 0, 100, 0, 0, 0, 121994, 0, 0, 0, 'Нискарский тюремщик to Player');
INSERT INTO `creature_text` VALUES (102363, 0, 0, 'Итак, на том и порешили. Кстати, поговори с Веленом. У него к тебе... весьма необычное дело.', 12, 0, 100, 0, 0, 0, 104649, 0, 0, 0, 'Алонсий Фаол to Player');
INSERT INTO `creature_text` VALUES (102365, 0, 0, 'Твоего друга увели в ямы ловцов Скверны под флагманским кораблем Пылающего Легиона.', 12, 0, 100, 0, 0, 0, 106191, 0, 0, 0, 'Селтэс Звездная Песня to player');
INSERT INTO `creature_text` VALUES (102365, 1, 0, 'Боюсь, его уже не спасти... Не рискуйте собой ради покойника!', 12, 0, 100, 0, 0, 0, 106192, 0, 0, 0, 'Селтэс Звездная Песня to player');
INSERT INTO `creature_text` VALUES (102366, 3, 0, 'Остерегайся василисков. Их взор проникает и сквозь это.', 12, 0, 100, 1, 0, 0, 104874, 3330, 3330, 0, 'Барбрул Варобрул to Player');
INSERT INTO `creature_text` VALUES (102368, 0, 0, 'Dalaran will burn!', 12, 0, 100, 0, 0, 0, 31651, 0, 0, 0, 'The Destroying Guardian of Square to Player');
INSERT INTO `creature_text` VALUES (102368, 0, 0, 'Даларан сгорит!', 12, 0, 100, 0, 0, 0, 31651, 0, 0, 0, 'Разрущающий страж Скверны to Player');
INSERT INTO `creature_text` VALUES (102369, 0, 0, 'Даларан сгорит!', 12, 0, 100, 0, 0, 0, 31651, 0, 0, 0, 'Ловец Скверны - разоритель to Player');
INSERT INTO `creature_text` VALUES (102370, 0, 0, 'Твои усилия были напрасны!', 12, 0, 100, 0, 0, 0, 31537, 0, 0, 0, 'Эредар-завоеватель to Player');
INSERT INTO `creature_text` VALUES (102370, 1, 0, 'Даларан сгорит!', 12, 0, 100, 0, 0, 0, 31651, 0, 0, 0, 'Эредар-завоеватель to Player');
INSERT INTO `creature_text` VALUES (102370, 2, 0, 'Смерть и уничтожение всем, кто против нас!', 12, 0, 100, 0, 0, 0, 31647, 0, 0, 0, 'Эредар-завоеватель to Player');
INSERT INTO `creature_text` VALUES (102370, 3, 0, 'Даларан должен пасть!', 12, 0, 100, 0, 0, 0, 31726, 0, 0, 0, 'Эредар-завоеватель to Player');
INSERT INTO `creature_text` VALUES (102377, 4, 0, 'Твои усилия были напрасны!', 12, 0, 100, 0, 0, 0, 0, 12602, 12602, 0, 'Подстрекатель Скверны to Player');
INSERT INTO `creature_text` VALUES (102380, 0, 0, 'Даларан сгорит!', 12, 0, 100, 0, 0, 0, 31651, 0, 0, 0, 'Чернокнижник Совета Теней to Player');
INSERT INTO `creature_text` VALUES (102381, 1, 0, 'I\'ll kill you!', 14, 0, 100, 0, 0, 0, 104921, 0, 184264, 0, 'Брандольф to Player');
INSERT INTO `creature_text` VALUES (102381, 3, 0, 'Идем, $n! Вместе мы изрубим этих наг на куски!', 12, 0, 100, 0, 0, 0, 105173, 71, 71, 0, 'Брандольф to Player');
INSERT INTO `creature_text` VALUES (102381, 4, 0, 'Давай-ка перебьем этих змей, $n!', 14, 0, 100, 15, 0, 0, 104922, 5706, 9149, 0, 'Брандольф to Player');
INSERT INTO `creature_text` VALUES (102387, 0, 0, 'Ух ты, игрушки!', 12, 0, 100, 0, 0, 0, 106137, 0, 0, 0, 'Саел\'орн to 0');
INSERT INTO `creature_text` VALUES (102387, 1, 0, 'Don\'t look behind you.', 12, 0, 100, 0, 0, 0, 106143, 0, 0, 0, 'Саел\'орн to 0');
INSERT INTO `creature_text` VALUES (102387, 2, 0, 'You can\'t leave, you can NEVER LEAVE!', 12, 0, 100, 0, 0, 0, 106153, 0, 0, 0, 'Саел\'орн to Вороная горгулья');
INSERT INTO `creature_text` VALUES (102387, 3, 0, 'And along came a spider...', 12, 0, 100, 0, 0, 0, 106144, 0, 0, 0, 'Саел\'орн to 0');
INSERT INTO `creature_text` VALUES (102387, 4, 0, 'Which do you like more, screaming or crying? I like to hear you do both.', 12, 0, 100, 0, 0, 0, 106148, 0, 0, 0, 'Саел\'орн to Player');
INSERT INTO `creature_text` VALUES (102387, 5, 0, 'Run. Heh,  it is fun to watch you run.', 12, 0, 100, 0, 0, 0, 106145, 0, 0, 0, 'Саел\'орн to 0');
INSERT INTO `creature_text` VALUES (102387, 6, 0, 'I have tried to teach them to kill you slowly... But they just won\'t listen.', 12, 0, 100, 0, 0, 0, 106146, 0, 0, 0, 'Саел\'орн to 0');
INSERT INTO `creature_text` VALUES (102387, 7, 0, 'What!? Why would you do this?', 12, 0, 100, 0, 0, 0, 106142, 0, 0, 0, 'Саел\'орн to 106848');
INSERT INTO `creature_text` VALUES (102388, 0, 0, 'Спас-с-сибо за вс-с-сю с-с-сделанную за меня грязную работу, $n. Но сохранить тебе жизнь я не могу – слишком много от тебя неприятнос-с-стей.', 12, 0, 100, 0, 0, 0, 105966, 0, 0, 0, 'Торил to Player');
INSERT INTO `creature_text` VALUES (102388, 1, 0, 'Ты умрешь во славу Садж\'тар!', 12, 0, 100, 0, 0, 0, 101739, 0, 0, 0, 'Повелительница приливов Садж\'тар to Player');
INSERT INTO `creature_text` VALUES (102388, 2, 0, 'Yandvik lost all the leaders, and now I can easily destroy it from the inside!', 12, 0, 100, 0, 0, 0, 105967, 0, 0, 0, 'Повелительница приливов Садж\'тар to 0');
INSERT INTO `creature_text` VALUES (102388, 3, 0, 'Мои наги пройдут по Яндвику волной разрушения!', 12, 0, 100, 0, 0, 0, 105968, 0, 0, 0, 'Повелительница приливов Садж\'тар to 0');
INSERT INTO `creature_text` VALUES (102388, 4, 0, 'Довольно!', 12, 0, 100, 0, 0, 0, 129681, 0, 0, 0, 'Повелительница приливов Садж\'тар to 0');
INSERT INTO `creature_text` VALUES (102388, 5, 0, 'Нас-с-с не ос-с-становить. Яндвик падет!', 12, 0, 100, 1, 0, 0, 106795, 0, 0, 0, 'Повелительница приливов Садж\'тар to 0');
INSERT INTO `creature_text` VALUES (102391, 0, 0, '$n, разрушительный луч существа наведен на меня. Измени положение последнего зеркала, чтобы расколоть его щит!', 12, 0, 100, 0, 0, 57307, 98935, 0, 0, 0, 'Алтруис Страдалец to Player');
INSERT INTO `creature_text` VALUES (102391, 1, 0, 'Щит сломан! Скорее уходим, пока это существо не пришло в себя!', 12, 0, 100, 0, 0, 57306, 98934, 0, 0, 0, 'Алтруис Страдалец to Player');
INSERT INTO `creature_text` VALUES (102393, 3, 0, 'Отлично! Теперь он в ловушке. Возвращаемся к остальным.', 12, 0, 100, 603, 0, 57333, 101469, 6141, 6141, 0, 'Кайн Ярость Солнца to Player');
INSERT INTO `creature_text` VALUES (102395, 0, 0, 'Твои усилия были напрасны!', 12, 0, 100, 0, 0, 0, 31537, 0, 0, 0, 'Разведчик-убийца to Player');
INSERT INTO `creature_text` VALUES (102395, 1, 0, 'Даларан должен пасть!', 12, 0, 100, 0, 0, 0, 31726, 0, 0, 0, 'Killer scout to Player');
INSERT INTO `creature_text` VALUES (102397, 0, 0, 'Даларан должен пасть!', 12, 0, 100, 0, 0, 0, 31726, 0, 0, 0, 'Могучий повелитель гнева to Player');
INSERT INTO `creature_text` VALUES (102398, 4, 0, 'Даларан должен пасть!', 12, 0, 100, 0, 0, 0, 31726, 13484, 13484, 0, 'Пылающий инфернал to Player');
INSERT INTO `creature_text` VALUES (102400, 0, 0, 'Смерть и уничтожение всем, кто против нас!', 12, 0, 100, 0, 0, 0, 31647, 0, 0, 0, 'Эредарская сумеречная целительница to Player');
INSERT INTO `creature_text` VALUES (102400, 1, 0, 'Твои усилия были напрасны!', 12, 0, 100, 0, 0, 0, 31537, 0, 0, 0, 'Эредарская сумеречная целительница to Player');
INSERT INTO `creature_text` VALUES (102410, 0, 0, 'Жители Яндвика! Соберитесь у огня и услышьте славные вести!', 14, 0, 100, 1, 0, 0, 105614, 0, 0, 0, 'Торил to Player');
INSERT INTO `creature_text` VALUES (102410, 1, 0, 'Многие из наших братьев и сестер погибли от оружия Садж\'тар. Калдер, Эйнар и ярл Трондир пали жертвами ее вероломства.', 14, 0, 100, 1, 0, 0, 105618, 0, 0, 0, 'Торил to Player');
INSERT INTO `creature_text` VALUES (102410, 2, 0, 'Да будет жить в веках память о них!', 14, 0, 100, 1, 0, 0, 105626, 0, 0, 0, 'Торил to Player');
INSERT INTO `creature_text` VALUES (102410, 3, 0, 'Своим спасением Яндвик обязан |3-2($c), что стоит сейчас перед нами. Да славится $n, убийца Повелительницы приливов Садж\'тар!', 14, 0, 100, 1, 0, 0, 105620, 0, 0, 0, 'Торил to Player');
INSERT INTO `creature_text` VALUES (102410, 4, 0, 'Как последняя из старейшин, я должна вести Яндвик к процветанию.', 14, 0, 100, 1, 0, 0, 105621, 0, 0, 0, 'Торил to Player');
INSERT INTO `creature_text` VALUES (102410, 5, 0, 'И потому я объявляю: отныне нашим ярлом будет $n! Да здравствует $n!', 14, 0, 100, 1, 0, 0, 105622, 0, 0, 0, 'Торил to Player');
INSERT INTO `creature_text` VALUES (102425, 0, 0, 'Нам надо... Крылоклюв, нет!', 12, 0, 100, 0, 0, 0, 109646, 0, 0, 0, 'Серена Перо Ветра to Player');
INSERT INTO `creature_text` VALUES (102425, 1, 0, 'Спасибо за все, Крылоклюв. Надеюсь, мы снова встретимся.', 12, 0, 100, 2, 0, 0, 109149, 0, 0, 0, 'Серена Перо Ветра to Player');
INSERT INTO `creature_text` VALUES (102425, 2, 0, 'Не обращай на него внимания. Пожалуйста, найди Лотрия. Во время нападения мы потеряли друг друга из вида... думаю, он еще держится в библиотеке.', 12, 0, 100, 0, 0, 0, 109650, 0, 0, 0, 'Серена Перо Ветра to Player');
INSERT INTO `creature_text` VALUES (102425, 3, 0, 'Не беспокойся за меня. Сосредоточься на поисках Лотриуса.', 12, 0, 100, 1, 0, 0, 109150, 0, 0, 0, 'Серена Перо Ветра to Player');
INSERT INTO `creature_text` VALUES (102426, 0, 0, 'Крылоклюв разворачивается и бросается на вас!', 16, 0, 100, 0, 0, 0, 109647, 0, 0, 0, 'Крылоклюв to Player');
INSERT INTO `creature_text` VALUES (102431, 0, 0, 'The hunger never ends...', 14, 0, 100, 0, 0, 57578, 105143, 0, 0, 0, 'Принцесса крови Тал\'ена to Player');
INSERT INTO `creature_text` VALUES (102442, 0, 0, 'Тысяча благодарностей!', 12, 0, 100, 0, 0, 0, 104752, 0, 0, 0, 'Пленник оплота Оскверненной Души to 102450');
INSERT INTO `creature_text` VALUES (102442, 0, 1, 'I am free! I am free!', 12, 0, 100, 0, 0, 0, 104748, 0, 0, 0, 'Пленник оплота Оскверненной Души to 102450');
INSERT INTO `creature_text` VALUES (102442, 0, 2, 'Мана... Мне нужна мана! Ночной Колодец... нет... нет! Мне надо вернуться!', 12, 0, 100, 0, 0, 0, 104753, 0, 0, 0, 'Пленник оплота Оскверненной Души to 102450');
INSERT INTO `creature_text` VALUES (102442, 0, 3, 'Наконец-то я спасен!', 12, 0, 100, 0, 0, 0, 104747, 0, 0, 0, 'Пленник оплота Оскверненной Души to 102450');
INSERT INTO `creature_text` VALUES (102446, 0, 0, 'Even the best laid plans don\'t survive contact with the enemy.', 14, 0, 100, 0, 0, 57579, 106196, 0, 0, 0, 'Повелитель Скверны Бетруг to Player');
INSERT INTO `creature_text` VALUES (102446, 1, 0, 'Such insolence! We will make your death terrible, and your suffering long.', 14, 0, 100, 0, 0, 57580, 106197, 0, 0, 0, 'Повелитель Скверны Бетруг to 0');
INSERT INTO `creature_text` VALUES (102446, 2, 0, 'We will unleash a scourge upon this citadel never before known to your kind!', 14, 0, 100, 0, 0, 57585, 106202, 0, 0, 0, 'Повелитель Скверны Бетруг to 0');
INSERT INTO `creature_text` VALUES (102446, 3, 0, 'Our hatred knows no bounds!', 14, 0, 100, 0, 0, 57582, 106199, 0, 0, 0, 'Повелитель Скверны Бетруг to Player');
INSERT INTO `creature_text` VALUES (102446, 4, 0, '|TInterface\\Icons\\INV_Sword_48:20|tОсвободите |3-1($n) от уз, пока $gего:ее, не |cFFFF0000|Hspell:202361|h[казнили!]|h|r', 41, 0, 100, 0, 0, 57582, 106189, 0, 0, 0, 'Повелитель Скверны Бетруг to Player');
INSERT INTO `creature_text` VALUES (102446, 5, 0, 'My lord.. I have.. failed you...', 14, 0, 100, 0, 0, 57586, 106203, 0, 0, 0, 'Повелитель Скверны Бетруг to Player');
INSERT INTO `creature_text` VALUES (102459, 0, 0, 'Нет! Клинок должен оставаться погребенным!', 12, 0, 100, 0, 0, 0, 104738, 0, 0, 0, 'Лаит Ша\'ол to Неглубокая могила');
INSERT INTO `creature_text` VALUES (102459, 1, 0, 'Ты его не получишь!', 12, 0, 100, 0, 0, 0, 104739, 0, 0, 0, 'Лаит Ша\'ол to 102473');
INSERT INTO `creature_text` VALUES (102462, 3, 0, 'Во времена относительного спокойствия мы очень хотели вернуть им этот долг, но вот только не знали, как. И, к нашему удивлению, однажды они обратились к нам с просьбой. Свет явил великое чудо – им удалось пленить бога Бездны, значительно ослабив его.', 12, 0, 100, 0, 0, 0, 0, 10, 10, 0, 'Пророк Велен to Player');
INSERT INTO `creature_text` VALUES (102462, 3, 0, 'Мы заключили падшего в особую темницу, чтобы общаться с ним, постигая тайны Круговерти Пустоты. В эту темницу мы и направимся.', 12, 0, 100, 0, 0, 63404, 104676, 28107, 28107, 0, 'Пророк Велен to Player');
INSERT INTO `creature_text` VALUES (102462, 3, 0, 'С помощью наару мы создали некое место, которое должно было стать темницей для бога Бездны; в котором мы могли исследовать и его, и самих наару, и Круговерть Пустоты. Это место я и хочу тебе показать.', 12, 0, 100, 0, 0, 0, 0, 26457, 26457, 0, 'Пророк Велен to Player');
INSERT INTO `creature_text` VALUES (102469, 0, 0, 'Нет! Я не хочу возвращаться!', 12, 0, 100, 0, 0, 0, 104851, 0, 0, 0, 'Забытый мертвец to Player');
INSERT INTO `creature_text` VALUES (102469, 0, 1, 'Составь мне компанию в могиле!', 12, 0, 100, 0, 0, 0, 104855, 0, 0, 0, 'Забытый мертвец to Player');
INSERT INTO `creature_text` VALUES (102478, 0, 0, 'Ты $gвернулся:вернулась; с артефактом! Молодец, $n!', 12, 0, 100, 0, 0, 0, 103492, 0, 0, 0, 'Эммарель Стражница Тени to Player');
INSERT INTO `creature_text` VALUES (102496, 3, 0, 'Беги, пока еще можешь!', 12, 0, 100, 0, 0, 68461, 121708, 232, 232, 0, 'Шал\'дорай-слуга to Player');
INSERT INTO `creature_text` VALUES (102507, 0, 0, '%s is trying to escape in a panic!', 16, 0, 100, 0, 0, 0, 1150, 0, 0, 0, 'Молельщик Темносвета to 0');
INSERT INTO `creature_text` VALUES (102522, 0, 0, 'О нет, Ариден... ну зачем только ты вытащил на свет этот проклятый клинок?', 12, 0, 100, 0, 0, 0, 104740, 0, 0, 0, 'Лаит Ша\'ол to Player');
INSERT INTO `creature_text` VALUES (102522, 1, 0, 'Его нужно вернуть. Он не сможет контролировать ту неукротимую ярость, что заключена в нем.', 12, 0, 100, 0, 0, 0, 104741, 0, 0, 0, 'Лаит Ша\'ол to Player');
INSERT INTO `creature_text` VALUES (102522, 2, 0, 'Отправляйся в катакомбы. Проклятие нужно снять!', 12, 0, 100, 0, 0, 0, 104743, 0, 0, 0, 'Лаит Ша\'ол to Player');
INSERT INTO `creature_text` VALUES (102527, 0, 0, 'Берегись! Это ловушка!', 14, 0, 100, 0, 0, 0, 105468, 0, 0, 0, 'Торил to Player');
INSERT INTO `creature_text` VALUES (102527, 1, 0, 'Yandvik will never surrender to your vile nagas! Never!', 14, 0, 100, 0, 0, 0, 105469, 0, 0, 0, 'Торил to Player');
INSERT INTO `creature_text` VALUES (102527, 2, 0, 'Помоги мне выбраться из клетки!', 14, 0, 100, 0, 0, 0, 105389, 0, 0, 0, 'Торил to Player');
INSERT INTO `creature_text` VALUES (102532, 0, 0, 'А ты $gупорный:упорная;.', 12, 0, 100, 0, 0, 0, 103613, 0, 0, 0, 'Ариден to Player');
INSERT INTO `creature_text` VALUES (102532, 1, 0, 'Ну хорошо! Если ты так жаждешь завладеть силой Апокалипсиса, сейчас я сам продемонстрирую ее тебе!', 12, 0, 100, 0, 0, 0, 104764, 0, 0, 0, 'Ариден to Player');
INSERT INTO `creature_text` VALUES (102532, 2, 0, 'Он взывает к душам тех, кто давно умер... ВОССТАНЬТЕ!', 14, 0, 100, 0, 0, 0, 106114, 0, 0, 0, 'Ариден to 0');
INSERT INTO `creature_text` VALUES (102532, 3, 0, 'Не только тебе дано управлять нежитью, рыцарь смерти!', 14, 0, 100, 0, 0, 0, 106126, 0, 0, 0, 'Ариден to 0');
INSERT INTO `creature_text` VALUES (102532, 4, 0, 'Д-духи мертвых! Помогите вашему повелителю!', 14, 0, 100, 0, 0, 0, 106127, 0, 0, 0, 'Ариден to 0');
INSERT INTO `creature_text` VALUES (102532, 5, 0, 'Нет! Его сила... она моя!', 14, 0, 100, 0, 0, 0, 104766, 0, 0, 0, 'Ариден to Player');
INSERT INTO `creature_text` VALUES (102532, 6, 0, 'Этот голос... отец, это ты?!', 12, 0, 100, 0, 0, 0, 104768, 0, 0, 0, 'Ариден to Player');
INSERT INTO `creature_text` VALUES (102533, 0, 0, 'Она никогда не была твоей, Ариден. Не ты владел Апокалипсисом. Это его темная сила владела тобой, и это она использовала тебя как свое оружие.', 12, 0, 100, 0, 0, 0, 104767, 0, 0, 0, 'Лаит Ша\'ол to Player');
INSERT INTO `creature_text` VALUES (102533, 1, 0, 'Спи спокойно, сын мой. Проклятие больше не довлеет над тобой.', 12, 0, 100, 0, 0, 0, 104769, 0, 0, 0, 'Лаит Ша\'ол to Player');
INSERT INTO `creature_text` VALUES (102533, 2, 0, 'Ты свободен.', 12, 0, 100, 0, 0, 0, 104770, 0, 0, 0, 'Лаит Ша\'ол to Player');
INSERT INTO `creature_text` VALUES (102534, 0, 0, 'They say I died in the Deadmines. Fools - it\'s all too simple to mimic the effects of death when your assailants\' minds are addled by neurotoxins.', 12, 0, 100, 0, 0, 0, 104800, 0, 0, 0, 'Ванесса ван Клиф to Player');
INSERT INTO `creature_text` VALUES (102534, 1, 0, 'Did you know that I was favored to become the final Shadow and that despite their assurances every single one of them put in their lot with you instead?', 12, 0, 100, 0, 0, 0, 104790, 0, 0, 0, 'Ванесса ван Клиф to Player');
INSERT INTO `creature_text` VALUES (102534, 2, 0, 'It\'s a pity, really - Jorach did so much for me. He won\'t be happy when he comes to and you\'re dead.', 12, 0, 100, 0, 0, 0, 104792, 0, 0, 0, 'Ванесса ван Клиф to Player');
INSERT INTO `creature_text` VALUES (102534, 3, 0, 'Let\'s see how long you last.', 12, 0, 100, 0, 0, 0, 104791, 0, 0, 0, 'Ванесса ван Клиф to 0');
INSERT INTO `creature_text` VALUES (102534, 4, 0, 'Its nothing personal. Its just business.', 12, 0, 100, 0, 0, 0, 108891, 0, 0, 0, 'Ванесса ван Клиф to 0');
INSERT INTO `creature_text` VALUES (102534, 4, 0, 'Ничего личного. Просто бизнес.', 12, 0, 100, 0, 0, 0, 108891, 0, 0, 0, 'Ванесса ван Клиф to 0');
INSERT INTO `creature_text` VALUES (102534, 5, 0, 'Come on, show me the \'legendary fighter\' I\'ve heard so much about!', 12, 0, 100, 0, 0, 0, 109241, 0, 0, 0, 'Ванесса ван Клиф to 0');
INSERT INTO `creature_text` VALUES (102534, 6, 0, 'No! I\'ve worked too hard to be undermined by the likes of you!', 12, 0, 100, 0, 0, 0, 109242, 0, 0, 0, 'Ванесса ван Клиф to 0');
INSERT INTO `creature_text` VALUES (102534, 7, 0, 'Stop. Stop! No more!', 12, 0, 100, 0, 0, 0, 104798, 0, 0, 0, 'Ванесса ван Клиф to 0');
INSERT INTO `creature_text` VALUES (102534, 8, 0, 'I think Ill take your generous offer.', 12, 0, 100, 0, 0, 0, 104823, 0, 0, 0, 'Ванесса ван Клиф to Ванесса ван Клиф');
INSERT INTO `creature_text` VALUES (102534, 8, 0, 'Пожалуй, я воспользуюсь вашим щедрым предложением.', 12, 0, 100, 0, 0, 0, 104823, 0, 0, 0, 'Ванесса ван Клиф to Ванесса ван Клиф');
INSERT INTO `creature_text` VALUES (102546, 0, 0, 'Прошу прощения. Приветствую.', 14, 0, 100, 0, 0, 0, 104794, 0, 0, 0, 'Номи to Player');
INSERT INTO `creature_text` VALUES (102546, 1, 0, 'Отыщи меня в Даларане! Сможем обменяться рецептами!', 12, 0, 100, 2, 0, 0, 104850, 0, 0, 0, 'Номи to Player');
INSERT INTO `creature_text` VALUES (102561, 0, 0, '<cough> Spare me your paltry words, Sliver. I\'ve not taken my final breath yet!', 12, 0, 100, 0, 0, 0, 105016, 0, 0, 0, 'Минерва Воронья Скорбь to Player');
INSERT INTO `creature_text` VALUES (102561, 1, 0, 'These cursed demons are going to learn a new meaning of suffering when I get free! A beast called Zalinor carries the keystone that unlocks these bindings. Set me free and I\'ll help you rip the bastards apart!', 12, 0, 100, 0, 0, 0, 105042, 0, 0, 0, 'Минерва Воронья Скорбь to Player');
INSERT INTO `creature_text` VALUES (102561, 2, 0, 'Ah! That\'s much better!', 12, 0, 100, 0, 0, 0, 105089, 0, 0, 0, 'Минерва Воронья Скорбь to Player');
INSERT INTO `creature_text` VALUES (102561, 3, 0, 'We don\'t have much time! Margrave was taken to the citadel. The only things I\'ve seen come out of that place are demons and corpses! We\'ll need to break in there to get him out.', 12, 0, 100, 0, 0, 0, 105090, 0, 0, 0, 'Минерва Воронья Скорбь to Player');
INSERT INTO `creature_text` VALUES (102561, 4, 0, 'What!? Don\'t you have any sense of duty to aid your fellow knights?', 12, 0, 100, 0, 0, 0, 105303, 0, 0, 0, 'Минерва Воронья Скорбь to 0');
INSERT INTO `creature_text` VALUES (102561, 5, 0, 'You\'re a cold one, Sliver. Fine! I\'ll help, but if we have any chance of getting Margrave out alive, we should take it.', 12, 0, 100, 0, 0, 0, 105325, 0, 0, 0, 'Минерва Воронья Скорбь to 0');
INSERT INTO `creature_text` VALUES (102561, 6, 0, 'Легион забрал мое оружие, так что будет честно, если я возьму оружие у демонов.', 12, 0, 100, 0, 0, 0, 105182, 0, 0, 0, 'Минерва Воронья Скорбь to 0');
INSERT INTO `creature_text` VALUES (102561, 7, 0, 'What\'s the delay? Go already!', 12, 0, 100, 0, 0, 0, 105284, 0, 0, 0, 'Минерва Воронья Скорбь to Player');
INSERT INTO `creature_text` VALUES (102561, 8, 0, 'О-о-ой!!! О-о-ой!!!', 12, 0, 100, 0, 0, 0, 105239, 0, 0, 0, 'Минерва Воронья Скорбь to Player');
INSERT INTO `creature_text` VALUES (102561, 9, 0, 'Противник на подходе! Надо перехватить!', 14, 0, 100, 0, 0, 0, 105293, 0, 0, 0, 'Минерва Воронья Скорбь to Нискарская вестница погибели');
INSERT INTO `creature_text` VALUES (102561, 10, 0, 'Poor Margrave! The final death claimed you at last.', 12, 0, 100, 0, 0, 0, 105372, 0, 0, 0, 'Минерва Воронья Скорбь to Player');
INSERT INTO `creature_text` VALUES (102570, 3, 0, 'Увы, Орест погиб. Нужно отправить весть Халдарону. Идем, Быстрошпор.', 12, 0, 100, 1, 0, 61792, 107305, 19719, 19719, 0, 'Вериса Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (102574, 0, 0, 'Мы – стражи диких земель.', 12, 0, 100, 0, 0, 0, 105146, 0, 0, 0, 'Эммарель Стражница Тени to Player');
INSERT INTO `creature_text` VALUES (102574, 1, 0, 'Мы – орлы в небесах.', 12, 0, 100, 0, 0, 0, 105147, 0, 0, 0, 'Эммарель Стражница Тени to Player');
INSERT INTO `creature_text` VALUES (102574, 2, 0, 'Мы ступаем на одинокую тропу...', 12, 0, 100, 0, 0, 0, 105148, 0, 0, 0, 'Эммарель Стражница Тени to Player');
INSERT INTO `creature_text` VALUES (102574, 3, 0, 'Ибо мы следуем Незримому пути.', 12, 0, 100, 0, 0, 0, 105149, 0, 0, 0, 'Эммарель Стражница Тени to Player');
INSERT INTO `creature_text` VALUES (102574, 4, 0, 'Смотрите! Там, в небе! Это Он\'ара! Дух орла вернулся!', 12, 0, 100, 0, 0, 0, 105184, 0, 0, 0, 'Эммарель Стражница Тени to Player');
INSERT INTO `creature_text` VALUES (102578, 0, 0, 'Следуй за мной, $n.', 12, 0, 100, 0, 0, 0, 105041, 0, 0, 0, 'Эммарель Стражница Тени to Player');
INSERT INTO `creature_text` VALUES (102578, 1, 0, 'Орден Незримого пути был основан после Войны древних для того, чтобы защитить обитателей Азерота. Отсюда, с Расколотых островов, мы следили за возвращением Легиона.', 12, 0, 100, 0, 0, 0, 105028, 0, 0, 0, 'Эммарель Стражница Тени to Player');
INSERT INTO `creature_text` VALUES (102578, 2, 0, 'Когда мы прибыли сюда, чтобы заложить Приют Стрелка, Он\'ара – дух орла – появился перед нами и благословил нас. С тех пор мы называем эту гору Пиком Когтя.', 12, 0, 100, 0, 0, 0, 105029, 0, 0, 0, 'Эммарель Стражница Тени to Player');
INSERT INTO `creature_text` VALUES (102578, 3, 0, 'За прошедшие столетия я потеряла большинство своих товарищей. Те из нас, кто еще остался в живых, решили отправиться на поиски героев, которые могли бы в трудный час прийти на помощь Незримому пути.', 12, 0, 100, 0, 0, 0, 105032, 0, 0, 0, 'Эммарель Стражница Тени to Player');
INSERT INTO `creature_text` VALUES (102578, 4, 0, 'Незримый Путь станет еще сильнее, чем прежде!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Эммарель Стражница Тени to Player');
INSERT INTO `creature_text` VALUES (102583, 0, 0, '|TInterface\\Icons\\spell_fire_ragnaros_molteninferno.blp:20|t$n |cFFFF0000|Hspell:185824|h[взрывается]|h|r в момент гибели!', 16, 0, 100, 0, 0, 0, 96142, 0, 0, 0, 'Выжигатель Скверны to Выжигатель Скверны');
INSERT INTO `creature_text` VALUES (102587, 3, 0, 'Алтарь готов, Бетильда.', 12, 0, 100, 0, 0, 63410, 105144, 136692, 136692, 0, 'Саа\'ра to Player');
INSERT INTO `creature_text` VALUES (102587, 3, 0, 'Отличная работа, госпожа Глубокомолот!', 12, 0, 100, 0, 0, 0, 0, 71357, 71357, 0, 'Саа\'ра to Player');
INSERT INTO `creature_text` VALUES (102588, 0, 0, 'I don\'t know what you just did... but I LIKE it!', 12, 0, 0, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (102588, 0, 1, 'Thanks for the heal!', 12, 0, 0, 0, 0, 0, 104950, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (102588, 0, 2, 'Ugh! Oh, my head...', 12, 0, 0, 0, 0, 0, 104945, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (102588, 0, 3, 'What just hit me!?', 12, 0, 0, 0, 0, 0, 104946, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (102588, 0, 4, 'What the...?! I guess that feels better.', 12, 0, 0, 0, 0, 0, 104948, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (102588, 0, 5, 'Who throws a bandage at someone? Honestly?', 12, 0, 0, 0, 0, 0, 104949, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (102591, 3, 0, 'Ха! Он разозлился. Ну давай, брул!', 12, 0, 100, 0, 0, 0, 105998, 30111, 30111, 0, 'Камнегрыз-рыбрул to Манаспинный василиск');
INSERT INTO `creature_text` VALUES (102592, 4, 0, 'Сила и честь!', 12, 0, 100, 5, 0, 0, 113533, 25141, 25141, 0, 'Пехотинец Альянса to Player');
INSERT INTO `creature_text` VALUES (102594, 0, 0, 'Да, вполне. Большое тебе спасибо!', 12, 0, 100, 0, 0, 0, 104894, 0, 0, 0, 'Синь Гогельмогель to Player');
INSERT INTO `creature_text` VALUES (102600, 0, 0, 'Эй, послушай! Окажи услугу и встань вон на тот переключатель.', 12, 0, 100, 5, 0, 67475, 117624, 0, 0, 0, 'Чародейка Вальтруа to Player');
INSERT INTO `creature_text` VALUES (102600, 1, 0, 'А, я узнаю этот символ. Талисра решила нанять чужака… неужели наши дела настолько плохи?', 12, 0, 100, 5, 0, 67478, 117635, 0, 0, 0, 'Чародейка Вальтруа to Player');
INSERT INTO `creature_text` VALUES (102600, 2, 0, '<вздыхает> Ладно. Меня зовут Вальтруа.', 12, 0, 100, 1, 0, 67479, 117672, 0, 0, 0, 'Чародейка Вальтруа to Player');
INSERT INTO `creature_text` VALUES (102600, 3, 0, 'Наконец-то, проход открыт.', 12, 0, 100, 0, 0, 67476, 117627, 0, 0, 0, 'Чародейка Вальтруа to Player');
INSERT INTO `creature_text` VALUES (102600, 4, 0, 'А сейчас я убью тебя. Есть что сказать напоследок?', 12, 0, 100, 0, 0, 67477, 117629, 0, 0, 0, 'Чародейка Вальтруа to Player');
INSERT INTO `creature_text` VALUES (102600, 5, 0, 'Здесь, вдали от Ночного Колодца, важен каждый источник магии.', 12, 0, 100, 1, 0, 67480, 118569, 0, 0, 0, 'Чародейка Вальтруа to Player');
INSERT INTO `creature_text` VALUES (102600, 6, 0, 'Как и Талисра, я думаю, что нас спасут старые каналы, проводящие энергию силовых линий.', 12, 0, 100, 1, 0, 67481, 118570, 0, 0, 0, 'Чародейка Вальтруа to Player');
INSERT INTO `creature_text` VALUES (102600, 7, 0, 'Нужно только пробудить их.', 12, 0, 100, 1, 0, 67482, 118571, 0, 0, 0, 'Чародейка Вальтруа to Player');
INSERT INTO `creature_text` VALUES (102600, 8, 0, 'Этот змеевик пропускает поток высокой мощности. Змеевик низкой мощности – в дальнем конце здания.', 12, 0, 100, 1, 0, 67427, 105571, 0, 0, 0, 'Чародейка Вальтруа to Player');
INSERT INTO `creature_text` VALUES (102600, 9, 0, 'Оба потока идут в центральный зал. С помощью змеевиков и переключателей активируй все четыре печати в зале!', 12, 0, 100, 1, 0, 67428, 105575, 0, 0, 0, 'Чародейка Вальтруа to Player');
INSERT INTO `creature_text` VALUES (102600, 10, 0, 'Иди своей дорогой. Я сама доберусь до Шал\'\'Арана.', 12, 0, 100, 1, 0, 67429, 105742, 0, 0, 0, 'Чародейка Вальтруа to Player');
INSERT INTO `creature_text` VALUES (102601, 0, 0, 'Aww! Come on, you didn\'t have to take them just yet. Let\'s get outta here Shoob.', 12, 0, 100, 18, 0, 0, 104896, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (102609, 0, 0, 'Hey, those are mine! You won\'t get away with this, $gmister:lady;!', 12, 0, 100, 18, 0, 0, 104903, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (102610, 0, 0, 'Taking a kid\'s fireworks? That\'s pretty messed up. Mark my words, you\'ll regret it!', 12, 0, 100, 18, 0, 0, 104907, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (102611, 0, 0, 'My beautiful fireworks! I\'m going to tell on you!', 12, 0, 100, 18, 0, 0, 104910, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (102622, 1, 0, 'Гнев Азшары поглотит тебя!', 12, 0, 100, 0, 0, 52706, 92728, 0, 0, 0, 'Мраководный оракул to Player');
INSERT INTO `creature_text` VALUES (102622, 1, 0, 'Тебе нас не одолеть!', 12, 0, 100, 0, 0, 52701, 93438, 0, 0, 0, 'Мраководный оракул to Player');
INSERT INTO `creature_text` VALUES (102622, 4, 0, 'За королеву!', 12, 0, 100, 0, 0, 52703, 119770, 10575, 10575, 0, 'Мраководный оракул to Player');
INSERT INTO `creature_text` VALUES (102623, 1, 0, 'На горе тебе не место!', 12, 0, 100, 0, 0, 57966, 107085, 0, 0, 0, 'Буян Каменного Кулака to Player');
INSERT INTO `creature_text` VALUES (102623, 1, 0, 'Эти скалы принадлежат дрогбарам!', 12, 0, 100, 0, 0, 54871, 96416, 0, 0, 0, 'Буян Каменного Кулака to Player');
INSERT INTO `creature_text` VALUES (102625, 1, 0, 'Трепещ-щите перед Морозной Чеш-шуей!', 12, 0, 100, 0, 0, 0, 104403, 0, 0, 0, 'Оживительница из клана Морозной Чешуи to Player');
INSERT INTO `creature_text` VALUES (102636, 0, 0, 'Vanessa gives you a final look and then walks off.', 16, 0, 100, 0, 0, 0, 116462, 0, 0, 0, 'Ванесса ван Клиф to Player');
INSERT INTO `creature_text` VALUES (102641, 0, 0, 'Ну вот, $n. Мы провели в изысканиях недели и, думаю, раскопали об истории твоего оружия все, что только можно.', 12, 0, 100, 1, 0, 0, 120333, 0, 0, 0, 'Филий Искросташ to Player');
INSERT INTO `creature_text` VALUES (102641, 0, 0, 'Спасибо за такой внушительный вклад в наше исследование. Надеюсь, что результаты наших изысканий оказались полезными.', 12, 0, 100, 0, 0, 0, 120335, 0, 0, 0, 'Филий Искросташ to Player');
INSERT INTO `creature_text` VALUES (102641, 0, 0, 'Это исследование весьма занимательно! Я как раз вписываю переведенную историю в книгу.', 12, 0, 100, 1, 0, 0, 120019, 0, 0, 0, 'Филий Искросташ to Player');
INSERT INTO `creature_text` VALUES (102641, 3, 0, 'В книгу добавлены новые факты по истории твоего оружия. Тебе стоит взглянуть, когда будет время.', 12, 0, 100, 1, 0, 0, 120022, 228299, 228299, 0, 'Филий Искросташ to Player');
INSERT INTO `creature_text` VALUES (102641, 3, 0, 'Последняя глава нашей книги завершена. Тебе определенно стоит прочесть.', 12, 0, 100, 1, 0, 0, 120029, 11974, 11974, 0, 'Филий Искросташ to Player');
INSERT INTO `creature_text` VALUES (102641, 3, 0, 'У твоего оружия открылось очень интересное прошлое. Это добавлено в книгу – взгляни, если желаешь.', 12, 0, 100, 1, 0, 0, 120023, 143719, 143719, 0, 'Филий Искросташ to Player');
INSERT INTO `creature_text` VALUES (102641, 3, 0, 'У твоего оружия, как оказалось, довольно-таки богатое прошлое. В книгу добавлены новые переводы, можешь взглянуть.', 12, 0, 100, 1, 0, 0, 120024, 779907, 790679, 0, 'Филий Искросташ to Player');
INSERT INTO `creature_text` VALUES (102655, 0, 0, 'Over here! I think you will like the changes we\'ve made!', 14, 0, 100, 0, 0, 0, 105027, 0, 0, 0, 'Алонсий Фаол to Player');
INSERT INTO `creature_text` VALUES (102655, 1, 0, 'I\'d like to introduce you to Calia Menethil. Yes that Menethil. She has been an incredible help these past few years. I\'d like for her to work for you, helping the helpless, taking your word to the world, all that.', 12, 0, 100, 0, 0, 0, 105153, 0, 0, 0, 'Алонсий Фаол to Player');
INSERT INTO `creature_text` VALUES (102655, 2, 0, 'A great start! We\'ve arranged with the Kirin Tor a permanent passage back to Dalaran. When you need to return there will be a portal in Dalaran. Good luck!', 12, 0, 100, 0, 0, 0, 106260, 0, 0, 0, 'Алонсий Фаол to Player');
INSERT INTO `creature_text` VALUES (102670, 1, 0, '', 12, 0, 100, 0, 0, 65817, 100930, 0, 0, 0, 'Боевой маг - ветеран to Player');
INSERT INTO `creature_text` VALUES (102670, 1, 0, 'За шал\'дорай!', 12, 0, 100, 0, 0, 65896, 116673, 0, 0, 0, 'Защитница из Сумеречной стражи to 78116');
INSERT INTO `creature_text` VALUES (102670, 2, 0, 'А-а-а!', 12, 0, 100, 0, 0, 65893, 135125, 0, 0, 0, 'Защитница из Сумеречной стражи to Страж сурамарского щита');
INSERT INTO `creature_text` VALUES (102670, 3, 0, 'You cannot harm us!', 12, 0, 100, 0, 0, 65896, 116672, 2041057, 2041057, 0, 'Duskwatch Defender to Frenzied Withered');
INSERT INTO `creature_text` VALUES (102670, 4, 0, 'For the shal\'dorei!', 12, 0, 100, 0, 0, 65896, 116673, 3428, 3428, 0, 'Duskwatch Defender to Frenzied Withered');
INSERT INTO `creature_text` VALUES (102672, 0, 0, 'You seek the Dream? It seeks you, too.', 14, 0, 100, 0, 0, 58662, 105003, 0, 0, 0, 'Низендра to 0');
INSERT INTO `creature_text` VALUES (102672, 1, 0, 'Your wakened life is a disease!', 14, 0, 100, 0, 0, 58671, 109017, 0, 0, 0, 'Низендра to Player');
INSERT INTO `creature_text` VALUES (102672, 2, 0, '|TInterface\\Icons\\spell_nature_insect_swarm2:20|t Низендра делает глубокий вдох, готовясь поразить своих противников |cFFFF0404|Hspell:202977|h[\"Зараженным дыханием\"]|h|r!', 41, 0, 100, 0, 0, 58668, 106119, 0, 0, 0, 'Низендра to Ловчий дыхания');
INSERT INTO `creature_text` VALUES (102672, 3, 0, 'You have no more need of this life!', 14, 0, 100, 0, 0, 58668, 109013, 0, 0, 0, 'Низендра to Ловчий дыхания');
INSERT INTO `creature_text` VALUES (102672, 3, 1, 'Breathe deeply. Embrace your fate.', 14, 0, 100, 0, 0, 58667, 109011, 0, 0, 0, 'Низендра to Ловчий дыхания');
INSERT INTO `creature_text` VALUES (102672, 4, 0, 'Нет... Столько еще надо сделать... КО МНЕ!', 14, 0, 100, 0, 0, 58669, 109014, 0, 0, 0, 'Низендра to 0');
INSERT INTO `creature_text` VALUES (102672, 5, 0, 'Rest easy. Our new Dream will remake your world...', 14, 0, 100, 0, 0, 58666, 105001, 0, 0, 0, 'Низендра to 0');
INSERT INTO `creature_text` VALUES (102672, 6, 0, 'The Dream has changed. Yet I will protect it still.', 14, 0, 100, 0, 0, 58664, 105004, 0, 0, 0, 'Низендра to 0');
INSERT INTO `creature_text` VALUES (102672, 7, 0, 'The Dream has always shown me truth and it showed me your deaths.', 14, 0, 100, 0, 0, 58663, 105005, 0, 0, 0, 'Низендра to 0');
INSERT INTO `creature_text` VALUES (102672, 8, 0, 'Consumed from within!', 14, 0, 100, 0, 0, 58670, 109016, 0, 0, 0, 'Низендра to Player');
INSERT INTO `creature_text` VALUES (102672, 17, 0, 'Вас пожрут изнутри!', 14, 0, 100, 0, 0, 58670, 109016, 0, 0, 0, 'Низендра to Player');
INSERT INTO `creature_text` VALUES (102672, 19, 0, 'Эта жизнь больше тебе не нужна!', 14, 0, 100, 0, 0, 58668, 109013, 0, 0, 0, 'Низендра to Ловчий дыхания');
INSERT INTO `creature_text` VALUES (102672, 20, 0, 'Бодрствование – это болезнь!', 14, 0, 100, 0, 0, 58671, 109017, 0, 0, 0, 'Низендра to Player');
INSERT INTO `creature_text` VALUES (102672, 24, 0, 'Спите спокойно. Новый Сон преобразит ваш мир...', 14, 0, 100, 0, 0, 58666, 105001, 0, 0, 0, 'Низендра to 0');
INSERT INTO `creature_text` VALUES (102672, 45, 0, 'Сон изменился, но я все равно буду защищать его.', 14, 0, 100, 0, 0, 58664, 105004, 0, 0, 0, 'Низендра to 0');
INSERT INTO `creature_text` VALUES (102672, 49, 0, 'Дышите глубже. Примите судьбу.', 14, 0, 100, 0, 0, 58667, 109011, 0, 0, 0, 'Низендра to Ловчий дыхания');
INSERT INTO `creature_text` VALUES (102672, 82, 0, 'Сон всегда показывает истину – и он показал мне вашу гибель.', 14, 0, 100, 0, 0, 58663, 105005, 0, 0, 0, 'Низендра to 0');
INSERT INTO `creature_text` VALUES (102679, 0, 0, 'The strands of life have been severed! The dreamers must be avenged!', 14, 0, 100, 0, 0, 58510, 106175, 0, 0, 0, 'Исондра to Player');
INSERT INTO `creature_text` VALUES (102679, 1, 0, 'Come forth, dreamers... and claim your vengeance!', 14, 0, 100, 0, 0, 58500, 106176, 0, 0, 0, 'Исондра to Player');
INSERT INTO `creature_text` VALUES (102679, 2, 0, 'All will succumb to the Nightmare!', 14, 0, 100, 0, 0, 58512, 106309, 0, 0, 0, 'Исондра to 0');
INSERT INTO `creature_text` VALUES (102679, 3, 0, 'Все гаснет… все исчезает…', 14, 0, 100, 0, 0, 58511, 106183, 0, 0, 0, 'Исондра to Player');
INSERT INTO `creature_text` VALUES (102681, 0, 0, 'Peace is but a fleeting dream! Let the Nightmare reign!', 14, 0, 100, 0, 0, 58509, 106181, 0, 0, 0, 'Таэрар to Player');
INSERT INTO `creature_text` VALUES (102681, 1, 0, 'Children of madness! I release you upon this world!', 14, 0, 100, 0, 0, 58501, 106182, 0, 0, 0, 'Таэрар to 0');
INSERT INTO `creature_text` VALUES (102682, 0, 0, 'Я вижу сумрак в ваших сердцах! Нечестивцам не обрести покоя!', 14, 0, 100, 0, 0, 58508, 106179, 0, 0, 0, 'Летон to Player');
INSERT INTO `creature_text` VALUES (102682, 1, 0, 'Ваши злобные душонки придадут мне сил!', 14, 0, 100, 0, 0, 58502, 106180, 0, 0, 0, 'Летон to 0');
INSERT INTO `creature_text` VALUES (102682, 2, 0, '|TInterface\\Icons\\Spell_Shadow_AuraOfDarkness:20|t%s begins to cast a spell |cFFFF0000|Hspell:203888|h[\"Вытягивание души\"]|h|r!', 41, 0, 100, 0, 0, 58502, 106307, 0, 0, 0, 'Летон to 0');
INSERT INTO `creature_text` VALUES (102683, 0, 0, 'Надежда – болезнь души! Эта земля высохнет и умрет!', 14, 0, 100, 0, 0, 58500, 106177, 0, 0, 0, 'Эмерисс to 0');
INSERT INTO `creature_text` VALUES (102683, 1, 0, 'Почувствуйте порчу Кошмара!', 14, 0, 100, 0, 0, 58503, 106178, 0, 0, 0, 'Эмерисс to Эмерисс');
INSERT INTO `creature_text` VALUES (102685, 1, 0, 'Раздавлю, как устрицу.', 12, 0, 100, 0, 0, 0, 105362, 0, 0, 0, 'Садж\'тарский надзиратель to Player');
INSERT INTO `creature_text` VALUES (102685, 1, 0, 'Сейчас ты умрешь!', 12, 0, 100, 0, 0, 0, 129920, 0, 0, 0, 'Садж\'тарский надзиратель to Player');
INSERT INTO `creature_text` VALUES (102685, 2, 0, 'Холодный песок...', 12, 0, 100, 0, 0, 0, 105064, 0, 0, 0, 'Садж\'тарский надзиратель to Player');
INSERT INTO `creature_text` VALUES (102693, 0, 0, 'Ритуал прерван, и перед вами материализуется темная энергия!', 41, 0, 100, 0, 0, 0, 105085, 0, 0, 0, 'Сгущающаяся тьма to 991');
INSERT INTO `creature_text` VALUES (102693, 1, 0, 'Уничтожить... все... живое...', 12, 0, 100, 0, 0, 0, 105084, 0, 0, 0, 'Сгущающаяся тьма to 991');
INSERT INTO `creature_text` VALUES (102700, 0, 0, 'Времени в обрез. Этот портал перенесет нас прямо в Аметистовую крепость.', 12, 0, 100, 0, 0, 0, 105193, 0, 0, 0, 'Мерил Буря Скверны to Player');
INSERT INTO `creature_text` VALUES (102700, 1, 0, 'Когда раздобудешь артефакт, $n, возвращайся сюда. Я за это время постараюсь привести тут все в порядок.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Мерил Буря Скверны to Player');
INSERT INTO `creature_text` VALUES (102700, 2, 0, 'Ну что, Аказамзарак, все координаты у тебя. Сможешь провесить порталы?', 12, 0, 100, 0, 0, 0, 105822, 0, 0, 0, 'Мерил Буря Скверны to Player');
INSERT INTO `creature_text` VALUES (102700, 3, 0, 'Маги Азерота! Я собрал вас здесь пред лицом угрозы всему нашему миру!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Мерил Буря Скверны to Player');
INSERT INTO `creature_text` VALUES (102700, 4, 0, 'Повелитель ужаса Катра\'натир сбежал в Круговерть Пустоты, унеся с собой секреты Совета Тирисфаля.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Мерил Буря Скверны to Player');
INSERT INTO `creature_text` VALUES (102700, 5, 0, 'Мы должны отправиться за ним в Круговерть Пустоты и отыскать его там. Лишь тогда мы будем уверены, что он не сможет использовать свои знания против нас.', 12, 0, 100, 0, 0, 0, 105826, 0, 0, 0, 'Мерил Буря Скверны to Player');
INSERT INTO `creature_text` VALUES (102700, 6, 0, 'С этой целью я восстанавливаю орден Стражей Тирисфаля. И я призываю вас присоединиться к нам и поднять оружие против Легиона! Вы с нами?!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Мерил Буря Скверны to Player');
INSERT INTO `creature_text` VALUES (102700, 7, 0, 'Значит, решено.', 12, 0, 100, 0, 0, 0, 46157, 0, 0, 0, 'Мерил Буря Скверны to Player');
INSERT INTO `creature_text` VALUES (102700, 8, 0, 'Отлично. В присутствии величайших магов Азерота нарекаю тебя $n, $gКолдун:Колдунья; Стражей Тирисфаля.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Мерил Буря Скверны to Player');
INSERT INTO `creature_text` VALUES (102700, 9, 0, 'Этот древний титул в свое время носили старейшие из магов Азерота, и он символизирует огромную ответственность, возложенную на Стражей Тирисфаля. Носи же его с гордостью и честью.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Мерил Буря Скверны to Player');
INSERT INTO `creature_text` VALUES (102700, 10, 0, 'А теперь ступай в Зал Войны и познакомься с нашим новым союзником из гоблинов. Можете сразу начинать планировать ваши дальнейшие шаги.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Мерил Буря Скверны to Player');
INSERT INTO `creature_text` VALUES (102700, 11, 0, 'Мне нужно отлучиться по личному делу...', 12, 0, 100, 0, 0, 0, 105993, 0, 0, 0, 'Мерил Буря Скверны to Player');
INSERT INTO `creature_text` VALUES (102700, 12, 0, 'So Arrexis was ambushed by this eredar, Balaadur?  He is probably in possession of the staff now then...', 12, 0, 100, 1, 0, 0, 112317, 0, 0, 0, 'Мерил Буря Скверны to Player');
INSERT INTO `creature_text` VALUES (102700, 13, 0, 'Let us recreate Arrexis\'s ritual! If we start the ritual at an invasion point, that will attract Balaadur\'s attention. He will likely ambush us, but we will be ready.', 12, 0, 100, 1, 0, 0, 112327, 0, 0, 0, 'Мерил Буря Скверны to Player');
INSERT INTO `creature_text` VALUES (102709, 0, 0, 'Самое сложное досталось не мне! Я всего лишь подала идею.', 12, 0, 100, 0, 0, 0, 105145, 0, 0, 0, 'Бетильда Глубокомолот to Player');
INSERT INTO `creature_text` VALUES (102714, 0, 0, 'Защитим портал!', 12, 0, 100, 0, 0, 0, 105116, 0, 0, 0, 'Эредарская призывательница to Player');
INSERT INTO `creature_text` VALUES (102724, 0, 0, 'For the Brood Queen.', 12, 0, 100, 0, 0, 56969, 105113, 0, 0, 0, 'Злобная подчинительница душ to Морская взывательница из клана Змеиных Колец');
INSERT INTO `creature_text` VALUES (102726, 1, 0, 'Убейте их! Используйте заклинание невидимости!', 12, 0, 100, 0, 0, 56976, 105123, 0, 0, 0, 'Эредарская колдунья to Player');
INSERT INTO `creature_text` VALUES (102738, 2, 0, 'I\'m defeated ... so insignificant ...', 12, 0, 100, 0, 0, 0, 104678, 0, 0, 0, 'Калдер to Player');
INSERT INTO `creature_text` VALUES (102738, 3, 0, 'Yandvik is our land! Nobody called you here!', 12, 0, 100, 0, 0, 0, 104897, 10314, 10314, 0, 'Калдер to Player');
INSERT INTO `creature_text` VALUES (102739, 1, 0, '$gЖалкий чужак:Жалкая чужестранка; смеет бросать мне вызов?', 12, 0, 100, 0, 0, 0, 104898, 0, 0, 0, 'Эйнар to Player');
INSERT INTO `creature_text` VALUES (102739, 2, 0, 'Увидимся... в Хельхейме.', 12, 0, 100, 0, 0, 0, 104679, 0, 0, 0, 'Эйнар to Player');
INSERT INTO `creature_text` VALUES (102748, 3, 0, 'При виде вас тварь начинает рычать.', 16, 0, 100, 0, 0, 0, 0, 14440, 14440, 0, 'Прирученный манаклык to Player');
INSERT INTO `creature_text` VALUES (102775, 3, 0, 'Славься, ярл Трондир!', 12, 0, 100, 66, 0, 0, 105685, 38313, 38313, 0, 'Фьолрик to Player');
INSERT INTO `creature_text` VALUES (102796, 1, 0, 'В этой пещере ты найдеш-ш-шь свою с-с-смерть, |3-6($r)!', 12, 0, 100, 0, 0, 0, 105343, 0, 0, 0, 'Сирена Наз\'жул to Player');
INSERT INTO `creature_text` VALUES (102796, 2, 0, 'Повелительница приливов... Я... подвела тебя…', 12, 0, 100, 0, 0, 0, 105344, 0, 0, 0, 'Сирена Наз\'жул to Player');
INSERT INTO `creature_text` VALUES (102799, 3, 0, 'Coming, $p?', 12, 0, 100, 0, 0, 58261, 105175, 6982, 6982, 0, 'Джейс Темный Ткач to Player');
INSERT INTO `creature_text` VALUES (102799, 3, 0, 'Тебе понравится, $p. Догоняй!', 12, 0, 100, 1, 0, 64688, 114963, 2173, 2173, 0, 'Кор\'вас Кровавый Шип to Player');
INSERT INTO `creature_text` VALUES (102799, 3, 0, 'Ты идешь, $p?', 12, 0, 100, 0, 0, 58261, 0, 5731, 5731, 0, 'Джейс Темный Ткач to Player');
INSERT INTO `creature_text` VALUES (102801, 3, 0, '$p! You\'re alive!', 12, 0, 100, 0, 0, 0, 105154, 528, 528, 0, 'Kor\'vas Bloodthorn to Player');
INSERT INTO `creature_text` VALUES (102801, 3, 0, '$p! Ты снова с нами!', 12, 0, 100, 0, 0, 0, 105154, 31, 31, 0, 'Кор\'вас Кровавый Шип to Player');
INSERT INTO `creature_text` VALUES (102803, 3, 0, 'Вблизи боевые клинки выглядят еще более впечатляющими.', 12, 0, 100, 1, 0, 64687, 107832, 7760, 7760, 0, 'Аша Воронья Песнь to Player');
INSERT INTO `creature_text` VALUES (102803, 3, 0, 'Вблизи клинки Искусителя выглядят еще более впечатляющими.', 12, 0, 100, 1, 0, 64686, 105155, 9832, 9832, 0, 'Аша Воронья Песнь to Player');
INSERT INTO `creature_text` VALUES (102820, 0, 0, 'Отличный день! Правда, Великий мастер?', 12, 0, 100, 0, 0, 66721, 116306, 0, 0, 0, 'Чэнь Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (102820, 1, 0, 'Мои таланты хмелевара к твоим услугам. Только скажи – и я примусь за дело.', 12, 0, 100, 2, 0, 66706, 105780, 0, 0, 0, 'Чэнь Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (102824, 0, 0, 'А вот и ты, $p. Я тебя уже заждалась!', 12, 0, 100, 113, 0, 58269, 105218, 0, 0, 0, 'Верховная мать Злоба to Player');
INSERT INTO `creature_text` VALUES (102824, 3, 0, 'It\'s been far too long, $p. Far too long.', 12, 0, 100, 0, 0, 0, 0, 29, 29, 0, 'Верховная мать Злоба to Player');
INSERT INTO `creature_text` VALUES (102824, 3, 0, 'А вот и ты, $p. Я тебя уже заждалась.', 12, 0, 100, 0, 0, 0, 0, 108, 108, 0, 'Верховная мать Злоба to Player');
INSERT INTO `creature_text` VALUES (102826, 0, 0, 'Наги пытают морского великана. Что нам делать?', 12, 0, 100, 0, 0, 0, 107347, 0, 0, 0, 'Эрунак Говорящий с Камнем to Player');
INSERT INTO `creature_text` VALUES (102826, 1, 0, 'Fear not, my enormous friend. The Earthen Ring is here.', 12, 0, 100, 0, 0, 0, 107387, 0, 0, 0, 'Эрунак Говорящий с Камнем to Граш');
INSERT INTO `creature_text` VALUES (102826, 2, 0, 'Well done, my friend! He has seen our missing shaman AND the artifact we seek. Now we must find a way through this ice barrier...', 12, 0, 100, 0, 0, 0, 107470, 0, 0, 0, 'Эрунак Говорящий с Камнем to Player');
INSERT INTO `creature_text` VALUES (102826, 3, 0, '...Да, это подойдет.', 12, 0, 100, 0, 0, 0, 107471, 0, 0, 0, 'Эрунак Говорящий с Камнем to Player');
INSERT INTO `creature_text` VALUES (102826, 4, 0, 'The giant has seen Sharas\'dal, the Scepter of Tides! We are close. Keep him alive and he will protect us.', 12, 0, 100, 0, 0, 0, 107599, 0, 0, 0, 'Эрунак Говорящий с Камнем to Player');
INSERT INTO `creature_text` VALUES (102826, 5, 0, 'Stay back! These creatures are being controlled by another.', 12, 0, 100, 0, 0, 0, 107600, 0, 0, 0, 'Эрунак Говорящий с Камнем to Player');
INSERT INTO `creature_text` VALUES (102826, 6, 0, 'Что тут делают прислужники Азшары?', 12, 0, 100, 0, 0, 0, 107601, 0, 0, 0, 'Эрунак Говорящий с Камнем to Player');
INSERT INTO `creature_text` VALUES (102826, 7, 0, '%s falls to one knee, gravely wounded.', 16, 0, 100, 0, 0, 0, 109484, 0, 0, 0, 'Эрунак Говорящий с Камнем to 0');
INSERT INTO `creature_text` VALUES (102826, 8, 0, '%s is |cFFFF0404|Hspell:204237|h[Gravely Wounded]|h|r and cannot fight until healed!', 41, 0, 100, 0, 0, 0, 109485, 0, 0, 0, 'Эрунак Говорящий с Камнем to 0');
INSERT INTO `creature_text` VALUES (102826, 9, 0, 'If the Scepter is here, this may be our only chance to capture it and turn its power against the Legion!', 12, 0, 100, 0, 0, 0, 107639, 0, 0, 0, 'Эрунак Говорящий с Камнем to Player');
INSERT INTO `creature_text` VALUES (102828, 1, 0, 'Сейчас ты умрешь!', 12, 0, 100, 0, 0, 0, 129920, 0, 0, 0, 'Садж\'тарский пескодробитель to Player');
INSERT INTO `creature_text` VALUES (102828, 2, 0, 'Какая... сила...', 12, 0, 100, 0, 0, 0, 105367, 0, 0, 0, 'Садж\'тарский пескодробитель to Player');
INSERT INTO `creature_text` VALUES (102831, 0, 0, '%s призывает ближайшие существа на помощь.', 16, 0, 100, 0, 0, 0, 107617, 0, 0, 0, 'Зитренайская призывательница приливов to 0');
INSERT INTO `creature_text` VALUES (102839, 0, 0, '|TInterface\\Icons\\ability_fomor_boss_shout:20|t%s begins a |cFFFF0404|Hspell:209748|h[Deafening Roar]|h|r. Stop casting!', 41, 0, 100, 0, 0, 0, 108810, 0, 0, 0, 'Кра\'лисс to 0');
INSERT INTO `creature_text` VALUES (102840, 1, 0, 'У нас незваные гос-с-сти...', 12, 0, 100, 0, 0, 0, 105270, 0, 0, 0, 'Командир Раз\'джира to Player');
INSERT INTO `creature_text` VALUES (102840, 2, 0, 'Yandvik will be our shh ...', 12, 0, 100, 0, 0, 0, 105271, 0, 0, 0, 'Командир Раз\'джира to Player');
INSERT INTO `creature_text` VALUES (102841, 1, 0, 'Что это?! $gЧужак:чужестранка; вмеш-ш-шивается в наш-ш-ши переговоры.', 12, 0, 100, 0, 0, 0, 105268, 0, 0, 0, 'Командир Малт\'хис to Player');
INSERT INTO `creature_text` VALUES (102841, 4, 0, 'Надо с-с-сообщить Повелительнице приливов...', 12, 0, 100, 0, 0, 0, 105269, 10795, 10795, 0, 'Командир Малт\'хис to Player');
INSERT INTO `creature_text` VALUES (102844, 2, 0, 'Моя с-с-смерть ничего не изменит...', 12, 0, 100, 0, 0, 0, 105273, 0, 0, 0, 'Командир Кел\'тарисс to Player');
INSERT INTO `creature_text` VALUES (102844, 3, 0, 'Что это?!', 12, 0, 100, 0, 0, 0, 105272, 9178, 9178, 0, 'Командир Кел\'тарисс to Player');
INSERT INTO `creature_text` VALUES (102846, 0, 0, 'My staff... in the hands of the enemy! We must get it back!', 12, 0, 100, 0, 0, 0, 112318, 0, 0, 0, 'Алоди to Player');
INSERT INTO `creature_text` VALUES (102846, 1, 0, 'That just might work. We will need information on this ritual and the power to pull it off. Let us get to it.', 12, 0, 100, 0, 0, 0, 112328, 0, 0, 0, 'Алоди to Player');
INSERT INTO `creature_text` VALUES (102846, 2, 0, 'You will need to speak to the manager at the Bank of Dalaran to gain access. I have cast a sigil on you that should give you permission. Also there might still be personal wards up inside. They shouldn\'t harm you but be careful.', 12, 0, 100, 0, 0, 0, 112335, 0, 0, 0, 'Алоди to Player');
INSERT INTO `creature_text` VALUES (102850, 0, 0, 'Похоже, повелитель ужаса все время на шаг опережает нас. Проход в той стороне! Скорее!', 12, 0, 100, 0, 0, 0, 105194, 0, 0, 0, 'Мерил Буря Скверны to Player');
INSERT INTO `creature_text` VALUES (102850, 1, 0, 'Все кончено, повелитель ужаса. Твоему владычеству пришел конец!', 14, 0, 100, 0, 0, 0, 105195, 0, 0, 0, 'Мерил Буря Скверны to Player');
INSERT INTO `creature_text` VALUES (102852, 0, 0, 'Секрет штормового меда принадлежит мне! Я ни с кем не стану им делиться!', 12, 0, 100, 5, 0, 0, 105452, 0, 0, 0, 'Морирн to Player');
INSERT INTO `creature_text` VALUES (102855, 0, 0, 'Дальше ты не пройдешь, леди Келетресс!', 12, 0, 100, 603, 0, 71476, 122297, 0, 0, 0, 'Эгира to Леди Келетресс');
INSERT INTO `creature_text` VALUES (102855, 1, 0, 'Ведьма Легиона мертва. Это надо отпраздновать, Великий мастер!', 12, 0, 100, 0, 0, 71475, 107898, 0, 0, 0, 'Эгира to Player');
INSERT INTO `creature_text` VALUES (102862, 2, 0, 'Я возвращаюсь в море...', 12, 0, 100, 0, 0, 0, 104988, 0, 0, 0, 'Посланник племени Глубокоходов to Player');
INSERT INTO `creature_text` VALUES (102864, 0, 0, 'Лорд Седогрив клялся и божился, что мы будем сражаться. Я не подписывался сутками стоять на вахте!', 12, 0, 100, 0, 0, 0, 106155, 0, 0, 0, 'Гилнеасский защитник to Гилнеасский чародей');
INSERT INTO `creature_text` VALUES (102875, 0, 0, 'Терпение. Враг сам придет к нам, и тогда мы обрушим на него всю ярость Гилнеаса!', 12, 0, 100, 0, 0, 0, 106159, 0, 0, 0, 'Гилнеасская чародейка to Стремительный коготь Гилнеаса');
INSERT INTO `creature_text` VALUES (102886, 1, 0, 'Вкусно? <Ага. Сейчас железяки снимем, будет нямка.>', 12, 0, 100, 0, 0, 54354, 102765, 0, 0, 0, 'Горный эттин to Player');
INSERT INTO `creature_text` VALUES (102886, 1, 0, 'Мочи! <Destroy!>', 12, 0, 100, 0, 0, 54355, 102766, 0, 0, 0, 'Горный эттин to Player');
