INSERT INTO `creature_text` VALUES (102886, 1, 0, 'Это мое! <Нет, мое!>', 12, 0, 100, 0, 0, 54352, 102763, 0, 0, 0, 'Горный эттин to Player');
INSERT INTO `creature_text` VALUES (102902, 3, 0, 'Достойнейший выбор, ты молодец! Король обезьян – настоящий боец!', 12, 0, 100, 4, 0, 66659, 107555, 2110, 2110, 0, 'Король обезьян to Player');
INSERT INTO `creature_text` VALUES (102938, 0, 0, 'Малфурион! Где ты, любимый?', 14, 0, 100, 1, 0, 68234, 115739, 0, 0, 0, 'Тиранда Шелест Ветра to Player');
INSERT INTO `creature_text` VALUES (102969, 1, 0, 'Никто не смеет бросать нам вызов!', 12, 0, 100, 0, 0, 66026, 116708, 0, 0, 0, 'Ночнорожденная-диверсантка to Ночнорожденный-мятежник');
INSERT INTO `creature_text` VALUES (102969, 2, 0, 'Тор\'терас фалар!', 12, 0, 100, 0, 0, 66017, 116790, 0, 0, 0, 'Ночнорожденная-диверсантка to Ночнорожденный-мятежник');
INSERT INTO `creature_text` VALUES (102973, 0, 0, 'I warned Baron of Discord about your imminent arrival, but he did not wait for you. This fool does not understand that certain death awaits him there!', 12, 0, 100, 0, 0, 0, 105377, 0, 0, 0, 'Ванесса Фейргрейвс to Player');
INSERT INTO `creature_text` VALUES (102977, 0, 0, 'Эта сторона портала теперь полностью под нашим контролем.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Рантуко Зловещее Касание to Player');
INSERT INTO `creature_text` VALUES (102996, 0, 0, 'Мы на месте. Похищенный рецепт должен быть где-то здесь.', 12, 0, 100, 0, 0, 66631, 105379, 0, 0, 0, 'Эгира to Player');
INSERT INTO `creature_text` VALUES (102996, 3, 0, 'Мельба, моя наставница, передала мне свои знания – в том числе приемы древних врайкульских хмелеваров.', 12, 0, 100, 1, 0, 66641, 105462, 96804, 96804, 0, 'Эгира to Player');
INSERT INTO `creature_text` VALUES (102996, 4, 0, 'Хорошо! Но у моей наставницы получалось лучше.', 12, 0, 100, 5, 0, 66632, 105383, 3766, 3766, 0, 'Эгира to Player');
INSERT INTO `creature_text` VALUES (103022, 0, 0, 'Come. Do not delay us.', 12, 0, 100, 0, 0, 0, 107924, 0, 0, 0, 'Тиранда Шелест Ветра to Player');
INSERT INTO `creature_text` VALUES (103022, 1, 0, 'The path splits. Wait, what is that?', 12, 0, 100, 0, 0, 0, 107925, 0, 0, 0, 'Тиранда Шелест Ветра to Player');
INSERT INTO `creature_text` VALUES (103022, 3, 0, 'Feathers and blood... It is still wet! There may still be time!', 12, 0, 100, 0, 0, 58040, 107143, 0, 0, 0, 'Тиранда Шелест Ветра to Player');
INSERT INTO `creature_text` VALUES (103022, 4, 0, 'The corruption is thick here. What is that across the bridge? Follow closely.', 12, 0, 100, 0, 0, 0, 107926, 0, 0, 0, 'Тиранда Шелест Ветра to Player');
INSERT INTO `creature_text` VALUES (103022, 5, 0, 'Elothir? By Elune... not you too! Have you seen my husband?', 12, 0, 100, 0, 0, 58038, 107145, 0, 0, 0, 'Тиранда Шелест Ветра to Player');
INSERT INTO `creature_text` VALUES (103023, 0, 0, 'Портал в Даларан находится над входом в зал. Туда ведет пандус.', 12, 0, 100, 0, 0, 0, 106341, 0, 0, 0, 'Тактик Сухой Трут to Player');
INSERT INTO `creature_text` VALUES (103025, 0, 0, 'Move, you fools! Show $p some respect!', 12, 0, 100, 5, 0, 0, 105523, 0, 0, 0, 'Военачальник Гаардун to Player');
INSERT INTO `creature_text` VALUES (103055, 3, 0, 'Хорошо. Сосредоточь свой разум. Ты пройдешь испытание согласно традициям нашего ордена.', 12, 0, 100, 0, 0, 0, 125026, 30084, 84042, 0, 'Кир\'алтиус to Player');
INSERT INTO `creature_text` VALUES (103067, 1, 0, 'The darkness gonna take you!', 12, 0, 100, 0, 0, 57965, 107084, 0, 0, 0, 'Камнегрыз-пращник to Player');
INSERT INTO `creature_text` VALUES (103067, 1, 0, 'These are drogbar rocks! Drogbar stones!', 12, 0, 100, 0, 0, 54871, 96416, 0, 0, 0, 'Bitestone Slinger to Player');
INSERT INTO `creature_text` VALUES (103067, 1, 0, 'На горе тебе не место!', 12, 0, 100, 0, 0, 57966, 107085, 0, 0, 0, 'Камнегрыз-пращник to Player');
INSERT INTO `creature_text` VALUES (103081, 3, 0, 'Расправив крылья, поворачивайте их против встречных потоков воздуха, чтобы подняться.', 12, 0, 100, 0, 0, 0, 119811, 17165, 17165, 0, 'Наставница Алландрия');
INSERT INTO `creature_text` VALUES (103092, 0, 0, 'Step right up! Behold the amazing magical talents of the Great Akazamzarak!', 12, 0, 100, 1, 0, 61692, 105635, 0, 0, 0, 'The Great Akazamzarak');
INSERT INTO `creature_text` VALUES (103092, 1, 0, 'Gaze in astonishment as I produce a rabbit from this ordinary hat!', 12, 0, 100, 1, 0, 61700, 105639, 0, 0, 0, 'The Great Akazamzarak');
INSERT INTO `creature_text` VALUES (103092, 2, 0, 'Thank you, thank you. Your tips are appreciated.', 12, 0, 100, 2, 0, 61701, 105640, 0, 0, 0, 'The Great Akazamzarak');
INSERT INTO `creature_text` VALUES (103092, 3, 0, 'Gather \'round and witness mystical wonders like you\'ve never seen before!', 12, 0, 100, 1, 0, 61693, 105636, 0, 0, 0, 'The Great Akazamzarak');
INSERT INTO `creature_text` VALUES (103092, 4, 0, 'Ladies and gentlemen, feast your eyes on these feats of prestidigitation!', 12, 0, 100, 1, 0, 61695, 105638, 0, 0, 0, 'The Great Akazamzarak');
INSERT INTO `creature_text` VALUES (103092, 5, 0, 'And now for my next trick! As you can see, there\'s nothing up my sleeve...', 12, 0, 100, 1, 0, 61702, 105641, 0, 0, 0, 'The Great Akazamzarak');
INSERT INTO `creature_text` VALUES (103092, 6, 0, 'And... bam!', 12, 0, 100, 0, 0, 61703, 105642, 0, 0, 0, 'The Great Akazamzarak');
INSERT INTO `creature_text` VALUES (103092, 7, 0, 'Okay, for this trick I\'ll need a volunteer.', 12, 0, 100, 1, 0, 61704, 105644, 0, 0, 0, 'The Great Akazamzarak');
INSERT INTO `creature_text` VALUES (103092, 8, 0, 'Pick a card, any card...', 12, 0, 100, 1, 0, 61705, 105645, 0, 0, 0, 'The Great Akazamzarak');
INSERT INTO `creature_text` VALUES (103092, 9, 0, 'Hey! Not that card! Give it back!', 12, 0, 100, 1, 0, 61706, 105647, 0, 0, 0, 'The Great Akazamzarak');
INSERT INTO `creature_text` VALUES (103092, 10, 0, 'Ladies and gentlemen, don\'t deny yourself the pleasure of seeing the real wonders of hand dexterity!', 12, 0, 100, 1, 0, 0, 105638, 0, 0, 0, 'The Great Akazamzarak to 0');
INSERT INTO `creature_text` VALUES (103092, 10, 0, 'Дамы и господа, не откажите себе в удовольствии увидеть настоящие чудеса ловкости рук!', 12, 0, 100, 1, 0, 0, 105638, 0, 0, 0, 'The Great Akazamzarak to 0');
INSERT INTO `creature_text` VALUES (103092, 11, 0, 'Thank you, thank you so much. I\'m very grateful to you for your donations.', 12, 0, 100, 0, 0, 0, 105640, 0, 0, 0, 'The Great Akazamzarak to Dave\'s Industrial Light and Magic Bunny (Small)(Sessile)');
INSERT INTO `creature_text` VALUES (103092, 11, 0, 'Спасибо, большое спасибо. Я очень благодарен вам за ваши пожертвования.', 12, 0, 100, 0, 0, 0, 105640, 0, 0, 0, 'The Great Akazamzarak to Dave\'s Industrial Light and Magic Bunny (Small)(Sessile)');
INSERT INTO `creature_text` VALUES (103092, 12, 0, 'И... Whoa, whoa, whoa!', 12, 0, 100, 0, 0, 0, 105642, 0, 0, 0, 'The Great Akazamzarak to Dave\'s Industrial Light and Magic Bunny (Small)(Sessile)');
INSERT INTO `creature_text` VALUES (103092, 12, 0, 'И... оп-па!', 12, 0, 100, 0, 0, 0, 105642, 0, 0, 0, 'The Great Akazamzarak to Dave\'s Industrial Light and Magic Bunny (Small)(Sessile)');
INSERT INTO `creature_text` VALUES (103092, 13, 0, 'Okay, for the next trick, I\'m gonna need an assistant. Who wants to volunteer?', 12, 0, 100, 1, 0, 0, 105644, 0, 0, 0, 'The Great Akazamzarak to Dave\'s Industrial Light and Magic Bunny (Small)(Sessile)');
INSERT INTO `creature_text` VALUES (103092, 13, 0, 'Так, для следующего фокуса мне понадобится помощник. Кто хочет быть добровольцем?', 12, 0, 100, 1, 0, 0, 105644, 0, 0, 0, 'The Great Akazamzarak to Dave\'s Industrial Light and Magic Bunny (Small)(Sessile)');
INSERT INTO `creature_text` VALUES (103092, 14, 0, 'Pull out the map. Any card.', 12, 0, 100, 0, 0, 0, 105645, 0, 0, 0, 'The Great Akazamzarak to Dave\'s Industrial Light and Magic Bunny (Small)(Sessile)');
INSERT INTO `creature_text` VALUES (103092, 14, 0, 'Вытяните карту. Любую карту.', 12, 0, 100, 0, 0, 0, 105645, 0, 0, 0, 'The Great Akazamzarak to Dave\'s Industrial Light and Magic Bunny (Small)(Sessile)');
INSERT INTO `creature_text` VALUES (103092, 15, 0, 'Hey! Not that one! Give it back!', 12, 0, 100, 61, 0, 0, 105647, 0, 0, 0, 'The Great Akazamzarak to Dave\'s Industrial Light and Magic Bunny (Small)(Sessile)');
INSERT INTO `creature_text` VALUES (103092, 15, 0, 'Эй! Не эту! А ну отдай!', 12, 0, 100, 61, 0, 0, 105647, 0, 0, 0, 'The Great Akazamzarak to Dave\'s Industrial Light and Magic Bunny (Small)(Sessile)');
INSERT INTO `creature_text` VALUES (103092, 16, 0, 'Вы остолбенеете, когда я на ваших глазах вытащу кролика из самой обычной шляпы!', 12, 0, 100, 0, 0, 0, 105639, 0, 0, 0, 'The Great Akazamzarak to Dave\'s Industrial Light and Magic Bunny (Small)(Sessile)');
INSERT INTO `creature_text` VALUES (103111, 3, 0, 'Да здравствует ярл $n! Ура!', 14, 0, 100, 66, 0, 0, 105627, 29816, 29816, 0, 'Яндвикский щитолом to Player');
INSERT INTO `creature_text` VALUES (103120, 0, 0, 'Wow! A golden Dr. Boom!', 12, 0, 100, 5, 0, 0, 105646, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (103122, 3, 0, 'Слава павшим!', 14, 0, 100, 0, 0, 0, 105629, 15023, 15023, 0, 'Яндвикская призывательница рун to Player');
INSERT INTO `creature_text` VALUES (103126, 0, 0, 'Убить незваного гостя!', 14, 0, 100, 0, 0, 0, 105655, 0, 0, 0, 'Яндвикский щитолом to Player');
INSERT INTO `creature_text` VALUES (103127, 3, 0, 'I feel trouble.', 12, 0, 100, 0, 0, 0, 105666, 15793, 15793, 0, 'Яндвикская призывательница рун to Player');
INSERT INTO `creature_text` VALUES (103129, 0, 0, 'Лунных стражей больше нет. Нашу цитадель ничто не спасет.', 12, 0, 100, 1, 0, 66384, 117110, 0, 0, 0, 'Сирана Ткущая Звезды to Player');
INSERT INTO `creature_text` VALUES (103133, 0, 0, 'I\'m awake! Definitely NOT sleeping! I was just... just resting my eyes.', 12, 0, 100, 0, 0, 0, 106010, 0, 0, 0, 'Наралекс to Player');
INSERT INTO `creature_text` VALUES (103133, 1, 0, 'I assume it is time for the ritual to begin? Very well, I\'ll meet you there.', 12, 0, 100, 0, 0, 0, 105865, 0, 0, 0, 'Наралекс to Player');
INSERT INTO `creature_text` VALUES (103135, 0, 0, 'Hey mon, you not be comin\' ta Zen\'tabra for some fish. What ya be wantin\'?', 12, 0, 100, 0, 0, 0, 105677, 0, 0, 0, 'Зен\'табра to Player');
INSERT INTO `creature_text` VALUES (103136, 0, 0, 'So, time to see if we can really be makin\' this happen. This gonna\' be needin\' everything we have ta give and more.', 12, 0, 100, 1, 0, 0, 105873, 0, 0, 0, 'Зен\'табра to Player');
INSERT INTO `creature_text` VALUES (103136, 3, 0, 'De spirits, they be silent ta\' me on dis. That mean dey not opposed ta it either. Very well, I\'ll meet ye at da ritual.', 12, 0, 100, 1, 0, 0, 105881, 10578, 10578, 0, 'Зен\'табра to Player');
INSERT INTO `creature_text` VALUES (103136, 3, 0, 'Zen\'tabra closes her eyes for a moment.', 16, 0, 100, 0, 0, 0, 105874, 5778, 5778, 0, 'Зен\'табра to Player');
INSERT INTO `creature_text` VALUES (103144, 0, 0, 'My... thanks. Though we have never met before, I sense great courage in your heart. Yes... you are the hero I need!', 12, 0, 100, 0, 0, 0, 105775, 0, 0, 0, 'Торадин to 1037');
INSERT INTO `creature_text` VALUES (103144, 1, 0, 'Long ago, my yearning for knowledge led me here, to a tomb that lies beneath the lake. Within its cold stone walls, I found something long buried... a terrible darkness, an ancient evil.', 12, 0, 100, 0, 0, 0, 105776, 0, 0, 0, 'Торадин to 1037');
INSERT INTO `creature_text` VALUES (103144, 2, 0, 'My men begged me to walk away. I refused. My pride would be my downfall... and theirs. The evil stirred, unleashing death upon us. With my last breath, I drove my blade, Stromkar, into the beast\'s skull.', 12, 0, 100, 0, 0, 0, 105781, 0, 0, 0, 'Торадин to 1037');
INSERT INTO `creature_text` VALUES (103144, 3, 0, 'For many ages, the sword has kept the beast from rising again. But now it stirs once more, so I must ask you delve into the depths of the tomb and finish what I started. I will go with you, to offer what aid I can.', 12, 0, 100, 0, 0, 0, 105782, 0, 0, 0, 'Торадин to 1037');
INSERT INTO `creature_text` VALUES (103144, 4, 0, 'Even in death, the monster is restless. Its corruption has spread throughout the tomb. You will have to fight your way through!', 14, 0, 100, 0, 0, 0, 105866, 0, 0, 0, 'Торадин to Player');
INSERT INTO `creature_text` VALUES (103148, 0, 0, 'Чуан!', 14, 0, 100, 0, 0, 0, 107053, 0, 0, 0, 'Чэнь Буйный Портер to Инфернал-разрушитель');
INSERT INTO `creature_text` VALUES (103148, 1, 0, 'Ли Ли, пойдем-ка проведаем малышей.', 12, 0, 100, 0, 0, 0, 107055, 0, 0, 0, 'Чэнь Буйный Портер to Монах школы Тигра');
INSERT INTO `creature_text` VALUES (103148, 2, 0, 'Бегите, дети мои! Я защищу вас от этого монстра!', 14, 0, 100, 0, 0, 0, 99913, 0, 0, 0, 'Чэнь Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (103148, 3, 0, 'Спасибо за помощь, друг! А эта гнусная тварь сильна!', 12, 0, 100, 0, 0, 0, 99965, 0, 0, 0, 'Чэнь Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (103148, 4, 0, 'Дети! Ведите себя хорошо!', 14, 0, 100, 0, 0, 0, 99966, 0, 0, 0, 'Чэнь Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (103148, 5, 0, 'Идемте, дети мои. Скорее в храм. $n, нужна твоя помощь. Тут повсюду отродья тьмы.', 12, 0, 100, 0, 0, 0, 100187, 0, 0, 0, 'Чэнь Буйный Портер to Player');
