INSERT INTO `creature_text` VALUES (103148, 7, 0, 'Великий мастер Хайт! Как я рад вас видеть!', 12, 0, 100, 0, 0, 0, 100193, 0, 0, 0, 'Чэнь Буйный Портер to Девятая Цзя');
INSERT INTO `creature_text` VALUES (103148, 8, 0, 'Это все благодаря |3-2($n). $GОн подоспел:Она подоспела; вовремя и $gспас:спасла; нас.', 12, 0, 100, 0, 0, 0, 100195, 0, 0, 0, 'Чэнь Буйный Портер to Девятая Цзя');
INSERT INTO `creature_text` VALUES (103155, 0, 0, 'Бр-р... Никогда не привыкну.', 12, 0, 100, 0, 0, 67408, 105777, 0, 0, 0, 'Чародейка Вальтруа to Player');
