INSERT INTO `creature_text` VALUES (103162, 1, 0, 'Вкуси магии Скверны!', 12, 0, 100, 0, 0, 56847, 105128, 0, 0, 0, 'Эредарская молельщица to Player');
INSERT INTO `creature_text` VALUES (103162, 1, 0, 'Моя родина – Аргус, величайший из миров Великой Тьмы.', 12, 0, 100, 0, 0, 56983, 105127, 0, 0, 0, 'Эредарская молельщица to Player');
