INSERT INTO `creature_text` VALUES (103175, 3, 0, 'Все равно его репутацию уже ничто не вернет. Именно поэтому ты до сих пор ходишь в учениках.', 12, 0, 100, 0, 0, 66069, 109179, 5937, 5937, 0, 'Леспин Серебристый Лист to Player');
