INSERT INTO `creature_text` VALUES (103176, 1, 0, 'Смерть уже заждалась.', 12, 0, 100, 0, 0, 55332, 127315, 0, 0, 0, 'Мо\'арг-крошитель to Player');
INSERT INTO `creature_text` VALUES (103176, 1, 0, 'Ха-ха-ха! Я тебя зарублю!', 12, 0, 100, 0, 0, 55326, 94883, 0, 0, 0, 'Мо\'арг-крошитель to Player');
INSERT INTO `creature_text` VALUES (103178, 3, 0, 'Тяжело больно. Тягай еще!', 14, 0, 100, 0, 0, 0, 105887, 168564, 168564, 0, 'Свинорыл - падальщик');
INSERT INTO `creature_text` VALUES (103180, 1, 0, 'Смерть врагам Легиона!', 12, 0, 100, 0, 0, 55146, 96647, 0, 0, 0, 'Гнусный вестник ужаса to Player');
INSERT INTO `creature_text` VALUES (103180, 1, 0, 'Сочный мозг. Хрустящие кости.', 12, 0, 100, 0, 0, 55150, 96651, 0, 0, 0, 'Гнусный вестник ужаса to Player');
INSERT INTO `creature_text` VALUES (103180, 1, 0, 'Ужаснись!', 12, 0, 100, 0, 0, 55142, 96645, 0, 0, 0, 'Гнусный вестник ужаса to Player');
INSERT INTO `creature_text` VALUES (103180, 3, 0, 'Это была роковая ошибка.', 12, 0, 100, 0, 0, 55149, 96650, 225335, 225335, 0, 'Гнусный вестник ужаса to Player');
INSERT INTO `creature_text` VALUES (103181, 0, 0, 'Прошу прощения, мастер. Я все еще работаю над тем срочным заказом…', 12, 0, 100, 0, 0, 66170, 110366, 0, 0, 0, 'Ученик Ардриас to Player');
INSERT INTO `creature_text` VALUES (103181, 3, 0, 'Прошу прощения, мастер. Я все еще работаю над той застежкой по срочному заказу...', 12, 0, 100, 0, 0, 66170, 110366, 692411, 692411, 0, 'Ученик Ардриас to Player');
INSERT INTO `creature_text` VALUES (103183, 2, 0, 'Мои... угри...', 12, 0, 100, 0, 0, 0, 105857, 0, 0, 0, 'Рок\'наш to Player');
INSERT INTO `creature_text` VALUES (103183, 3, 0, 'Ах ты, жалкая тварь! Мои угри сожрут тебя живьем!', 12, 0, 100, 0, 0, 0, 105856, 28408, 28408, 0, 'Рок\'наш to Player');
INSERT INTO `creature_text` VALUES (103183, 4, 0, 'Дайте мне заряд!', 12, 0, 100, 0, 0, 0, 118833, 11851, 11851, 0, 'Рок\'наш');
INSERT INTO `creature_text` VALUES (103183, 4, 0, 'Пора кое-кого поджарить!', 12, 0, 100, 0, 0, 0, 118836, 16804, 16804, 0, 'Рок\'наш');
INSERT INTO `creature_text` VALUES (103183, 4, 0, 'Хозяин зовет вас!', 12, 0, 100, 0, 0, 0, 105834, 6792, 6792, 0, 'Рок\'наш');
INSERT INTO `creature_text` VALUES (103204, 0, 0, 'Мы должны добраться до Брэмбли, пока не поздно!', 12, 0, 100, 0, 0, 0, 105916, 0, 0, 0, 'Ангус Грозовой Отвар to player');
INSERT INTO `creature_text` VALUES (103214, 2, 0, 'Хочу... есть...', 12, 0, 100, 0, 0, 0, 105895, 0, 0, 0, 'Хар\'кесс Ненасытный to Player');
INSERT INTO `creature_text` VALUES (103214, 4, 0, 'I will eat you!', 12, 0, 100, 0, 0, 0, 105897, 4254, 4254, 0, 'Хар\'кесс Ненасытный');
INSERT INTO `creature_text` VALUES (103218, 0, 0, 'I can stand no more of this dallying! The Dark Lady must let us take the fight to the Alliance dogs now!', 12, 0, 100, 0, 0, 0, 106363, 0, 0, 0, 'Отрекшийся-обманщик to Отрекшийся - ректор');
INSERT INTO `creature_text` VALUES (103231, 1, 0, 'I will break your will.', 12, 0, 100, 0, 0, 55379, 102160, 0, 0, 0, 'Мрачный инквизитор to Player');
INSERT INTO `creature_text` VALUES (103231, 1, 0, 'От меня не спрячешься.', 12, 0, 100, 0, 0, 55374, 102155, 0, 0, 0, 'Мрачный инквизитор to Player');
INSERT INTO `creature_text` VALUES (103231, 1, 0, 'Перед смертью ты выдашь мне все секреты.', 12, 0, 100, 0, 0, 55373, 102154, 0, 0, 0, 'Мрачный инквизитор to Player');
INSERT INTO `creature_text` VALUES (103231, 1, 0, 'Это будет познавательно.', 12, 0, 100, 0, 0, 55375, 102156, 0, 0, 0, 'Мрачный инквизитор to Player');
INSERT INTO `creature_text` VALUES (103231, 1, 0, 'Я и мертвого могу заставить говорить.', 12, 0, 100, 0, 0, 55376, 102157, 0, 0, 0, 'Мрачный инквизитор to Player');
INSERT INTO `creature_text` VALUES (103245, 0, 0, 'The enemy will have to fight us on our terms. Stay patient. Soon, we will have out fill of battle!', 12, 0, 100, 0, 0, 0, 106366, 0, 0, 0, 'Отрекшийся-осквернитель to Отрекшийся-ректор');
INSERT INTO `creature_text` VALUES (103344, 0, 0, 'Моя кора – ваша погибель.', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Дубосерд to 0');
INSERT INTO `creature_text` VALUES (103344, 1, 0, 'Горите!', 14, 0, 100, 0, 0, 0, 133419, 0, 0, 0, 'Дубосерд to 0');
INSERT INTO `creature_text` VALUES (103344, 1, 1, 'Вы станете поленьями в моем костре!', 14, 0, 100, 0, 0, 0, 106133, 0, 0, 0, 'Дубосерд to 0');
INSERT INTO `creature_text` VALUES (103344, 1, 2, 'Готовьтесь пойти на дрова!', 14, 0, 100, 0, 0, 0, 106130, 0, 0, 0, 'Дубосерд to 0');
INSERT INTO `creature_text` VALUES (103344, 2, 0, 'I\'ll break you like brushwood.', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Дубосерд to Player');
INSERT INTO `creature_text` VALUES (103344, 2, 1, 'Мои ветви отрастут вновь, а ваши руки и ноги – нет.', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Дубосерд to Player');
INSERT INTO `creature_text` VALUES (103344, 2, 2, 'Я вырву вас с корнем.', 14, 0, 100, 0, 0, 0, 106002, 0, 0, 0, 'Дубосерд to 0');
INSERT INTO `creature_text` VALUES (103344, 2, 3, 'Сейчас я вырву корень всех проблем.', 14, 0, 100, 0, 0, 0, 106001, 0, 0, 0, 'Дубосерд to 0');
INSERT INTO `creature_text` VALUES (103344, 3, 0, 'Вам здесь не укорениться.', 14, 0, 100, 0, 0, 0, 106003, 0, 0, 0, 'Дубосерд to 0');
INSERT INTO `creature_text` VALUES (103344, 4, 0, 'Дайте-ка я вас обниму. Крепко-крепко.', 14, 0, 100, 0, 0, 0, 106136, 0, 0, 0, 'Дубосерд to 0');
INSERT INTO `creature_text` VALUES (103344, 5, 0, 'Я же просил... оставить меня в покое...', 14, 0, 100, 0, 0, 0, 105982, 0, 0, 0, 'Дубосерд to Player');
INSERT INTO `creature_text` VALUES (103347, 0, 0, 'Всех не защитить...', 12, 0, 100, 603, 0, 0, 105978, 0, 0, 0, 'Ньелл Ткущий Звезды to Player');
INSERT INTO `creature_text` VALUES (103348, 0, 0, 'Стража больше нет.', 12, 0, 100, 457, 0, 0, 105979, 0, 0, 0, 'Галарт Речное Дерево to Player');
INSERT INTO `creature_text` VALUES (103363, 1, 0, 'I will tear your flesh and devour your soul.', 12, 0, 100, 0, 0, 55016, 97911, 0, 0, 0, 'Бездушный тюремщик to Player');
INSERT INTO `creature_text` VALUES (103363, 1, 0, 'Еще один пленник для моей тюрьмы.', 12, 0, 100, 0, 0, 55014, 97909, 0, 0, 0, 'Бездушный тюремщик to Player');
INSERT INTO `creature_text` VALUES (103363, 1, 0, 'Твоя душа будет моей!', 12, 0, 100, 0, 0, 55013, 97908, 0, 0, 0, 'Бездушный тюремщик to Player');
INSERT INTO `creature_text` VALUES (103363, 1, 0, 'Ты так хочешь оказаться в клетке?', 12, 0, 100, 0, 0, 55018, 97913, 0, 0, 0, 'Бездушный тюремщик to Player');
INSERT INTO `creature_text` VALUES (103432, 4, 0, 'For Brood Queen Tyranna. For the Legion!', 12, 0, 100, 0, 0, 55193, 96664, 21209, 21209, 0, 'Центурион королевы to Player');
INSERT INTO `creature_text` VALUES (103437, 0, 0, 'Кому в лобешник?', 12, 0, 100, 0, 0, 0, 106067, 0, 0, 0, 'Ангус Грозовой Отвар to player');
INSERT INTO `creature_text` VALUES (103437, 0, 1, 'Кому нужна секира, когда есть стальные кулаки!', 12, 0, 100, 0, 0, 0, 106065, 0, 0, 0, 'Ангус Грозовой Отвар to player');
INSERT INTO `creature_text` VALUES (103437, 0, 2, 'Ой, что у тебя с лицом?! ЭТО ВСЕ МОЙ КУЛАК!', 12, 0, 100, 0, 0, 0, 106071, 0, 0, 0, 'Ангус Грозовой Отвар to player');
INSERT INTO `creature_text` VALUES (103437, 0, 3, 'Ай! Кто тут упал с уродского дерева и ударился о мои кулаки!', 12, 0, 100, 0, 0, 0, 106066, 0, 0, 0, 'Ангус Грозовой Отвар to player');
INSERT INTO `creature_text` VALUES (103437, 0, 4, 'Не бойся, бить буду аккуратно!', 12, 0, 100, 0, 0, 0, 106069, 0, 0, 0, 'Ангус Грозовой Отвар to player');
INSERT INTO `creature_text` VALUES (103437, 1, 0, 'Ах ты, а тут демоны погрызли... Да, до сих пор мокрая...', 12, 0, 100, 0, 0, 0, 106446, 0, 0, 0, 'Ангус Грозовой Отвар to player');
INSERT INTO `creature_text` VALUES (103437, 2, 0, 'Я и теперь слышу в голове голос Брэмбли, он велит мне выбраться отсюда живым.', 12, 0, 100, 0, 0, 0, 106514, 0, 0, 0, 'Ангус Грозовой Отвар to player');
INSERT INTO `creature_text` VALUES (103453, 0, 0, 'The curs will bring themselves to us. We will blunt their rage and feast on their flesh!', 12, 0, 100, 0, 0, 0, 106365, 0, 0, 0, 'Отрекшийся - ректор to Отрекшийся-обманщик');
INSERT INTO `creature_text` VALUES (103458, 0, 0, 'If a wild beast smells fear on you, you\'re dead. Or sausage. If they smell sausage, you\'ll be mauled.', 12, 0, 100, 396, 0, 0, 106358, 0, 0, 0, 'Beastmaster Tagh to Player');
INSERT INTO `creature_text` VALUES (103458, 3, 0, 'You gotta lock their gaze. That\'s the secret to a good taming!', 12, 0, 100, 5, 0, 0, 106357, 1320, 1320, 0, 'Beastmaster Tagh to Player');
INSERT INTO `creature_text` VALUES (103458, 3, 0, 'Главное – наладить с питомцем зрительный контакт. Это залог успешной дрессировки!', 12, 0, 100, 0, 0, 0, 106357, 7, 7, 0, 'Повелитель зверей Тах to Player');
INSERT INTO `creature_text` VALUES (103458, 3, 0, 'Не стой над душой, когда другой охотник приручает зверя. Это очень личное.', 12, 0, 100, 396, 0, 0, 106359, 26, 26, 0, 'Повелитель зверей Тах to Player');
INSERT INTO `creature_text` VALUES (103484, 1, 0, 'Ну что, пора собирать диск?', 12, 0, 100, 0, 0, 0, 106439, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (103484, 2, 0, 'ТЬФУ! Вот только не говори, что нам опять какой-то детали не хватает!', 12, 0, 100, 0, 0, 0, 106443, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (103484, 3, 0, 'Так, вот эта деталь идет сюда...', 12, 0, 100, 0, 0, 0, 106440, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (103484, 4, 0, 'Вроде все детали на месте. А откуда взялась эта дырка посередине... А?', 12, 0, 100, 0, 0, 0, 106441, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (103485, 0, 0, 'Начнем, $N.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (103485, 1, 1, '\r\nО великий Эчеро, я возвращаю твои кости к матери земле и молюсь, чтобы твой дух был в этом мире.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (103488, 3, 0, 'Excellent work! Even now the foul creep of the Nightmare has begun to fade.', 12, 0, 100, 0, 0, 0, 0, 2137, 2137, 0, 'Хранитель Ремул to Player');
INSERT INTO `creature_text` VALUES (103488, 3, 0, 'The way is clear. Come, let us continue to our destination.', 12, 0, 100, 0, 0, 0, 106097, 6957, 6957, 0, 'Хранитель Ремул to Player');
INSERT INTO `creature_text` VALUES (103489, 3, 0, 'I will remove these nightmarish vines from our path, but the source of fel that gave birth to them is somewhere else. $n, find this source and destroy it!', 12, 0, 100, 0, 0, 0, 0, 38644, 38644, 0, 'Хранитель Ремул to Player');
INSERT INTO `creature_text` VALUES (103489, 3, 0, 'Не может быть... Кошмар? Даже в этой части Изумрудного Сна? Но как такое возможно?', 12, 0, 100, 0, 0, 0, 0, 34169, 34169, 0, 'Хранитель Ремул to Player');
INSERT INTO `creature_text` VALUES (103489, 3, 0, 'Приветствую тебя на Тропе Снов, $n. Здесь дорогу, которая заняла бы на Азероте несколько дней, можно пройти за пару шагов.', 12, 0, 100, 0, 0, 0, 0, 2680, 2680, 0, 'Хранитель Ремул to Player');
INSERT INTO `creature_text` VALUES (103506, 0, 0, 'Приходи к нам в Круг Воли, $gчернокнижник:чернокнижница;. Мой портал доставит тебя прямиком туда. Мы будем ждать.', 12, 0, 100, 1, 0, 61694, 105637, 0, 0, 0, 'Ритссин Хмурый Огонь  to Player');
INSERT INTO `creature_text` VALUES (103511, 0, 0, 'Заблудившийся шерстоперый котенок замечает вас и в страхе убегает прочь.', 16, 0, 100, 0, 0, 0, 110347, 0, 0, 0, 'Заблудившийся шерстоперый котенок to Player');
INSERT INTO `creature_text` VALUES (103515, 0, 0, 'ГОТОВЬСЯ ВСТРЕТИТЬСЯ СО СВОЕЙ СУДЬБОЙ, $N! ИБО Я, ЗМЕЕУСТЫШ ТАНКИНС, ПРИНЕСУ ЭТОМУ МИРУ ПОГИБЕЛЬ!', 14, 0, 100, 0, 0, 0, 106410, 0, 0, 0, 'Змееустыш Танкинс to Player');
INSERT INTO `creature_text` VALUES (103515, 1, 0, 'Постой! Пожалуйста, не убивай меня!', 12, 0, 100, 0, 0, 0, 106411, 0, 0, 0, 'Змееустыш Танкинс to Player');
INSERT INTO `creature_text` VALUES (103529, 0, 0, 'Мне нужен... воздух...', 12, 0, 100, 0, 0, 0, 106880, 0, 0, 0, 'Одержимый врайкул to Player');
INSERT INTO `creature_text` VALUES (103529, 0, 3, 'I am free!', 12, 0, 100, 0, 0, 0, 132206, 0, 0, 0, 'Одержимый врайкул to Player');
INSERT INTO `creature_text` VALUES (103529, 0, 4, 'Где... что...', 12, 0, 100, 0, 0, 0, 106882, 0, 0, 0, 'Одержимый врайкул to Player');
INSERT INTO `creature_text` VALUES (103529, 0, 5, 'Тупой кальмар!', 12, 0, 100, 0, 0, 0, 106883, 0, 0, 0, 'Одержимый врайкул to Player');
INSERT INTO `creature_text` VALUES (103546, 3, 0, 'Краснокопыт осторожно взрыхляет почву копытом.', 16, 0, 100, 0, 0, 0, 108537, 10889, 10889, 0, 'Красноног Древний to Player');
INSERT INTO `creature_text` VALUES (103567, 3, 0, 'Принц эттинов повержен! Уходим отсюда!', 12, 0, 100, 0, 0, 0, 106149, 74797, 74797, 0, 'Пленная тауренка из племени Речной Гривы to Принц холмов Руггу');
INSERT INTO `creature_text` VALUES (103568, 0, 0, 'Готово!', 12, 0, 100, 4, 0, 0, 108841, 0, 0, 0, 'Мейруна Лунное Крыло to Player');
INSERT INTO `creature_text` VALUES (103568, 1, 0, 'I escaped from Val\'shara when Ysera attacked the temple. I was very lucky that along the way we met with Mitandros.', 12, 0, 100, 4, 0, 0, 0, 0, 0, 0, 'Мейруна Лунное Крыло to Player');
INSERT INTO `creature_text` VALUES (103569, 0, 0, 'Кругом растет свежая зеленая еда, а враги твои малочисленны. Взамен мы просим лишь о твоем благословении.', 12, 0, 100, 0, 0, 0, 108539, 0, 0, 0, 'Митандрос Железная Роща to Player');
INSERT INTO `creature_text` VALUES (103569, 1, 0, 'Что это?..', 12, 0, 100, 0, 0, 0, 111018, 0, 0, 0, 'Митандрос Железная Роща to Player');
INSERT INTO `creature_text` VALUES (103569, 2, 0, 'Надо же, это в самом деле редкий цветок.', 12, 0, 100, 0, 0, 0, 119990, 0, 0, 0, 'Митандрос Железная Роща to Player');
INSERT INTO `creature_text` VALUES (103569, 3, 0, 'Благодарю тебя, благородный Краснокопыт. Мы почтительно принимаем твое благословение.', 12, 0, 100, 0, 0, 0, 119991, 0, 0, 0, 'Митандрос Железная Роща to Player');
INSERT INTO `creature_text` VALUES (103569, 4, 0, 'Лунный колодец будет питать и защищать все, что его окружает. Наконец-то мы можем назвать это место своим домом.', 12, 0, 100, 2, 0, 0, 120016, 0, 0, 0, 'Митандрос Железная Роща to Player');
INSERT INTO `creature_text` VALUES (103569, 5, 0, 'Спасибо за помощь. Для нас это очень много значит.', 12, 0, 100, 2, 0, 0, 119944, 0, 0, 0, 'Митандрос Железная Роща to Player');
INSERT INTO `creature_text` VALUES (103570, 0, 0, 'Ну вот! Твоим котятам больше ничего не угрожает, Йоулон. Ты дашь нам свое благословение, чтобы мы вместе могли жить на этой земле?', 12, 0, 100, 1, 0, 0, 110918, 0, 0, 0, 'Мардранел Лесное Сердце to Player');
INSERT INTO `creature_text` VALUES (103570, 1, 0, 'Спасибо, Ваше Величество.', 12, 0, 100, 2, 0, 0, 110920, 0, 0, 0, 'Мардранел Лесное Сердце to Player');
INSERT INTO `creature_text` VALUES (103570, 2, 0, 'Спасибо.', 12, 0, 100, 2, 0, 0, 131567, 0, 0, 0, 'Мардранел Лесное Сердце to Player');
INSERT INTO `creature_text` VALUES (103570, 3, 0, 'Мы из деревни под Сурамаром. Еле-еле спаслись, надо сказать.', 12, 0, 100, 0, 0, 0, 119937, 0, 0, 0, 'Мардранел Лесное Сердце to Player');
INSERT INTO `creature_text` VALUES (103570, 4, 0, 'Я буду ждать тебя на той стороне.', 12, 0, 100, 0, 0, 0, 110286, 0, 0, 0, 'Мардранел Лесное Сердце to Player');
INSERT INTO `creature_text` VALUES (103571, 0, 0, 'Наши труды не были напрасны. Матриарх ущелья Лунного Шепота наблюдает издалека.', 12, 0, 100, 0, 0, 0, 108550, 0, 0, 0, 'Солендра Падающее Перо to Player');
INSERT INTO `creature_text` VALUES (103571, 1, 0, 'Я доставлю благословение лично Митандросу. Буду ждать тебя у него!', 12, 0, 100, 0, 0, 0, 120002, 0, 0, 0, 'Солендра Падающее Перо to Player');
INSERT INTO `creature_text` VALUES (103571, 2, 0, 'Пусть удача всегда сопутствует тебе в охоте, сестра.', 12, 0, 100, 0, 0, 0, 108551, 0, 0, 0, 'Солендра Падающее Перо to Player');
INSERT INTO `creature_text` VALUES (103571, 3, 0, 'Спасибо!', 12, 0, 100, 2, 0, 0, 131553, 0, 0, 0, 'Солендра Падающее Перо to Player');
INSERT INTO `creature_text` VALUES (103571, 4, 0, 'Кошмар шел за нами по пятам. Он отстал от нас только когда мы вошли в Сурамар.', 12, 0, 100, 2, 0, 0, 119940, 0, 0, 0, 'Солендра Падающее Перо to Player');
INSERT INTO `creature_text` VALUES (103575, 0, 0, 'Старайтесь, мои наги! Мы уничтожим Яндвик!', 14, 0, 100, 0, 0, 0, 106170, 0, 0, 0, 'Повелитель рифов Раж\'хис');
INSERT INTO `creature_text` VALUES (103575, 1, 0, 'Yandvik our-shh.', 12, 0, 100, 0, 0, 0, 105320, 0, 0, 0, 'Повелитель рифов Раж\'хис to Player');
INSERT INTO `creature_text` VALUES (103575, 4, 0, 'Тиш-ш-шина бездонной пучины...', 12, 0, 100, 0, 0, 0, 105330, 28324, 28324, 0, 'Повелитель рифов Раж\'хис to Player');
INSERT INTO `creature_text` VALUES (103575, 4, 0, 'Чешуйчатые стражи, заморозьте врага до смерти!', 12, 0, 100, 0, 0, 0, 119735, 12306, 12306, 0, 'Повелитель рифов Раж\'хис');
INSERT INTO `creature_text` VALUES (103580, 3, 0, 'Если кто и может помочь вам найти этот Столп Созидания, так это мой дедушка.', 12, 0, 100, 1, 0, 58372, 106193, 9024, 9024, 0, 'Стеллагоса to Player');
INSERT INTO `creature_text` VALUES (103643, 0, 0, 'I see you.', 12, 0, 100, 0, 0, 55372, 132218, 0, 0, 0, 'Гноящийся инквизитор to Player');
INSERT INTO `creature_text` VALUES (103643, 0, 1, 'Я и мертвого могу заставить говорить.', 12, 0, 100, 0, 0, 0, 102157, 0, 0, 0, 'Гноящийся инквизитор to Player');
INSERT INTO `creature_text` VALUES (103643, 0, 2, 'I will break your will.', 12, 0, 100, 0, 0, 0, 102160, 0, 0, 0, 'Гноящийся инквизитор to Player');
INSERT INTO `creature_text` VALUES (103656, 1, 0, 'Снаружи жестко! <А внутри мягко!>', 12, 0, 100, 0, 0, 54349, 102760, 0, 0, 0, 'Горнот Заблудший to Player');
INSERT INTO `creature_text` VALUES (103656, 1, 0, 'Это мое место! <Жрать давай!>', 12, 0, 100, 0, 0, 54351, 102762, 0, 0, 0, 'Горнот Заблудший to Player');
INSERT INTO `creature_text` VALUES (103656, 1, 0, 'Это мое! <Нет, мое!>', 12, 0, 100, 0, 0, 54352, 102763, 0, 0, 0, 'Горнот Заблудший to Player');
INSERT INTO `creature_text` VALUES (103660, 0, 0, 'I prefer the appearance of the raven that Atiesh gives me. The saddle rubs.', 12, 0, 100, 0, 0, 58376, 106570, 0, 0, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (103660, 1, 0, 'Иллидари разбили лагерь на дальней стороне острова. Быть может, они помогут нам отыскать Столп Созидания.', 12, 0, 100, 0, 0, 58375, 106573, 0, 0, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (103660, 2, 0, 'Смотри-ка... Наги! У нас появился конкурент – сама королева Азшара.', 12, 0, 100, 0, 0, 58374, 106574, 0, 0, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (103660, 3, 0, 'Должно быть, она тоже ищет Столп Созидания. Интересно...', 12, 0, 100, 0, 0, 58373, 106575, 0, 0, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (103685, 0, 0, 'Нельзя доверять смертным работу демона!', 14, 0, 100, 0, 0, 60131, 111057, 0, 0, 0, 'AGGRO');
INSERT INTO `creature_text` VALUES (103685, 1, 0, 'Утоли мой голод!', 14, 0, 100, 0, 0, 60123, 111049, 0, 0, 0, 'FEAST_OF_BLOOD');
INSERT INTO `creature_text` VALUES (103685, 2, 0, 'Вы сгниете!', 14, 0, 100, 0, 0, 60121, 111047, 0, 0, 0, 'CARRION_PLAGUE');
INSERT INTO `creature_text` VALUES (103685, 3, 0, '|TInterface\\Icons\\spell_deathknight_necroticplague.blp:20|t Вы поражены |cFFF00000|Hspell:206480|h[\"Трупным заражением\"]|h|r!', 42, 0, 100, 0, 0, 0, 122230, 0, 0, 0, 'CARRION_PLAGUE WARN');
INSERT INTO `creature_text` VALUES (103685, 4, 0, 'Гибель близка!', 14, 0, 100, 0, 0, 60124, 111050, 0, 0, 0, 'SAY_ECHOES_OF_THE_VOID');
INSERT INTO `creature_text` VALUES (103685, 4, 1, 'Свет оставил вас!', 14, 0, 100, 0, 0, 60125, 29512, 0, 0, 0, 'SAY_ECHOES_OF_THE_VOID');
INSERT INTO `creature_text` VALUES (103685, 5, 0, 'Вы навеваете на меня сон – и я отплачу вам тем же!', 14, 0, 100, 0, 0, 60122, 111048, 0, 0, 0, 'CHANGE_PHASE');
INSERT INTO `creature_text` VALUES (103685, 6, 0, 'Прислужники! Живо ко мне!', 14, 0, 100, 0, 0, 60126, 111052, 0, 0, 0, 'SUMMON_GUARDS');
INSERT INTO `creature_text` VALUES (103685, 6, 1, 'Покажите этим ничтожествам, как сражаться!', 14, 0, 100, 0, 0, 60128, 111053, 0, 0, 0, 'SUMMON_GUARDS');
INSERT INTO `creature_text` VALUES (103685, 7, 0, 'Смешно! Можно подумать, у вас был шанс прервать ритуал...', 14, 0, 100, 0, 0, 60133, 111063, 0, 0, 0, 'WIPE');
INSERT INTO `creature_text` VALUES (103685, 7, 1, 'Побили пару эльфов, пережравших магии, и решили, что одолеете истинного сына Легиона?!', 14, 0, 100, 0, 0, 60130, 111056, 0, 0, 0, 'WIPE');
INSERT INTO `creature_text` VALUES (103685, 7, 2, 'Какой предсказуемый исход.', 14, 0, 100, 0, 0, 60134, 111064, 0, 0, 0, 'WIPE');
INSERT INTO `creature_text` VALUES (103685, 8, 0, 'Быстрая смерть! Какое везение!', 14, 0, 100, 0, 0, 69581, 111061, 0, 0, 0, 'SAY_KILL_PLAYER');
INSERT INTO `creature_text` VALUES (103685, 8, 1, 'I will burn your corpse!', 14, 0, 100, 0, 0, 60132, 111060, 0, 0, 0, 'SAY_KILL_PLAYER');
INSERT INTO `creature_text` VALUES (103685, 9, 0, 'Невозможно... Вы... низшие...', 14, 0, 100, 0, 0, 60135, 111065, 0, 0, 0, 'SAY_DEATH');
INSERT INTO `creature_text` VALUES (103693, 3, 0, 'Уделишь мне минутку, $n? У меня есть кое-что интересное.', 12, 0, 100, 0, 0, 0, 122734, 24611, 68196, 0, 'Экипировщик Рейнолдс to Player');
INSERT INTO `creature_text` VALUES (103712, 0, 0, 'Йоулон наклоняется и выдергивает перо из своей лапы.', 16, 0, 100, 0, 0, 0, 110919, 0, 0, 0, 'Йоулон to Player');
INSERT INTO `creature_text` VALUES (103712, 1, 0, 'Йоулон кладет перо на ладонь Мардранела.', 16, 0, 100, 0, 0, 0, 110921, 0, 0, 0, 'Йоулон to Player');
INSERT INTO `creature_text` VALUES (103758, 0, 0, 'Знали бы вы, что там, за гранью…', 14, 0, 100, 0, 0, 58430, 107996, 0, 0, 0, 'Звездный авгур Этрей to 0');
INSERT INTO `creature_text` VALUES (103758, 1, 0, 'Возвращайся в пустоту.', 14, 0, 100, 0, 0, 58446, 108114, 0, 0, 0, 'Звездный авгур Этрей to Player');
INSERT INTO `creature_text` VALUES (103758, 2, 0, 'Страшитесь знамения звезд!', 14, 0, 100, 0, 0, 58433, 107999, 0, 0, 0, 'Звездный авгур Этрей to 0');
INSERT INTO `creature_text` VALUES (103758, 3, 0, 'Звезды зажигаются – ваши жизни гаснут!', 14, 0, 100, 0, 0, 58435, 108001, 0, 0, 0, 'Звездный авгур Этрей to 0');
INSERT INTO `creature_text` VALUES (103758, 4, 0, 'Звезды судили вас и сочли недостойными.', 14, 0, 100, 0, 0, 58445, 108100, 0, 0, 0, 'Звездный авгур Этрей to 0');
INSERT INTO `creature_text` VALUES (103758, 5, 0, 'Аннигиляция неизбежна.', 14, 0, 100, 0, 0, 58447, 108115, 0, 0, 0, 'Звездный авгур Этрей to Player');
INSERT INTO `creature_text` VALUES (103758, 6, 0, 'Не надейтесь... В будущем все еще хуже.', 14, 0, 100, 0, 0, 58448, 108126, 0, 0, 0, 'Звездный авгур Этрей to Player');
INSERT INTO `creature_text` VALUES (103758, 9, 0, 'Звезды судили вас и сочли ничтожеством.', 14, 0, 100, 0, 0, 58445, 108100, 0, 0, 0, 'Звездный авгур Этраэус to 0');
INSERT INTO `creature_text` VALUES (103758, 10, 0, 'Возвращайтесь в ничто.', 14, 0, 100, 0, 0, 58446, 108114, 0, 0, 0, 'Звездный авгур Этраэус to Player');
INSERT INTO `creature_text` VALUES (103758, 13, 0, 'Знали бы вы, что там дальше...', 14, 0, 100, 0, 0, 58430, 107996, 0, 0, 0, 'Звездный авгур Этраэус to 0');
INSERT INTO `creature_text` VALUES (103758, 16, 0, '|TInterface\\Icons\\spell_frost_ring of frost:20|t%s begins to cast a spell |cFFFF0000|Hspell:206949|h[\"Леденящее кольцо\"]|h|r!', 41, 0, 100, 0, 0, 0, 107846, 0, 0, 0, 'Звездный авгур Этраэус to Звездный авгур Этраэус');
INSERT INTO `creature_text` VALUES (103758, 39, 0, '|TInterface\\Icons\\ability_felarakkoa_feldetonation_green:20|t%s begins to cast a spell |cFFFF0000|Hspell:206517|h[\"Кольцо энергии Скверны\"]|h|r!', 41, 0, 100, 0, 0, 0, 107847, 0, 0, 0, 'Звездный авгур Этраэус to Звездный авгур Этраэус');
INSERT INTO `creature_text` VALUES (103758, 222, 0, '|TInterface\\Icons\\spell_priest_shadoworbs:20|t%s begins to cast a spell |cFFFF0000|Hspell:207439|h[\"Кольцо Бездны\"]|h|r!', 41, 0, 100, 0, 0, 0, 107848, 0, 0, 0, 'Звездный авгур Этраэус to Звездный авгур Этраэус');
INSERT INTO `creature_text` VALUES (103761, 0, 0, 'Почему так поздно? Я должна была быть первой!', 12, 0, 100, 5, 0, 66953, 115849, 0, 0, 0, 'Кор\'вас Кровавый Шип to Player');
INSERT INTO `creature_text` VALUES (103761, 1, 0, 'Иллидари, мы нужны в Мардуме! Выдвигаемся!', 14, 0, 100, 0, 0, 82813, 125228, 0, 0, 0, 'Кор\'вас Кровавый Шип to Player');
INSERT INTO `creature_text` VALUES (103761, 2, 0, 'Сегодня мы приблизили погибель Легиона! Давайте же отпразднуем нашу победу!', 12, 0, 100, 0, 0, 82843, 129214, 0, 0, 0, 'Кор\'вас Кровавый Шип to Player');
INSERT INTO `creature_text` VALUES (103761, 3, 0, 'Готовьте клинки для грядущих битв! Никто не устоит перед иллидари!', 12, 0, 100, 0, 0, 82844, 129215, 0, 0, 0, 'Кор\'вас Кровавый Шип to Player');
INSERT INTO `creature_text` VALUES (103769, 0, 0, 'Кошмару покорялись все: драконы, боги, герои... Я покончу с вами – легко и быстро.', 14, 0, 100, 0, 0, 0, 109326, 0, 0, 0, 'Ксавий to 0');
INSERT INTO `creature_text` VALUES (103769, 1, 0, 'Пора обрубить вашу последнюю связь с миром смертных!', 14, 0, 100, 0, 0, 0, 109350, 0, 0, 0, 'Ксавий to Player');
INSERT INTO `creature_text` VALUES (103769, 1, 1, 'I will cut you off from the world of the living!', 14, 0, 100, 0, 0, 0, 109349, 0, 0, 0, 'Ксавий to Player');
INSERT INTO `creature_text` VALUES (103769, 1, 2, 'I will hit you in the heart!', 14, 0, 100, 0, 0, 0, 109348, 0, 0, 0, 'Ксавий to Player');
INSERT INTO `creature_text` VALUES (103769, 1, 3, 'I will break all your hopes!', 14, 0, 100, 0, 0, 0, 109353, 0, 0, 0, 'Ксавий to 0');
INSERT INTO `creature_text` VALUES (103769, 2, 0, '|cFFFF0000%s|r begins to cast a spell |cFFFF0000|Hspell:210264|h[\"Проявление порчи\"]|h|r!', 41, 0, 100, 0, 0, 0, 120241, 0, 0, 0, 'Ксавий to 0');
INSERT INTO `creature_text` VALUES (103769, 3, 0, 'Вы падете один за одним!', 14, 0, 100, 0, 0, 0, 109352, 0, 0, 0, 'Ксавий to 0');
INSERT INTO `creature_text` VALUES (103769, 4, 0, 'Познайте разрушительную мощь Кошмара!', 14, 0, 100, 0, 0, 0, 109351, 0, 0, 0, 'Ксавий to 0');
INSERT INTO `creature_text` VALUES (103769, 5, 0, 'Кошмары... нельзя... забыть...', 14, 0, 100, 0, 0, 0, 109347, 0, 0, 0, 'Ксавий to Зловещий охотник');
INSERT INTO `creature_text` VALUES (103778, 0, 0, 'Да хранит тебя Элуна, $n. Нам срочно нужно поговорить!', 12, 0, 100, 0, 0, 0, 106125, 0, 0, 0, 'Наралекс to Player');
INSERT INTO `creature_text` VALUES (103778, 1, 0, 'Следуй за мной. Из Рощи Снов мы попадем на Тропу Снов. Так ты сможешь мгновенно переместиться даже в самые отдаленные уголки Азерота.', 12, 0, 100, 0, 0, 0, 106128, 0, 0, 0, 'Наралекс to Player');
INSERT INTO `creature_text` VALUES (103797, 3, 0, '$c, подойди ко мне. Дай мне взглянуть на эти клинки, от них несет смертью.', 12, 0, 100, 4, 0, 0, 112376, 251551, 251551, 0, 'Брульф Грузный to Player');
INSERT INTO `creature_text` VALUES (103797, 3, 0, '$gОхотник:Охотница;! Что это у тебя за лук? Такие под стать только лучшим из лучших. Наверное, он попал сюда из другого мира?', 12, 0, 100, 1, 0, 0, 123334, 2393, 2393, 0, 'Брульф Грузный to Player');
INSERT INTO `creature_text` VALUES (103797, 3, 0, 'О, я чувствую их жажду. Они были выкованы во мраке. Многих сгубило их касание.', 12, 0, 100, 0, 0, 0, 112377, 875732, 875732, 0, 'Брульф Грузный to Player');
INSERT INTO `creature_text` VALUES (103807, 2, 0, 'Тор\'терас фалар!', 12, 0, 100, 0, 0, 65656, 116790, 0, 0, 0, 'Делар Стрижиный Луг to Player');
INSERT INTO `creature_text` VALUES (103809, 3, 0, 'Серебряный Клык кладет на землю гладкий блестящий клык ночного саблезуба.', 16, 0, 100, 0, 0, 0, 108545, 19736, 19736, 0, 'Серебряный Клык to Player');
INSERT INTO `creature_text` VALUES (103827, 3, 0, 'Жалкая тварь. В последний раз ты вмешиваешься в наши дела.', 14, 0, 100, 0, 0, 0, 106479, 134, 134, 0, 'Король Моргалаш to Player');
INSERT INTO `creature_text` VALUES (103832, 0, 0, 'Возвращайся, когда соберешь достаточно сущности лесов. Больше я никому не могу доверить это задание.', 12, 0, 100, 0, 0, 66765, 117108, 0, 0, 0, 'Хранитель Ремул to Player');
INSERT INTO `creature_text` VALUES (103833, 3, 0, 'Идем. Мы должны разыскать друидов Валь\'шары и попросить у них помощи.', 12, 0, 100, 0, 0, 6515, 0, 9189, 9189, 0, 'Хранитель Ремул to Player');
INSERT INTO `creature_text` VALUES (103833, 3, 0, 'Ну вот, мы на месте, $n. Ах! Я запомнил эти леса именно такими...', 12, 0, 100, 0, 0, 0, 0, 351, 351, 0, 'Хранитель Ремул to Player');
INSERT INTO `creature_text` VALUES (103833, 3, 0, 'Роща Снов защищена древней магией. Здесь все братья и сестры во природе найдут тихое пристанище.', 12, 0, 100, 0, 0, 0, 0, 2439, 2439, 0, 'Хранитель Ремул to Player');
INSERT INTO `creature_text` VALUES (103860, 0, 0, 'Предательница мертва. Спасибо, мы перед вами в долгу.', 12, 0, 100, 0, 0, 0, 103228, 0, 0, 0, 'Дреланим Шелест Ветра to 0');
INSERT INTO `creature_text` VALUES (103860, 1, 0, 'I will go to the surface. You should tell other Guards about what happened here.', 12, 0, 100, 0, 0, 0, 103229, 0, 0, 0, 'Дреланим Шелест Ветра to 0');
INSERT INTO `creature_text` VALUES (103860, 2, 0, 'Да укроет вас ночь, и да укажет свет луны ваших врагов.', 12, 0, 100, 0, 0, 0, 103230, 0, 0, 0, 'Дреланим Шелест Ветра to 0');
INSERT INTO `creature_text` VALUES (103876, 1, 0, 'Жалкий каркан! Не лезь, куда не надо!', 12, 0, 100, 0, 0, 57967, 107086, 0, 0, 0, 'Сумрачный хранитель осколков to Player');
INSERT INTO `creature_text` VALUES (103876, 1, 0, 'Сейчас захрустят твои кости!', 12, 0, 100, 0, 0, 54860, 96412, 0, 0, 0, 'Сумрачный хранитель осколков to Player');
INSERT INTO `creature_text` VALUES (103876, 1, 0, 'Шан ронир! Вали к тауренам!', 12, 0, 100, 0, 0, 57968, 107087, 0, 0, 0, 'Сумрачный хранитель осколков to Player');
INSERT INTO `creature_text` VALUES (103886, 1, 0, 'Эти скалы принадлежат дрогбарам!', 12, 0, 100, 0, 0, 54871, 96416, 0, 0, 0, 'Сумрачный рудонос to Player');
INSERT INTO `creature_text` VALUES (103886, 1, 0, 'Я выпущу тебе кишки.', 12, 0, 100, 0, 0, 54862, 96414, 0, 0, 0, 'Сумрачный рудонос to Player');
INSERT INTO `creature_text` VALUES (103886, 3, 0, 'Тьма поглотит тебя!', 12, 0, 100, 0, 0, 57965, 117302, 5264, 5264, 0, 'Сумрачный рудонос to Player');
INSERT INTO `creature_text` VALUES (103933, 3, 0, 'Кто посмел бросить вызов королю Моргалашу?', 41, 0, 100, 0, 0, 0, 106491, 10353, 10353, 0, 'Король Моргалаш to Player');
INSERT INTO `creature_text` VALUES (103933, 3, 0, 'Раздавить жалкую тварь!', 41, 0, 100, 0, 0, 0, 106493, 41612, 41612, 0, 'Король Моргалаш to Player');
INSERT INTO `creature_text` VALUES (103976, 0, 0, 'Отлично, $n! Ты справляешься на ура и, похоже, $gспособен:способна; на большее... Обращайся, если интересно!', 12, 0, 100, 0, 0, 0, 146434, 0, 0, 0, 'Почтальон to Player');
INSERT INTO `creature_text` VALUES (104014, 0, 0, 'Хабар, навар! Мне нужна добыча!', 12, 0, 100, 0, 0, 0, 106517, 0, 0, 0, 'Подлый вор');
INSERT INTO `creature_text` VALUES (104016, 0, 0, 'Можно я это просто заберу?', 12, 0, 100, 0, 0, 0, 106521, 0, 0, 0, 'Алчный мародер');
INSERT INTO `creature_text` VALUES (104070, 0, 0, 'Клянусь, я отправил чек по почте этим утром! Нельзя же меня оставлять совсем без гроша!', 12, 0, 100, 20, 0, 0, 144897, 0, 0, 0, 'Киран Камнелоб to Player');
INSERT INTO `creature_text` VALUES (104070, 1, 0, 'Лапы прочь, мохнатик!', 12, 0, 100, 25, 0, 0, 144902, 0, 0, 0, 'Киран Камнелоб to Player');
INSERT INTO `creature_text` VALUES (104070, 2, 0, 'И заберу! Я честно купил эти доспехи, хоть пока за них и не заплатил.', 12, 0, 100, 0, 0, 0, 144904, 0, 0, 0, 'Киран Камнелоб to Player');
INSERT INTO `creature_text` VALUES (104070, 3, 0, 'Так-то лучше. Броня, достойная могучего героя, чьи деяния сотрясут основы Азерота!', 12, 0, 100, 0, 0, 0, 144905, 0, 0, 0, 'Киран Камнелоб to Player');
INSERT INTO `creature_text` VALUES (104070, 4, 0, 'Ну, я в таверну.', 12, 0, 100, 0, 0, 0, 144906, 0, 0, 0, 'Киран Камнелоб to Player');
INSERT INTO `creature_text` VALUES (104070, 5, 0, 'Спасибо за помощь, $Gприятель:подруга;! За мной должок!', 12, 0, 100, 3, 0, 0, 144907, 0, 0, 0, 'Киран Камнелоб to Player');
INSERT INTO `creature_text` VALUES (104091, 3, 0, 'Easy! Easy there! I hope that vrykul leaves soon!', 14, 0, 100, 0, 0, 0, 105357, 2918, 2918, 0, 'Стражница Кирин-Тора to Player');
INSERT INTO `creature_text` VALUES (104091, 3, 0, 'Кто-то проник в хранилище!', 12, 0, 100, 0, 0, 76471, 126502, 3813, 3813, 0, 'Страж Кирин-Тора to Player');
INSERT INTO `creature_text` VALUES (104091, 3, 0, 'Мы должны сохранять спокойствие, пока Совет не завершит расследование.', 12, 0, 100, 0, 0, 0, 129380, 43709, 43709, 0, 'Стражница Кирин-Тора to Player');
INSERT INTO `creature_text` VALUES (104091, 3, 0, 'Мы с Советом активно работаем над урегулированием ситуации.', 12, 0, 100, 1, 0, 0, 129379, 1498230, 1498230, 0, 'Страж Кирин-Тора to Player');
INSERT INTO `creature_text` VALUES (104091, 3, 0, 'Пожалуйста, сохраняйте спокойствие и соблюдайте очередность. Банкир обслужит вас, как только сможет.', 12, 0, 100, 1, 0, 0, 129587, 1660, 1660, 0, 'Страж Кирин-Тора to Player');
INSERT INTO `creature_text` VALUES (104091, 3, 0, 'Просим вас проявить терпение – скоро мы сообщим вам все подробности.', 12, 0, 100, 1, 0, 0, 129591, 74979, 74979, 0, 'Страж Кирин-Тора to Player');
INSERT INTO `creature_text` VALUES (104091, 3, 0, 'Совет просит сохранять спокойствие, пока они оценивают ситуацию.', 12, 0, 100, 0, 0, 0, 129382, 24616, 24616, 0, 'Страж Кирин-Тора to Player');
INSERT INTO `creature_text` VALUES (104091, 3, 0, 'Спокойно! Всем сохранять спокойствие! Этот врайкул надолго здесь не задержится!', 14, 0, 100, 0, 0, 0, 105357, 4082, 4082, 0, 'Страж Кирин-Тора to Player');
INSERT INTO `creature_text` VALUES (104154, 0, 0, 'Вы уже забыли об унижении на Расколотом берегу? О том, как ваш король умолял убить его, стоя на коленях? Вы тоже будете молить меня о пощаде?', 14, 0, 100, 0, 0, 58778, 109300, 0, 0, 0, 'Гул\'дан to Player');
INSERT INTO `creature_text` VALUES (104154, 1, 0, 'Альянс рассыплется в прах. Ваш малолетний король склонится предо мной – и вы станете служить мне!', 14, 0, 100, 0, 0, 58780, 109302, 0, 0, 0, 'Гул\'дан to Player');
