INSERT INTO `creature_text` VALUES (104349, 0, 0, 'Боюсь, что Сон в еще худшем состоянии, чем мы предполагали.', 12, 0, 100, 0, 0, 0, 106846, 0, 0, 0, 'Наралекс to Player');
INSERT INTO `creature_text` VALUES (104349, 1, 0, 'Now just relax and focus your mind on the Dream. I will guide your spirit from here.', 12, 0, 100, 0, 0, 0, 106848, 0, 0, 0, 'Наралекс to Player');
INSERT INTO `creature_text` VALUES (104359, 2, 0, 'Yandvik will fall!', 12, 0, 100, 0, 0, 0, 101740, 0, 0, 0, 'Повелительница приливов Садж\'тар to Player');
INSERT INTO `creature_text` VALUES (104365, 3, 0, 'Гнусные врайкулы! Я вам еще отомщу!', 12, 0, 100, 4, 0, 0, 106865, 1780, 1780, 0, 'Пленник Крутогорья to Player');
INSERT INTO `creature_text` VALUES (104365, 3, 0, 'Давно пора найти какое-нибудь оружие!', 12, 0, 100, 4, 0, 0, 106869, 2970, 2970, 0, 'Пленница Крутогорья to Player');
INSERT INTO `creature_text` VALUES (104365, 3, 0, 'Они атаковали нас быстро и решительно. Такого нападения врайкулов я уже много лет не припомню.', 12, 0, 100, 4, 0, 0, 106870, 2999, 2999, 0, 'Пленник Крутогорья to Player');
INSERT INTO `creature_text` VALUES (104365, 3, 0, 'Спасибо, $gвоитель:воительница;. Дальше я уже доберусь без твоей помощи.', 12, 0, 100, 4, 0, 0, 106867, 2131, 2131, 0, 'Пленница Крутогорья to Player');
INSERT INTO `creature_text` VALUES (104369, 0, 0, 'Опять Леспин шлет своих головорезов? Достал он меня. Мой питомец преподаст тебе урок!', 12, 0, 100, 0, 0, 65816, 106876, 0, 0, 0, 'Морнат to Player');
INSERT INTO `creature_text` VALUES (104369, 1, 0, 'Морнат говорит: Ладно, забирай! Только не бей меня больше. Сундук вон там.', 12, 0, 100, 0, 0, 65816, 106876, 0, 0, 0, 'Морнат to Player');
INSERT INTO `creature_text` VALUES (104382, 3, 0, 'Можем долететь на этих гиппогрифов до Штормхейма и там ждать сигнала Вухо!', 12, 0, 100, 396, 0, 0, 0, 8181, 8181, 0, 'Апата Крутогор to Player');
INSERT INTO `creature_text` VALUES (104385, 0, 0, 'The first trial will reveal your might. You will face the strongest of my followers in combat. Show me that you possess the strength of the bear!', 12, 0, 100, 0, 0, 0, 107141, 0, 0, 0, 'Урсол to Player');
INSERT INTO `creature_text` VALUES (104385, 1, 0, 'Before you awaits your next trial. Defeat all of the spirits and you will have proven you possess the courage of the bear!', 12, 0, 100, 0, 0, 0, 107154, 0, 0, 0, 'Урсол to Player');
INSERT INTO `creature_text` VALUES (104385, 2, 0, 'Magnificent! Your courage is undeniable! A final trial awaits you.', 12, 0, 100, 0, 0, 0, 107155, 0, 0, 0, 'Урсол to Player');
INSERT INTO `creature_text` VALUES (104385, 3, 0, 'The final trial will not be easy, $c. Speak with me when you are ready.', 12, 0, 100, 0, 0, 0, 107508, 0, 0, 0, 'Урсол to Player');
INSERT INTO `creature_text` VALUES (104385, 4, 0, 'Very good, $n. The spirit of the bear truly dwells within you!', 12, 0, 100, 0, 0, 0, 107516, 0, 0, 0, 'Урсол to Player');
INSERT INTO `creature_text` VALUES (104394, 0, 0, 'Он смеет присылать кого-то вместо себя? Негодяй.', 12, 0, 100, 0, 0, 65846, 109380, 0, 0, 0, 'Леди Анастэ to Player');
INSERT INTO `creature_text` VALUES (104398, 0, 0, 'I thought I was finished!', 12, 0, 100, 0, 0, 0, 106906, 0, 0, 0, 'Башана Рунический Тотем to Башана Рунический Тотем');
INSERT INTO `creature_text` VALUES (104398, 1, 0, 'I\'d prefer to avoid these horrors. Let\'s sneak past them!', 12, 0, 100, 0, 0, 0, 106907, 0, 0, 0, 'Башана Рунический Тотем to Башана Рунический Тотем');
INSERT INTO `creature_text` VALUES (104399, 0, 0, 'Кто посмел... Так, а это еще что?', 12, 0, 100, 1, 0, 61779, 107196, 0, 0, 0, 'Принц Билаал to Player');
INSERT INTO `creature_text` VALUES (104399, 1, 0, 'Еще один вор ищет сокровища хранилища Нексуса?', 12, 0, 100, 1, 0, 61644, 107197, 0, 0, 0, 'Принц Билаал to Player');
INSERT INTO `creature_text` VALUES (104399, 2, 0, 'Слишком поздно, |3-6($c)! Вся мощь хранилища уже направлена на расширение пролома в Круговерти Пустоты.', 12, 0, 100, 1, 0, 61764, 107198, 0, 0, 0, 'Принц Билаал to Player');
INSERT INTO `creature_text` VALUES (104399, 3, 0, 'С этой силой братство Эфириум будет непобедимым. Мы сами станем Бездной!', 12, 0, 100, 15, 0, 61765, 107199, 0, 0, 0, 'Принц Билаал to Player');
INSERT INTO `creature_text` VALUES (104406, 0, 0, 'Мои сородичи спасены, а морской страж убит. Теперь я вернусь в Яндвик.', 12, 0, 100, 0, 0, 0, 106960, 0, 0, 0, 'Брютаг to Player');
INSERT INTO `creature_text` VALUES (104415, 0, 0, 'Зачем противиться своей гибели?', 14, 0, 100, 0, 0, 58486, 107976, 0, 0, 0, 'Хрономатическая аномалия to 0');
INSERT INTO `creature_text` VALUES (104415, 1, 0, '|TInterface\\Icons\\ability_mage_timewarp:20|tВремя идет как обычно.', 41, 0, 100, 0, 0, 58486, 117025, 0, 0, 0, 'Хрономатическая аномалия to Хрономатическая аномалия');
INSERT INTO `creature_text` VALUES (104415, 2, 0, 'Своевременная смерть.', 14, 0, 100, 0, 0, 58487, 107978, 0, 0, 0, 'Хрономатическая аномалия to Player');
INSERT INTO `creature_text` VALUES (104415, 2, 1, 'Так предсказуемо. Так примитивно.', 14, 0, 100, 0, 0, 58488, 107980, 0, 0, 0, 'Хрономатическая аномалия to Player');
INSERT INTO `creature_text` VALUES (104415, 2, 2, 'Это был лишь вопрос времени.', 14, 0, 100, 0, 0, 58497, 107994, 0, 0, 0, 'Хрономатическая аномалия to 0');
INSERT INTO `creature_text` VALUES (104415, 2, 3, 'Время на МОЕЙ стороне.', 14, 0, 100, 0, 0, 58489, 107981, 0, 0, 0, 'Хрономатическая аномалия to Player');
INSERT INTO `creature_text` VALUES (104415, 3, 0, 'Из Ночного Колодца выпадает угасающая частица времени.', 16, 0, 100, 0, 0, 0, 119483, 0, 0, 0, 'Хрономатическая аномалия to Хрономатическая аномалия');
INSERT INTO `creature_text` VALUES (104415, 4, 0, '|TInterface\\Icons\\spell_mage_temporalshield:20|t |cFFFF0000|Hspell:219815|h[Временные сферы]|h|r выплывают из Ночного Колодца!', 41, 0, 100, 0, 0, 0, 118992, 0, 0, 0, 'Хрономатическая аномалия to 0');
INSERT INTO `creature_text` VALUES (104415, 5, 0, '|TInterface\\Icons\\inv_archonpet:20|t%s начинает читать заклинание |cFFFF0000|Hspell:211927|h[\"Переполняющая мощь\"]|h|r!', 41, 0, 100, 0, 0, 0, 107970, 0, 0, 0, 'Хрономатическая аномалия to Хрономатическая аномалия');
INSERT INTO `creature_text` VALUES (104415, 6, 0, 'Мое время пришло.', 14, 0, 100, 0, 0, 58496, 107992, 0, 0, 0, 'Хрономатическая аномалия to Player');
INSERT INTO `creature_text` VALUES (104415, 7, 0, '|TInterface\\Icons\\ability_mage_timewarp:20|tВремя идет очень медленно.', 41, 0, 100, 0, 0, 58493, 117027, 0, 0, 0, 'Хрономатическая аномалия to Хрономатическая аномалия');
INSERT INTO `creature_text` VALUES (104415, 8, 0, 'Чувствуете? Время замирает.', 14, 0, 100, 0, 0, 58493, 107987, 0, 0, 0, 'Хрономатическая аномалия to Хрономатическая аномалия');
INSERT INTO `creature_text` VALUES (104415, 9, 0, '|TInterface\\Icons\\ability_mage_timewarp:20|tВремя идет гораздо быстрее.', 41, 0, 100, 0, 0, 0, 117026, 0, 0, 0, 'Хрономатическая аномалия to Хрономатическая аномалия');
INSERT INTO `creature_text` VALUES (104415, 108, 0, 'Точно. Идеально. Неизбежно.', 14, 0, 100, 0, 0, 58495, 107990, 0, 0, 0, 'Хрономатическая аномалия to Хрономатическая аномалия');
INSERT INTO `creature_text` VALUES (104415, 130, 0, 'Время работает против вас.', 15, 0, 100, 0, 0, 58490, 107982, 0, 0, 0, 'Хрономатическая аномалия to Player');
INSERT INTO `creature_text` VALUES (104415, 131, 0, '|TInterface\\Icons\\spell_mage_altertime:20|tВы получили |cFFFF0000|Hspell:206617|h[часовую бомбу]|h|r.', 42, 0, 100, 0, 0, 0, 107969, 0, 0, 0, 'Хрономатическая аномалия to Player');
INSERT INTO `creature_text` VALUES (104415, 136, 0, 'Время никого не ждет.', 14, 0, 100, 0, 0, 58494, 107988, 0, 0, 0, 'Хрономатическая аномалия to Хрономатическая аномалия');
INSERT INTO `creature_text` VALUES (104454, 1, 0, 'От тебя мокрое место останется!', 12, 0, 100, 0, 0, 0, 104984, 0, 0, 0, 'Стражник из племени Глубокоходов to Player');
INSERT INTO `creature_text` VALUES (104478, 0, 0, 'Заблудившийся совокотенок медленно поднимает голову и оглядывается по сторонам.', 16, 0, 100, 0, 0, 0, 110283, 0, 0, 0, 'Заблудившийся совокотенок to Player');
INSERT INTO `creature_text` VALUES (104478, 1, 0, 'Заблудившийся совокотенок замечает вас и в страхе бежит прочь.', 16, 0, 100, 0, 0, 0, 110284, 0, 0, 0, 'Заблудившийся совокотенок to Player');
INSERT INTO `creature_text` VALUES (104478, 2, 0, 'Заблудившийся совокотенок тянет носом воздух и весело щебечет, увидев знакомые места.', 16, 0, 100, 377, 0, 0, 106914, 0, 0, 0, 'Заблудившийся совокотенок to Player');
INSERT INTO `creature_text` VALUES (104484, 0, 0, 'Олокк... нас всех смыло...', 12, 0, 100, 0, 0, 0, 107396, 0, 0, 0, 'Олокк Кораблекрушитель to Player');
INSERT INTO `creature_text` VALUES (104484, 1, 0, 'Ваши корабли разбиты в щепки – а затем придет черед и ваших костей!', 12, 0, 100, 0, 0, 0, 107395, 0, 0, 0, 'Олокк Кораблекрушитель to Player');
INSERT INTO `creature_text` VALUES (104502, 0, 0, 'Я един с Бездной!', 14, 0, 100, 0, 0, 0, 107451, 0, 0, 0, 'Принц Билаал to Принц Билаал');
INSERT INTO `creature_text` VALUES (104513, 4, 0, 'И-И-И....', 12, 0, 100, 0, 0, 0, 107400, 19819, 19819, 0, 'Осквернилия to Player');
INSERT INTO `creature_text` VALUES (104519, 0, 0, 'Вы отстаете от нас на тысячи лет! И как же вы рассчитываете победить?', 12, 0, 100, 0, 0, 0, 107401, 0, 0, 0, 'Колериан to Player');
INSERT INTO `creature_text` VALUES (104519, 1, 0, 'Не... Невозможно!', 12, 0, 100, 0, 0, 0, 107403, 0, 0, 0, 'Колериан to Player');
INSERT INTO `creature_text` VALUES (104521, 0, 0, 'Как... где моя мана?!', 12, 0, 100, 0, 0, 0, 107404, 0, 0, 0, 'Селения to Хати');
INSERT INTO `creature_text` VALUES (104522, 0, 0, 'Великий магистр... уничтожит... всех вас...', 12, 0, 100, 0, 0, 0, 107407, 0, 0, 0, 'Селения to Хати');
INSERT INTO `creature_text` VALUES (104524, 4, 0, 'Силы... покидают меня...', 12, 0, 100, 0, 0, 0, 107424, 50803, 50803, 0, 'Ормагрогг to Player');
INSERT INTO `creature_text` VALUES (104528, 0, 0, 'Как вы посмели вторгнуться в мои сады?', 14, 0, 100, 0, 0, 68634, 115769, 0, 0, 0, 'AGGRO');
INSERT INTO `creature_text` VALUES (104528, 0, 1, 'Похоже, пора заняться прополкой!', 14, 0, 100, 0, 0, 68633, 115770, 0, 0, 0, 'AGGRO');
INSERT INTO `creature_text` VALUES (104528, 1, 0, 'Эй, не опалите бутоны, пока горите сами!', 14, 0, 100, 0, 0, 68642, 115754, 0, 0, 0, 'SOLAR_COLLAPSE');
INSERT INTO `creature_text` VALUES (104528, 2, 0, 'Один вредитель уничтожит другого!', 14, 0, 100, 0, 0, 68641, 115755, 0, 0, 0, 'PARASITIC_FETTER');
INSERT INTO `creature_text` VALUES (104528, 3, 0, 'Может, до вас, тугодумов, хоть так дойдет!', 14, 0, 100, 0, 0, 68640, 115756, 0, 0, 0, 'CONTROLLED_CHAOS');
INSERT INTO `creature_text` VALUES (104528, 4, 0, '|TINTERFACE\\ICONS\\Spell_Arcane_PrismaticCloak:20|t Чародей Тел\'арн begins to cast a spell |cFFF00000|Hspell:218807|h[\"Зов ночи\"]|h|r!', 41, 0, 100, 0, 0, 0, 121596, 0, 0, 0, 'CALL_OF_NIGHT');
INSERT INTO `creature_text` VALUES (104528, 5, 0, 'Ночь наступает!', 14, 0, 100, 0, 0, 68635, 115761, 0, 0, 0, 'CALL_OF_NIGHT');
INSERT INTO `creature_text` VALUES (104528, 6, 0, 'Увядайте от жары, сорняки!', 14, 0, 100, 0, 0, 68629, 115763, 0, 0, 0, 'PHASE_2');
INSERT INTO `creature_text` VALUES (104528, 6, 1, 'Солнце дарит тепло моим садам... Но вас оно сожжет!', 14, 0, 100, 0, 0, 68630, 115762, 0, 0, 0, 'PHASE_2');
INSERT INTO `creature_text` VALUES (104528, 7, 0, 'Узрите три аспекта жизни... И приготовьтесь к смерти!', 14, 0, 100, 0, 0, 68628, 115764, 0, 0, 0, 'PHASE_3');
INSERT INTO `creature_text` VALUES (104528, 7, 1, 'Вы надеетесь выстоять против трех первобытных сил, творящих саму жизнь?!', 14, 0, 100, 0, 0, 68627, 115765, 0, 0, 0, 'PHASE_3');
INSERT INTO `creature_text` VALUES (104528, 8, 0, 'Кто-нибудь... полейте цветы.', 14, 0, 100, 0, 0, 68631, 115774, 0, 0, 0, 'DEATH');
INSERT INTO `creature_text` VALUES (104528, 8, 1, 'Смерть — лишь часть великого цикла.', 14, 0, 100, 0, 0, 68624, 115771, 0, 0, 0, 'DEATH');
INSERT INTO `creature_text` VALUES (104528, 9, 0, 'Из твоего трупа выйдет отличный компост!', 14, 0, 100, 0, 0, 68625, 115767, 0, 0, 0, 'DEPRECATED? Kill Player');
INSERT INTO `creature_text` VALUES (104528, 9, 1, 'И где мне хранить столько удобрений?', 14, 0, 100, 0, 0, 68622, 115772, 0, 0, 0, 'DEPRECATED? Kill Player');
INSERT INTO `creature_text` VALUES (104528, 9, 2, 'Больше пищи червям!', 14, 0, 100, 0, 0, 68624, 115768, 0, 0, 0, 'DEPRECATED? Kill Player');
INSERT INTO `creature_text` VALUES (104529, 0, 0, 'YOUR POWER WILL BE MINE!', 12, 0, 100, 0, 0, 0, 106950, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (104535, 0, 0, 'Good to see you made it, $n. We need only a few more things before we can begin.', 12, 0, 100, 0, 0, 0, 106956, 0, 0, 0, 'Леа Каменная Лапа to Player');
INSERT INTO `creature_text` VALUES (104535, 1, 0, 'Great Ursol, wise bear of the woods. This daughter of the wilds calls out to you! Please accept these humble offerings and grace us with your presence!', 12, 0, 100, 0, 0, 0, 107322, 0, 0, 0, 'Леа Каменная Лапа to Player');
INSERT INTO `creature_text` VALUES (104535, 2, 0, 'Now that we have Ursol\'s blessing we can travel into the Emerald Dream and retrieve the claws! Come $n, let\'s not waste a single minute!', 12, 0, 100, 0, 0, 0, 107662, 0, 0, 0, 'Леа Каменная Лапа to Player');
INSERT INTO `creature_text` VALUES (104535, 3, 0, 'Calm your mind, let go and your spirit will enter into the Dream. I\'ll join you soon.', 12, 0, 100, 0, 0, 0, 106957, 0, 0, 0, 'Леа Каменная Лапа to Player');
INSERT INTO `creature_text` VALUES (104535, 4, 0, 'There is a noise from behind you!', 41, 0, 100, 1, 0, 0, 107346, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (104537, 0, 0, '|TInterface\\Icons\\inv_axe_2h_felfireraid_d_01:20|t %s подготавливает способность |cFFFF0000|Hspell:206675|h[\"Раскалывание сущности\"]|h|r.', 41, 0, 100, 0, 0, 58792, 112313, 0, 0, 0, 'Повелитель Скверны Кураз\'мал to Player');
INSERT INTO `creature_text` VALUES (104546, 0, 0, 'I will bring whatever power I have to bear against the Nexus-Prince, $c, but in my weakened state it will be mostly up to you to finish this threat.', 12, 0, 100, 0, 0, 0, 107440, 0, 0, 0, 'Азурегос to Player');
INSERT INTO `creature_text` VALUES (104546, 1, 0, 'I hope you are prepared.', 12, 0, 100, 0, 0, 0, 107441, 0, 0, 0, 'Азурегос to Player');
INSERT INTO `creature_text` VALUES (104546, 2, 0, 'Hah! So much the all-powerful void. You know, you\'re not so bad for a little $r.', 12, 0, 100, 0, 0, 0, 107380, 0, 0, 0, 'Азурегос to Player');
INSERT INTO `creature_text` VALUES (104546, 3, 0, 'You should be able to access the vault through the rift here. I can open a portal, but I\'ll have to remain here to keep it open.', 12, 0, 100, 0, 0, 0, 107381, 0, 0, 0, 'Азурегос to Player');
INSERT INTO `creature_text` VALUES (104546, 4, 0, 'The way is open. Hurry, little $r - I can\'t keep this open for long!', 12, 0, 100, 0, 0, 0, 107382, 0, 0, 0, 'Азурегос to Player');
INSERT INTO `creature_text` VALUES (104573, 0, 0, 'I would have surely been overwhelmed if you had not arrived. Thank you.', 12, 0, 100, 0, 0, 0, 107004, 0, 0, 0, 'Лиесса Страж Цветения to Player');
INSERT INTO `creature_text` VALUES (104573, 1, 0, 'Нам нужно будет поговорить с тобой в материальном мире.', 12, 0, 100, 0, 0, 0, 107005, 0, 0, 0, 'Лиесса Страж Цветения to Player');
INSERT INTO `creature_text` VALUES (104577, 0, 0, 'Да благословят нас всех древние.', 12, 0, 100, 0, 0, 0, 107061, 0, 0, 0, 'Лиесса Страж Цветения to Player');
INSERT INTO `creature_text` VALUES (104577, 1, 0, 'For centuries I have watched over G\'Hanir. Today it nearly fell to the corruption I swore to fight. It is time for another to take my place.', 12, 0, 100, 0, 0, 0, 107326, 0, 0, 0, 'Лиесса Страж Цветения to Player');
INSERT INTO `creature_text` VALUES (104577, 2, 0, 'Therefore, I hereby pass along my station to $p. A true protector of life... a true guardian of the Dream!', 12, 0, 100, 0, 0, 0, 107327, 0, 0, 0, 'Лиесса Страж Цветения to Player');
INSERT INTO `creature_text` VALUES (104615, 0, 0, 'Жалкий каркан! Не лезь, куда не надо!', 12, 0, 100, 0, 0, 57967, 107086, 0, 0, 0, 'Боец из племени Серого Камня to Player');
INSERT INTO `creature_text` VALUES (104628, 0, 0, 'Your allies have fallen! The ritual has failed!', 41, 0, 100, 0, 0, 0, 108058, 0, 0, 0, 'Лиесса Страж Цветения to Измор');
INSERT INTO `creature_text` VALUES (104630, 0, 0, 'Найди Брютага... убей наг...', 12, 0, 100, 0, 0, 0, 107146, 0, 0, 0, 'Келл to Player');
INSERT INTO `creature_text` VALUES (104636, 0, 0, 'I will destroy this corruption!', 14, 0, 100, 0, 0, 58873, 109533, 0, 0, 0, 'Кенарий to Player');
INSERT INTO `creature_text` VALUES (104636, 1, 0, '|TInterface\\Icons\\sha_spell_shadow_shadesofdarkness_nightmare:20|t%s begins to cast a spell |cFFFF0000|Hspell:212726|h[\"Силы Кошмара\"]|h|r!', 41, 0, 100, 0, 0, 58861, 111382, 0, 0, 0, 'Кенарий to Кенарий');
INSERT INTO `creature_text` VALUES (104636, 2, 0, 'Союзники! Ко мне!', 14, 0, 100, 0, 0, 58860, 109519, 0, 0, 0, 'Кенарий to Кенарий');
INSERT INTO `creature_text` VALUES (104636, 3, 0, 'Лозы жизни, увейте порченых.', 14, 0, 100, 0, 0, 58869, 109528, 0, 0, 0, 'Кенарий to 0');
INSERT INTO `creature_text` VALUES (104636, 4, 0, 'Природа восстанет против вас!', 14, 0, 100, 0, 0, 58861, 109520, 0, 0, 0, 'Кенарий to Кенарий');
INSERT INTO `creature_text` VALUES (104636, 5, 0, 'Сон должен жить!', 14, 0, 100, 0, 0, 69974, 109530, 0, 0, 0, 'Кенарий to Player');
