INSERT INTO `creature_text` VALUES (104714, 0, 0, 'That creature unworthy of you, great Ursol! I crush it in your name!', 12, 0, 100, 0, 0, 0, 107149, 0, 0, 0, 'Древний герой to Player');
INSERT INTO `creature_text` VALUES (104714, 1, 0, 'Too... strong! Stop! No more fight!', 14, 0, 100, 0, 0, 0, 107150, 0, 0, 0, 'Древний герой to 0');
INSERT INTO `creature_text` VALUES (104728, 0, 0, 'Ты должен помочь мне его найти! Я могу что-то сделать?', 12, 0, 100, 0, 0, 58045, 107175, 0, 0, 0, 'Тиранда Шелест Ветра to Player');
INSERT INTO `creature_text` VALUES (104728, 1, 0, 'Speak to me when you are ready to leave.', 12, 0, 100, 0, 0, 58070, 107622, 0, 0, 0, 'Тиранда Шелест Ветра to Player');
INSERT INTO `creature_text` VALUES (104728, 2, 0, 'There is no time to mourn. Elothir is beyond my reach now. Come, and do not delay.', 12, 0, 100, 0, 0, 0, 107928, 0, 0, 0, 'Тиранда Шелест Ветра to Player');
INSERT INTO `creature_text` VALUES (104728, 3, 0, 'Shala\'nir is just ahead. You should be ready for whatever foulness awaits us.', 12, 0, 100, 0, 0, 0, 107929, 0, 0, 0, 'Тиранда Шелест Ветра to Player');
INSERT INTO `creature_text` VALUES (104728, 4, 0, 'These hoofprints follow the path to the left. After them, quickly!', 12, 0, 100, 0, 0, 0, 108012, 0, 0, 0, 'Тиранда Шелест Ветра to Player');
INSERT INTO `creature_text` VALUES (104728, 5, 0, 'Malfurion?! Call out to me!', 12, 0, 100, 0, 0, 58065, 107553, 0, 0, 0, 'Тиранда Шелест Ветра to Player');
INSERT INTO `creature_text` VALUES (104728, 6, 0, 'Not without you!', 12, 0, 100, 0, 0, 58064, 107554, 0, 0, 0, 'Тиранда Шелест Ветра to Player');
INSERT INTO `creature_text` VALUES (104728, 7, 0, 'By Elune, your foulness will be purged from this place!', 12, 0, 100, 0, 0, 58063, 107556, 0, 0, 0, 'Тиранда Шелест Ветра to Player');
INSERT INTO `creature_text` VALUES (104732, 0, 0, 'У нас есть Кровавый камень. Его силы вполне хватит, чтобы сломить волю сестер, когда они ослабнут.', 12, 0, 100, 1, 0, 66518, 115417, 0, 0, 0, 'Кира Злобная Душа to Player');
INSERT INTO `creature_text` VALUES (104732, 1, 0, 'Это правда. Помни об этом, когда в следующий раз решишь обратиться ко мне...', 12, 0, 100, 1, 0, 66515, 107505, 0, 0, 0, 'Кира Злобная Душа to Player');
INSERT INTO `creature_text` VALUES (104732, 2, 0, 'Вы достойно поступили с другими чернокнижниками. Интересно.', 12, 0, 100, 1, 0, 66515, 109809, 0, 0, 0, 'Кира Злобная Душа to Player');
INSERT INTO `creature_text` VALUES (104739, 0, 0, 'Do not let anything disturb my meditation.', 12, 0, 100, 0, 0, 58034, 107923, 0, 0, 0, 'Тиранда Шелест Ветра to Player');
INSERT INTO `creature_text` VALUES (104739, 1, 0, 'Я вижу... волнующийся красный туман. Обнаженные клыки.', 12, 0, 100, 0, 0, 58033, 107125, 0, 0, 0, 'Тиранда Шелест Ветра to Player');
INSERT INTO `creature_text` VALUES (104739, 2, 0, 'Malfurion! He is in pain! Keep fighting, my love!', 12, 0, 100, 0, 0, 58032, 107126, 0, 0, 0, 'Тиранда Шелест Ветра to Player');
INSERT INTO `creature_text` VALUES (104739, 6, 0, 'Such malevolent laughter... Xavius, you cruel creature! I am coming for you!', 12, 0, 100, 0, 0, 58031, 107127, 0, 0, 0, 'Тиранда Шелест Ветра to Player');
INSERT INTO `creature_text` VALUES (104739, 7, 0, 'His location is still unclear, but he lives.', 12, 0, 100, 0, 0, 58030, 107123, 0, 0, 0, 'Тиранда Шелест Ветра to Player');
INSERT INTO `creature_text` VALUES (104739, 8, 0, 'We must go deeper into the nightmare. Wherever he is, peril is already upon him!', 12, 0, 100, 0, 0, 58029, 107124, 0, 0, 0, 'Тиранда Шелест Ветра to Player');
INSERT INTO `creature_text` VALUES (104744, 0, 0, 'Наши герои могут отследить передвижения врага. Я надеюсь, мы успеем спасти моих учеников...', 12, 0, 100, 1, 0, 68354, 116319, 0, 0, 0, 'Верховный старейшина Дождевая Туча to Player');
INSERT INTO `creature_text` VALUES (104745, 0, 0, 'Держать строй! Не дайте им захватить монастырь!', 12, 0, 100, 0, 0, 0, 107499, 0, 0, 0, 'Инструктор Мян to Player');
INSERT INTO `creature_text` VALUES (104754, 0, 0, 'У-ух... огонь!', 12, 0, 100, 0, 0, 0, 107461, 0, 0, 0, 'Сеющий Скверну бес to Player');
INSERT INTO `creature_text` VALUES (104754, 0, 1, 'Больше… огня. Еще, еще, ЕЩЕ!!!', 12, 0, 100, 0, 0, 0, 107465, 0, 0, 0, 'Сеющий Скверну бес to Player');
INSERT INTO `creature_text` VALUES (104754, 0, 2, 'I\'ll give you a good tan!', 12, 0, 100, 0, 0, 0, 107462, 0, 0, 0, 'Сеющий Скверну бес to Player');
INSERT INTO `creature_text` VALUES (104754, 0, 3, 'Славный огонь, такой красивый!', 12, 0, 100, 0, 0, 0, 107464, 0, 0, 0, 'Сеющий Скверну бес to Player');
INSERT INTO `creature_text` VALUES (104755, 0, 0, 'Довольно. Твое время вышло!', 12, 0, 100, 5, 0, 66727, 115841, 0, 0, 0, 'Леди Келетресс to Player');
INSERT INTO `creature_text` VALUES (104756, 0, 0, 'Легион будет жить вечно…', 12, 0, 100, 0, 0, 66665, 107455, 0, 0, 0, 'Скола Большой Меч to Player');
INSERT INTO `creature_text` VALUES (104758, 0, 0, '$c, я порву тебя в клочья!', 12, 0, 100, 0, 0, 0, 107457, 0, 0, 0, 'Страж Скверны - завоеватель to Чэнь Буйный Портер');
INSERT INTO `creature_text` VALUES (104758, 0, 1, 'I will bring down fire and rage on you!', 12, 0, 100, 0, 0, 0, 107458, 0, 0, 0, 'Страж Скверны - завоеватель to Player');
INSERT INTO `creature_text` VALUES (104758, 0, 2, '$c, ты погибнешь от моего клинка!', 12, 0, 100, 0, 0, 0, 107456, 0, 0, 0, 'Страж Скверны - завоеватель to Послушник монастыря Тянь');
INSERT INTO `creature_text` VALUES (104758, 0, 3, 'Твоим сородичам пришел конец.', 12, 0, 100, 0, 0, 0, 107460, 0, 0, 0, 'Страж Скверны - завоеватель to Player');
INSERT INTO `creature_text` VALUES (104758, 0, 4, 'Мы сожжем ваш монастырь дотла.', 12, 0, 100, 0, 0, 0, 107459, 0, 0, 0, 'Страж Скверны - завоеватель to Player');
INSERT INTO `creature_text` VALUES (104795, 3, 0, 'I will not rest until I restore the Council.', 12, 0, 100, 1, 0, 66535, 114576, 13906, 13906, 0, 'Ритцун Хмурый Огонь to Player');
INSERT INTO `creature_text` VALUES (104795, 3, 0, 'Веди нас на славную битву с Легионом.', 12, 0, 100, 1, 0, 63891, 104298, 23140, 23140, 0, 'Ритцун Хмурый Огонь to Player');
INSERT INTO `creature_text` VALUES (104795, 3, 0, 'Вот еще что, $gчернокнижник:чернокнижница;.', 12, 0, 100, 1, 0, 0, 0, 989, 989, 0, 'Ритссин Хмурый Огонь to Player');
INSERT INTO `creature_text` VALUES (104797, 0, 0, 'I will prepare for the revival of Thunderbolt. Find me when you charge Thunderfury!', 12, 0, 100, 669, 0, 67049, 111029, 0, 0, 0, 'Консул Селест to Player');
INSERT INTO `creature_text` VALUES (104799, 0, 0, 'Ysera - no!', 12, 0, 100, 0, 0, 58087, 107270, 0, 0, 0, 'Тиранда Шелест Ветра to Player');
INSERT INTO `creature_text` VALUES (104799, 1, 0, 'Come with me. Ysera won\'t stop until this whole realm is plunged into the Nightmare.', 12, 0, 100, 0, 0, 58086, 107271, 0, 0, 0, 'Тиранда Шелест Ветра to Player');
INSERT INTO `creature_text` VALUES (104799, 2, 0, 'Xavius is using Malfurion to bait me, to distract me from what must be done. We are the only ones who can stop Ysera.', 12, 0, 100, 0, 0, 58085, 107272, 0, 0, 0, 'Тиранда Шелест Ветра to Player');
INSERT INTO `creature_text` VALUES (104799, 3, 0, 'Ah, Malfurion, my beloved! For a hundred hundred years he slept beneath Moonglade.', 12, 0, 100, 0, 0, 58084, 107273, 0, 0, 0, 'Тиранда Шелест Ветра to Player');
INSERT INTO `creature_text` VALUES (104799, 4, 0, 'Whenever I was plagued with doubt, I would descend into his barrow. I watched over him as he slept.', 12, 0, 100, 0, 0, 58083, 107624, 0, 0, 0, 'Тиранда Шелест Ветра to Player');
INSERT INTO `creature_text` VALUES (104799, 5, 0, 'Even in slumber, his presence calmed me. I left my fears below the earth and emerged, ready to lead my people.', 12, 0, 100, 0, 0, 58082, 107625, 0, 0, 0, 'Тиранда Шелест Ветра to Player');
INSERT INTO `creature_text` VALUES (104799, 6, 0, 'When Malfurion returned to my side, we worked as one. As if he had never been away. Have you ever loved as I have?', 12, 0, 100, 0, 0, 58081, 107626, 0, 0, 0, 'Тиранда Шелест Ветра to Player');
INSERT INTO `creature_text` VALUES (104799, 7, 0, 'Now Xavius has me by the throat. I must abandon my beloved, and contend with the most beautiful creature I have ever known.', 12, 0, 100, 0, 0, 58080, 107627, 0, 0, 0, 'Тиранда Шелест Ветра to Player');
INSERT INTO `creature_text` VALUES (104820, 0, 0, 'Шинфель Преданная Погибели, превозмогая боль, что-то невнятно бормочет.', 16, 0, 100, 0, 0, 0, 107632, 0, 0, 0, 'Шинфель Преданная Погибели to Player');
INSERT INTO `creature_text` VALUES (104824, 0, 0, 'Эрнест Карлайл вздыхает.', 16, 0, 100, 0, 0, 0, 107648, 0, 0, 0, 'Эрнест Карлайл to Player');
INSERT INTO `creature_text` VALUES (104825, 3, 0, 'Есть вести от ночного дозора?', 12, 0, 100, 0, 0, 0, 107828, 63, 63, 91098, 'Халн Крутогор to Player');
INSERT INTO `creature_text` VALUES (104832, 0, 0, 'У тебя талант целителя. Силы возвращаются ко мне.', 12, 0, 100, 1, 0, 61645, 107358, 0, 0, 0, 'Азурегос to 1065');
INSERT INTO `creature_text` VALUES (104832, 1, 0, 'Ты хочешь проникнуть в хранилище? Зачем еще кому-то возвращаться в Нексус?', 12, 0, 100, 1, 0, 61646, 107359, 0, 0, 0, 'Азурегос to 1065');
INSERT INTO `creature_text` VALUES (104832, 2, 0, 'К сожалению, это довольно… сложно. Но если поможешь мне попасть в библиотеку, я найду способ. Нельзя допустить, чтобы Эфириум завладел артефактами.', 12, 0, 100, 1, 0, 61647, 107360, 0, 0, 0, 'Азурегос to 1065');
INSERT INTO `creature_text` VALUES (104832, 3, 0, 'Пожалуй, я приму более... удобный облик. Эти залы всегда казались мне слишком тесными.', 12, 0, 100, 1, 0, 61648, 107361, 0, 0, 0, 'Азурегос to 1065');
INSERT INTO `creature_text` VALUES (104849, 3, 0, 'Прочь, духи! И передайте Мраку, что ему нечего тут делать!', 14, 0, 100, 0, 0, 0, 107809, 109, 109, 91098, 'Эния Небесный Рог to Player');
INSERT INTO `creature_text` VALUES (104850, 4, 0, 'Я за тобой, смелей иди вперед! Прикрою тыл, коль ухнешь в переплет!', 12, 0, 100, 0, 0, 66666, 107468, 7487, 7487, 0, 'Король обезьян to Player');
INSERT INTO `creature_text` VALUES (104853, 0, 0, 'The Scepter of Tides... it\'s here. I found it.', 12, 0, 100, 0, 0, 0, 107634, 0, 0, 0, 'Говорящая с волнами Адель to Зитренайская призывательница приливов');
INSERT INTO `creature_text` VALUES (104853, 1, 0, 'С помощью кадуцея они сводят с ума обитателей этого места.', 12, 0, 100, 0, 0, 0, 107635, 0, 0, 0, 'Говорящая с волнами Адель to Зитренайская призывательница приливов');
INSERT INTO `creature_text` VALUES (104853, 2, 0, '%s falls to one knee, gravely wounded.', 16, 0, 100, 0, 0, 0, 109484, 0, 0, 0, 'Говорящая с волнами Адель to 0');
INSERT INTO `creature_text` VALUES (104853, 3, 0, '%s is |cFFFF0404|Hspell:204237|h[Gravely Wounded]|h|r and cannot fight until healed!', 41, 0, 100, 0, 0, 0, 109485, 0, 0, 0, 'Говорящая с волнами Адель to 0');
INSERT INTO `creature_text` VALUES (104855, 0, 0, 'I’ll set off for breaking the Evil Scar as soon as the recognition falls into the right hands.', 12, 0, 100, 1, 0, 66520, 114520, 0, 0, 0, 'Люлюбелла Непопамс to Player');
INSERT INTO `creature_text` VALUES (104856, 0, 0, 'The power of the Caduceus is MINE to command!', 14, 0, 100, 0, 0, 0, 108845, 0, 0, 0, 'Леди Зитрин to 0');
INSERT INTO `creature_text` VALUES (104856, 2, 0, '|TInterface\\Icons\\Spell_DeathKnight_MindFreeze:20|t%s casts |cFFFF0404|Hspell:209983|h[Control Thoughts]|h|r on Grash!', 41, 0, 100, 0, 0, 0, 108907, 0, 0, 0, 'Леди Зитрин to 0');
INSERT INTO `creature_text` VALUES (104856, 4, 0, 'The Earthen Ring will never have Azshara\'s power!', 14, 0, 100, 0, 0, 0, 108848, 0, 0, 0, 'Леди Зитрин to 0');
INSERT INTO `creature_text` VALUES (104856, 6, 0, 'You will drown in this place!', 14, 0, 100, 0, 0, 0, 108847, 0, 0, 0, 'Леди Зитрин to 0');
INSERT INTO `creature_text` VALUES (104864, 3, 0, 'Сию же секунду!', 12, 0, 100, 0, 0, 0, 0, 21301, 21301, 0, 'Курьер Быстрошпор to Player');
INSERT INTO `creature_text` VALUES (104864, 3, 0, 'Удивительно! Этот лук будет воодушевлять всякого следопыта, который его увидит!', 12, 0, 100, 1, 0, 61791, 107304, 12657, 12657, 0, 'Курьер Быстрошпор to Player');
INSERT INTO `creature_text` VALUES (104865, 0, 0, 'Преступлениях? Моя вина лишь в том, что я самый могущественный чернокнижник в Азероте!', 14, 0, 100, 603, 0, 66500, 108638, 0, 0, 0, 'Галлий Болотник to Player');
INSERT INTO `creature_text` VALUES (104865, 1, 0, 'Вперед, прислужники. Помогите мне одолеть врага!', 14, 0, 100, 603, 0, 66505, 108641, 0, 0, 0, 'Галлий Болотник to 0');
