INSERT INTO `creature_text` VALUES (105908, 0, 0, 'Нужно совсем не иметь мозгов, чтобы случайно явиться в Оплот Теней.', 12, 0, 100, 0, 0, 0, 104326, 0, 0, 0, 'Милтон Шлепс to Player');
INSERT INTO `creature_text` VALUES (105908, 3, 0, 'Ой, $p, не признал... Приношу свои извинения. Некоронованные ждут тебя.', 12, 0, 100, 396, 0, 0, 108826, 3183, 3183, 0, 'Милтон Шлепс to Player');
INSERT INTO `creature_text` VALUES (105910, 0, 0, 'Cato! I\'m glad ye could make it. The shield is close... I can feel it!', 12, 0, 100, 0, 0, 0, 108846, 0, 0, 0, 'Орик Чистосердечный to Player');
INSERT INTO `creature_text` VALUES (105910, 1, 0, 'Cato\'s the best healer around. We\'re lucky we didn\'t have to wait in a queue for his services!', 12, 0, 100, 0, 0, 0, 108850, 0, 0, 0, 'Орик Чистосердечный to Player');
INSERT INTO `creature_text` VALUES (105910, 2, 0, 'Tomb\'s up ahead. Let\'s get in there and find that shrine! We\'ll follow yer lead, $n.', 12, 0, 100, 0, 0, 0, 114138, 0, 0, 0, 'Орик Чистосердечный to Player');
INSERT INTO `creature_text` VALUES (105911, 0, 0, 'Lookee! Seems Yrgrim was the champion, and he came here after Tyr\'s fall.', 12, 0, 100, 0, 0, 0, 108921, 0, 0, 0, 'Орик Чистосердечный to Player');
INSERT INTO `creature_text` VALUES (105911, 1, 0, 'A glowing door... Wee bit obvious, don\'t ye think? What have we here?', 12, 0, 100, 0, 0, 0, 109030, 0, 0, 0, 'Орик Чистосердечный to Phantom Weaver of the Wind');
INSERT INTO `creature_text` VALUES (105911, 2, 0, 'This tablet says ye need to withstand the door\'s \"gaze\". I\'m bettin\' that\'s not gonna be pleasant. Care to give it a try, $n?', 12, 0, 100, 0, 0, 0, 109031, 0, 0, 0, 'Орик Чистосердечный to Phantom Weaver of the Wind');
INSERT INTO `creature_text` VALUES (105911, 3, 0, 'Amazing! Good to see the shield in your hands. Me \'n Cato will head to Dalaran and report to Tyrosus. If ye need a ride, my hippogryph\'s outside.', 12, 0, 100, 0, 0, 0, 109116, 0, 0, 0, 'Орик Чистосердечный to Yrgrim the Truthseeker');
INSERT INTO `creature_text` VALUES (105912, 0, 0, 'Use protection magic! That damage is too much for me to heal!', 12, 0, 100, 0, 0, 0, 114464, 0, 0, 0, 'Като to Player');
INSERT INTO `creature_text` VALUES (105917, 0, 0, 'Это устройство питает энергия Бездны, которая высвободилась, когда Малигос направил силовые линии Азерота в Круговерть Пустоты.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (105917, 0, 1, 'Это похоже на порталы эфириалов в Запределье.', 12, 0, 100, 1, 0, 0, 107049, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (105917, 0, 2, 'Это оружие осквернено энергией Круговери Пустоты.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (105918, 0, 0, 'Я всегда подозревал, что с тобой что-то не так.', 12, 0, 100, 0, 0, 66067, 108526, 0, 0, 0, 'Леспин Серебристый Лист to Player');
INSERT INTO `creature_text` VALUES (105918, 4, 0, 'Мои шедевры будут сиять вечно…', 12, 0, 100, 0, 0, 66066, 108909, 44440, 44440, 0, 'Леспин Серебристый Лист to Player');
INSERT INTO `creature_text` VALUES (105922, 0, 0, 'Вместе мы уничтожим пылающий Легион!', 12, 0, 100, 1, 0, 69555, 120507, 0, 0, 0, 'Джубека Тенелом to Player');
INSERT INTO `creature_text` VALUES (105928, 0, 0, 'Можешь рассчитывать на меня. Я готова сражаться с Легионом вместе с тобой.', 12, 0, 100, 1, 0, 0, 114521, 0, 0, 0, '');
