INSERT INTO `creature_text` VALUES (105979, 3, 0, 'Сейчас все прошло как по маслу, но в будущем на это потребуется больше времени.', 12, 0, 100, 1, 0, 0, 120226, 368989, 368989, 0, 'Лоника Безмолвный Клинок to Player');
INSERT INTO `creature_text` VALUES (105986, 3, 0, 'Уделишь мне минутку, $n? У меня есть кое-что интересное.', 12, 0, 100, 0, 0, 0, 122734, 2054490, 2054490, 0, 'Келси Стализвон to Player');
INSERT INTO `creature_text` VALUES (105998, 0, 0, 'Прекрасный выбор. Я позабочусь о том, чтобы все было сделано как следует. Всегда приятно работать с профи высшего класса вроде тебя, $n.', 12, 0, 100, 0, 0, 0, 120263, 0, 0, 0, 'Уинстон Вульф to Player');
