INSERT INTO `creature_text` VALUES (106016, 3, 0, 'Мы идем в самое логово зверя. Да хранит нас Свет!', 12, 0, 100, 0, 0, 62429, 109021, 11430, 11430, 0, 'Воздаятель Борос to Player');
