INSERT INTO `creature_text` VALUES (106017, 3, 0, 'There are others trapped inside. Please, rescue them if you can. Thank you again, $n!', 12, 0, 100, 1, 0, 62462, 108947, 14281, 14281, 0, 'Алора to Player');
INSERT INTO `creature_text` VALUES (106018, 3, 0, '|TInterface\\Icons\\INV_Misc_Bell_01:20|tКто-то тронул |cFFFF0000рыночные товары|r – сработала магическая сигнализация!', 41, 0, 100, 0, 0, 0, 109623, 260629, 260629, 0, 'Рыночные товары to Player');
