INSERT INTO `creature_text` VALUES (106134, 0, 0, '$n! О, Свет! У тебя получилось! Это место насквозь пропитано злом!', 12, 0, 100, 0, 0, 0, 109077, 0, 0, 0, 'Воздаятель Борос to Player');
INSERT INTO `creature_text` VALUES (106134, 1, 0, 'Джейс отправился вперед на разведку, но потом на нас напали демоны и установили преграду. Джейс оказался по другую сторону. Должно быть, он все еще где-то там.', 12, 0, 100, 0, 0, 0, 109112, 0, 0, 0, 'Воздаятель Борос to 0');
