INSERT INTO `creature_text` VALUES (106134, 3, 0, 'Идем. Джейс направлялся сюда, когда нас разделило.', 12, 0, 100, 0, 0, 0, 109091, 0, 0, 0, 'Воздаятель Борос to 0');
INSERT INTO `creature_text` VALUES (106134, 4, 0, 'Мы на твоей стороне, охотник на демонов!', 14, 0, 100, 0, 0, 0, 109093, 0, 0, 0, 'Воздаятель Борос to Player');
