INSERT INTO `creature_text` VALUES (106140, 0, 0, 'Alliance Outpost at Moonbrook! We\'re under attack! Moonbrook\'s been overrun!', 14, 0, 100, 0, 0, 0, 111394, 0, 0, 0, 'Капитан Алперт');
INSERT INTO `creature_text` VALUES (106153, 0, 0, 'Да будет так. Идемте, мои израненные друзья. Посмотрим, хватит ли у меня сил довести вас до безопасного места.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Tarvim Daywalker to Player');
INSERT INTO `creature_text` VALUES (106172, 0, 0, 'Who DARES challenge the Truthseeker?! I will freeze you where you stand!', 14, 0, 100, 0, 0, 0, 109585, 0, 0, 0, 'Runeshaper Griselda to Player');
INSERT INTO `creature_text` VALUES (106172, 1, 0, 'Protected in ice!', 14, 0, 100, 0, 0, 0, 109586, 0, 0, 0, 'Runeshaper Griselda to 0');
INSERT INTO `creature_text` VALUES (106172, 2, 0, 'Freeze!', 14, 0, 100, 0, 0, 0, 109587, 0, 0, 0, 'Runeshaper Griselda to Player');
INSERT INTO `creature_text` VALUES (106172, 3, 0, 'I have... failed...', 14, 0, 100, 0, 0, 0, 109588, 0, 0, 0, 'Runeshaper Griselda to Орик Чистосердечный');
INSERT INTO `creature_text` VALUES (106173, 0, 0, 'Я больше не желаю ничего слышать о твоих махинациях! Ты ставишь под угрозу все труды нашего отца!', 12, 0, 100, 274, 0, 67502, 109088, 0, 0, 0, 'Анарис Луноватая to Player');
INSERT INTO `creature_text` VALUES (106173, 1, 0, 'Она хочет и дальше навязывать мне свои идеи? Что ж, идем.', 12, 0, 100, 0, 0, 67236, 116442, 0, 0, 0, 'Анари Лунарх to Player');
INSERT INTO `creature_text` VALUES (106191, 0, 0, 'Пф! Это НАША гора! Никакие дикари-дрогбары нам не страшны. Пусть приходят. С нами сила всех духов этой горы. А у них – только палки, камни и слепая ярость.', 12, 0, 100, 0, 0, 0, 109107, 0, 0, 0, 'Old Nefu to Player');
INSERT INTO `creature_text` VALUES (106195, 1, 0, 'Жалкий каркан! Не лезь, куда не надо!', 12, 0, 100, 0, 0, 57967, 107086, 0, 0, 0, 'Берсерк из племени Камня Силы to Player');
INSERT INTO `creature_text` VALUES (106195, 1, 0, 'Мы захватим гору!', 12, 0, 100, 0, 0, 54861, 96413, 0, 0, 0, 'Берсерк из племени Камня Силы to Player');
INSERT INTO `creature_text` VALUES (106202, 0, 0, 'I grow weary. With a worthy successor, my long vigil is at an end. Fight for truth and justice, champion. In Tyr\'s name...', 12, 0, 100, 0, 0, 0, 109103, 0, 0, 0, 'Yrgrim the Truthseeker to Player');
INSERT INTO `creature_text` VALUES (106204, 0, 0, 'Встретимся у Нордрассила, Верховный друид.', 12, 0, 100, 1, 0, 66771, 111166, 0, 0, 0, 'Верховный друид Хамуул Рунический Тотем to Player');
INSERT INTO `creature_text` VALUES (106216, 3, 0, 'Пусть эти бесы хорошо послужат тебе.', 12, 0, 100, 1, 0, 0, 122347, 37060, 37060, 0, 'Мать бесов Дьяла to Player');
INSERT INTO `creature_text` VALUES (106228, 0, 0, 'Мы в твоем распоряжении.', 12, 0, 100, 1, 0, 69592, 120509, 0, 0, 0, 'Главная чернокнижница Алитесса to Player');
INSERT INTO `creature_text` VALUES (106239, 0, 0, 'Хватит с меня твоих интриг! Ты ставишь под угрозу все, что создал отец!', 12, 0, 100, 274, 0, 67503, 116440, 0, 0, 0, 'Анари Лунарх to Player');
INSERT INTO `creature_text` VALUES (106244, 0, 0, 'Welcome, champion, to Thunder Totem. In better days we would have warriors from the Bloodtotem tribe or eagle riders from the Skyhorn tribe. Today, we are so few we could barely keep a kobold out.', 12, 0, 100, 0, 0, 0, 99596, 0, 0, 0, 'Воитель Оро to Player');
INSERT INTO `creature_text` VALUES (106244, 1, 0, 'This platform will take you into the sacred Hall of Chieftains where Mayla Highmountain awaits you.', 12, 0, 100, 0, 0, 0, 99623, 0, 0, 0, 'Воитель Оро to Player');
INSERT INTO `creature_text` VALUES (106245, 0, 0, 'Hold it right there!', 14, 0, 100, 0, 0, 0, 109383, 0, 0, 0, 'Инна Расхитительница Гробниц to 1082');
INSERT INTO `creature_text` VALUES (106245, 1, 0, 'You are unworthy of this place. Leave at once!', 14, 0, 100, 0, 0, 0, 109384, 0, 0, 0, 'Инна Расхитительница Гробниц to 1082');
INSERT INTO `creature_text` VALUES (106250, 0, 0, 'Нельзя игнорировать предостережение Малорна. Великий отец в опасности. Мы должны помочь ему… или потеряем все.', 12, 0, 100, 0, 0, 66766, 117112, 0, 0, 0, 'Хранитель Ремул to Player');
INSERT INTO `creature_text` VALUES (106250, 1, 0, 'Возложи руки на Идола, и мы начнем ритуал.', 12, 0, 100, 0, 0, 66770, 109581, 0, 0, 0, 'Хранитель Ремул to Player');
INSERT INTO `creature_text` VALUES (106253, 0, 0, 'Taz\'dingo! T\'ank ya for gettin\' me outta dere!', 12, 0, 100, 0, 0, 0, 109200, 0, 0, 0, 'Бо\'джа to Player');
INSERT INTO `creature_text` VALUES (106253, 1, 0, '%s falls to one knee, gravely wounded.', 16, 0, 100, 0, 0, 0, 109484, 0, 0, 0, 'Бо\'джа to 0');
INSERT INTO `creature_text` VALUES (106253, 2, 0, '%s is |cFFFF0404|Hspell:204237|h[Gravely Wounded]|h|r and cannot fight until healed!', 41, 0, 100, 0, 0, 0, 109485, 0, 0, 0, 'Бо\'джа to 0');
INSERT INTO `creature_text` VALUES (106253, 10, 0, '%s опускается на колено, ослабев от серьезного ранения.', 16, 0, 100, 0, 0, 0, 104365, 0, 0, 0, 'Джейс Темный Ткач to 0');
INSERT INTO `creature_text` VALUES (106253, 10, 1, '%s не может сражаться из-за |cFFFF0404|Hspell:204237|h[серьезного ранения]|h|r и нуждается в исцелении!', 41, 0, 100, 0, 0, 0, 105953, 0, 0, 0, 'Джейс Темный Ткач to 0');
INSERT INTO `creature_text` VALUES (106256, 0, 0, 'Свобода... наконец-то. Как мне отсюда выбраться?', 12, 0, 100, 0, 0, 0, 122228, 0, 0, 0, 'Плененный ночнорожденный to Опутанная паутиной жертва');
INSERT INTO `creature_text` VALUES (106256, 3, 0, 'I ... think I can get out of here. Thanks.', 12, 0, 100, 0, 0, 0, 122227, 58, 58, 0, 'Плененный ночнорожденный to Опутанная паутиной жертва');
INSERT INTO `creature_text` VALUES (106256, 3, 0, 'Прошу, убей этих монстров всех до одного...', 12, 0, 100, 0, 0, 0, 122229, 97, 97, 0, 'Плененный ночнорожденный to Опутанная паутиной жертва');
INSERT INTO `creature_text` VALUES (106261, 0, 0, 'Уходи прочь!', 14, 0, 100, 0, 0, 67228, 116446, 0, 0, 0, 'Анари Лунарх to Player');
INSERT INTO `creature_text` VALUES (106261, 3, 0, 'Пф... Мое отсутствие заметят! Меня будут искать!', 12, 0, 100, 0, 0, 67232, 116441, 33012, 33012, 0, 'Анари Лунарх to Player');
INSERT INTO `creature_text` VALUES (106261, 4, 0, 'Ты помогаешь Ли\'лет уничтожить нашу семью! Вон отсюда!', 14, 0, 100, 397, 0, 67229, 116445, 2321, 2321, 0, 'Анари Лунарх to Player');
INSERT INTO `creature_text` VALUES (106275, 3, 0, 'Лунных стражей больше нет! Убить их всех!', 14, 0, 100, 0, 0, 0, 109256, 48027, 48027, 0, 'Командир Домитилль');
INSERT INTO `creature_text` VALUES (106275, 3, 0, 'Найти их! Обыскать каждый уголок!', 14, 0, 100, 0, 0, 0, 109254, 301249, 602424, 0, 'Командир Домитилль');
INSERT INTO `creature_text` VALUES (106299, 0, 0, 'Встретимся на месте. Поспешим, пока еще не поздно!', 12, 0, 100, 0, 0, 82139, 128418, 0, 0, 0, 'Тизали Ворона to Player');
INSERT INTO `creature_text` VALUES (106299, 1, 0, 'Ты не пожалеешь, верховный друид! Я готова!', 12, 0, 100, 66, 0, 80828, 126831, 0, 0, 0, 'Тизали Ворона to Player');
INSERT INTO `creature_text` VALUES (106318, 0, 0, 'У тебя хватает глупости надеяться противостоять мне, эредарской колдунье Пылающего Легиона?', 14, 0, 100, 0, 0, 0, 109375, 0, 0, 0, 'Леди Калиндрия to Воздаятель Борос');
INSERT INTO `creature_text` VALUES (106318, 1, 0, 'Так, мне все это надоело. Да поглотит тебя Тьма!', 14, 0, 100, 0, 0, 0, 109369, 0, 0, 0, 'Леди Калиндрия to 0');
INSERT INTO `creature_text` VALUES (106318, 2, 0, 'Вот мы и остались с тобой один на один! И твой Свет тебя не спасет!', 12, 0, 100, 0, 0, 0, 109370, 0, 0, 0, 'Леди Калиндрия to 0');
INSERT INTO `creature_text` VALUES (106318, 3, 0, 'No! My staff is absorbing the Light! Damn you, priest, and your accursed magic!', 14, 0, 100, 0, 0, 0, 109377, 0, 0, 0, 'Леди Калиндрия to 0');
INSERT INTO `creature_text` VALUES (106318, 4, 0, 'Мой посох будто чувствует Свет! Не-е-ет!', 14, 0, 100, 0, 0, 0, 109378, 0, 0, 0, 'Леди Калиндрия to 0');
INSERT INTO `creature_text` VALUES (106327, 0, 0, 'I can\'t hold them off!', 14, 0, 100, 0, 0, 0, 111303, 0, 0, 0, 'Фермер Сальден to 897');
INSERT INTO `creature_text` VALUES (106337, 0, 0, 'My brother spoke of a dragon called Lanigosa at Wymrest Temple. Start with her. I will prepare a strike force for the tomb.', 12, 0, 100, 0, 0, 0, 111582, 0, 0, 0, 'Травар to Player');
INSERT INTO `creature_text` VALUES (106337, 1, 0, 'I will prepare our forces for the journey to Tirisfal. You can make your way there through the portal to Dalaran Crater.', 12, 0, 100, 0, 0, 0, 109411, 0, 0, 0, 'Травар to Player');
INSERT INTO `creature_text` VALUES (106337, 2, 0, 'Найди наших уцелевших братьев и собери их у гробницы.', 12, 0, 100, 0, 0, 0, 109419, 0, 0, 0, 'Травар to Player');
INSERT INTO `creature_text` VALUES (106340, 0, 0, 'Это озеро станет прекрасным...', 12, 0, 100, 0, 0, 0, 109389, 0, 0, 0, 'Мелисандра Венитокс to Player');
INSERT INTO `creature_text` VALUES (106347, 0, 0, 'No more challengers! Disturb these kings no more!', 12, 0, 100, 0, 0, 0, 114409, 0, 0, 0, 'Призрачный защитник to Player');
INSERT INTO `creature_text` VALUES (106356, 0, 0, 'Марлан должен взглянуть на твой дар и оценить его – тогда и поговорим. Прощай.', 12, 0, 100, 0, 0, 0, 111126, 0, 0, 0, 'Чародейка Небесной академии to Player');
INSERT INTO `creature_text` VALUES (106356, 1, 0, 'Abra Cadabra!', 12, 0, 100, 0, 0, 0, 109629, 0, 0, 0, 'Empyrean Conjuror to Empyrean Astrologer');
INSERT INTO `creature_text` VALUES (106356, 1, 1, 'Шпионов и предателей ждет смерть!', 12, 0, 100, 0, 0, 0, 115615, 0, 0, 0, 'Чародей Небесной академии to Player');
INSERT INTO `creature_text` VALUES (106356, 1, 2, 'Вас всех бросят на произвол судьбы!', 12, 0, 100, 0, 0, 0, 115613, 0, 0, 0, 'Чародейка Небесной академии to Player');
INSERT INTO `creature_text` VALUES (106356, 1, 3, 'Ваше время вышло!', 12, 0, 100, 0, 0, 0, 115614, 0, 0, 0, 'Чародейка Небесной академии to Player');
INSERT INTO `creature_text` VALUES (106358, 1, 0, 'Так приказала ведьма-мать!', 12, 0, 100, 0, 0, 61235, 121824, 0, 0, 0, 'Проклинательница из племени Скального Когтя to Player');
INSERT INTO `creature_text` VALUES (106358, 1, 0, 'Умри, червь!', 12, 0, 100, 0, 0, 61240, 121830, 0, 0, 0, 'Проклинательница из племени Скального Когтя to Player');
INSERT INTO `creature_text` VALUES (106358, 2, 0, 'Ты не уйдешь... от проклятия!', 12, 0, 100, 0, 0, 61239, 121828, 0, 0, 0, 'Проклинательница из племени Скального Когтя to Player');
INSERT INTO `creature_text` VALUES (106358, 3, 0, 'Содрать плоть! Раскидать кости!', 12, 0, 100, 0, 0, 61238, 121827, 2643, 2643, 0, 'Проклинательница из племени Скального Когтя to Player');
INSERT INTO `creature_text` VALUES (106369, 1, 0, 'Теперь это наш край!', 12, 0, 100, 0, 0, 61232, 121821, 0, 0, 0, 'Охотница из племени Скального Когтя to Player');
INSERT INTO `creature_text` VALUES (106370, 0, 0, '%s falls to one knee, gravely wounded.', 16, 0, 100, 0, 0, 0, 109484, 0, 0, 0, 'Дювалл to 0');
