INSERT INTO `creature_text` VALUES (106375, 1, 0, 'А вот и наш обед пожаловал!', 12, 0, 100, 0, 0, 55203, 96654, 0, 0, 0, 'Плут из армии Оскверненной Души to Player');
INSERT INTO `creature_text` VALUES (106375, 1, 0, 'Поджарю с корочкой!', 12, 0, 100, 0, 0, 55210, 102137, 0, 0, 0, 'Плут из армии Оскверненной Души to Player');
INSERT INTO `creature_text` VALUES (106375, 1, 0, 'Сейчас я поджарю твои ноги!', 12, 0, 100, 0, 0, 55209, 96660, 0, 0, 0, 'Плут из армии Оскверненной Души to Player');
INSERT INTO `creature_text` VALUES (106375, 1, 0, 'Хватайте обед!', 12, 0, 100, 0, 0, 55202, 96653, 0, 0, 0, 'Плут из армии Оскверненной Души to Player');
INSERT INTO `creature_text` VALUES (106375, 1, 0, 'Эй! Что ты здесь делаешь?', 12, 0, 100, 0, 0, 55206, 96657, 0, 0, 0, 'Плут из армии Оскверненной Души to Player');
