INSERT INTO `creature_text` VALUES (106384, 2, 0, 'Проклинаю... тебя!', 12, 0, 100, 0, 0, 61241, 121831, 0, 0, 0, 'Ведьма-мать из племени Скального Когтя to Player');
INSERT INTO `creature_text` VALUES (106418, 0, 0, 'Пф... Мое отсутствие заметят! Меня будут искать!', 12, 0, 100, 0, 0, 67237, 116441, 0, 0, 0, 'Анари Лунарх to Player');
INSERT INTO `creature_text` VALUES (106429, 0, 0, 'Ah, there you are. We\'ve secured the area and found the bodies of our brethren.', 12, 0, 100, 0, 0, 0, 109479, 0, 0, 0, 'Травар to Player');
INSERT INTO `creature_text` VALUES (106429, 1, 0, 'The hammer is not far from the entrance but we will need to be quick. Even with the Twilight\'s Hammer gone I can sense evil coming from the tomb.', 12, 0, 100, 0, 0, 0, 109480, 0, 0, 0, 'Травар to Player');
INSERT INTO `creature_text` VALUES (106429, 2, 0, 'We will take a small team to lead the way. Aid us as best you can. Let me know when you are ready.', 12, 0, 100, 0, 0, 0, 109481, 0, 0, 0, 'Травар to Player');
INSERT INTO `creature_text` VALUES (106429, 3, 0, 'We will have to swim a little, but it won\'t take long.', 12, 0, 100, 0, 0, 0, 109570, 0, 0, 0, 'Травар to Player');
INSERT INTO `creature_text` VALUES (106429, 4, 0, 'Behold the Tomb of Tyr, built by the mighty... wait! What foul monsters are these?!', 12, 0, 100, 0, 0, 0, 109571, 0, 0, 0, 'Травар to Player');
INSERT INTO `creature_text` VALUES (106429, 5, 0, 'This cannot stand! Destroy the abominations!', 12, 0, 100, 0, 0, 0, 109572, 0, 0, 0, 'Травар to Player');
INSERT INTO `creature_text` VALUES (106429, 6, 0, 'It pains me to see this destruction, I have never seen beyond these doors until now. The Silver Hand is in there, let us go.', 12, 0, 100, 0, 0, 0, 109575, 0, 0, 0, 'Травар to Flesh Spawn');
INSERT INTO `creature_text` VALUES (106429, 7, 0, 'Look above you! Our sacred artifact is there, just beyond reach! Before we begin let us clear the room. Let me know when you are ready.', 12, 0, 100, 0, 0, 0, 109839, 0, 0, 0, 'Травар to Player');
INSERT INTO `creature_text` VALUES (106429, 8, 0, 'Watch yourselves, men! Let\'s cleanse this chamber!', 12, 0, 100, 0, 0, 0, 109893, 0, 0, 0, 'Травар to Player');
INSERT INTO `creature_text` VALUES (106429, 9, 0, '%s falls to one knee, gravely wounded.', 16, 0, 100, 0, 0, 0, 109484, 0, 0, 0, 'Травар to 0');
INSERT INTO `creature_text` VALUES (106429, 10, 0, '%s is |cFFFF0404|Hspell:204237|h[Gravely Wounded]|h|r and cannot fight until healed!', 41, 0, 100, 0, 0, 0, 109485, 0, 0, 0, 'Травар to 0');
INSERT INTO `creature_text` VALUES (106429, 17, 0, 'Well done everyone, lets... wait what is that?', 12, 0, 100, 0, 0, 0, 109897, 0, 0, 0, 'Травар to Flesh Spawn');
INSERT INTO `creature_text` VALUES (106429, 18, 0, 'At last we can begin. Now we will need to move quickly once the ritual is complete, it will close this tomb permanently once the hammer is in our possession.', 12, 0, 100, 0, 0, 0, 109903, 0, 0, 0, 'Травар to Г\'норц Безумный');
INSERT INTO `creature_text` VALUES (106429, 19, 0, 'Something is wrong. One of the sparks isn\'t working! Blast!', 12, 0, 100, 0, 0, 0, 109904, 0, 0, 0, 'Травар to Г\'норц Безумный');
INSERT INTO `creature_text` VALUES (106429, 20, 0, 'It must have been taken, perhaps by one of the crazed monsters. It can only be below and we need it. Whenever you are ready, champion.', 12, 0, 100, 0, 0, 0, 109905, 0, 0, 0, 'Травар to 106624');
INSERT INTO `creature_text` VALUES (106435, 3, 0, 'Эти вурдалаки готовы служить тебе, $gВладыка смерти:Владычица смерти;.', 12, 0, 100, 1, 0, 0, 122280, 21267, 21267, 0, 'Призыватель Тьмы Марох to Player');
INSERT INTO `creature_text` VALUES (106447, 3, 0, 'Обучение оруженосцев обычно занимает больше времени, но в этот раз я ускорю процесс.', 12, 0, 100, 1, 0, 0, 122289, 37961, 37961, 0, 'Командир Ансела to Player');
INSERT INTO `creature_text` VALUES (106457, 0, 0, 'Пусть стихии послужат тебе хорошо.', 12, 0, 100, 1, 0, 0, 122293, 0, 0, 0, 'Призыватель Морн to Player');
INSERT INTO `creature_text` VALUES (106459, 3, 0, 'Эти валарьяры-претенденты очень помогут нам в борьбе с Пылающим Легионом.', 12, 0, 100, 1, 0, 0, 122345, 21668, 21668, 0, 'Капитан Хьялмар Стальстрем to Player');
INSERT INTO `creature_text` VALUES (106468, 0, 0, 'Youve done well so far, but we must ensure you blend in at the party. Ill enhance your disguise, infusing it with latent magic to make you appear as one of the nobles.', 12, 0, 100, 396, 0, 0, 108585, 0, 0, 0, 'Lyleth Lunastre to 0');
INSERT INTO `creature_text` VALUES (106482, 0, 0, 'I feel the influence of Cenarius here. This portal leads to Moonglade, but dark magic blocks the path. We need to remove the barrier and open the portal.', 14, 0, 100, 0, 0, 61373, 112987, 0, 0, 0, 'Малфурион Ярость Бури to Player');
INSERT INTO `creature_text` VALUES (106482, 1, 0, 'Все эти дороги излучают осязаемое зло. Выбирайте путь, и помните – я с вами.', 14, 0, 100, 0, 0, 61374, 112988, 0, 0, 0, 'Малфурион Ярость Бури to Player');
INSERT INTO `creature_text` VALUES (106482, 2, 0, '|TInterface\\Icons\\spell_nature_protectionformnature:20|t%s начинает применять заклинание |cFF00FFFF|Hspell:212681|h[\"Очищающая земля\"]|h|r!', 41, 0, 100, 0, 0, 58869, 109528, 0, 0, 0, 'Малфурион Ярость Бури to Малфурион Ярость Бури');
INSERT INTO `creature_text` VALUES (106482, 3, 0, 'Готово! Кенарий пострадал, но он жив. Теперь я постараюсь очистить его от порчи.', 12, 0, 100, 0, 0, 58899, 109563, 0, 0, 0, 'Малфурион Ярость Бури to Player');
INSERT INTO `creature_text` VALUES (106482, 4, 0, 'Ксавий затаился в Провале Альн, в самом сердце Кошмара. Я бы помог вам, герои... но не могу туда пойти.', 12, 0, 100, 0, 0, 58900, 109564, 0, 0, 0, 'Малфурион Ярость Бури to Player');
INSERT INTO `creature_text` VALUES (106482, 5, 0, 'Моя связь со Сном дает мне силу... но она же делает меня уязвимым к тьме, затаившейся в Провале. Я заглядывал туда и знаю, что если я шагну внутрь, порча поглотит меня... как поглотила Кенария.', 12, 0, 100, 0, 0, 69640, 109565, 0, 0, 0, 'Малфурион Ярость Бури to Player');
INSERT INTO `creature_text` VALUES (106482, 6, 0, 'Ступайте же. Уничтожьте Владыку Кошмара. Только тогда Сон вернется в свое нормальное состояние... и Азерот будет спасен.', 12, 0, 100, 0, 0, 69639, 109566, 0, 0, 0, 'Малфурион Ярость Бури to Player');
INSERT INTO `creature_text` VALUES (106482, 7, 0, 'Надо положить конец Кошмару. Я могу перенести нас в самое сердце этого жуткого места.', 12, 0, 100, 0, 0, 61371, 112985, 0, 0, 0, 'Малфурион Ярость Бури to Player');
INSERT INTO `creature_text` VALUES (106495, 0, 0, 'Стручки сумеречной лилии? Так-так...', 12, 0, 100, 0, 0, 67258, 109660, 0, 0, 0, 'Вантир to Player');
INSERT INTO `creature_text` VALUES (106495, 1, 0, 'Прошу прощения. Я должен заняться этим немедленно.', 12, 0, 100, 0, 0, 67259, 109662, 0, 0, 0, 'Вантир to Player');
INSERT INTO `creature_text` VALUES (106514, 0, 0, 'Марлан должен взглянуть на твой дар и оценить его – тогда и поговорим. Прощай.', 12, 0, 100, 0, 0, 0, 111126, 0, 0, 0, 'Послушница Небесной академии to Player');
INSERT INTO `creature_text` VALUES (106514, 1, 0, 'Шпионов и предателей ждет смерть!', 12, 0, 100, 0, 0, 0, 115615, 0, 0, 0, 'Послушница Небесной академии to Player');
INSERT INTO `creature_text` VALUES (106514, 1, 1, 'Вас всех бросят на произвол судьбы!', 12, 0, 100, 0, 0, 0, 115613, 0, 0, 0, 'Послушница Небесной академии to Player');
INSERT INTO `creature_text` VALUES (106514, 1, 2, 'Ваше время вышло!', 12, 0, 100, 0, 0, 0, 115614, 0, 0, 0, 'Послушница Небесной академии to Эзара Верринда');
INSERT INTO `creature_text` VALUES (106514, 1, 3, 'Надо было присоединяться к нам, пока не поздно!', 12, 0, 100, 0, 0, 0, 115611, 0, 0, 0, 'Послушница Небесной академии to Эзара Верринда');
INSERT INTO `creature_text` VALUES (106516, 0, 0, 'Марлан должен взглянуть на твой дар и оценить его – тогда и поговорим. Прощай.', 12, 0, 100, 0, 0, 0, 111126, 0, 0, 0, 'Послушница Небесной академии to Player');
INSERT INTO `creature_text` VALUES (106516, 1, 0, 'Шпионов и предателей ждет смерть!', 12, 0, 100, 0, 0, 67683, 115615, 0, 0, 0, 'Астролог Небесной академии to Player');
INSERT INTO `creature_text` VALUES (106516, 1, 1, 'Ваше время вышло!', 12, 0, 100, 0, 0, 0, 115614, 0, 0, 0, 'Астролог Небесной академии to 78116');
INSERT INTO `creature_text` VALUES (106516, 2, 0, 'You are improving. Keep practicing.', 12, 0, 100, 0, 0, 0, 111396, 0, 0, 0, 'Empyrean Astrologer to 0');
INSERT INTO `creature_text` VALUES (106522, 0, 0, 'Вернем душу Иллидана в тело... Владыка Легиона не должен его заполучить.', 14, 0, 100, 0, 0, 68560, 119750, 0, 0, 0, 'Верховный маг Кадгар to Гул\'дан');
INSERT INTO `creature_text` VALUES (106522, 1, 0, 'Мы его остановим!', 14, 0, 100, 0, 0, 58946, 109610, 0, 0, 0, 'Верховный маг Кадгар to Гул\'дан');
INSERT INTO `creature_text` VALUES (106522, 2, 0, 'Не сдавайтесь! Гул\'дан вот-вот дрогнет! Мы победим!', 14, 0, 100, 0, 0, 58943, 109607, 0, 0, 0, 'Верховный маг Кадгар to Гул\'дан');
INSERT INTO `creature_text` VALUES (106522, 3, 0, 'Легион не получит наш мир!', 14, 0, 100, 0, 0, 58947, 109611, 0, 0, 0, 'Верховный маг Кадгар to 0');
INSERT INTO `creature_text` VALUES (106522, 5, 0, 'Бейтесь за Азерот! За все, что вам дорого!', 14, 0, 100, 0, 0, 58945, 109609, 0, 0, 0, 'Верховный маг Кадгар to Верховный маг Кадгар');
INSERT INTO `creature_text` VALUES (106523, 3, 0, 'Хорошо. До встречи, $n.', 12, 0, 100, 0, 0, 66479, 111114, 8247, 8247, 0, 'Эзара Верринда to Player');
INSERT INTO `creature_text` VALUES (106530, 3, 0, 'Да уж, неудачно вышло. Ты как раз вовремя!', 12, 0, 100, 0, 0, 66480, 110389, 6189, 6189, 0, 'Равандвир to Player');
INSERT INTO `creature_text` VALUES (106545, 0, 0, '|TInterface\\Icons\\inv_misc_trinket6oih_orb4:20|t |cFFFF0000|Hspell:211152|h[Усиленное Око Гул\'дана]|h|r смотрит на вас!', 42, 0, 100, 0, 0, 0, 119850, 0, 0, 0, 'Усиленное око Гул\'дана to Player');
INSERT INTO `creature_text` VALUES (106547, 1, 0, 'Hey! Tha...  thash MY drink! <hic!>', 14, 0, 100, 0, 0, 0, 109631, 0, 0, 0, 'Drunken Mage to Player');
INSERT INTO `creature_text` VALUES (106568, 1, 0, 'За королеву!', 12, 0, 100, 0, 0, 0, 119770, 0, 0, 0, 'Непокорная королевская стражница to Player');
INSERT INTO `creature_text` VALUES (106568, 1, 0, 'Смерть Сковальду!', 12, 0, 100, 0, 0, 0, 119771, 0, 0, 0, 'Непокорная королевская стражница to Player');
INSERT INTO `creature_text` VALUES (106568, 1, 0, 'Умрите, слуги ложного короля!', 12, 0, 100, 0, 0, 0, 119768, 0, 0, 0, 'Непокорная королевская стражница to Player');
INSERT INTO `creature_text` VALUES (106571, 3, 0, 'Нет, мать. Теперь я твой король!', 14, 0, 100, 0, 0, 0, 119776, 87688, 87688, 0, 'Ярл Сковальд to Королева Бретта');
INSERT INTO `creature_text` VALUES (106582, 1, 0, 'Огонь Скверны очистит эту землю!', 12, 0, 100, 0, 0, 0, 109788, 0, 0, 0, 'Бларгемал');
INSERT INTO `creature_text` VALUES (106582, 2, 0, 'Аркетракс... отомсти за меня!', 12, 0, 100, 0, 0, 0, 109789, 0, 0, 0, 'Бларгемал to Player');
INSERT INTO `creature_text` VALUES (106583, 0, 0, 'Азсуна падет перед мощью Легиона!', 14, 0, 100, 53, 0, 0, 109786, 0, 0, 0, 'Аркетракс to 0');
INSERT INTO `creature_text` VALUES (106583, 1, 0, 'Нас не... остановить...', 12, 0, 100, 0, 0, 0, 109787, 0, 0, 0, 'Аркетракс to Player');
INSERT INTO `creature_text` VALUES (106587, 4, 0, 'Глупый мальчишка. Думаешь, магия Скверны защитит тебя от моего гнева?', 14, 0, 100, 0, 0, 0, 119772, 15628, 15628, 0, 'Королева Бретта');
INSERT INTO `creature_text` VALUES (106587, 4, 0, 'Ты убил своего отца. Ты убил своих братьев. Но это не делает тебя законным правителем нашего народа!', 14, 0, 100, 0, 0, 0, 119773, 12335, 12335, 0, 'Королева Бретта');
INSERT INTO `creature_text` VALUES (106587, 4, 0, 'Ты... больше... не мой сын.', 14, 0, 100, 0, 0, 0, 119775, 70313, 70313, 0, 'Королева Бретта to Player');
INSERT INTO `creature_text` VALUES (106610, 0, 0, 'Calydus know where that is! Calydus can help!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Калид to Player');
INSERT INTO `creature_text` VALUES (106616, 3, 0, 'Мама говорит, мне еще рано пить вино.', 12, 0, 100, 0, 0, 65819, 112870, 107530, 107530, 0, 'Ночнорожденное дитя');
INSERT INTO `creature_text` VALUES (106616, 3, 0, 'Молнии! Пиу! Пиу!', 12, 0, 100, 18, 0, 65974, 112868, 8908, 22311, 0, 'Ночнорожденное дитя');
INSERT INTO `creature_text` VALUES (106616, 3, 0, 'Подожди чуть-чуть, вот-вот...', 12, 0, 100, 18, 0, 65978, 112874, 50670, 50670, 0, 'Ночнорожденное дитя');
INSERT INTO `creature_text` VALUES (106616, 3, 0, 'Прячься! А то съедят!', 12, 0, 100, 0, 0, 66108, 113109, 86043, 86043, 0, 'Ночнорожденное дитя to Ночнорожденное дитя');
INSERT INTO `creature_text` VALUES (106616, 3, 0, 'Пустота и боль вокруг...', 12, 0, 100, 18, 0, 65979, 112875, 54025, 54025, 0, 'Ночнорожденное дитя');
INSERT INTO `creature_text` VALUES (106616, 3, 0, 'Так нечестно! Почему ты всегда Великий магистр!', 12, 0, 100, 0, 0, 65976, 112871, 48080, 48080, 0, 'Ночнорожденное дитя');
INSERT INTO `creature_text` VALUES (106616, 3, 0, 'Учитель сказал, я самая сильная!', 12, 0, 100, 0, 0, 65977, 112872, 41629, 41629, 0, 'Ночнорожденное дитя');
INSERT INTO `creature_text` VALUES (106616, 3, 0, 'Щиты пробить невозможно! Они очень-очень мощные!', 12, 0, 100, 18, 0, 65988, 112869, 53108, 53108, 0, 'Ночнорожденное дитя');
INSERT INTO `creature_text` VALUES (106617, 3, 0, 'Давай-ка в другом месте поиграем.', 12, 0, 100, 0, 0, 65978, 113107, 169804, 169804, 0, 'Ночнорожденное дитя to Ночнорожденное дитя');
INSERT INTO `creature_text` VALUES (106617, 3, 0, 'Ну-ка, дети, встаньте в круг...', 12, 0, 100, 0, 0, 65778, 112876, 45702, 80238, 0, 'Ночнорожденное дитя');
INSERT INTO `creature_text` VALUES (106617, 3, 0, 'Пиу! Молнии! Пиу! Пиу!', 12, 0, 100, 0, 0, 65774, 112868, 96224, 96224, 0, 'Ночнорожденное дитя');
INSERT INTO `creature_text` VALUES (106617, 3, 0, 'Смерть нас вместе соберет!', 12, 0, 100, 0, 0, 65777, 112873, 60053, 60053, 0, 'Ночнорожденное дитя');
INSERT INTO `creature_text` VALUES (106617, 3, 0, 'Страшно…', 12, 0, 100, 0, 0, 65779, 113105, 3437, 3437, 0, 'Ночнорожденное дитя to Ночнорожденное дитя');
INSERT INTO `creature_text` VALUES (106617, 3, 0, 'Так нечестно! Я еще не был Великим магистром!', 12, 0, 100, 0, 0, 65771, 112871, 90206, 90206, 0, 'Ночнорожденное дитя');
INSERT INTO `creature_text` VALUES (106617, 3, 0, 'Учитель сказал, я самый сильный!', 12, 0, 100, 0, 0, 66003, 112872, 133204, 133204, 0, 'Ночнорожденное дитя');
INSERT INTO `creature_text` VALUES (106617, 3, 0, 'Щиты пробить невозможно! Они очень-очень мощные!', 12, 0, 100, 0, 0, 65773, 112869, 56604, 56604, 0, 'Ночнорожденное дитя');
INSERT INTO `creature_text` VALUES (106622, 0, 0, 'Члены Совета и его союзники! Начнем ритуал призыва.', 12, 0, 100, 1, 0, 66522, 108886, 0, 0, 0, 'Ритцун Хмурый Огонь to Player');
INSERT INTO `creature_text` VALUES (106629, 1, 0, 'БЛЕРГ!', 12, 0, 100, 0, 0, 0, 109899, 0, 0, 0, 'Блерг to Player');
INSERT INTO `creature_text` VALUES (106639, 0, 0, 'Very well. Once we clear an area, reinforcements will secure it. Down we go.', 12, 0, 100, 0, 0, 0, 109987, 0, 0, 0, 'Травар to Player');
INSERT INTO `creature_text` VALUES (106639, 1, 0, '%s falls to one knee, gravely wounded.', 16, 0, 100, 0, 0, 0, 109484, 0, 0, 0, 'Травар to 0');
INSERT INTO `creature_text` VALUES (106639, 2, 0, '%s is |cFFFF0404|Hspell:204237|h[Gravely Wounded]|h|r and cannot fight until healed!', 41, 0, 100, 0, 0, 0, 109485, 0, 0, 0, 'Травар to 0');
INSERT INTO `creature_text` VALUES (106639, 3, 0, 'Argent Crusaders, secure this area! We will press on!', 14, 0, 100, 0, 0, 0, 110109, 0, 0, 0, 'Травар to Player');
INSERT INTO `creature_text` VALUES (106639, 4, 0, 'I\'ll take care of this!', 12, 0, 100, 0, 0, 0, 110221, 0, 0, 0, 'Травар to Player');
INSERT INTO `creature_text` VALUES (106639, 5, 0, 'This chasm oozes darkness... Light protect us! That thing has what we need. Prepare yourselves, friends.', 12, 0, 100, 0, 0, 0, 110258, 0, 0, 0, 'Травар to Player');
INSERT INTO `creature_text` VALUES (106643, 0, 0, 'Ритуал прерван. Фигура в Ночном Колодце пробуждается.', 16, 0, 100, 0, 0, 0, 122436, 0, 0, 0, 'INTRO');
INSERT INTO `creature_text` VALUES (106643, 1, 0, 'Ваше время вышло!', 14, 0, 100, 0, 0, 58561, 108669, 0, 0, 0, 'AGGRO');
INSERT INTO `creature_text` VALUES (106643, 2, 0, 'Волны времени сметут вас!', 14, 0, 100, 0, 0, 58566, 108674, 0, 0, 0, 'ARCANETIC_RING');
INSERT INTO `creature_text` VALUES (106643, 3, 0, '|TInterface\\Icons\\Spell_Mage_ArcaneOrb.blp:20|t |cFFFF0000|Hspell:228877|h[Кинетические кольца]|h|r схлопываются, двигаясь от края комнаты к центру!', 41, 0, 100, 0, 0, 0, 124215, 0, 0, 0, 'ARCANETIC_RING');
INSERT INTO `creature_text` VALUES (106643, 4, 0, 'Я здесь доминирую, не вы!', 14, 0, 100, 0, 0, 58565, 108673, 0, 0, 0, 'SPANNING_SINGULARITY');
INSERT INTO `creature_text` VALUES (106643, 5, 0, 'Вы живы лишь здесь и сейчас. А мои возможности безграничны!', 14, 0, 100, 11, 0, 58568, 108676, 0, 0, 0, 'Change Phase 2');
INSERT INTO `creature_text` VALUES (106643, 6, 0, 'Времена меняются! Ваша смерть все ближе!', 14, 0, 100, 11, 0, 58569, 108677, 0, 0, 0, 'CHANGE_PHASE_3');
INSERT INTO `creature_text` VALUES (106643, 7, 0, 'Время нестабильно – сейчас вы сами в этом убедитесь.', 14, 0, 100, 0, 0, 58567, 108675, 0, 0, 0, 'EPOCHERIC_ORB');
INSERT INTO `creature_text` VALUES (106643, 8, 0, '|TInterface\\Icons\\SPELL_ARCANE_INVOCATION:20|t С неба падают |cFFFF0000|Hspell:210022|h[эпохальные сферы]|h|r!', 41, 0, 100, 0, 0, 0, 124211, 0, 0, 0, 'EPOCHERIC_ORB');
INSERT INTO `creature_text` VALUES (106643, 9, 0, 'Мне подвластно само время!', 14, 0, 100, 0, 0, 58562, 108670, 0, 0, 0, 'DELPHURIC_BEAM');
INSERT INTO `creature_text` VALUES (106643, 10, 0, '|TInterface\\Icons\\Spell_Mage_ArcaneOrb.blp:20|t%s направляет на своих врагов |cFFFF0000|Hspell:214278|h[пророческий луч]|h|r !', 41, 0, 100, 0, 0, 0, 124214, 0, 0, 0, 'DELPHURIC_BEAM');
INSERT INTO `creature_text` VALUES (106643, 11, 0, 'Хотите пронзить ткань времени? Или навсегда остаться в Вечности?', 14, 0, 100, 0, 0, 58563, 112413, 0, 0, 0, 'CONFLEXIVE_BURST');
INSERT INTO `creature_text` VALUES (106643, 12, 0, '|TInterface\\Icons\\6BF_Explosive_Shard.blp:20|t%s пускает импульсы |cFFFF0000|Hspell:209598|h[искривляющей]|h|r энергии в своих противников!', 41, 0, 100, 0, 0, 58563, 124213, 0, 0, 0, 'CONFLEXIVE_BURST');
INSERT INTO `creature_text` VALUES (106643, 13, 0, 'Неизбежный исход.', 14, 0, 100, 0, 0, 58565, 108673, 0, 0, 0, 'KILL_PLAYER');
INSERT INTO `creature_text` VALUES (106643, 13, 1, 'I foresaw this.', 14, 0, 100, 0, 0, 58572, 108680, 0, 0, 0, 'KILL_PLAYER');
INSERT INTO `creature_text` VALUES (106643, 14, 0, 'Немыслимо! Победа должна была достаться мне...', 14, 0, 100, 0, 0, 58575, 108683, 0, 0, 0, 'DEATH');
INSERT INTO `creature_text` VALUES (106643, 15, 0, 'Безрадостная победа. Но так было необходимо.', 14, 0, 100, 0, 0, 58576, 108684, 0, 0, 0, 'EVADE');
INSERT INTO `creature_text` VALUES (106643, 16, 0, 'Вы обречены на смерть.', 14, 0, 100, 0, 0, 58574, 108682, 0, 0, 0, 'UNK');
INSERT INTO `creature_text` VALUES (106643, 17, 0, 'Время... уходит! Как это возможно?', 14, 0, 100, 0, 0, 58571, 108679, 0, 0, 0, 'UNK');
INSERT INTO `creature_text` VALUES (106643, 18, 0, 'I control the flow of time! You won’t win!', 14, 0, 100, 0, 0, 58570, 112415, 0, 0, 0, 'UNK');
INSERT INTO `creature_text` VALUES (106652, 0, 0, 'I grow tired of your interference! DIE!', 14, 0, 100, 0, 0, 0, 111304, 0, 0, 0, 'Mephistroth to 0');
INSERT INTO `creature_text` VALUES (106652, 1, 0, 'Enjoy this victory while you can, Warlock. This isn\'t over between us; it has only just begun!', 14, 0, 100, 0, 0, 0, 110991, 0, 0, 0, 'Mephistroth to 0');
INSERT INTO `creature_text` VALUES (106654, 1, 0, 'Мы избраны править миром!', 12, 0, 100, 0, 0, 66106, 116707, 0, 0, 0, 'Лунный серп из Сумеречной стражи to Player');
INSERT INTO `creature_text` VALUES (106654, 2, 0, 'Не... возможно...', 12, 0, 100, 0, 0, 65688, 116429, 0, 0, 0, 'Лунный серп из Сумеречной стражи to Player');
INSERT INTO `creature_text` VALUES (106654, 3, 0, 'I will crush you, worm!', 12, 0, 100, 0, 0, 65708, 116706, 56826, 56826, 0, 'Лунный серп из Сумеречной стражи to Player');
INSERT INTO `creature_text` VALUES (106654, 3, 0, 'Мы знаем, где твоя семья. Говори, или они тоже умрут!', 12, 0, 100, 0, 0, 0, 124224, 2868, 2868, 0, 'Лунный серп из Сумеречной стражи');
INSERT INTO `creature_text` VALUES (106655, 3, 0, 'А, Фело\'мелорн. Огненный столб! Вот это мастерство, вот это изящество...', 12, 0, 100, 0, 0, 0, 110124, 372449, 372449, 211587, 'Чаромант Вридиэль to Player');
INSERT INTO `creature_text` VALUES (106655, 3, 0, 'Спасибо тебе, $n. Я проработал целую вечность, но не видел ничего подобного.', 12, 0, 100, 1, 0, 0, 110130, 1241739, 1241739, 0, 'Чаромант Вридиэль to Player');
INSERT INTO `creature_text` VALUES (106655, 3, 0, 'Это не какой-нибудь колун, который только и годится, чтобы уродовать плоть. Это гибкий клинок настоящего мастера тактики. В сталь вживлены потоки энергии тайной магии. Я чувствую, как по моим рукам расходится жар. Восхитительно!', 12, 0, 100, 0, 0, 0, 0, 1944440, 1944440, 0, 'Чаромант Вридиэль to Player');
INSERT INTO `creature_text` VALUES (106656, 0, 0, 'Демоны Легиона убили мою семью. Умри!', 12, 0, 100, 0, 0, 67683, 117139, 0, 0, 0, 'Эзара Верринда to 0');
INSERT INTO `creature_text` VALUES (106656, 1, 0, 'В магии Скверны нет ничего \"экзотического\".', 12, 0, 100, 0, 0, 66482, 117133, 0, 0, 0, 'Эзара Верринда to 0');
INSERT INTO `creature_text` VALUES (106656, 2, 0, 'I do a great job with both sword and magic. What do you like more?', 12, 0, 100, 0, 0, 67684, 117141, 0, 0, 0, 'Эзара Верринда to 0');
INSERT INTO `creature_text` VALUES (106656, 4, 0, 'Смерть от меча или от чар – мне все равно!', 12, 0, 100, 0, 0, 66474, 117137, 0, 0, 0, 'Эзара Верринда to 0');
INSERT INTO `creature_text` VALUES (106656, 9, 0, 'Равандвир, ты не должен был уходить один!', 12, 0, 100, 0, 0, 66480, 111443, 0, 0, 0, 'Эзара Верринда to Player');
INSERT INTO `creature_text` VALUES (106676, 0, 0, 'Destroy the monster! For the Light!', 14, 0, 100, 0, 0, 0, 110291, 0, 0, 0, 'Травар to Player');
INSERT INTO `creature_text` VALUES (106676, 1, 0, 'NO! They are just... gone! You will be remembered.', 14, 0, 100, 0, 0, 0, 110308, 0, 0, 0, 'Травар to Ужасная аберрация');
INSERT INTO `creature_text` VALUES (106676, 2, 0, '%s falls to one knee, gravely wounded.', 16, 0, 100, 0, 0, 0, 109484, 0, 0, 0, 'Травар to 0');
INSERT INTO `creature_text` VALUES (106676, 3, 0, '%s is |cFFFF0404|Hspell:204237|h[Gravely Wounded]|h|r and cannot fight until healed!', 41, 0, 100, 0, 0, 0, 109485, 0, 0, 0, 'Травар to 0');
INSERT INTO `creature_text` VALUES (106676, 4, 0, 'We\'ve done it, I\'ll get the piece! The monster\'s death has made the area unstable somehow.', 12, 0, 100, 0, 0, 0, 110306, 0, 0, 0, 'Травар to Ужасная аберрация');
INSERT INTO `creature_text` VALUES (106676, 5, 0, 'I\'m done! We must hurry, we\'ve stirred up the energies here!', 12, 0, 100, 0, 0, 0, 110307, 0, 0, 0, 'Травар to Ужасная аберрация');
INSERT INTO `creature_text` VALUES (106676, 6, 0, 'Our men are down! Help them, I will cover you!', 12, 0, 100, 0, 0, 0, 110310, 0, 0, 0, 'Травар to Player');
INSERT INTO `creature_text` VALUES (106676, 7, 0, 'Something is coming from the void, something big. We need to move now!', 12, 0, 100, 0, 0, 0, 110351, 0, 0, 0, 'Травар to Player');
INSERT INTO `creature_text` VALUES (106676, 8, 0, 'Excellent, and no time to waste, let\'s move!', 12, 0, 100, 0, 0, 0, 110311, 0, 0, 0, 'Травар to 1092');
INSERT INTO `creature_text` VALUES (106680, 0, 0, 'Время нестабильно – сейчас вы сами в этом убедитесь.', 14, 0, 100, 0, 0, 77036, 112409, 0, 0, 0, 'EPOCHERIC_ORB');
INSERT INTO `creature_text` VALUES (106680, 1, 0, '|TInterface\\Icons\\SPELL_ARCANE_INVOCATION:20|t С неба падают |cFFFF0000|Hspell:210022|h[эпохальные сферы]|h|r!', 41, 0, 100, 0, 0, 0, 124211, 0, 0, 0, 'EPOCHERIC_ORB');
INSERT INTO `creature_text` VALUES (106692, 0, 0, 'Foolish Warlock, did you really think I would remain unaware of your infantile plot to steal my treasure? You underestimate the power of the Nathrezim!', 12, 0, 100, 0, 0, 0, 110315, 0, 0, 0, 'Mephistroth to Эредарский ткач рока');
INSERT INTO `creature_text` VALUES (106692, 1, 0, 'As for you, treacherous skull, I\'ll deal with you later.', 12, 0, 100, 0, 0, 0, 110316, 0, 0, 0, 'Mephistroth to Эредарский ткач рока');
INSERT INTO `creature_text` VALUES (106692, 2, 0, 'Come to me servant of hellfire! Rid me of this uninvited guest.', 12, 0, 100, 0, 0, 0, 110317, 0, 0, 0, 'Mephistroth to Эредарский ткач рока');
INSERT INTO `creature_text` VALUES (106696, 0, 0, 'Вот и все. Я избавлюсь от пепла и вернусь в лагерь к Мейруне. Спасибо тебе.', 12, 0, 100, 0, 0, 0, 110907, 0, 0, 0, 'Календрос Железная Роща to Player');
INSERT INTO `creature_text` VALUES (106708, 0, 0, 'I have become void!', 14, 0, 100, 0, 0, 0, 107451, 0, 0, 0, 'Nexus-Prince Bilaal to Nexus-Prince Bilaal');
INSERT INTO `creature_text` VALUES (106719, 0, 0, 'Life... to consume...', 14, 0, 100, 0, 0, 0, 110352, 0, 0, 0, 'Охотник Мордот to Player');
INSERT INTO `creature_text` VALUES (106719, 1, 0, 'Why... flee? The Void... inevitable...', 12, 0, 100, 0, 0, 0, 110358, 0, 0, 0, 'Охотник Мордот to Травар');
INSERT INTO `creature_text` VALUES (106720, 0, 0, 'I feel the curse loosening! The ritual must not be interrupted!', 12, 0, 100, 0, 0, 70424, 113579, 0, 0, 0, 'Сверган Покров Шторма to Player');
INSERT INTO `creature_text` VALUES (106720, 1, 0, 'Готово! Говорящие с костями больше не помешают мне занять свое место в Чертогах Доблести!', 14, 0, 100, 0, 0, 70425, 113580, 0, 0, 0, 'Сверган Покров Шторма to Player');
INSERT INTO `creature_text` VALUES (106747, 0, 0, 'We\'re here. Now, let me see if I can find a way into the vault...', 12, 0, 100, 0, 0, 0, 107366, 0, 0, 0, 'Азурегос to Player');
INSERT INTO `creature_text` VALUES (106747, 1, 0, 'It\'s as I suspected, the main entrance to the vault was broken off when Malygos created the breach into the Twisting Nether during the Nexus Wars.', 12, 0, 100, 0, 0, 0, 107367, 0, 0, 0, 'Азурегос to Player');
INSERT INTO `creature_text` VALUES (106747, 2, 0, 'There must be some other way to reach it if the powers held inside can still be felt within the Nexus...', 12, 0, 100, 0, 0, 0, 107368, 0, 0, 0, 'Азурегос to Player');
INSERT INTO `creature_text` VALUES (106747, 3, 0, 'A-ha! That\'s... wait a minute... the energy from the vault is surging.  Brace yourself!', 12, 0, 100, 0, 0, 0, 107369, 0, 0, 0, 'Азурегос to Player');
INSERT INTO `creature_text` VALUES (106747, 4, 0, 'Wait... didn\'t there used to be a bridge here?', 12, 0, 100, 0, 0, 0, 107372, 0, 0, 0, 'Азурегос to Player');
INSERT INTO `creature_text` VALUES (106747, 5, 0, 'Oh great, more ethereals...', 12, 0, 100, 0, 0, 0, 107374, 0, 0, 0, 'Азурегос to Player');
INSERT INTO `creature_text` VALUES (106747, 6, 0, 'Wait, it looks like they\'re channeling the energy from the vault into the breach. This is not good.', 12, 0, 100, 0, 0, 0, 107375, 0, 0, 0, 'Азурегос to Player');
INSERT INTO `creature_text` VALUES (106747, 7, 0, 'Come! We will show these ethereals that I still have some fight left in me!', 12, 0, 100, 0, 0, 0, 107376, 0, 0, 0, 'Азурегос to Player');
INSERT INTO `creature_text` VALUES (106749, 0, 0, 'Now what?! One day, I will free myself of your control and... wait... you\'re not my master.', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Череп ман\'ари to Player');
INSERT INTO `creature_text` VALUES (106750, 0, 0, 'Hmph... fine then. Come to Felsoul Hold if you truly believe you can free me. You better be up for the challenge or you\'ll be joining me in eternal torment!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Череп ман\'ари to Player');
INSERT INTO `creature_text` VALUES (106752, 1, 0, 'На горе тебе не место!', 12, 0, 100, 0, 0, 57966, 107085, 0, 0, 0, 'Доргруб Жестокий to Player');
INSERT INTO `creature_text` VALUES (106756, 0, 0, 'Никто больше не будет повелевать мной!', 15, 0, 100, 0, 0, 64080, 113504, 0, 0, 0, 'Алунет to Player');
INSERT INTO `creature_text` VALUES (106756, 0, 1, 'I am perfect.', 15, 0, 100, 0, 0, 64083, 113507, 0, 0, 0, 'Алунет to Player');
INSERT INTO `creature_text` VALUES (106756, 0, 2, 'Ты меня не подчинишь!', 15, 0, 100, 0, 0, 64079, 113503, 0, 0, 0, 'Алунет to Player');
INSERT INTO `creature_text` VALUES (106756, 0, 3, 'Смертным не дано познать мудрость Алунет!', 15, 0, 100, 0, 0, 64084, 113505, 0, 0, 0, 'Алунет to Player');
INSERT INTO `creature_text` VALUES (106756, 0, 4, '*крик отчаяния*', 15, 0, 100, 0, 0, 64075, 113509, 0, 0, 0, 'Алунет to Player');
INSERT INTO `creature_text` VALUES (106756, 0, 5, 'Ты думаешь, что сможешь управлять мной? Как предыдущий \"Хранитель\"? Ты всего лишь дитя!', 15, 0, 100, 0, 0, 64076, 113510, 0, 0, 0, 'Алунет to Player');
INSERT INTO `creature_text` VALUES (106756, 0, 6, 'Думаешь, ты сможешь управлять этой силой? Ну-ну...', 15, 0, 100, 0, 0, 64077, 113511, 0, 0, 0, 'Алунет to Player');
INSERT INTO `creature_text` VALUES (106756, 1, 0, 'Идем, дитя. Обрушим хаос на твоих врагов. А я погляжу, что ты умеешь.', 15, 0, 100, 0, 0, 64078, 113512, 0, 0, 0, 'Алунет to Player');
INSERT INTO `creature_text` VALUES (106770, 1, 0, 'I will help!', 12, 0, 100, 0, 0, 30541, 117242, 0, 0, 0, 'Ли Ли Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (106770, 1, 0, 'Не бойся, я тебя прикрою!', 12, 0, 100, 0, 0, 30541, 117241, 0, 0, 0, 'Ли Ли Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (106770, 1, 0, 'У меня есть то, что нужно!', 12, 0, 100, 0, 0, 30541, 117243, 0, 0, 0, 'Ли Ли Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (106774, 0, 0, '%s falls to one knee, gravely wounded.', 16, 0, 100, 0, 0, 0, 109484, 0, 0, 0, 'Пехотинец Серебряного Авангарда to 0');
