INSERT INTO `creature_text` VALUES (106777, 0, 0, 'Everything is in place, I can complete the ritual! Everyone, defend the tunnel!', 14, 0, 100, 0, 0, 0, 110357, 0, 0, 0, 'Травар to 0');
INSERT INTO `creature_text` VALUES (106777, 1, 0, 'Hold! I am not yet done!', 14, 0, 100, 0, 0, 0, 110360, 0, 0, 0, 'Травар to 0');
INSERT INTO `creature_text` VALUES (106777, 2, 0, 'Almost there... just a few more moments!', 14, 0, 100, 0, 0, 0, 110361, 0, 0, 0, 'Травар to 0');
INSERT INTO `creature_text` VALUES (106777, 3, 0, 'It is done. Quickly, $p, take up the hammer! The ritual will cause the entire tomb to collapse as a failsafe!', 14, 0, 100, 0, 0, 0, 110362, 0, 0, 0, 'Травар to 0');
INSERT INTO `creature_text` VALUES (106777, 4, 0, 'We... made it. We have the Silver Hand as well.', 12, 0, 100, 0, 0, 0, 110975, 0, 0, 0, 'Травар to 0');
INSERT INTO `creature_text` VALUES (106777, 5, 0, 'The collapse was part of the failsafe, without the hammer powering the wards, anyone could have opened Tyr\'s sarcophagus, desecrated his remains.', 12, 0, 100, 0, 0, 0, 110976, 0, 0, 0, 'Травар to 0');
INSERT INTO `creature_text` VALUES (106777, 6, 0, 'You wield the Hand well I think. Return to Tyrosus, he will want to hear of our success. I will see you again.', 12, 0, 100, 0, 0, 0, 110977, 0, 0, 0, 'Травар to 0');
INSERT INTO `creature_text` VALUES (106795, 0, 0, 'Марлан должен взглянуть на твой дар и оценить его – тогда и поговорим. Прощай.', 12, 0, 100, 0, 0, 0, 111126, 0, 0, 0, 'Послушница Небесной академии to Player');
INSERT INTO `creature_text` VALUES (106798, 1, 0, 'Верховный маг Варгот узнает об этом!', 12, 0, 100, 0, 0, 0, 111007, 0, 0, 0, 'Нора Блэкфайр to Player');
INSERT INTO `creature_text` VALUES (106811, 0, 0, 'Fire the guns! Cover the path in Hellfire! Don\'t let that Warlock through!', 14, 0, 100, 0, 0, 0, 110913, 0, 0, 0, 'Mephistroth to Дикий бес');
INSERT INTO `creature_text` VALUES (106811, 1, 0, 'Your persistence will be the death of you! Minions, attack!', 14, 0, 100, 0, 0, 0, 110986, 0, 0, 0, 'Mephistroth to Player');
INSERT INTO `creature_text` VALUES (106812, 4, 0, 'Достойный противник!', 12, 0, 100, 0, 0, 67075, 117232, 9656, 9656, 0, 'Послушник школы Быка');
