INSERT INTO `creature_text` VALUES (106881, 3, 0, 'Да-да, болтайся туда-сюда и привлекай к себе внимание.', 15, 0, 100, 0, 0, 0, 111113, 1321159, 1321159, 0, '\"Челнок\" Аллен to Player');
INSERT INTO `creature_text` VALUES (106881, 3, 0, 'Ну ладно, тогда я пошел.', 12, 0, 100, 0, 0, 0, 111095, 2134, 2134, 0, 'Навигатор Аллен to Player');
INSERT INTO `creature_text` VALUES (106881, 3, 0, 'Только заклинаю тебя – не оставляй следов, которые могли бы привести их к нам.', 12, 0, 100, 0, 0, 0, 111096, 6259, 6259, 0, 'Навигатор Аллен to Player');
INSERT INTO `creature_text` VALUES (106885, 1, 0, 'Шнуропс бамкает напрасно – с королем шутить опасно!', 12, 0, 100, 0, 0, 86762, 117246, 0, 0, 0, 'Послушник школы Тигра');
INSERT INTO `creature_text` VALUES (106885, 4, 0, 'Гадкий шнуропс сейчас получит!', 12, 0, 100, 0, 0, 67214, 117244, 1388, 1388, 0, 'Послушник школы Тигра');
INSERT INTO `creature_text` VALUES (106897, 0, 0, 'Наши раны уже достаточно исцелились. Я продолжу разведку. Прощай, Лилрия.', 12, 0, 100, 0, 0, 0, 122610, 0, 0, 0, 'Ночноглаз to Player');
INSERT INTO `creature_text` VALUES (106897, 1, 0, 'Встретимся в сурамаре,  герой! Я буду ждать тебя в зверинце.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Ночноглаз to Player');
INSERT INTO `creature_text` VALUES (106901, 3, 0, 'Что с собой, $n? Ты $r? Сейчас больше смахиваешь на демона.', 12, 0, 100, 6, 0, 0, 122089, 3850, 3850, 0, 'Сильвия Олений Рог to Player');
INSERT INTO `creature_text` VALUES (106904, 3, 0, 'Пусть слава Одина направляет тебя, валарьяр.', 12, 0, 100, 66, 0, 0, 122275, 45780, 45780, 0, 'Вальдемар Искатель Бурь to Player');
INSERT INTO `creature_text` VALUES (106905, 0, 0, 'Герои, пелена спала с моих глаз. Тени отступают, и Сон вновь зовет.', 12, 0, 100, 0, 0, 71040, 121690, 0, 0, 0, 'Малорн to Стражница Сна');
INSERT INTO `creature_text` VALUES (106905, 1, 0, 'Спасибо, герои.', 12, 0, 100, 0, 0, 71043, 121707, 0, 0, 0, 'Малорн to Стражница Сна');
