INSERT INTO `creature_text` VALUES (106930, 3, 0, 'I have already said that I will bring this information to the attention of Lord Greymane. And no need to tell anyone about it, |3-6($c).', 12, 0, 100, 273, 0, 0, 111133, 29985, 29985, 0, 'Лейтенант Сертис to Player');
INSERT INTO `creature_text` VALUES (106942, 3, 0, 'Я буду добавлять новые сведения по мере нашего исследования. Тебе стоит взглянуть, когда будет время.', 12, 0, 100, 1, 0, 0, 120260, 8255, 8255, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (106951, 3, 0, 'Лучше, если все это останется между нами, |3-6($c). Не зря говорят: болтун – находка для шпиона.', 12, 0, 100, 273, 0, 0, 111151, 27834, 27834, 0, 'Советница Тихая Заводь to Player');
