INSERT INTO `creature_text` VALUES (107023, 0, 0, '|TINTERFACE\\ICONS\\warrior_talent_icon_thunderstruck:20|t Нитхегг высвобождает |cFFFF0404|Hspell:212841|h[\"Сотрясающий разряд\"]|h|r!', 42, 0, 100, 0, 0, 0, 111614, 0, 0, 0, 'Нитхёгг to Player');
INSERT INTO `creature_text` VALUES (107105, 1, 0, 'Борись до конца... Люблю, когда жертва измотана.', 12, 0, 100, 0, 0, 0, 123258, 0, 0, 0, 'Праматерь Лизакс to Player');
INSERT INTO `creature_text` VALUES (107106, 2, 0, 'Нам... нет числа.', 14, 0, 100, 0, 0, 61034, 112516, 0, 0, 189103, 'Возжигатель Аз\'ротэль to Горный пехотинец Дун Морога');
