INSERT INTO `creature_text` VALUES (107244, 3, 0, 'Так эту банду возглавляет Аркетракс? Вот это неожиданно.', 15, 0, 100, 0, 0, 0, 111569, 985768, 985768, 0, 'Тейд Шумакер to Player');
INSERT INTO `creature_text` VALUES (107244, 3, 0, 'Что это за паноптикум? И вот ЭТО они называют ритуалом? Да я мог бы обрушить на этих эредаров огненный ливень, и то хуже бы не стало – благо, некуда.', 15, 0, 100, 0, 0, 0, 111573, 571164, 571164, 0, 'Тейд Шумакер to Player');
INSERT INTO `creature_text` VALUES (107245, 3, 0, 'Нужно продолжать патрулирование. Неизвестно, сколько еще будет таких вторжений поблизости.', 12, 0, 100, 0, 0, 0, 122438, 1342, 1342, 0, 'Мариус Проклятие Скверны to Player');
INSERT INTO `creature_text` VALUES (107253, 0, 0, 'Лорина ты найдешь на площади, Силессу – у каналов, Сириллу – на базаре. Ты их сразу узнаешь.', 12, 0, 100, 0, 0, 66207, 117194, 0, 0, 0, 'Арлуин to Player');
INSERT INTO `creature_text` VALUES (107253, 1, 0, 'Так вот что она задумала? Храбрая девочка! Я бы помог, но…', 12, 0, 100, 0, 0, 68043, 119243, 0, 0, 0, 'Арлуин to Player');
INSERT INTO `creature_text` VALUES (107253, 2, 0, 'Пожалуйста. С моей помощью она станет советницей еще до полудня.', 12, 0, 100, 0, 0, 66192, 116295, 0, 0, 0, 'Арлуин to Player');
INSERT INTO `creature_text` VALUES (107253, 3, 0, 'Арлуин жестом подзывает вас.', 16, 0, 100, 0, 0, 0, 121788, 0, 0, 0, 'Арлуин to Player');
INSERT INTO `creature_text` VALUES (107253, 4, 0, 'I\'ll go home. I have nothing to do among esteemed ladies and gentlemen.', 12, 0, 100, 1, 0, 66188, 116511, 0, 0, 0, 'Арлуин to Player');
INSERT INTO `creature_text` VALUES (107253, 5, 0, 'Кстати, я принимаю твою благодарность!', 12, 0, 100, 603, 0, 66189, 116512, 0, 0, 0, 'Арлуин to Player');
INSERT INTO `creature_text` VALUES (107253, 6, 0, 'Как мило. Ты хочешь нас обоих в гроб загнать? Нет? Я так и думал.', 12, 0, 100, 0, 0, 0, 115306, 0, 0, 0, 'Арлуин to Player');
INSERT INTO `creature_text` VALUES (107253, 7, 0, 'Месть? Можно придумать что-то получше. Гораздо лучше. На самом деле...', 12, 0, 100, 0, 0, 0, 115305, 0, 0, 0, 'Арлуин to Player');
INSERT INTO `creature_text` VALUES (107269, 1, 0, 'Посмотрим, что ты прячешь в закромах своей души.', 12, 0, 100, 0, 0, 0, 123255, 0, 0, 0, 'Инквизитор Тивос to Player');
INSERT INTO `creature_text` VALUES (107280, 3, 0, 'Да будет так. Обрати эту великую силу против врагов Тирисфаля!', 12, 0, 100, 1, 0, 0, 111741, 203303, 203303, 0, 'Элдрагоса to Player');
INSERT INTO `creature_text` VALUES (107289, 0, 0, 'Galakrond\'s Rest can be dangerous. Perhaps a tracking spell will be of use.', 12, 0, 100, 0, 0, 0, 111593, 0, 0, 0, 'Ланигоса to Player');
INSERT INTO `creature_text` VALUES (107289, 1, 0, 'There! A bit of magic to help you follow in Galford\'s footsteps. Good luck!', 12, 0, 100, 0, 0, 0, 113741, 0, 0, 0, 'Ланигоса to Player');
INSERT INTO `creature_text` VALUES (107301, 4, 0, 'Отдай! Отдай!', 12, 0, 100, 0, 0, 0, 116370, 13753, 34069, 0, 'Изображение кобольда to Player');
