INSERT INTO `creature_text` VALUES (107302, 4, 0, 'Грлмр лмрглр рмлмргл рлгмрлр мрглрмргл грлмргл?', 12, 0, 100, 0, 0, 23615, 113855, 0, 0, 0, 'Ловец жемчуга Фин to Player');
