INSERT INTO `creature_text` VALUES (107302, 6, 0, 'Глмрг! Гмлргм мглрмглр мрмглр!', 12, 0, 100, 0, 0, 0, 114794, 0, 0, 0, 'Ловец жемчуга Фин to Player');
INSERT INTO `creature_text` VALUES (107309, 0, 0, 'They\'re out here at Molsen Farm too! Get out here! They\'re everywhere!', 14, 0, 100, 0, 0, 0, 111592, 0, 0, 0, 'Изгнанный батрак');
INSERT INTO `creature_text` VALUES (107311, 0, 0, 'Alexston\'s been overrun as well! We\'ve taken shelter in the barn!', 14, 0, 100, 0, 0, 0, 111613, 0, 0, 0, 'Скиталец западных пустошей');
INSERT INTO `creature_text` VALUES (107314, 0, 0, 'The one you seek carried a spark of Tyr. Only a tiny one, but it drew him here... and drew others to him.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Дружелюбный дух таунка to Player');
INSERT INTO `creature_text` VALUES (107314, 1, 0, 'The spark showed him a vision. As Tyr lost his hand, the drakes tried to help, but only another keeper could save him. One keeper in particular.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Дружелюбный дух таунка to Player');
INSERT INTO `creature_text` VALUES (107314, 2, 0, 'Your friend learned of Tyr\'s companion, cursed to forever wander the titans\' path. He chased after the stone giant, catching up to it at a chasm... just as his own fate caught up to him.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Дружелюбный дух таунка to Player');
INSERT INTO `creature_text` VALUES (107315, 3, 0, 'Как и те, кто пришел до меня, обязуюсь служить тебе и Незримому пути.', 12, 0, 100, 1, 0, 70475, 121033, 360, 360, 0, 'Лорен Штормовое Копыто to Player');
INSERT INTO `creature_text` VALUES (107316, 0, 0, 'Если под твоим началом орден будет помогать нуждающимся, ты можешь рассчитывать на мою поддержку.', 12, 0, 100, 1, 0, 69559, 120512, 0, 0, 0, 'Повелительница зверей Хилейр to Player');
INSERT INTO `creature_text` VALUES (107317, 0, 0, 'Клянусь до конца быть верной тебе и Незримому пути!', 12, 0, 100, 1, 0, 70476, 121038, 0, 0, 0, 'Эммарель Стражница Тени to Player');
INSERT INTO `creature_text` VALUES (107318, 0, 0, 'Тихо, малявки! Вас ждет та же участь!', 12, 0, 100, 0, 0, 66315, 115406, 118414, 118414, 0, 'Стражница Моргана to Player');
