INSERT INTO `creature_text` VALUES (107324, 3, 0, 'Бежим! У них оружие!', 12, 0, 100, 11, 0, 0, 121053, 237672, 237672, 0, 'Сурамарское дитя');
INSERT INTO `creature_text` VALUES (107324, 3, 0, 'Чужаки!', 12, 0, 100, 0, 0, 0, 121049, 344502, 344502, 0, 'Сурамарское дитя');
