INSERT INTO `creature_text` VALUES (107333, 0, 0, 'Тебя сюда не звали! Умри!', 12, 0, 100, 0, 0, 69824, 115553, 0, 0, 0, 'Надзиратель Дюрант to 0');
INSERT INTO `creature_text` VALUES (107333, 1, 0, 'Марго сама подписала себе приговор! Как и вы.', 12, 0, 100, 0, 0, 69825, 115554, 0, 0, 0, 'Надзиратель Дюрант to 0');
INSERT INTO `creature_text` VALUES (107333, 2, 0, 'Вы поплатитесь за свою дерзость!', 12, 0, 100, 0, 0, 69827, 115597, 0, 0, 0, 'Надзиратель Дюрант to 0');
