INSERT INTO `creature_text` VALUES (107333, 4, 0, 'П-предатели...', 12, 0, 100, 0, 0, 69829, 111870, 0, 0, 0, 'Надзиратель Дюрант to Силесса');
INSERT INTO `creature_text` VALUES (107334, 3, 0, 'А можно обойтись без этого? Я пишу важную статью, а когда всякие балбесы палят куда ни попадя, очень трудно сосредоточиться.', 12, 0, 100, 0, 0, 0, 111929, 2633223, 2633223, 0, 'Сифорн to Player');
