INSERT INTO `creature_text` VALUES (107425, 0, 0, 'Удачно я тебя нашел. Еще бы немного, и дух вон.', 12, 0, 100, 1, 0, 66562, 115371, 0, 0, 0, 'Рексар to Player');
INSERT INTO `creature_text` VALUES (107435, 0, 0, '$n, you think I would spy on my own? Maybe we should look closer at you instead!', 12, 0, 100, 463, 0, 0, 111882, 0, 0, 0, 'Suspicious Noble to Player');
