INSERT INTO `creature_text` VALUES (107521, 2, 0, 'Нас не остановить...', 12, 0, 100, 0, 0, 66112, 117693, 0, 0, 0, 'Посланник Легиона to Player');
INSERT INTO `creature_text` VALUES (107521, 2, 0, 'Это… безнадежно…', 12, 0, 100, 0, 0, 65907, 117087, 0, 0, 0, 'Посланник Легиона to Player');
INSERT INTO `creature_text` VALUES (107525, 1, 0, 'Жемчужина моя!', 12, 0, 100, 0, 0, 0, 113610, 0, 0, 0, 'Воспевательница зла Лир\'кесс to Player');
INSERT INTO `creature_text` VALUES (107532, 3, 0, 'До встречи, $n.', 12, 0, 100, 0, 0, 0, 111948, 2200, 2200, 0, 'Равандвир to Player');
INSERT INTO `creature_text` VALUES (107535, 0, 0, 'Твоя смерть будет быстрой и мучительной!', 12, 0, 100, 0, 0, 0, 105124, 0, 0, 0, 'Эредарский истязатель души to Player');
INSERT INTO `creature_text` VALUES (107535, 0, 1, 'Моя родина – Аргус, величайший из миров Великой Тьмы.', 12, 0, 100, 0, 0, 0, 105127, 0, 0, 0, 'Эредарский истязатель души to Player');
INSERT INTO `creature_text` VALUES (107542, 3, 0, 'Берегись! Нас атакуют!', 14, 0, 100, 5, 0, 0, 111968, 277125, 277125, 0, 'Дозорный Альянса to Player');
INSERT INTO `creature_text` VALUES (107542, 3, 0, 'Генерал!', 14, 0, 100, 25, 0, 0, 111966, 305578, 305578, 0, 'Дозорный Альянса to Player');
INSERT INTO `creature_text` VALUES (107567, 4, 0, 'Начинаю процедуру самоликвидации.', 12, 0, 100, 0, 0, 0, 115099, 7551, 7551, 0, 'Страж сурамарского щита');
INSERT INTO `creature_text` VALUES (107574, 0, 0, 'Люди Штормграда! Граждане Альянса! Слушайте своего короля!', 14, 7, 100, 22, 0, 0, 31675, 0, 0, 0, 'Король Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (107574, 1, 0, 'Сегодня состоялось первое из многих поражений Плети! Рыцари смерти, некогда служившие Королю-личу, вырвались из его гибельной хватки и сформировали новый союз, чтобы противостоять его тирании!', 14, 7, 100, 22, 0, 0, 31688, 0, 0, 0, 'Король Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (107574, 2, 0, 'Вы радушно примете этих прошлых героев Альянса и будете относиться к ним с уважением, которым вы бы почтили любого союзника Штормграда!', 14, 7, 100, 22, 0, 0, 31677, 0, 0, 0, 'Король Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (107574, 3, 0, 'Слава Альянсу!', 14, 7, 100, 71, 0, 0, 87266, 0, 0, 0, 'Король Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (107574, 8, 0, 'I had this compass crafted for my father\'s birthday. I gave it to him a few months before my ship ran aground on Pandaria. A lifetime ago...', 12, 0, 100, 1, 0, 83223, 128735, 0, 0, 0, 'Король Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (107574, 9, 0, 'Since this war with the Legion began, I\'ve barely had a chance to think or breathe... or to mourn. Perhaps it\'s time to change that.', 12, 0, 100, 1, 0, 83224, 128736, 0, 0, 0, 'Король Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (107574, 10, 0, 'Thank you for bringing my father\'s compass back to me. It is a reminder of how much I miss him.', 12, 0, 100, 1, 0, 83225, 128737, 0, 0, 0, 'Король Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (107588, 0, 0, 'Fool! Did you really think a little piece of wood would stop me?!', 12, 0, 100, 0, 0, 0, 113308, 0, 0, 0, 'Blood-Thane Lucard to Player');
INSERT INTO `creature_text` VALUES (107588, 1, 0, 'You will bleed!', 14, 0, 100, 0, 0, 0, 113309, 0, 0, 0, 'Blood-Thane Lucard to Player');
INSERT INTO `creature_text` VALUES (107588, 2, 0, 'The blood sustains me!', 12, 0, 100, 0, 0, 0, 113310, 0, 0, 0, 'Blood-Thane Lucard to Drained Corsair');
INSERT INTO `creature_text` VALUES (107588, 3, 0, '|TInterface\\Icons\\ability_ironmaidens_bloodritual:20|t Lucard begins to |cFFFF0404|Hspell:215000|h[Consume the Living]|h|r! Slay the corsair quickly!', 41, 0, 100, 0, 0, 0, 113657, 0, 0, 0, 'Blood-Thane Lucard to Drained Corsair');
INSERT INTO `creature_text` VALUES (107588, 6, 0, 'I.. am not... defeated...', 12, 0, 100, 0, 0, 0, 113311, 0, 0, 0, 'Blood-Thane Lucard to Player');
INSERT INTO `creature_text` VALUES (107590, 0, 0, 'Впервые увидев тебя, я молила духов о том, чтобы тебе можно было доверять. И вот благодаря тебе наши племена вновь едины.', 12, 0, 100, 0, 0, 61018, 112330, 0, 0, 0, 'Майла Крутогор to Player');
INSERT INTO `creature_text` VALUES (107590, 3, 0, 'Именем племен Крутогорья вверяю этот молот тебе и твоим союзникам. Пусть он сокрушит любого врага!', 12, 0, 100, 0, 0, 61019, 112331, 39091, 39091, 0, 'Майла Крутогор to Player');
INSERT INTO `creature_text` VALUES (107593, 0, 0, 'Another annoyance?! I\'m going to enjoy seeing this world burn!', 12, 0, 100, 0, 0, 0, 112478, 0, 0, 0, 'Algromon to Player');
INSERT INTO `creature_text` VALUES (107598, 3, 0, 'Большинству – хватит. А остальным… О! Well Met.', 12, 0, 100, 0, 0, 67584, 113883, 3417, 3417, 0, 'Вантир to Player');
INSERT INTO `creature_text` VALUES (107598, 3, 0, 'Не спорю, политика – дело… не из приятных. Но представь, какие возможности откроются перед нами...', 12, 0, 100, 1, 0, 67586, 116280, 5910, 5910, 0, 'Вантир to Player');
INSERT INTO `creature_text` VALUES (107600, 3, 0, 'Мне кажется, что за нами никто даже не наблюдает.', 12, 0, 100, 6, 0, 0, 114131, 188888, 188888, 0, 'Горожанка-шал\'дорай to Player');
INSERT INTO `creature_text` VALUES (107603, 3, 0, 'I am starving!', 12, 0, 100, 0, 0, 0, 114601, 12966, 12966, 0, 'Горожанин-шал\'дорай to Надсмотрщик виноградников');
INSERT INTO `creature_text` VALUES (107603, 3, 0, 'I\'m shal\'dorei! You must reckon with me!', 12, 0, 100, 0, 0, 0, 114780, 1753702, 1753702, 0, 'Горожанин-шал\'дорай to Player');
INSERT INTO `creature_text` VALUES (107603, 3, 0, 'Пожалуйста, помоги мне... прошу...', 12, 0, 100, 0, 0, 0, 114606, 8392, 8392, 0, 'Горожанин-шал\'дорай to Надсмотрщик виноградников');
INSERT INTO `creature_text` VALUES (107603, 3, 0, 'Пропусти меня! Прошу!', 12, 0, 100, 0, 0, 0, 114604, 12402, 12402, 0, 'Горожанин-шал\'дорай to Надсмотрщик виноградников');
INSERT INTO `creature_text` VALUES (107603, 3, 0, 'Умоляю... Я больше не могу!', 12, 0, 100, 0, 0, 0, 114602, 9390832, 9390832, 0, 'Горожанин-шал\'дорай to Надсмотрщица виноградников');
INSERT INTO `creature_text` VALUES (107603, 3, 0, 'Я знаю, что у вас припасено для себя! Прошу, дай хоть глоточек!', 12, 0, 100, 0, 0, 0, 114605, 95106, 95106, 0, 'Горожанин-шал\'дорай to Надсмотрщица виноградников');
INSERT INTO `creature_text` VALUES (107604, 3, 0, 'Новая партия прибыла? Скажи, что да... прошу...', 12, 0, 100, 0, 0, 0, 114136, 8407, 8407, 0, 'Страдающий горожанин to Player');
INSERT INTO `creature_text` VALUES (107604, 3, 0, 'С-скоро, детка. Скоро попьешь, обещаю.', 12, 0, 100, 273, 0, 0, 114127, 76871, 76871, 0, 'Страдающий горожанин to Player');
INSERT INTO `creature_text` VALUES (107606, 3, 0, 'Пап, я хочу пить... можно мне немного манасока?', 12, 0, 100, 18, 0, 0, 114126, 71742, 71742, 0, 'Ночнорожденное дитя to Player');
INSERT INTO `creature_text` VALUES (107608, 3, 0, 'Видишь эти борозды, идущие вдоль клинка? По ним яд проникает глубже в раны. Эти кинжалы пьют кровь и выплевывают яд. Смертоносный шедевр!', 12, 0, 100, 0, 0, 0, 112366, 26336, 26336, 0, 'Гревин Слайт to Player');
INSERT INTO `creature_text` VALUES (107622, 0, 0, 'Don\'t forget to push the premier account!', 12, 0, 100, 0, 0, 0, 112342, 0, 0, 0, 'Glutonia to 0');
INSERT INTO `creature_text` VALUES (107622, 1, 0, 'Look at all of it... hopefully none of it goes missing...', 12, 0, 100, 0, 0, 0, 112343, 0, 0, 0, 'Glutonia to 0');
INSERT INTO `creature_text` VALUES (107622, 2, 0, 'Hah! You don\'t look like him! Fine, let\'s see here...', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Glutonia to Player');
INSERT INTO `creature_text` VALUES (107622, 3, 0, 'Well, you have his sigil. How did you get that? Well I guess I have no choice.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Glutonia to Player');
INSERT INTO `creature_text` VALUES (107622, 4, 0, 'You tell that lug if he wants to pick up where we left off I\'m here every day every hour!', 12, 0, 100, 11, 0, 0, 0, 0, 0, 0, 'Glutonia to Player');
INSERT INTO `creature_text` VALUES (107622, 5, 0, 'I’ll tell you what the Legion needed - all my fat! Protective amulets from the Legion are very expensive!', 12, 0, 100, 5, 0, 0, 125608, 0, 0, 0, 'Обжория to Player');
INSERT INTO `creature_text` VALUES (107624, 1, 0, 'Прощайся с жизнью!', 12, 0, 100, 0, 0, 0, 112561, 0, 0, 0, 'Мстительная душа to Player');
INSERT INTO `creature_text` VALUES (107624, 1, 0, 'Ты умрешь здесь среди проклятых!', 12, 0, 100, 0, 0, 0, 112562, 0, 0, 0, 'Мстительная душа to Player');
INSERT INTO `creature_text` VALUES (107624, 2, 0, 'Проклятие все еще действует...', 12, 0, 100, 0, 0, 0, 112564, 0, 0, 0, 'Мстительная душа to Player');
INSERT INTO `creature_text` VALUES (107624, 2, 0, 'Проклятие... никогда не даст мне покоя...', 12, 0, 100, 0, 0, 0, 112563, 0, 0, 0, 'Мстительная душа to Player');
INSERT INTO `creature_text` VALUES (107624, 3, 0, 'Мана предала нас...', 12, 0, 100, 18, 0, 0, 112448, 88344, 88344, 0, 'Мстительная душа');
INSERT INTO `creature_text` VALUES (107625, 3, 0, 'Прощайся с жизнью!', 12, 0, 100, 0, 0, 0, 112561, 6803, 6803, 0, 'Мстительная душа to Player');
INSERT INTO `creature_text` VALUES (107628, 1, 0, 'Ты умрешь здесь, среди проклятых!', 12, 0, 100, 0, 0, 0, 112562, 0, 0, 0, 'Мстительная душа to Player');
INSERT INTO `creature_text` VALUES (107628, 2, 0, 'Проклятие все еще действует...', 12, 0, 100, 0, 0, 0, 112564, 0, 0, 0, 'Мстительная душа to Player');
INSERT INTO `creature_text` VALUES (107628, 2, 0, 'Проклятие... никогда не даст мне покоя...', 12, 0, 100, 0, 0, 0, 112563, 0, 0, 0, 'Мстительная душа to Player');
INSERT INTO `creature_text` VALUES (107628, 3, 0, 'Мана предала нас...', 12, 0, 100, 18, 0, 0, 112448, 38095, 38095, 0, 'Мстительная душа');
INSERT INTO `creature_text` VALUES (107628, 3, 0, 'Не подходи... НЕ ПОДХОДИ!', 12, 0, 100, 18, 0, 0, 112381, 106721, 106721, 0, 'Мстительная душа');
INSERT INTO `creature_text` VALUES (107628, 3, 0, 'Прощайся с жизнью!', 12, 0, 100, 0, 0, 0, 112561, 39933, 39933, 0, 'Мстительная душа to Player');
INSERT INTO `creature_text` VALUES (107629, 2, 0, 'Проклятие... никогда не даст мне покоя...', 12, 0, 100, 0, 0, 0, 112563, 0, 0, 0, 'Мстительная душа to Player');
INSERT INTO `creature_text` VALUES (107632, 0, 0, 'Знаешь, мы не просто так поселились подальше от дворца.', 12, 0, 100, 274, 0, 67567, 116281, 0, 0, 0, 'Ли\'лет Лунарх to Player');
INSERT INTO `creature_text` VALUES (107632, 1, 0, 'Да... Я могла бы столько сделать для всех…', 12, 0, 100, 1, 0, 67568, 116282, 0, 0, 0, 'Ли\'лет Лунарх to Player');
INSERT INTO `creature_text` VALUES (107632, 2, 0, 'Встретимся в Святилище Порядка. Я подготовлюсь к церемонии.', 12, 0, 100, 1, 0, 68065, 119291, 0, 0, 0, 'Ли\'лет Лунарх to Player');
INSERT INTO `creature_text` VALUES (107632, 3, 0, 'Спасибо за доверие. Это большая честь для меня.', 12, 0, 100, 0, 0, 66267, 116515, 0, 0, 0, 'Ли\'лет Лунарх to Player');
INSERT INTO `creature_text` VALUES (107632, 4, 0, 'Милорд, вы забываетесь. Решение уже принято.', 12, 0, 100, 1, 0, 66265, 116519, 0, 0, 0, 'Ли\'лет Лунарх to Player');
INSERT INTO `creature_text` VALUES (107632, 5, 0, 'Тал\'ашар? Да будет так. Мой защитник – перед тобой.', 12, 0, 100, 25, 0, 68147, 119381, 0, 0, 0, 'Ли\'лет Лунарх to Player');
INSERT INTO `creature_text` VALUES (107632, 6, 0, 'I will work on your disguise, and you can fight like us.', 15, 0, 100, 0, 0, 68150, 119388, 0, 0, 0, 'Ли\'лет Лунарх to Player');
INSERT INTO `creature_text` VALUES (107632, 7, 0, 'Надеюсь, ты добудешь мне должность.', 12, 0, 100, 1, 0, 68151, 119389, 0, 0, 0, 'Ли\'лет Лунарх to Player');
INSERT INTO `creature_text` VALUES (107632, 8, 0, 'Жаль, что до этого дошло. Хорошо, что мы справились.', 12, 0, 100, 0, 0, 68152, 119395, 0, 0, 0, 'Ли\'лет Лунарх to Player');
INSERT INTO `creature_text` VALUES (107632, 9, 0, 'Только скажи – и я верну тебя в таверну.', 12, 0, 100, 0, 0, 68153, 119396, 0, 0, 0, 'Ли\'лет Лунарх to Player');
INSERT INTO `creature_text` VALUES (107633, 0, 0, 'Демоны! Кругом демоны!', 12, 0, 100, 1, 0, 0, 112393, 0, 0, 0, 'Истощенный узник to Player');
INSERT INTO `creature_text` VALUES (107633, 0, 1, 'Спасибо. Я чуть не погибла.', 12, 0, 100, 1, 0, 0, 112394, 0, 0, 0, 'Истощенная узница to Player');
INSERT INTO `creature_text` VALUES (107633, 0, 2, 'Спасибо тебе большое... А можно еще немного еды?', 12, 0, 100, 1, 0, 0, 112397, 0, 0, 0, 'Истощенный узник to Player');
INSERT INTO `creature_text` VALUES (107638, 3, 0, 'Убейте его! Спасите рощу!', 14, 0, 100, 0, 0, 0, 102283, 372156, 372156, 0, 'Друид-хранитель to Player');
INSERT INTO `creature_text` VALUES (107643, 3, 0, 'Отведай-ка!', 14, 0, 100, 0, 0, 0, 113089, 14411, 14411, 0, 'Врайкул-моряк');
INSERT INTO `creature_text` VALUES (107657, 1, 0, 'Моя магия прикончит тебя!', 12, 0, 100, 0, 0, 0, 123252, 0, 0, 0, 'Чародей Шал\'иман to Player');
INSERT INTO `creature_text` VALUES (107659, 3, 0, 'Что это был за шум?..', 12, 0, 100, 0, 0, 0, 112660, 23997, 23997, 0, 'Тайный фанатик to Player');
INSERT INTO `creature_text` VALUES (107674, 3, 0, 'So long sucker!', 14, 0, 100, 22, 0, 0, 113057, 9925, 9925, 0, 'Snaggle Sixtrigger to Player');
INSERT INTO `creature_text` VALUES (107683, 0, 0, 'Fools. Your pathetic souls will fuel the Legion\'s coming!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Caden Shadowgaze to Player');
INSERT INTO `creature_text` VALUES (107683, 1, 0, 'Stand down, $r scum. You know not the forces you tempt.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Caden Shadowgaze to 0');
INSERT INTO `creature_text` VALUES (107683, 1, 1, 'Can you not feel it? The warm embrace of the Legion comes, you need only welcome it!', 12, 0, 100, 0, 0, 0, 112652, 0, 0, 0, 'Caden Shadowgaze to 0');
INSERT INTO `creature_text` VALUES (107683, 1, 2, 'You are not worth saving. Die, miserable worm!', 12, 0, 100, 0, 0, 0, 112653, 0, 0, 0, 'Caden Shadowgaze to 0');
INSERT INTO `creature_text` VALUES (107683, 4, 0, 'You are too late. We are... everywhere...', 12, 0, 100, 0, 0, 0, 112654, 0, 0, 0, 'Caden Shadowgaze to Player');
INSERT INTO `creature_text` VALUES (107696, 1, 0, 'I will avenge Farondis for his pride, which has destroyed us all!', 12, 0, 100, 0, 0, 0, 112566, 0, 0, 0, 'Лорд Таш\'ирель to Player');
INSERT INTO `creature_text` VALUES (107702, 0, 0, 'Are you here for me? Or just what\'s left here?', 12, 0, 100, 0, 0, 0, 112474, 0, 0, 0, 'Merina to Player');
INSERT INTO `creature_text` VALUES (107702, 1, 0, 'I was left here to watch over the site and no one ever came to get me. I wasn\'t strong enough to defend the site, or I was here too long, I\'m not sure.', 12, 0, 100, 0, 0, 0, 112475, 0, 0, 0, 'Merina to Player');
INSERT INTO `creature_text` VALUES (107702, 2, 0, 'If you want that ritual to work you will need the focus that was left here. I tried to keep it from the monsters but I was too weak.', 12, 0, 100, 0, 0, 0, 112476, 0, 0, 0, 'Merina to Player');
INSERT INTO `creature_text` VALUES (107702, 3, 0, 'You can find it over there, by my corpse. I think its keeping me here. Take it far from here and maybe I can leave too.', 12, 0, 100, 0, 0, 0, 112477, 0, 0, 0, 'Merina to Player');
INSERT INTO `creature_text` VALUES (107712, 0, 0, 'Куда же она подевалась…', 12, 0, 100, 0, 0, 66374, 113899, 0, 0, 0, 'Верина to Player');
INSERT INTO `creature_text` VALUES (107712, 1, 0, 'Думаешь, Корина была с другими детьми?', 12, 0, 100, 0, 0, 67583, 113185, 0, 0, 0, 'Верина to Player');
INSERT INTO `creature_text` VALUES (107712, 2, 0, 'Малышка моя! Ты цела? С тобой все хорошо?', 12, 0, 100, 0, 0, 66378, 113268, 0, 0, 0, 'Верина to Player');
INSERT INTO `creature_text` VALUES (107712, 3, 0, 'Тише-тише… Все будет хорошо.', 12, 0, 100, 0, 0, 66379, 113271, 0, 0, 0, 'Верина to Player');
INSERT INTO `creature_text` VALUES (107712, 4, 0, 'Спасибо! Не знаю, как и выразить свою благодарность!', 12, 0, 100, 0, 0, 66376, 114305, 0, 0, 0, 'Верина to Player');
INSERT INTO `creature_text` VALUES (107715, 0, 0, 'Наше! Это знание ТОЛЬКО НАШЕ!', 12, 0, 100, 0, 0, 0, 112493, 0, 0, 0, 'Агонизирующий дух to Player');
INSERT INTO `creature_text` VALUES (107715, 1, 0, 'Как ты смеешь осквернять наши священные тексты?', 12, 0, 100, 0, 0, 0, 112497, 0, 0, 0, 'Агонизирующий дух to Player');
INSERT INTO `creature_text` VALUES (107715, 1, 0, 'Наша магия не для иноземцев!', 12, 0, 100, 0, 0, 0, 112489, 0, 0, 0, 'Агонизирующий дух to Player');
INSERT INTO `creature_text` VALUES (107715, 2, 0, 'Магия спасет нас... иначе и быть не может.', 12, 0, 100, 0, 0, 0, 112499, 0, 0, 0, 'Агонизирующий дух to Player');
INSERT INTO `creature_text` VALUES (107716, 0, 0, 'Very well! We will begin. Perhaps something easy to start. Some imps perhaps?', 12, 0, 100, 0, 0, 0, 112636, 0, 0, 0, 'Дайо Дряхлый to Player');
INSERT INTO `creature_text` VALUES (107716, 1, 0, 'Easily defeated but in large numbers deadly. Perhaps something that hits a little harder?', 12, 0, 100, 0, 0, 0, 112690, 0, 0, 0, 'Дайо Дряхлый to Fiendish Trickster');
INSERT INTO `creature_text` VALUES (107716, 2, 0, 'I expected no less from someone acquainted with crippling ice magic. But how about something as skilled in spellcasting as yourself?', 12, 0, 100, 0, 0, 0, 112698, 0, 0, 0, 'Дайо Дряхлый to Empowered Wrathguard');
INSERT INTO `creature_text` VALUES (107716, 3, 0, 'Well done, but Balaadur has been hunting mages for longer most have lived. You must be ready to use all of your abilities to face him.', 12, 0, 100, 0, 0, 0, 112701, 0, 0, 0, 'Дайо Дряхлый to Eredar Mage');
INSERT INTO `creature_text` VALUES (107716, 4, 0, 'For the final piece of the puzzle, a simple stone. A stone from Balaadur\'s personal realm, left here by accident on one of his many ambushes.', 12, 0, 100, 0, 0, 0, 112702, 0, 0, 0, 'Дайо Дряхлый to Eredar Mage');
INSERT INTO `creature_text` VALUES (107716, 5, 0, 'I gave Arrexis a piece so he could attune his ritual to demonic energies, you may have what remains. May it bring better luck to you.', 12, 0, 100, 0, 0, 0, 112787, 0, 0, 0, 'Дайо Дряхлый to Eredar Mage');
INSERT INTO `creature_text` VALUES (107717, 1, 0, 'Не шути с нами!', 12, 0, 100, 0, 0, 66026, 117690, 0, 0, 0, 'Капеллан Легиона to Player');
INSERT INTO `creature_text` VALUES (107717, 1, 0, 'Уничтожить!', 12, 0, 100, 0, 0, 66155, 117686, 0, 0, 0, 'Капеллан Легиона to Player');
INSERT INTO `creature_text` VALUES (107717, 2, 0, 'Нас не остановить...', 12, 0, 100, 0, 0, 66162, 117693, 0, 0, 0, 'Капеллан Легиона to Player');
INSERT INTO `creature_text` VALUES (107717, 2, 0, 'Не может быть...', 12, 0, 100, 0, 0, 66168, 121822, 0, 0, 0, 'Капеллан Легиона to Player');
INSERT INTO `creature_text` VALUES (107717, 3, 0, 'Помогла тебе твоя магия?', 12, 0, 100, 0, 0, 66146, 113072, 58146, 58146, 0, 'Капеллан Легиона to Смелая горожанка');
INSERT INTO `creature_text` VALUES (107717, 3, 0, 'Что тут у нас? Кто-то хочет спрятаться?', 12, 0, 100, 0, 0, 66152, 117675, 93907, 93907, 0, 'Капеллан Легиона');
INSERT INTO `creature_text` VALUES (107720, 0, 0, 'Нас не остановить...', 12, 0, 100, 0, 0, 66112, 117693, 0, 0, 0, 'Сквернокрылый вестник ужаса to Player');
INSERT INTO `creature_text` VALUES (107724, 3, 0, 'Жду тебя наверху. Сообщим новости остальным.', 12, 0, 100, 1, 0, 66850, 112963, 379, 379, 0, 'Кайн Ярость Солнца to Player');
INSERT INTO `creature_text` VALUES (107732, 0, 0, 'Little Moozy is a gorgeous calf.', 12, 0, 100, 0, 0, 0, 112958, 0, 0, 0, 'Ethel Waterwise to Player');
INSERT INTO `creature_text` VALUES (107732, 1, 0, 'Why is everyone always in such a hurry?', 12, 0, 100, 0, 0, 0, 102334, 0, 0, 0, 'Ethel Waterwise to Player');
INSERT INTO `creature_text` VALUES (107732, 3, 0, 'Slow down, $gbull calf:heifer;!', 12, 0, 100, 0, 0, 0, 112945, 0, 0, 0, 'Ethel Waterwise to Player');
INSERT INTO `creature_text` VALUES (107732, 4, 0, 'When do you think Sella will give me more grandchildren?', 12, 0, 100, 0, 0, 0, 112956, 0, 0, 0, 'Ethel Waterwise to Player');
INSERT INTO `creature_text` VALUES (107732, 5, 0, 'Come closer, $r.', 12, 0, 100, 0, 0, 0, 112951, 0, 0, 0, 'Ethel Waterwise to Player');
INSERT INTO `creature_text` VALUES (107732, 6, 0, 'What a nice young $gman:woman;.', 12, 0, 100, 0, 0, 0, 112953, 0, 0, 0, 'Ethel Waterwise to Player');
INSERT INTO `creature_text` VALUES (107732, 12, 0, 'My daughter really is a beautiful woman.', 12, 0, 100, 0, 0, 0, 112955, 0, 0, 0, 'Ethel Waterwise to Player');
INSERT INTO `creature_text` VALUES (107732, 13, 0, 'I guess you\'d rather I fall off the edge of Thunder Totem than escort me.', 12, 0, 100, 0, 0, 0, 112947, 0, 0, 0, 'Ethel Waterwise to Player');
INSERT INTO `creature_text` VALUES (107732, 15, 0, 'What are you waiting for? Let\'s get moving!', 12, 0, 100, 0, 0, 0, 112959, 0, 0, 0, 'Ethel Waterwise to Player');
INSERT INTO `creature_text` VALUES (107732, 16, 0, 'Get where I can see you!', 12, 0, 100, 0, 0, 0, 112950, 0, 0, 0, 'Ethel Waterwise to Player');
INSERT INTO `creature_text` VALUES (107732, 19, 0, 'Have you seen Moozy\'s smile? Gets it from me, I suspect.', 12, 0, 100, 0, 0, 0, 112957, 0, 0, 0, 'Ethel Waterwise to Player');
INSERT INTO `creature_text` VALUES (107732, 24, 0, 'There\'s my Moozy...', 12, 0, 100, 0, 0, 0, 113021, 0, 0, 0, 'Ethel Waterwise to Player');
INSERT INTO `creature_text` VALUES (107736, 0, 0, 'Yes, yes! They grow closer with each moment!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Felcaller Whitley to Player');
INSERT INTO `creature_text` VALUES (107736, 1, 0, 'You will not interrupt my work. The Legion will have this world!', 12, 0, 100, 0, 0, 0, 112590, 0, 0, 0, 'Felcaller Whitley to 0');
INSERT INTO `creature_text` VALUES (107736, 1, 1, 'Our new masters can make us more than mere mortals, don\'t you see?', 12, 0, 100, 0, 0, 0, 112591, 0, 0, 0, 'Felcaller Whitley to 0');
INSERT INTO `creature_text` VALUES (107736, 1, 2, 'The Veiled Hand will rule this shattered world. We herald a new beginning!', 12, 0, 100, 0, 0, 0, 112592, 0, 0, 0, 'Felcaller Whitley to 0');
INSERT INTO `creature_text` VALUES (107736, 1, 3, 'Holy hellfire will rain from the sky and bathe us in the Legion\'s might. We will become unstoppable!', 12, 0, 100, 0, 0, 0, 112595, 0, 0, 0, 'Felcaller Whitley to 0');
INSERT INTO `creature_text` VALUES (107736, 5, 0, 'Usurper. Thief. Scoundrel! You will not deny our glorious destiny!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Felcaller Whitley to 0');
INSERT INTO `creature_text` VALUES (107738, 1, 0, 'Слишком поздно, Варгот уже победил!', 12, 0, 100, 0, 0, 0, 114168, 0, 0, 0, 'Инквизитор Норвир to Player');
INSERT INTO `creature_text` VALUES (107757, 3, 0, 'Ты знаешь, что камень души ночнорожденных позволяет управлять миллионом смертных? Вот будет потеха, когда я заполучу его!', 12, 0, 100, 0, 0, 67656, 113014, 1523, 367070, 0, 'Миллхаус Манашторм to Player');
INSERT INTO `creature_text` VALUES (107757, 4, 0, 'Узрите Миллхауса Великолепного!', 12, 0, 100, 0, 0, 67900, 117406, 1813, 22311, 0, 'Миллхаус Манашторм');
INSERT INTO `creature_text` VALUES (107768, 3, 0, 'Не забывайся, Шекл. $n владеет Клинками Ужаса. Эти проклятые мечи отправили много моряков на тот свет.', 12, 0, 100, 0, 0, 0, 112645, 860927, 860927, 0, '\"Громила\" Джек to Player');
INSERT INTO `creature_text` VALUES (107772, 3, 0, 'Это твое оружие – оно проклято, $n. Сколько моряков оно погубило на моем веку, кто бы знал. Осторожнее с ним.', 12, 0, 100, 1, 0, 0, 112655, 507350, 507350, 0, 'Старик Тоомба to Player');
INSERT INTO `creature_text` VALUES (107804, 0, 0, 'Это то самое место. Но тут полным-полно мурлоков.', 12, 0, 100, 1, 0, 0, 112741, 0, 0, 0, 'Нат Пэгл to Player');
INSERT INTO `creature_text` VALUES (107804, 1, 0, 'Если избавиться от этих мурлоков, весь день сможем рыбачить без хлопот.', 12, 0, 100, 1, 0, 0, 112742, 0, 0, 0, 'Нат Пэгл to Player');
INSERT INTO `creature_text` VALUES (107804, 2, 0, 'У тебя получилось, $p! Давай-ка выловим этих бешенок!', 14, 0, 100, 4, 0, 0, 112829, 0, 0, 0, 'Нат Пэгл to 1025');
INSERT INTO `creature_text` VALUES (107804, 3, 0, 'Пока я бегал и ловил бешенок, заметил большие пузыри в этой лагуне.', 12, 0, 100, 0, 0, 0, 113270, 0, 0, 0, 'Нат Пэгл to Player');
INSERT INTO `creature_text` VALUES (107804, 4, 0, 'Интересно, с чего это они так переполошились. Наверное, их что-то испугало. Посмотрим, получится ли у меня выманить это нечто.', 12, 0, 100, 1, 0, 0, 113274, 0, 0, 0, 'Нат Пэгл to Player');
INSERT INTO `creature_text` VALUES (107804, 5, 0, 'Ну конечно! Там внизу определенно что-то есть. Будем ловить, $p.', 12, 0, 100, 1, 0, 0, 113275, 0, 0, 0, 'Нат Пэгл to Player');
INSERT INTO `creature_text` VALUES (107804, 6, 0, 'Это что еще такое?!', 14, 0, 100, 25, 0, 0, 113283, 0, 0, 0, 'Нат Пэгл to Player');
INSERT INTO `creature_text` VALUES (107804, 7, 0, 'Скорее! Давайте огнем жгите или... в общем, убейте хоть чем-нибудь!', 14, 0, 100, 430, 0, 0, 113285, 0, 0, 0, 'Нат Пэгл to Player');
INSERT INTO `creature_text` VALUES (107804, 8, 0, 'Вот это зверь! И к тому же отличный улов.', 12, 0, 100, 4, 0, 0, 113789, 0, 0, 0, 'Нат Пэгл to Player');
INSERT INTO `creature_text` VALUES (107804, 9, 0, 'Знаешь, из этой штуки у него на голове получится отличная удочка...', 12, 0, 100, 6, 0, 0, 113792, 0, 0, 0, 'Нат Пэгл to Player');
INSERT INTO `creature_text` VALUES (107804, 10, 0, 'Да, знаю, это мерзко, но ты только взгляни! На такое рыба набрасывается целыми косяками.', 12, 0, 100, 0, 0, 0, 113793, 0, 0, 0, 'Нат Пэгл to Player');
INSERT INTO `creature_text` VALUES (107804, 11, 0, 'Вот, возьми. Отнеси Марсии в Даларан. Она умеет делать отличные удочки.', 12, 0, 100, 1, 0, 0, 113794, 0, 0, 0, 'Нат Пэгл to Player');
INSERT INTO `creature_text` VALUES (107804, 12, 0, 'Можешь вернуться на моей лодке, если других способов нет.', 12, 0, 100, 25, 0, 0, 113795, 0, 0, 0, 'Нат Пэгл to Player');
INSERT INTO `creature_text` VALUES (107806, 0, 0, 'Наконец-то Стромгард мой! Думали, я отдам его своему отцу? Болваны!', 12, 0, 100, 274, 0, 69395, 113581, 0, 0, 0, 'Принц Гален Троллебой to Player');
INSERT INTO `creature_text` VALUES (107806, 1, 0, 'Стража! Взять их!', 14, 0, 100, 0, 0, 69396, 113582, 0, 0, 0, 'Принц Гален Троллебой to Player');
INSERT INTO `creature_text` VALUES (107829, 3, 0, 'Squad reporting for duty!', 12, 0, 100, 66, 0, 0, 113114, 14466, 14466, 0, 'Рекрут Альянса to Вербовщик Гирбернс');
INSERT INTO `creature_text` VALUES (107837, 0, 0, 'All goes as planned. Once brother Shadowgaze arrives we leave for Stormwind!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Commander Althea Ebonlocke to Player');
INSERT INTO `creature_text` VALUES (107837, 1, 0, 'We will not be stopped!', 12, 0, 100, 0, 0, 0, 15166, 0, 0, 0, 'Commander Althea Ebonlocke to 0');
INSERT INTO `creature_text` VALUES (107837, 1, 1, 'The Herald has promised us freedom and respect - we will not be denied!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Commander Althea Ebonlocke to 0');
INSERT INTO `creature_text` VALUES (107837, 3, 0, 'You will not interfere. The Legion will rise!', 12, 0, 100, 0, 0, 0, 113017, 0, 0, 0, 'Commander Althea Ebonlocke to 0');
INSERT INTO `creature_text` VALUES (107848, 0, 0, 'С удовольствием.', 12, 0, 100, 0, 0, 66484, 122527, 0, 0, 0, 'Леди Киранис to Верховный маг Варгот');
INSERT INTO `creature_text` VALUES (107848, 1, 0, 'Познай истинную мощь!', 12, 0, 100, 0, 0, 56981, 105125, 0, 0, 0, 'Леди Киранис to Player');
INSERT INTO `creature_text` VALUES (107850, 1, 0, 'Это мое место! <Жрать давай!>', 12, 0, 100, 0, 0, 54351, 102762, 0, 0, 0, 'Горный эттин to Player');
INSERT INTO `creature_text` VALUES (107859, 1, 0, 'More bones for the Legion!', 12, 0, 100, 0, 0, 0, 112978, 0, 0, 218111, 'Шеф-повар Груаль');
INSERT INTO `creature_text` VALUES (107859, 1, 0, 'Больше костей для Легиона!', 12, 0, 100, 0, 0, 0, 112978, 0, 0, 218111, 'Шеф-повар Груаль');
INSERT INTO `creature_text` VALUES (107859, 2, 0, 'You are... too late...', 12, 0, 100, 0, 0, 0, 112981, 0, 0, 218167, 'Шеф-повар Груаль to Player');
INSERT INTO `creature_text` VALUES (107859, 2, 0, 'Ты... слишком поздно...', 12, 0, 100, 0, 0, 0, 112981, 0, 0, 218167, 'Шеф-повар Груаль to Player');
INSERT INTO `creature_text` VALUES (107866, 3, 0, 'I heard something over here.', 12, 0, 100, 397, 0, 63555, 125447, 178770, 178770, 0, 'Дозорный Фрейзер to Player');
INSERT INTO `creature_text` VALUES (107866, 3, 0, 'There\'s someone here.', 12, 0, 100, 396, 0, 0, 112969, 76609, 76609, 0, 'Watcher Fraizer to Player');
INSERT INTO `creature_text` VALUES (107872, 3, 0, 'Что это за звук?', 12, 0, 100, 6, 0, 0, 112908, 195259, 195259, 0, 'Дозорная Ройс to Player');
INSERT INTO `creature_text` VALUES (107875, 3, 0, 'Did you hear that?', 12, 0, 100, 6, 0, 0, 132099, 70921, 70921, 0, 'Watcher Bukouris to Player');
INSERT INTO `creature_text` VALUES (107875, 3, 0, 'We aren\'t alone, brothers and sisters.', 12, 0, 100, 396, 0, 0, 112970, 98639, 98639, 0, 'Дозорная Бакурис to Player');
INSERT INTO `creature_text` VALUES (107879, 3, 0, 'I heard something over here.', 12, 0, 100, 397, 0, 0, 125447, 69369, 69369, 0, 'Watcher Petras to Player');
INSERT INTO `creature_text` VALUES (107926, 1, 0, 'А, свежее мяско!', 12, 0, 100, 0, 0, 0, 112917, 0, 0, 0, 'Ганнвал Мясник to Player');
INSERT INTO `creature_text` VALUES (107926, 4, 0, 'I will devour ... your heart ...', 12, 0, 100, 0, 0, 0, 112918, 41404, 41404, 0, 'Ганнвал Мясник to 28297');
INSERT INTO `creature_text` VALUES (107931, 0, 0, 'I cannot find your papers, citizen.', 12, 0, 0, 0, 0, 0, 113096, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (107933, 0, 0, 'Удачи тебе!', 12, 0, 100, 0, 0, 0, 127447, 0, 0, 0, 'Вербовщик Гирбернс to Рекрут Альянса');
INSERT INTO `creature_text` VALUES (107933, 3, 0, 'Штормград ценит верную службу!', 12, 0, 100, 66, 0, 0, 113100, 17913, 17913, 0, 'Вербовщик Гирбернс to Рекрут Альянса');
INSERT INTO `creature_text` VALUES (107934, 0, 0, 'I can help you over here, $n.', 12, 0, 0, 0, 0, 0, 112937, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (107935, 0, 0, 'Excellent, the third fleet ships are loading up at the end of the dock.', 12, 0, 0, 0, 0, 0, 113881, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (107959, 0, 0, 'Глупые наземники! Убирайтесь!', 12, 0, 100, 0, 0, 67498, 113069, 0, 0, 0, 'Орафисса to Player');
INSERT INTO `creature_text` VALUES (107959, 1, 0, 'Ешьте их кости, дети мои!', 12, 0, 100, 0, 0, 67494, 118589, 0, 0, 0, 'Орафисса to Player');
INSERT INTO `creature_text` VALUES (107959, 2, 0, 'Грр… Паразиты…', 12, 0, 100, 0, 0, 67500, 113076, 0, 0, 0, 'Орафисса to Player');
INSERT INTO `creature_text` VALUES (107959, 3, 0, 'Аркан\'дор возродится! Его сила поможет нам вернуться на поверхность!', 12, 0, 100, 0, 0, 67493, 118590, 0, 0, 0, 'Орафисса to 0');
INSERT INTO `creature_text` VALUES (107959, 4, 0, 'Никто не выскользнет из моих сетей!', 12, 0, 100, 0, 0, 67495, 118591, 0, 0, 0, 'Орафисса to Player');
INSERT INTO `creature_text` VALUES (107959, 6, 0, 'Моя награда… мое бремя…', 12, 0, 100, 0, 0, 67496, 118592, 0, 0, 0, 'Орафисса to Player');
INSERT INTO `creature_text` VALUES (107962, 0, 0, 'As I fell, I saw one of the nighfallen slinking into our caves.', 12, 0, 100, 463, 0, 0, 114497, 0, 0, 0, 'Цедону to Player');
INSERT INTO `creature_text` VALUES (107963, 0, 0, 'You come here, at this hour, and ask for such a thing? Leave me be, hunter?', 12, 0, 0, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (107963, 1, 0, 'За что нам все эти несчастья?', 12, 0, 100, 0, 0, 0, 114496, 0, 0, 0, 'Хармира <Чародейка> to player');
INSERT INTO `creature_text` VALUES (107964, 0, 0, 'Thank you, little $c. Senegos was wise to trust in you.', 12, 0, 100, 0, 0, 0, 114495, 0, 0, 0, 'Бераз to Player');
INSERT INTO `creature_text` VALUES (107966, 0, 0, 'Благодарю за гостеприимство. Очень жаль, что повод для нашей встречи такой безрадостный.', 12, 0, 100, 1, 0, 66560, 114929, 0, 0, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (107966, 1, 0, 'Спасибо вам.', 12, 0, 100, 1, 0, 66561, 114930, 0, 0, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (107968, 0, 0, 'Иллидари! Этот бес стащил гримуар! Поймай его!', 12, 0, 100, 0, 0, 56935, 113077, 0, 0, 0, 'Верховный маг Лан\'далок to Player');
INSERT INTO `creature_text` VALUES (107972, 3, 0, 'Assassin! Run for your lives!', 12, 0, 100, 0, 0, 0, 113022, 192127, 192127, 0, 'Лорд Элло Чернодрев to Командир Алтея Чернодрев');
INSERT INTO `creature_text` VALUES (107972, 4, 0, 'Killer! Спасайся, кто может!', 12, 0, 100, 0, 0, 0, 113022, 2328, 2328, 0, 'Лорд Элло Чернодрев to Командир Алтея Чернодрев');
INSERT INTO `creature_text` VALUES (107973, 0, 0, 'Вперед, к сторожевой башне.', 12, 0, 100, 1, 0, 66580, 112989, 0, 0, 0, 'Эммарель Стражница Тени to Player');
INSERT INTO `creature_text` VALUES (107973, 1, 0, 'Верховный маг, мы заметили огни. Вам нужна поддержка?', 12, 0, 100, 1, 0, 66581, 113019, 0, 0, 0, 'Эммарель Стражница Тени to Player');
INSERT INTO `creature_text` VALUES (107973, 2, 0, 'Незримый путь готов предоставить любую помощь.', 12, 0, 100, 1, 0, 66582, 113207, 0, 0, 0, 'Эммарель Стражница Тени to Player');
INSERT INTO `creature_text` VALUES (107981, 0, 0, 'Ну что, найдем пару косточек?', 12, 0, 100, 1, 0, 66584, 113247, 0, 0, 0, 'Ловчий Блейк to Player');
INSERT INTO `creature_text` VALUES (107984, 3, 0, 'Нельзя спорить с Одином. Он сказал, ты ведешь, значит, ты ведешь.', 12, 0, 100, 66, 0, 69369, 120496, 9872, 9872, 0, 'Рагнвальд Драконорожденный to Player');
INSERT INTO `creature_text` VALUES (107985, 3, 0, 'Враги содрогнутся!', 12, 0, 100, 15, 0, 69561, 120479, 9386, 9386, 0, 'Финна Бьорнсдоттир to Player');
INSERT INTO `creature_text` VALUES (107986, 3, 0, 'Как хорошо снова жить и сражаться! Нас ждет славная битва!', 12, 0, 100, 1, 0, 69370, 120498, 6110, 6110, 0, 'Сверган Покров Шторма to Player');
INSERT INTO `creature_text` VALUES (107987, 0, 0, 'Воины Ульдуара, услышьте Одина!', 14, 0, 100, 22, 0, 68390, 119679, 0, 0, 0, 'Химдалль to Player');
INSERT INTO `creature_text` VALUES (107995, 0, 0, 'Смотри! Они идут к озерцу дедушки!', 12, 0, 100, 0, 0, 63226, 106024, 0, 0, 0, 'Стеллагоса to Player');
INSERT INTO `creature_text` VALUES (107995, 1, 0, 'It\'s not too late. We\'ll just jump the mountains to cut them off, and...', 12, 0, 100, 0, 0, 63227, 113250, 0, 0, 0, 'Стеллагоса to Player');
INSERT INTO `creature_text` VALUES (107995, 2, 0, 'No, no... they\'re everywhere!', 12, 0, 100, 0, 0, 63228, 113251, 0, 0, 0, 'Стеллагоса to Player');
INSERT INTO `creature_text` VALUES (107995, 3, 0, 'Hang on, $n. We\'re going in low.', 12, 0, 100, 0, 0, 63229, 113253, 0, 0, 0, 'Стеллагоса to Player');
INSERT INTO `creature_text` VALUES (107997, 0, 0, 'Так, посмотрим…', 12, 0, 100, 0, 0, 67572, 113182, 0, 0, 0, 'Силгрин to Player');
INSERT INTO `creature_text` VALUES (107997, 3, 0, 'Силгрин быстро перелистывает пачку писем.', 16, 0, 100, 0, 0, 0, 113183, 920, 920, 0, 'Силгрин to Player');
INSERT INTO `creature_text` VALUES (108003, 0, 0, 'You won\'t leave? Very well, I\'ll end this chase myself!', 14, 0, 100, 0, 0, 0, 113120, 0, 0, 0, 'Verstok Darkbough to Player');
INSERT INTO `creature_text` VALUES (108003, 1, 0, 'The Fangs are mine! MINE!!', 12, 0, 100, 0, 0, 0, 113090, 0, 0, 0, 'Verstok Darkbough to Player');
INSERT INTO `creature_text` VALUES (108003, 2, 0, 'No! I\'ll never submit!', 14, 0, 100, 0, 0, 0, 113121, 0, 0, 0, 'Verstok Darkbough to 0');
INSERT INTO `creature_text` VALUES (108004, 1, 0, 'Ощути холод могилы!', 12, 0, 100, 0, 0, 66046, 116424, 0, 0, 0, 'Призрачный защитник to Player');
INSERT INTO `creature_text` VALUES (108004, 2, 0, 'I have found peace.', 12, 0, 100, 0, 0, 66047, 102391, 0, 0, 0, 'Призрачный защитник to Player');
INSERT INTO `creature_text` VALUES (108008, 0, 0, 'Вон она!', 12, 0, 100, 0, 0, 67499, 113189, 0, 0, 0, 'Странник дола Фародин to Player');
