INSERT INTO `creature_text` VALUES (108008, 3, 0, 'Уже близко.', 12, 0, 100, 0, 0, 67535, 113654, 0, 0, 0, 'Странник дола Фародин to Player');
INSERT INTO `creature_text` VALUES (108008, 4, 0, 'Я знаю о твоих попытках восстановить телепорты. Этот маяк выведет нас на твоих друзей-шал\'дорай.', 12, 0, 100, 0, 0, 67536, 113655, 0, 0, 0, 'Странник дола Фародин to Player');
INSERT INTO `creature_text` VALUES (108018, 3, 0, 'Я могу сделать многое для усиления оплота класса, однако придется принять несколько непростых решений.', 12, 0, 100, 1, 0, 0, 115427, 39134, 39134, 0, 'Архивариус Мелинда to Player');
INSERT INTO `creature_text` VALUES (108044, 0, 0, 'Стромгард принадлежит мне! Я не отдам его ни отцу, ни кому другому!', 12, 0, 100, 22, 0, 69393, 113248, 0, 0, 0, 'Принц Гален Троллебой to Player');
INSERT INTO `creature_text` VALUES (108044, 1, 0, 'I am a prince! How dare you...', 12, 0, 100, 0, 0, 69394, 113249, 0, 0, 0, 'Принц Гален Троллебой to Player');
INSERT INTO `creature_text` VALUES (108051, 0, 0, 'Hurry up an\' finish blessin\' those bandages, we need to get them off to the front!', 12, 0, 0, 0, 0, 0, 113320, 0, 0, 0, '');
