INSERT INTO `creature_text` VALUES (108059, 0, 0, 'Sir, yes, sir! Nobody enters the city on our watch.', 14, 0, 100, 0, 0, 0, 113206, 0, 0, 0, 'Stormwind City Guard to Player');
INSERT INTO `creature_text` VALUES (108059, 0, 1, 'Blades at the ready!', 14, 0, 100, 0, 0, 0, 113223, 0, 0, 0, 'Stormwind City Guard to Player');
INSERT INTO `creature_text` VALUES (108059, 0, 2, 'I heard something.', 12, 0, 100, 0, 0, 0, 114094, 0, 0, 0, 'Stormwind City Guard to Player');
INSERT INTO `creature_text` VALUES (108059, 0, 3, 'By the Light, what was that?', 12, 0, 100, 0, 0, 0, 114100, 0, 0, 0, 'Stormwind City Guard to Player');
INSERT INTO `creature_text` VALUES (108059, 0, 4, 'There\'s an intruder nearby.', 12, 0, 100, 0, 0, 0, 114101, 0, 0, 0, 'Stormwind City Guard to Player');
INSERT INTO `creature_text` VALUES (108059, 0, 5, 'I\'ll find you in the king\'s name!', 12, 0, 100, 0, 0, 0, 114099, 0, 0, 0, 'Stormwind City Guard to Player');
INSERT INTO `creature_text` VALUES (108059, 0, 6, 'Show yourself, cretin!', 12, 0, 100, 0, 0, 0, 114102, 0, 0, 0, 'Stormwind City Guard to Player');
INSERT INTO `creature_text` VALUES (108063, 0, 0, 'Мамочка!', 12, 0, 100, 0, 0, 66227, 114304, 0, 0, 0, 'Корина to Player');
INSERT INTO `creature_text` VALUES (108063, 1, 0, 'Они кричали и запихнули меня в клетку… Я так испугалась!', 12, 0, 100, 0, 0, 66251, 113269, 0, 0, 0, 'Корина to Player');
INSERT INTO `creature_text` VALUES (108068, 0, 0, 'I can go home!', 12, 0, 0, 0, 0, 0, 114055, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (108068, 0, 1, 'I did not know if I would make it out of this alive. Thank you.', 12, 0, 0, 0, 0, 0, 114054, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (108068, 0, 2, 'I have to check on my husband. Thank you!', 12, 0, 0, 0, 0, 0, 114058, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (108068, 0, 3, 'I was innocent, but they didn\'t listen...', 12, 0, 0, 0, 0, 0, 114056, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (108068, 0, 4, 'I\'m free! I\'m really free!', 12, 0, 0, 0, 0, 0, 42584, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (108068, 0, 5, 'That is the last time I complain about the demons!', 12, 0, 0, 0, 0, 0, 114057, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (108070, 3, 0, 'I am free! I am truly free!', 12, 0, 100, 0, 0, 0, 114059, 16792, 16792, 0, 'Осужденная жительница Сурамара to Player');
INSERT INTO `creature_text` VALUES (108070, 3, 0, 'It’s not my fault, but they didn’t listen ...', 12, 0, 100, 0, 0, 0, 114056, 58575, 58575, 0, 'Осужденная жительница Сурамара to Player');
INSERT INTO `creature_text` VALUES (108070, 3, 0, 'Я могу вернуться домой!', 12, 0, 100, 0, 0, 0, 114055, 71660, 71660, 0, 'Осужденная жительница Сурамара to Player');
INSERT INTO `creature_text` VALUES (108071, 0, 0, 'Сейчас я поджарю твои ноги!', 12, 0, 100, 0, 0, 55209, 96660, 0, 0, 0, 'Подлый бес to Player');
INSERT INTO `creature_text` VALUES (108075, 0, 0, 'I want to see my mom ...', 12, 0, 100, 0, 0, 66239, 113217, 0, 0, 0, 'Корина to Player');
INSERT INTO `creature_text` VALUES (108075, 1, 0, 'АЙ! НЕТ!', 12, 0, 100, 0, 0, 66240, 113218, 0, 0, 0, 'Корина to Player');
INSERT INTO `creature_text` VALUES (108075, 1, 0, 'Что ты делаешь?', 12, 0, 100, 0, 0, 66237, 127524, 0, 0, 0, 'Корина to Player');
INSERT INTO `creature_text` VALUES (108075, 2, 0, '...А-а!', 12, 0, 100, 0, 0, 66250, 113246, 0, 0, 0, 'Корина to Player');
INSERT INTO `creature_text` VALUES (108075, 4, 0, 'I ... * sob * know this street. Like...', 12, 0, 100, 0, 0, 66242, 113220, 0, 0, 0, 'Корина to Player');
INSERT INTO `creature_text` VALUES (108075, 5, 0, 'Мм-м... Да. ДА! Это дом Вантира!', 12, 0, 100, 0, 0, 66243, 113221, 0, 0, 0, 'Корина to Player');
INSERT INTO `creature_text` VALUES (108075, 6, 0, 'Пока!', 12, 0, 100, 0, 0, 66244, 116996, 0, 0, 0, 'Корина to Player');
INSERT INTO `creature_text` VALUES (108075, 7, 0, 'Мамочка!', 12, 0, 100, 0, 0, 0, 114304, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (108080, 0, 0, 'Не перелетим, так пробьем. Откройте ворота.', 12, 0, 100, 25, 0, 66852, 113538, 0, 0, 0, 'Кайн Ярость Солнца to Player');
INSERT INTO `creature_text` VALUES (108080, 1, 0, 'Мы только хотим поговорить, Акама.', 12, 0, 100, 1, 0, 66903, 114038, 0, 0, 0, 'Кайн Ярость Солнца to 0');
INSERT INTO `creature_text` VALUES (108080, 2, 0, 'Убей этого пса, $p! Хотя… постой. Есть одна идея.', 14, 0, 100, 0, 0, 66896, 113553, 0, 0, 0, 'Кайн Ярость Солнца to Акама');
INSERT INTO `creature_text` VALUES (108080, 3, 0, 'Даже не думай об этом. Аллари, действуй!', 12, 0, 100, 1, 0, 66899, 113557, 0, 0, 0, 'Кайн Ярость Солнца to Player');
INSERT INTO `creature_text` VALUES (108080, 4, 0, 'Злоба, поднимись к нам. Мы возвращаемся на \"Молот Скверны\".', 14, 0, 100, 22, 0, 66902, 113560, 0, 0, 0, 'Кайн Ярость Солнца to Акама');
INSERT INTO `creature_text` VALUES (108085, 0, 0, 'Миллхаус, у тебя настоящий талант ввязываться в неприятности!', 12, 0, 100, 0, 0, 67659, 113231, 0, 0, 0, 'Проекция Кейлека to Миллхаус Манашторм');
INSERT INTO `creature_text` VALUES (108085, 1, 0, 'Верховный маг Варгот тоже ищет камень души ночнорожденных. Вы должны объединить усилия и опередить его.', 12, 0, 100, 0, 0, 67660, 113232, 0, 0, 0, 'Проекция Кейлека to Миллхаус Манашторм');
INSERT INTO `creature_text` VALUES (108086, 3, 0, 'Барон бежит обратно в лагерь, виляя хвостом.', 16, 0, 100, 0, 0, 0, 114068, 77119, 77119, 0, 'Барон to Player');
INSERT INTO `creature_text` VALUES (108090, 0, 0, 'Вижу, вы не отступитесь. Так тому и быть, иллидари. Готовьтесь к смерти.', 14, 0, 100, 25, 0, 66891, 113543, 0, 0, 0, 'Акама to Player');
INSERT INTO `creature_text` VALUES (108090, 1, 0, 'Мне рассказывали о тебе, $p. Но не могу сказать, что я впечатлен.', 12, 0, 100, 0, 0, 66892, 113544, 0, 0, 0, 'Акама to Player');
INSERT INTO `creature_text` VALUES (108090, 2, 0, 'Моя очередь удивить тебя.', 12, 0, 100, 0, 0, 66893, 113545, 0, 0, 0, 'Акама to Player');
INSERT INTO `creature_text` VALUES (108090, 3, 0, 'Покончим с этим. Ты же знаешь, что такое честь, иллидари?', 12, 0, 100, 0, 0, 66894, 113546, 0, 0, 0, 'Акама to Player');
INSERT INTO `creature_text` VALUES (108090, 4, 0, 'Небеса, явитесь!', 14, 0, 100, 0, 0, 68821, 118783, 0, 0, 0, 'Акама to 0');
INSERT INTO `creature_text` VALUES (108090, 5, 0, 'Тучи сгущаются…', 14, 0, 100, 0, 0, 68817, 118782, 0, 0, 0, 'Акама to Player');
INSERT INTO `creature_text` VALUES (108090, 7, 0, 'Неплохо. Но твой мертвый повелитель был сильнее.', 12, 0, 100, 0, 0, 61929, 113937, 0, 0, 0, 'Акама to 0');
INSERT INTO `creature_text` VALUES (108090, 11, 0, 'Скажи, иллидари… тебе понравилось сидеть взаперти столько лет?', 12, 0, 100, 0, 0, 66889, 113938, 0, 0, 0, 'Акама to 0');
INSERT INTO `creature_text` VALUES (108090, 12, 0, '|TInterface\\Icons\\shaman_pvp_lightninglasso:20|t%s произносит заклинание |cFFFF0000|Hspell:220101|h[\"Смертоносная молния\"]|h|r! Прервите его!', 41, 0, 100, 0, 0, 0, 118755, 0, 0, 0, 'Акама to Акама');
INSERT INTO `creature_text` VALUES (108090, 15, 0, 'Это невозможно.', 12, 0, 100, 0, 0, 66890, 113939, 0, 0, 0, 'Акама to 0');
INSERT INTO `creature_text` VALUES (108090, 16, 0, 'Хватит… Прикончи меня.', 12, 0, 100, 0, 0, 66898, 113556, 0, 0, 0, 'Акама to Player');
INSERT INTO `creature_text` VALUES (108090, 17, 0, 'Нет...', 12, 0, 100, 0, 0, 66853, 141636, 0, 0, 0, 'Акама to Акама');
INSERT INTO `creature_text` VALUES (108096, 0, 0, 'Отойди от ребенка!', 12, 0, 100, 463, 0, 66321, 113259, 0, 0, 0, 'Послушник из Сумеречной стражи to Player');
INSERT INTO `creature_text` VALUES (108097, 0, 0, 'You\'ve done it, I can feel arcane levels rising in the area. The demons have pulled back temporarily as well, I think they intend to really hit us hard.', 12, 0, 100, 0, 0, 0, 113260, 0, 0, 0, 'Мерил Буря Скверны to Player');
INSERT INTO `creature_text` VALUES (108097, 1, 0, 'This next part will be complicated. We will need to start the ritual here to draw all that power. While we do that, we will most likely be attacked.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Мерил Буря Скверны to Player');
INSERT INTO `creature_text` VALUES (108097, 2, 0, 'We\'ll have to stop casting to fight off the attackers, but will need to get back to it when we can. Once we are close, that is when I expect to be ambushed by Balaadur.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Мерил Буря Скверны to Player');
INSERT INTO `creature_text` VALUES (108097, 3, 0, 'Prepare yourself. When you are ready, speak to me and we can begin the incantations.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Мерил Буря Скверны to Player');
INSERT INTO `creature_text` VALUES (108097, 4, 0, 'Good! It looks like we are about to be attacked as well. Begin quickly!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Мерил Буря Скверны to Player');
INSERT INTO `creature_text` VALUES (108097, 5, 0, 'They are coming, I may need some help!', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Мерил Буря Скверны to Player');
INSERT INTO `creature_text` VALUES (108097, 6, 0, 'Above you, its forming!', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Мерил Буря Скверны to 1122');
INSERT INTO `creature_text` VALUES (108097, 7, 0, 'We are close! Can you feel it?!', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Мерил Буря Скверны to 1122');
INSERT INTO `creature_text` VALUES (108133, 3, 0, 'О, Громила, да у тебя компания!', 14, 0, 100, 0, 0, 0, 113369, 259188, 259188, 0, 'Канонир с \"Черного Паруса\" to Player');
INSERT INTO `creature_text` VALUES (108136, 1, 0, 'Букашка.... Громила сокрушит тебя!', 12, 0, 100, 0, 0, 0, 123253, 0, 0, 0, 'Громила to Player');
INSERT INTO `creature_text` VALUES (108136, 3, 0, 'Громила задаст им хорошую взбучку!', 14, 0, 100, 0, 0, 0, 113370, 102681, 102681, 0, 'Громила to Player');
INSERT INTO `creature_text` VALUES (108138, 3, 0, 'Покидая дом, не забывайте о символике! Покупайте гербовые накидки!', 12, 0, 100, 603, 0, 0, 113299, 50961, 50961, 0, 'Ребекка Лафлин to Player');
INSERT INTO `creature_text` VALUES (108138, 3, 0, 'Покупайте гербовые накидки! Пусть все видят, за кого вы сражаетесь!', 12, 0, 100, 603, 0, 0, 113296, 99981, 99981, 0, 'Ребекка Лафлин to Player');
INSERT INTO `creature_text` VALUES (108148, 0, 0, 'Ну, посмотрим, какой у тебя нюх.', 12, 0, 100, 1, 0, 66572, 113289, 0, 0, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (108148, 1, 0, 'Молодец! Пойдем поищем магов!', 12, 0, 100, 603, 0, 66573, 113290, 0, 0, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (108148, 2, 0, 'Смотри! Вон наши маги! И их окружили прислужники псаря.', 12, 0, 100, 25, 0, 66563, 113357, 0, 0, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (108148, 3, 0, 'Скорей! Убьем чудищ, пока они никого не сожрали.', 12, 0, 100, 1, 0, 66564, 113358, 0, 0, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (108148, 4, 0, 'Мы остановим тебя, Хаккар!', 14, 0, 100, 22, 0, 66571, 113371, 0, 0, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (108168, 0, 0, 'This display is meaningless! Your doom is at hand!', 14, 0, 100, 0, 0, 0, 113373, 0, 0, 0, 'Balaadur to 0');
INSERT INTO `creature_text` VALUES (108168, 4, 0, 'You have the strength of an archmage, but I have a few more tricks to show you...', 14, 0, 100, 0, 0, 0, 113374, 0, 0, 0, 'Balaadur to 0');
INSERT INTO `creature_text` VALUES (108174, 3, 0, 'Уф, наработался... пора и отдохнуть.', 12, 0, 100, 0, 0, 0, 113337, 5700, 5700, 0, 'Трюмная крыса');
INSERT INTO `creature_text` VALUES (108183, 3, 0, 'Никто не смеет мешать кормлению!', 14, 0, 100, 396, 0, 66565, 113362, 2143, 2143, 0, 'Псарь Хаккар to Player');
INSERT INTO `creature_text` VALUES (108186, 3, 0, 'Мы так никогда план не выполним. Живее шевелитесь, олухи!', 12, 0, 100, 0, 0, 0, 113359, 90266, 90266, 0, 'Интендант Золикс');
INSERT INTO `creature_text` VALUES (108186, 3, 0, 'Шекл не обрадуется, если мы провозимся долго! Давайте-ка поторапливайтесь с погрузкой!', 12, 0, 100, 0, 0, 0, 113360, 432455, 432455, 0, 'Интендант Золикс');
INSERT INTO `creature_text` VALUES (108188, 0, 0, 'Кто ты?', 12, 0, 100, 11, 0, 65802, 118520, 0, 0, 0, 'Имперский чаротворец to Player');
INSERT INTO `creature_text` VALUES (108188, 1, 0, 'Смерть идет за тобой!', 12, 0, 100, 274, 0, 65814, 116703, 0, 0, 0, 'Имперский чаротворец to Player');
INSERT INTO `creature_text` VALUES (108188, 3, 0, 'Что-то тут не так...', 12, 0, 100, 0, 0, 65803, 124630, 13382, 13382, 0, 'Имперский чаротворец to Player');
INSERT INTO `creature_text` VALUES (108188, 3, 0, 'Это иллюзия! Что ты скрываешь?', 12, 0, 100, 0, 0, 65819, 124629, 3058, 3058, 0, 'Имперский чаротворец to Player');
INSERT INTO `creature_text` VALUES (108190, 1, 0, 'Не щадить чужеземцев!', 12, 0, 100, 25, 0, 65710, 116701, 0, 0, 0, 'Ищейка Великого магистра to Player');
INSERT INTO `creature_text` VALUES (108190, 3, 0, 'Кто ты?', 12, 0, 100, 25, 0, 66005, 118520, 198716, 198716, 0, 'Ищейка Великого магистра to Player');
INSERT INTO `creature_text` VALUES (108197, 3, 0, 'The Withered Leyhunter detects your presence!', 16, 0, 100, 0, 0, 54959, 113375, 249395, 249395, 0, 'Withered Leyhunter to Player');
INSERT INTO `creature_text` VALUES (108200, 0, 0, 'Сейчас, дорогуша. Врата уже открываются.', 12, 0, 100, 1, 0, 66886, 113555, 0, 0, 0, 'Верховная мать Злоба to Акама');
INSERT INTO `creature_text` VALUES (108213, 0, 0, 'С удовольствием.', 12, 0, 100, 396, 0, 66900, 122527, 0, 0, 0, 'Аллари Пожирательница Душ to Player');
INSERT INTO `creature_text` VALUES (108214, 0, 0, 'Какой хаос, залюбуешься! Ну-ка заглянем в карманы паре стражей. Глядишь, найдем среди них сектантов!', 12, 0, 100, 603, 0, 15003, 116241, 0, 0, 0, 'Гарона Полуорчиха to 0');
INSERT INTO `creature_text` VALUES (108220, 0, 0, 'I\'m alive again!', 14, 0, 100, 15, 0, 66901, 113559, 0, 0, 0, 'Тень Акамы to Акама');
INSERT INTO `creature_text` VALUES (108230, 0, 0, 'Тебе не уйти от судьбы!', 12, 0, 100, 0, 0, 52484, 98060, 0, 0, 0, 'Мистик из клана Говорящих с костями to Player');
INSERT INTO `creature_text` VALUES (108230, 0, 1, 'Короли отвергнут тебя!', 12, 0, 100, 0, 0, 52487, 98058, 0, 0, 0, 'Мистик из клана Говорящих с костями to Player');
INSERT INTO `creature_text` VALUES (108230, 2, 0, 'Тебе не победить...', 12, 0, 100, 0, 0, 70425, 98067, 0, 0, 0, 'Мистик из клана Говорящих с костями to Сверган Покров Шторма');
INSERT INTO `creature_text` VALUES (108242, 0, 0, 'Щенок гончей Скверны, почуяв запах магии, бросается в пещеру.', 16, 0, 100, 0, 0, 0, 113690, 0, 0, 0, 'Щенок гончей Скверны to Player');
INSERT INTO `creature_text` VALUES (108244, 0, 0, 'Странники будут сражаться вместе с тобой против Хаккара!', 12, 0, 100, 1, 0, 66586, 113890, 0, 0, 0, 'Халдарон Светлое Крыло to Player');
INSERT INTO `creature_text` VALUES (108245, 0, 0, 'Часовые помогут тебе. Вместе мы одолеем псаря.', 12, 0, 100, 1, 0, 66618, 113893, 0, 0, 0, 'Шандриса Оперенная Луна to Player');
INSERT INTO `creature_text` VALUES (108247, 0, 0, 'Встретимся в Покое. Удачного тебе пути.', 12, 0, 100, 603, 0, 69281, 118904, 0, 0, 0, 'Верховный маг Кейлек to Player');
INSERT INTO `creature_text` VALUES (108250, 3, 0, 'Я знаю все, что знал Акама, и даже больше. Я помогу тебе уничтожить Легион.', 12, 0, 100, 1, 0, 66952, 115848, 1462, 1462, 0, 'Тень Акамы to Player');
INSERT INTO `creature_text` VALUES (108263, 0, 0, 'Король-Бог сокрушит тебя!', 14, 0, 100, 0, 0, 0, 97603, 0, 0, 0, 'Felskorn Warmonger to Felskorn Raider');
INSERT INTO `creature_text` VALUES (108283, 0, 0, 'Я выпущу тебе кишки.', 12, 0, 100, 0, 0, 54862, 96414, 0, 0, 0, 'Дикарь из племени Камня Силы to Player');
INSERT INTO `creature_text` VALUES (108304, 0, 0, 'We must free Keeper Remulos at once! I know a shortcut to the cave he is being held in. Come, $n. Follow me!', 12, 0, 100, 0, 0, 0, 114548, 0, 0, 0, 'Guviena Bladesong to Player');
INSERT INTO `creature_text` VALUES (108311, 3, 0, 'Иллидари, мы нужны в Мардуме! Выдвигаемся!', 14, 0, 100, 0, 0, 82813, 125228, 2401, 2401, 0, 'Кор\'вас Кровавый Шип to Player');
INSERT INTO `creature_text` VALUES (108326, 3, 0, 'Спасибо, $n. Я не подведу.', 12, 0, 100, 66, 0, 66944, 115838, 17475, 17475, 0, 'Аша Воронья Песнь to Player');
INSERT INTO `creature_text` VALUES (108337, 1, 0, 'За Азшару!', 12, 0, 100, 0, 0, 52729, 93050, 0, 0, 0, 'Древний шепот мороза to Player');
INSERT INTO `creature_text` VALUES (108337, 4, 0, 'Этот сад не для таких, как ты.', 12, 0, 100, 0, 0, 52728, 93048, 7529, 7529, 0, 'Древний шепот мороза to Player');
INSERT INTO `creature_text` VALUES (108339, 0, 0, 'Прости меня, Азшара!', 2, 0, 100, 0, 0, 0, 93656, 0, 0, 0, 'Древняя призывательница огня to Player');
INSERT INTO `creature_text` VALUES (108339, 1, 0, 'Во всем виноват Фарондис!', 12, 0, 100, 0, 0, 52904, 93653, 0, 0, 0, 'Древняя призывательница огня to Player');
INSERT INTO `creature_text` VALUES (108339, 1, 0, 'Останься с нами... навечно...', 12, 0, 100, 0, 0, 52903, 100972, 0, 0, 0, 'Древняя призывательница огня to Player');
INSERT INTO `creature_text` VALUES (108339, 3, 0, 'Почему королева покинула нас?', 12, 0, 100, 0, 0, 0, 93654, 149137, 149137, 0, 'Древняя призывательница огня to Player');
INSERT INTO `creature_text` VALUES (108340, 0, 0, 'Почему королева покинула нас?', 2, 0, 100, 0, 0, 0, 93654, 0, 0, 0, 'Древняя ледяная ведьма to Player');
INSERT INTO `creature_text` VALUES (108340, 1, 0, 'Во всем виноват Фарондис!', 12, 0, 100, 0, 0, 52904, 93653, 0, 0, 0, 'Древняя ледяная ведьма to Player');
INSERT INTO `creature_text` VALUES (108342, 3, 0, 'Все! Все! Мы больше не друзья.', 12, 0, 100, 18, 0, 67680, 114925, 8464, 8464, 0, 'Миллхаус Манашторм to Player');
INSERT INTO `creature_text` VALUES (108344, 0, 0, 'Он достал еще вина?', 12, 0, 100, 0, 0, 0, 113848, 0, 0, 0, 'Донатьен to Player');
INSERT INTO `creature_text` VALUES (108344, 1, 0, 'Донатьен торопливо делает глоток манавина.', 16, 0, 100, 0, 0, 0, 113851, 0, 0, 0, 'Донатьен to Player');
INSERT INTO `creature_text` VALUES (108345, 0, 0, 'Ч-что?', 12, 0, 100, 0, 0, 0, 113839, 0, 0, 0, 'Клотэр to Player');
INSERT INTO `creature_text` VALUES (108345, 1, 0, 'Клотэр неуверенно пробует вино, а затем делает большой глоток.', 16, 0, 100, 0, 0, 0, 113841, 0, 0, 0, 'Клотэр to Player');
INSERT INTO `creature_text` VALUES (108346, 0, 0, 'Но тебе надо...', 12, 0, 100, 0, 0, 0, 113820, 0, 0, 0, 'Лунетта to Player');
INSERT INTO `creature_text` VALUES (108346, 1, 0, 'Лунетта кивает и делает глоток.', 16, 0, 100, 0, 0, 0, 113822, 0, 0, 0, 'Лунетта to Player');
INSERT INTO `creature_text` VALUES (108347, 0, 0, 'Хвала Ночному Колодцу... Я уж думала, мне конец.', 12, 0, 100, 0, 0, 0, 113831, 0, 0, 0, 'Амбрена to Player');
INSERT INTO `creature_text` VALUES (108347, 1, 0, 'Амбрена делает глоток.', 16, 0, 100, 0, 0, 0, 113836, 0, 0, 0, 'Амбрена to Player');
INSERT INTO `creature_text` VALUES (108366, 0, 0, 'Вы слышите вдали карканье вороны, и снова становится тихо...', 16, 0, 100, 0, 0, 0, 114046, 0, 0, 0, 'Давно брошенный гиппогрифф to Player');
INSERT INTO `creature_text` VALUES (108366, 1, 0, 'You hear a faint caw in the distance and then silence...', 16, 0, 0, 0, 0, 0, 114046, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (108375, 0, 0, 'Дай мне осмотреть это чудище, и я выявлю его слабости.', 12, 0, 100, 1, 0, 66588, 113819, 0, 0, 0, 'Кира Злобная Душа to Player');
INSERT INTO `creature_text` VALUES (108375, 3, 0, 'Я впервые вижу подобную чешую.', 12, 0, 100, 1, 0, 66589, 113811, 7906, 7906, 0, 'Кира Злобная Душа to Player');
INSERT INTO `creature_text` VALUES (108385, 3, 0, 'Другое дело. Спасибо. Я так тебе благодарна.', 12, 0, 100, 0, 0, 0, 113823, 2105, 2105, 0, 'Лунетта to Player');
INSERT INTO `creature_text` VALUES (108386, 3, 0, 'Тут недалеко. Спасибо.', 12, 0, 100, 0, 0, 0, 113834, 5806, 5806, 0, 'Амбрена to Player');
INSERT INTO `creature_text` VALUES (108387, 3, 0, 'Вантир снова помог нам... как ему это удается?', 12, 0, 100, 0, 0, 0, 113852, 216, 216, 0, 'Донатьен to Player');
INSERT INTO `creature_text` VALUES (108388, 3, 0, 'Тихо ты... Поблагодаришь от меня Вантира?', 12, 0, 100, 0, 0, 0, 113843, 3430, 3430, 0, 'Клотэр to Player');
INSERT INTO `creature_text` VALUES (108389, 0, 0, 'Мрглрг, мрглмрлг грлмрлг?', 12, 0, 100, 0, 0, 416, 114372, 0, 0, 0, 'Мргрлллгрл to Player');
INSERT INTO `creature_text` VALUES (108389, 3, 0, 'Мрглгр! Грмлг мгрмлг грмлг. Мрглмрг?', 12, 0, 100, 0, 0, 419, 114374, 9731, 9731, 0, 'Мргрлллгрл to Player');
INSERT INTO `creature_text` VALUES (108390, 3, 0, 'Мрглрмлр? Грлмрлгр!', 12, 0, 100, 0, 0, 18075, 114362, 2150, 2150, 0, 'Мистер Мрглглрм to Player');
INSERT INTO `creature_text` VALUES (108397, 3, 0, 'I smell a cultist.', 12, 0, 100, 396, 0, 0, 0, 1706233, 1706233, 0, 'Stormwind Royal Guard to Player');
INSERT INTO `creature_text` VALUES (108401, 0, 0, 'Халдарон сказал, что ты придешь.', 12, 0, 100, 1, 0, 66554, 113865, 0, 0, 0, 'Кузнец Кирил to Player');
INSERT INTO `creature_text` VALUES (108401, 1, 0, 'Наверное, непросто было достать такую древесину и руду.', 12, 0, 100, 1, 0, 66555, 113870, 0, 0, 0, 'Кузнец Кирил to Player');
INSERT INTO `creature_text` VALUES (108401, 2, 0, 'С этим материалом трудно работать, но поверь, результат тебе понравится.', 12, 0, 100, 1, 0, 66556, 113874, 0, 0, 0, 'Кузнец Кирил to Player');
INSERT INTO `creature_text` VALUES (108401, 3, 0, 'Твое оружие будет острым и прочным. Оно пробьет любой доспех и никогда не подведет тебя в бою.', 12, 0, 100, 1, 0, 66557, 113876, 0, 0, 0, 'Кузнец Кирил to Player');
INSERT INTO `creature_text` VALUES (108401, 4, 0, 'Надо сказать, это мое лучшее творение.', 12, 0, 100, 1, 0, 66558, 113877, 0, 0, 0, 'Кузнец Кирил to Player');
INSERT INTO `creature_text` VALUES (108401, 5, 0, 'Смотри, чтобы оно не досталось врагу.', 12, 0, 100, 1, 0, 66559, 113878, 0, 0, 0, 'Кузнец Кирил to Player');
INSERT INTO `creature_text` VALUES (108410, 0, 0, 'Delivery of cannons and cannonballs directly from the great forge for you', 12, 0, 0, 0, 0, 0, 113880, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (108411, 3, 0, 'Думаешь, этого хватит?', 12, 0, 100, 0, 0, 67571, 113882, 12207, 12207, 0, 'Силгрин to Player');
INSERT INTO `creature_text` VALUES (108411, 3, 0, 'Они ждут тебя в таверне.', 12, 0, 100, 0, 0, 67575, 116279, 30492, 30492, 0, 'Силгрин to Player');
INSERT INTO `creature_text` VALUES (108411, 3, 0, 'Успокойся. Мы найдем ее.', 12, 0, 100, 0, 0, 67569, 113900, 55887, 55887, 0, 'Силгрин to Player');
INSERT INTO `creature_text` VALUES (108435, 3, 0, 'Come on out, ya scoundrel!', 12, 0, 100, 396, 0, 0, 114104, 55676, 55676, 0, 'Ружейник Стальгорна to Player');
INSERT INTO `creature_text` VALUES (108435, 3, 0, 'Somethin\' ain\'t right.', 12, 0, 100, 396, 0, 0, 114110, 326447, 326447, 0, 'Ironforge Rifleman to Player');
INSERT INTO `creature_text` VALUES (108435, 3, 0, 'Так, тут кто-то есть.', 12, 0, 100, 396, 0, 23504, 114103, 9129, 9129, 0, 'Стальгорнская ружейница to Player');
INSERT INTO `creature_text` VALUES (108446, 1, 0, 'Прости меня, Азшара!', 12, 0, 100, 0, 0, 52902, 93656, 0, 0, 0, 'Вневременный силомаг to Player');
INSERT INTO `creature_text` VALUES (108456, 0, 0, 'Если битвы не избежать, я буду рад сражаться за тебя.', 12, 0, 100, 0, 0, 69556, 120515, 0, 0, 0, 'Халдарон Светлое Крыло to Player');
INSERT INTO `creature_text` VALUES (108457, 0, 0, 'Поджарю с корочкой!', 12, 0, 100, 0, 0, 55210, 102137, 0, 0, 0, 'Бес морозной Скверны to Верховный маг Варгот');
INSERT INTO `creature_text` VALUES (108457, 0, 1, 'Сейчас я поджарю твои ноги!', 12, 0, 100, 0, 0, 55202, 96660, 0, 0, 0, 'Бес морозной Скверны to Player');
INSERT INTO `creature_text` VALUES (108457, 0, 2, 'Хватайте обед!', 12, 0, 100, 0, 0, 55202, 96653, 0, 0, 0, 'Бес морозной Скверны to Player');
INSERT INTO `creature_text` VALUES (108457, 0, 3, 'А вот и наш обед пожаловал!', 12, 0, 100, 0, 0, 55203, 96654, 0, 0, 0, 'Бес морозной Скверны to Player');
INSERT INTO `creature_text` VALUES (108457, 0, 4, 'На помощь! Тут плохиши!', 12, 0, 100, 0, 0, 55204, 96655, 0, 0, 0, 'Бес морозной Скверны to Player');
INSERT INTO `creature_text` VALUES (108457, 0, 5, 'Мы пожрем твою душу! И тело тоже.', 12, 0, 100, 0, 0, 55205, 96656, 0, 0, 0, 'Бес морозной Скверны to Player');
INSERT INTO `creature_text` VALUES (108460, 0, 0, 'Я жива? Спасибо!', 12, 0, 100, 0, 0, 0, 114018, 32355, 32355, 0, 'Раненый друид-хранительница to Player');
INSERT INTO `creature_text` VALUES (108460, 0, 1, 'Спасибо! Здорово, что есть такие искатели приключений, как ты.', 12, 0, 100, 0, 0, 0, 114017, 32355, 32355, 0, 'Раненый друид-хранительница to Player');
INSERT INTO `creature_text` VALUES (108460, 0, 2, 'Please, stop Shadow Xavius.', 12, 0, 100, 0, 0, 0, 114019, 0, 0, 0, 'Раненый друид-хранительница to Player');
INSERT INTO `creature_text` VALUES (108460, 0, 2, 'Прошу тебя, останови Тень Ксавия.', 12, 0, 100, 0, 0, 0, 114019, 0, 0, 0, 'Раненый друид-хранительница to Player');
INSERT INTO `creature_text` VALUES (108466, 3, 0, 'Я буду хорошо себя вести, правда!', 12, 0, 100, 0, 0, 0, 120280, 68043, 68043, 0, 'Напуганный сурамарский ребенок to Player');
INSERT INTO `creature_text` VALUES (108479, 3, 0, 'I ... I really thought we lost them for good.', 12, 0, 100, 0, 0, 0, 114063, 4629, 4629, 0, 'Эстинна Звездное Копье to Player');
INSERT INTO `creature_text` VALUES (108479, 3, 0, 'Что? Каллистия и Олотил здесь?', 12, 0, 100, 5, 0, 0, 114062, 1773, 1773, 0, 'Эстинна Звездное Копье to Player');
INSERT INTO `creature_text` VALUES (108481, 0, 0, 'Got some o\' the forge\'s best armor polish for the troops here, $n. Don\'t be shipping off without gettin\' some o\' those scuffs out first.', 12, 0, 0, 0, 0, 0, 114060, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (108490, 1, 0, 'Do not touch the candle!', 12, 0, 100, 0, 0, 54337, 102744, 0, 0, 0, 'Золотоискатель-дозорный to Player');
INSERT INTO `creature_text` VALUES (108490, 1, 0, 'А-а-а-а-а!', 12, 0, 100, 0, 0, 54335, 102746, 0, 0, 0, 'Золотоискатель-дозорный to Player');
INSERT INTO `creature_text` VALUES (108490, 1, 0, 'Нет!', 12, 0, 100, 0, 0, 72571, 135514, 0, 0, 0, 'Золотоискатель-дозорный to Player');
INSERT INTO `creature_text` VALUES (108490, 1, 0, 'Свеча! Мое!', 12, 0, 100, 0, 0, 54337, 102745, 0, 0, 0, 'Золотоискатель-дозорный to Player');
INSERT INTO `creature_text` VALUES (108491, 1, 0, 'Бррибитит!', 12, 0, 100, 0, 0, 54344, 102755, 0, 0, 0, 'Золотоискатель-надсмотрщик to Player');
INSERT INTO `creature_text` VALUES (108491, 1, 0, 'Иккиника!', 12, 0, 100, 0, 0, 54337, 102748, 0, 0, 0, 'Золотоискатель-надсмотрщик to Player');
INSERT INTO `creature_text` VALUES (108491, 1, 0, 'Ррибитак!', 12, 0, 100, 0, 0, 54340, 102749, 0, 0, 0, 'Золотоискатель-надсмотрщик to Player');
INSERT INTO `creature_text` VALUES (108492, 0, 0, 'Сила, которую я призову, привлечет к себе демонов.', 12, 0, 100, 1, 0, 66591, 114081, 0, 0, 0, 'Лиана Звездная Пыль to Player');
INSERT INTO `creature_text` VALUES (108492, 1, 0, 'Компоненты очень взрывоопасны. Если прервать ритуал, может произойти катастрофа.', 12, 0, 100, 1, 0, 66592, 114082, 0, 0, 0, 'Лиана Звездная Пыль to Player');
INSERT INTO `creature_text` VALUES (108492, 2, 0, 'I will do everything I can. Your task is to keep the demons away from me and my table.', 12, 0, 100, 1, 0, 66593, 114083, 0, 0, 0, 'Лиана Звездная Пыль to 108574');
INSERT INTO `creature_text` VALUES (108492, 3, 0, 'I\'m almost done. Another minute of quiet work - and it will be ready ...', 12, 0, 100, 1, 0, 66594, 114084, 0, 0, 0, 'Лиана Звездная Пыль to 108574');
INSERT INTO `creature_text` VALUES (108492, 4, 0, 'Вот и все, оружие готово.', 12, 0, 100, 1, 0, 66595, 114085, 0, 0, 0, 'Лиана Звездная Пыль to 108574');
INSERT INTO `creature_text` VALUES (108492, 5, 0, 'I cast my best spell.', 12, 0, 100, 1, 0, 66596, 114086, 0, 0, 0, 'Лиана Звездная Пыль to 108574');
INSERT INTO `creature_text` VALUES (108495, 3, 0, 'Ночнорожденный молчит.', 16, 0, 100, 0, 0, 0, 111977, 170599, 170599, 213348, 'Смелый горожанин to Смелый горожанин');
INSERT INTO `creature_text` VALUES (108496, 3, 0, 'Ваша магия – просто мерзость!', 12, 0, 100, 0, 0, 65942, 112852, 127995, 127995, 0, 'Смелая горожанка to Смелая горожанка');
INSERT INTO `creature_text` VALUES (108501, 3, 0, 'Meet at the hall. There\'s still work to be done.', 12, 0, 100, 1, 0, 0, 0, 878, 878, 0, 'Гарона Полуорчиха to Player');
INSERT INTO `creature_text` VALUES (108501, 3, 0, 'Встретимся в Оплоте. У нас много работы.', 12, 0, 100, 1, 0, 63561, 114092, 1271, 1271, 0, 'Гарона Полуорчиха to Player');
INSERT INTO `creature_text` VALUES (108527, 0, 0, 'Я Лорам... нет, Я РАЗЕЛИХ! Довольно, демон, ты подчинишься мне. Я одолею тебя!', 14, 0, 100, 463, 0, 0, 114730, 0, 0, 0, 'Лорам Многоног');
INSERT INTO `creature_text` VALUES (108527, 3, 0, 'НИЧТО НЕ СПАСЕТ ВАШ МИР ОТ ИСПЕПЕЛЕНИЯ!', 14, 8, 100, 463, 0, 0, 114731, 2158019, 2158019, 0, 'Лорам Многоног');
INSERT INTO `creature_text` VALUES (108567, 0, 0, 'Вурдалаки… Ну конечно!', 12, 0, 100, 0, 0, 69433, 115851, 0, 0, 0, 'Тассариан to Player');
INSERT INTO `creature_text` VALUES (108567, 1, 0, 'Сюда, живо!', 12, 0, 100, 0, 0, 69418, 114323, 0, 0, 0, 'Тассариан to Player');
INSERT INTO `creature_text` VALUES (108567, 2, 0, 'Кольтиру держат где-то здесь! Надо спешить!', 12, 0, 100, 0, 0, 69432, 115804, 0, 0, 0, 'Тассариан to Player');
INSERT INTO `creature_text` VALUES (108572, 0, 0, 'Почту за честь.', 12, 0, 100, 2, 0, 66946, 115837, 0, 0, 0, 'Кайн Ярость Солнца to Player');
INSERT INTO `creature_text` VALUES (108572, 1, 0, 'Вечные страдания. Никакой пощады Карии.', 12, 0, 100, 66, 0, 0, 107431, 0, 0, 0, 'Алтруис Страдалец to Player');
INSERT INTO `creature_text` VALUES (108572, 2, 0, 'Аллари вела отряд на поиски потерянных боевых клинков. Возможно, мы наконец нашли их.', 12, 0, 100, 1, 0, 0, 107681, 0, 0, 0, 'Алтруис Страдалец to Player');
INSERT INTO `creature_text` VALUES (108572, 3, 0, 'Встретимся у крепости.', 12, 0, 100, 1, 0, 0, 105832, 0, 0, 0, 'Алтруис Страдалец to Player');
INSERT INTO `creature_text` VALUES (108576, 0, 0, 'Быстрее, $gохотник:охотница; на демонов. Мне не нравится, как выглядит тот костер.', 12, 0, 100, 1, 0, 0, 115907, 0, 0, 0, 'Маласса Тень to Player');
INSERT INTO `creature_text` VALUES (108576, 1, 0, 'Увидимся на мосту впереди. Но сначала надо найти мое оружие.', 12, 0, 100, 1, 0, 0, 114333, 0, 0, 0, 'Маласса Тень to Player');
INSERT INTO `creature_text` VALUES (108578, 1, 0, 'Убью тебя – и смогу попасть в Чертоги Доблести.', 12, 0, 100, 0, 0, 0, 114303, 0, 0, 0, 'Рунный скальд Ролло to Player');
INSERT INTO `creature_text` VALUES (108578, 2, 0, 'Теперь мне нет места в Чертогах...', 12, 0, 100, 0, 0, 0, 114351, 0, 0, 210038, 'Рунный скальд Ролло to Player');
INSERT INTO `creature_text` VALUES (108579, 1, 0, 'Тебя на куски разорвет.', 12, 0, 100, 0, 0, 0, 114289, 0, 0, 0, 'Повелитель рун Рагнар to Player');
INSERT INTO `creature_text` VALUES (108579, 2, 0, 'Запомните мое имя.', 12, 0, 100, 0, 0, 0, 114342, 0, 0, 210038, 'Повелитель рун Рагнар to Player');
INSERT INTO `creature_text` VALUES (108580, 1, 0, 'Надо же, выйдет подходящая жертва. Ха-ха-ха!', 12, 0, 100, 0, 0, 0, 114290, 0, 0, 0, 'Рунный мудрец Флоки to Player');
INSERT INTO `creature_text` VALUES (108580, 2, 0, 'Забери меня, Сковальд.', 12, 0, 100, 0, 0, 0, 114349, 0, 0, 210038, 'Рунный мудрец Флоки to Player');
INSERT INTO `creature_text` VALUES (108583, 0, 0, 'We have brought crystals infused with the Light from the Exodar. Allow us a moment to enhance your weapon before you battle with the Legion.', 12, 0, 0, 0, 0, 0, 114258, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (108586, 0, 0, 'Sit, friend, and enjoy this meal. You cannot fight on an empty stomach.', 12, 0, 0, 0, 0, 0, 114259, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (108600, 0, 0, 'Жизнь почти покинула меня...', 12, 0, 100, 0, 0, 53219, 98904, 0, 0, 0, 'Sister of the Moon to Player');
INSERT INTO `creature_text` VALUES (108600, 0, 1, 'I ... I am saved!', 12, 0, 100, 0, 0, 53222, 93828, 0, 0, 0, 'Sister of the Moon to Player');
INSERT INTO `creature_text` VALUES (108600, 0, 2, 'Спасибо тебе!', 12, 0, 100, 0, 0, 53213, 136433, 0, 0, 0, 'Sister of the Moon to Player');
INSERT INTO `creature_text` VALUES (108607, 0, 1, 'Как вы посмели напасть на Мардум?', 12, 0, 100, 0, 0, 55191, 96662, 0, 0, 0, 'Страж Скверны - завоеватель to Player');
INSERT INTO `creature_text` VALUES (108607, 0, 2, 'Моя жизнь — служение.', 12, 0, 100, 0, 0, 55194, 102129, 0, 0, 0, 'Страж Скверны - завоеватель to Player');
INSERT INTO `creature_text` VALUES (108613, 0, 0, 'Мы можем победить Катра\'натира! Активируй камень, пока мы его сдерживаем!', 12, 0, 100, 0, 0, 66493, 114624, 0, 0, 0, 'Верховный маг Кадгар to Катра\'натир');
INSERT INTO `creature_text` VALUES (108613, 1, 0, 'Получилось! Теперь Катра\'натир заточен в камне души ночнорожденных!', 12, 0, 100, 0, 0, 66489, 114756, 0, 0, 0, 'Верховный маг Кадгар to 108897');
INSERT INTO `creature_text` VALUES (108614, 0, 0, 'Прекрасно! Теперь надо поскорее доставить камень в Оплот Хранителя.', 12, 0, 100, 0, 0, 66490, 114757, 0, 0, 0, 'Верховный маг Кейлек to 108897');
