INSERT INTO `creature_text` VALUES (108652, 0, 0, 'Мои лучники готовы. Они расстреляют любого демона, который к нам сунется.', 12, 0, 100, 1, 0, 66611, 115776, 0, 0, 0, 'Вериса Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (108652, 1, 0, 'Получилось?', 12, 0, 100, 6, 0, 66612, 115777, 0, 0, 0, 'Вериса Ветрокрылая to Player');
