INSERT INTO `creature_text` VALUES (108663, 0, 0, 'МНЕ НЕВЕДОМ СТРАХ! Я убью вас собственными руками. А тебе, $p, особый привет от Карии и Варедиса.', 14, 0, 100, 0, 0, 0, 114357, 0, 0, 0, 'Лохабер to 0');
INSERT INTO `creature_text` VALUES (108663, 1, 0, 'Если бы вы знали, что будет дальше...', 12, 0, 100, 0, 0, 0, 114376, 0, 0, 0, 'Лохабер to Player');
INSERT INTO `creature_text` VALUES (108672, 0, 0, 'Спасибо! Такое чувство, что я провел здесь целую вечность.', 12, 0, 100, 0, 0, 66496, 114427, 0, 0, 0, 'Верховный маг Варгот to Маг Скверны Алирония');
INSERT INTO `creature_text` VALUES (108692, 0, 0, 'If there is any chance of aid, then we will come. Stay, Dawnbreaker.', 12, 0, 100, 0, 0, 0, 114391, 0, 0, 0, 'Травар to Player');
INSERT INTO `creature_text` VALUES (108693, 0, 0, 'Tyrosus is a good man - count me in. It\'s too dangerous these days to not seek a bit of help.', 12, 0, 100, 0, 0, 0, 114400, 0, 0, 0, 'Орик Чистосердечный to Player');
