INSERT INTO `creature_text` VALUES (108782, 0, 0, 'Ему самое место вон там, над картой.', 12, 0, 100, 25, 0, 66987, 116325, 0, 0, 0, 'Белат Клинок Рассвета to Player');
INSERT INTO `creature_text` VALUES (108782, 3, 0, 'Это честь для меня, $n. Когда все начиналось, я и не думал, что стану одним из твоих ближайших соратников. Я не подведу.', 12, 0, 100, 2, 0, 66966, 115912, 0, 0, 0, 'Белат Клинок Рассвета to Player');
