INSERT INTO `creature_text` VALUES (108791, 0, 0, 'I hope we are not too late to save Keeper Remulos.', 12, 0, 100, 0, 0, 0, 114550, 0, 0, 0, 'Guviena Bladesong to Player');
INSERT INTO `creature_text` VALUES (108793, 0, 0, 'Невидимость длится недолго. Идем.', 12, 0, 100, 0, 0, 66488, 114522, 0, 0, 0, 'Верховный маг Варгот to Player');
