INSERT INTO `creature_text` VALUES (108825, 1, 0, 'С магией всегда такая проблема: никогда не знаешь, когда она обратится против тебя!', 14, 0, 100, 0, 0, 0, 122460, 0, 0, 0, 'Кудесник Маргосс to Аквос Освобожденный');
INSERT INTO `creature_text` VALUES (108825, 3, 0, 'Терпеть не могу водных элементалей... Особенно таких больших!', 14, 0, 100, 0, 0, 0, 122459, 0, 0, 0, 'Кудесник Маргосс to Аквос Освобожденный');
INSERT INTO `creature_text` VALUES (108825, 8, 0, 'Рыбалка потом, сначала помоги мне с этим элементалем!', 14, 0, 100, 0, 0, 0, 122458, 0, 0, 0, 'Кудесник Маргосс to Аквос Освобожденный');
INSERT INTO `creature_text` VALUES (108837, 0, 0, 'Силою Света я навсегда изгоню отсюда Плеть!', 12, 0, 100, 0, 0, 69427, 115333, 0, 0, 0, 'Главнокомандующий Годчайльд to Player');
