INSERT INTO `creature_text` VALUES (108872, 4, 0, 'В этом павильоне происходит дегустация. Подаем только лучшие сорта – многие поставляются во дворец.', 12, 0, 100, 0, 0, 66290, 114980, 0, 0, 0, 'Марго to Player');
INSERT INTO `creature_text` VALUES (108872, 5, 0, 'Попробуй сегодняшние образцы, не стесняйся. Как закончишь, мы продолжим экскурсию.', 12, 0, 100, 0, 0, 66291, 114982, 0, 0, 0, 'Марго to Player');
INSERT INTO `creature_text` VALUES (108875, 1, 0, 'Соблюдай порядок, или придется отвечать за последствия.', 12, 0, 100, 0, 0, 66022, 114599, 0, 0, 0, 'Надсмотрщица виноградников');
